INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('1', '붉은배지느러미발도요', 'Phalaropus fulicarius', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Phalaropus', '지느러미발도요속', 'fulicarius', '붉은배지느러미발도요', '유럽에서는 주로 엷은 회색인 겨울깃에, 아랫부분은 흰색이고, 부리는 어두운 색이며, 눈 주위에 검은색의 두꺼운 줄무늬가 있는 것을 볼 수 있다. 등에 줄무늬가 없는 것과 부리가 짧고 두꺼운 것으로 붉은목깝작도요(red-necked phalaropes)와 구분할 수 있다. 여름에 성체는 목 주위를 제외하고는 몸 아랫면이 붉은색이고, 머리는 검은색과 흰색의 줄무늬가 있으며, 부리는 주로 노란색이다. 겨울에는 바다에서 월동한다. 날씨가 좋지 않을 때에는 해안가에서 발견된다. 아이슬란드 서부와 시베리아에서 번식하고 아프리카와 남아메리카에서 월동한다. 유럽은 통과지이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001751', '21.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('2', '제비물떼새', 'Glareola maldivarum', 'Forster', '1795', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Glareolidae', '제비물떼새과', 'Glareola', '제비물떼새속', 'maldivarum', '제비물떼새', '도요목 제비물떼새과의 조류로 국내에는 봄과 가을에 매우 적은 수가 통과하는 나그네새이다. 몸길이 26.5cm. 날개는 폭이 좁으며 길어 접었을 때 꼬리 뒤로 돌출된다. 몸 윗면은 어두운 회갈색이며 날개는 검은색이다. 날 때 허리가 백색으로 보인다. 부리는 검은색이며 기부는 붉은색이다. 가슴과 배는 크림색 아랫배는 흰색이다. 멱은 흐린 황백색이며 가장자리에 검은색 선이 있다. 날 때 아랫날개덮깃이 적갈색이다. 작은 무리를 이루어 행동하며 날면서 파리나, 벌목의 곤충들을 잡아먹는다. 경쾌하고 빠르게 날아가는 모습이 제비와 유사하다. 둥지는 만들지 않고 땅 위에 그대로 산란한다. 산란기는 3~6월이며, 산란 개수는 2~3개이다. 시베리아 동북부, 몽골 북동부, 중국 동북부, 인도차이나반도, 인도, 필리핀, 대만에서 번식하고, 겨울에는 동남아시아에서 호주까지 월동한다. 국내에는 해안가의 풀밭, 하천, 농경지에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001754', '26.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('3', '괭이갈매기', 'Larus crassirostris', 'Vieillot', '1818', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Larus', '갈매기속', 'crassirostris', '괭이갈매기', '우리나라 무인도서에서 번식하고 겨울을 나는 흔한 텃새이다. 고양이 울음소리와 비슷한 소리를 낸다 하여 괭이갈매기로 이름 붙여졌다. 부리가 노랗고 끝이 붉은색이며, 날 때 꼬리 끝에 검은 띠가 있는 것이 특징이다. 몸은 전체적으로 흰색이며, 등과 날개덮깃은 진한 회색이다. 발과 눈은 노란색이며, 눈 테두리가 빨간색이다. 겨울에는 머리 뒤에 갈색의 줄무늬가 있다. 어린 새는 몸 전체가 갈색이며, 다리는 분홍색, 부리는 분홍색에 끝이 검은색이다. 서해안과 남해안의 무인도서에서 대규모 무리로 번식을 하며, 이른 봄부터 모여들어 8월까지 번식한다. 물고기 떼가 모이는 곳에 주로 모이고 해안, 강, 하구, 하천 등에서 볼 수 있으며, 물고기, 곤충, 물풀 등을 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001757', '46.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('4', '갈매기', 'Larus canus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Larus', '갈매기속', 'canus', '갈매기', '도요목 갈매기과에 속하는 조류이다. 날개 길이는 34-38cm이다. 여름깃은 머리가 흰색이고, 목과 배 및 꼬리는 흰색이다. 등은 연한 흰색, 꼬리는 흰색이고, 부리는 초록색을 띤 회색 또는 연두색이며 끝부분이 노란색이다. 겨울깃은 머리가 흰색에 어두운색의 세로무늬가 있다. 바닷가의 간사지, 암초, 바다 위 또는 강어귀, 어장에 살며 흔한 겨울새이다. 둥지는 해안의 작은 언덕이나 풀밭, 작은 섬 등에 떼를 지어 짓는다. 동해안, 남해안, 낙동강 하구, 한강 등지에 월동하고 세계적으로는 구북구 및 신북구 동부, 주로 북위 48-68도 사이에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001758', '43.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('5', '수리갈매기', 'Larus glaucescens', 'Naumann', '1840', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Larus', '갈매기속', 'glaucescens', '수리갈매기', '도요목 갈매기과의 새이다. 몸길이는 60~68cm이며, 날개 편 길이는 약 137mm이다. 수컷이 암컷보다 크다. 성체는 엷은 회흑색과 함께 흰색이다. 날개는 엷은 회색이며 작은 한 부분은 흰색이다. 부리는 크고 무거우며, 빨간 점이 있는 노란색이다. 눈 주위는 자줏빛이 도는 분홍색이다. 알은 담황색이다. 주로 해안의 염수 지역 및 만(灣), 강어귀, 섬, 바닷가, 개펄에서 서식한다. 알래스카에서는 큰 둥지를 틀며, 남쪽의 다른 지역에서는 그보다 작은 둥지를 튼다. 5월 말~7월에 2~3개의 알을 낳는다. 부화 기간은 26~28일이다. 부화 후 35~54일 지나면 날 수 있을 만큼 자란다. 완전한 성체가 되기까지는 4년이 걸린다. 수명은 20년이다. 북태평양 지역(알래스카~워싱턴 주)에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001759', '64.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('6', '흰갈매기', 'Larus hyperboreus', 'Gunnerus', '1767', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Larus', '갈매기속', 'hyperboreus', '흰갈매기', '도요목 갈매기과에 속하는 조류이다. 전체 길이는 70cm 정도로 등은 엷은 회색이며, 흰색의 첫째날개깃을 가진 대형갈매기이다. 부리는 황색이고 붉은 점이 있으며, 다리는 분홍색이다. 알은 밝은 갈색에 진한 갈색 무늬가 있다. 호수나 강가, 해안 등지에 살며 절벽에 이끼나 풀로 엮어 둥지를 만들며 알을 3개 정도 낳는다. 낙동강 하구에 서식하고 세계적으로는 알래스카, 캐나다 극지방·아극지방, 미국 북부지방에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001760', '70.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('7', '작은흰갈매기', 'Larus glaucoides', 'B.Meyer', '1822', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Larus', '갈매기속', 'glaucoides', '작은흰갈매기', '도요목 갈매기과의 새이다. 몸의 크기는 흰갈매기보다 뚜렷하게 작다. 부리는 짧고 부리 위와 아래의 꺾어진 각도도 작다. 부리 끝의 검은색은 기부로 갈수록 서서히 엷어지며 살구색으로 바뀐다. 흰갈매기는 부리 끝의 검은색 부분이 좁으며 기부의 살구색과 경계가 뚜렷하므로 구별된다. 머리 윗부분은 둥근 형태로 흰갈매기의 평평한 형태와 차이가 있다. 홍채의 색도 암갈색을 띠고 있다. 날개를 접고 있을 때 첫째날개깃의 끝은 꽁지 끝보다 뒤로 나온 것이 뚜렷하다. 흰갈매기는 꽁지 끝보다 약간 더 긴 편이다. 다리도 흰갈매기보다 짧아서 앉아 있을 때 키의 차이가 뚜렷하게 나타난다. 유럽 북서부, 북미 북동부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001761', '57.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('8', '옅은재갈매기', 'Larus smithsonianus', 'Coues', '1862', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Larus', '갈매기속', 'smithsonianus', '옅은재갈매기', '옅은재갈매기는 북아메리카에서 번식하는 대형 갈매기입니다. 성체는 흰색 몸에 회색 등과 날개를 가지며, 검은 날개 끝과 하얀 반점이 특징입니다. 어린 새는 회갈색으로 유럽재갈매기보다 진한 색을 띱니다. 다양한 서식지에서 발견되며, 해안, 호수, 강, 주차장, 쓰레기 처리장 등에서 생활합니다.  먹이는 무척추동물, 물고기, 심지어 다른 새의 알까지 포함한 폭넓은 종류를 섭취합니다. 주로 물 근처에 집단으로 둥지를 짓고 세 개의 알을 땅 위에 낳습니다.  성체는 몸길이 53~66cm 정도이며, 날개 길이는 120~155cm입니다. 다리는 주로 분홍색이나 때때로 청색이나 노란빛을 띠기도 합니다. 부리는 노란색이고 아래턱에 빨간 점이 있습니다.  옅은재갈매기의 번식지는 북미 대부분에 걸쳐 있으며, 겨울에는 멕시코나 중미, 카리브해까지 남하하기도 합니다. 다양한 울음소리를 가지고 있으며, 위험을 알리거나 짝을 부르기 위한 소리를 냅니다.  19세기에는 사냥으로 인해 개체수가 감소했으나, 이후 보호 조치와 먹이 자원의 증가로 인해 개체수가 빠르게 회복되었습니다. DDT 사용의 영향으로 난각이 얇아지는 문제를 겪기도 했습니다. 지금은 일부 지역에서 개체수가 감소하기도 합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001764', '59.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('9', '줄무늬노랑발갈매기', 'Larus heuglini', 'Bree', '1876', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Larus', '갈매기속', 'heuglini', '줄무늬노랑발갈매기', '도요목 갈매기과의 새이다. 등판이 재갈매기나 한국재갈매기에 비해 진한 특징이 있다. 등 무늬는 괭이갈매기보다도 더 진하다. 발이 노란 특징을 보이는데, 번식기에 가까워지면서 발이 아주 노란 개체가 더 많이 보인다. 덩치의 경우, 재갈매기에 비해 아주 작은 개체가 있다. 특히 작은 것도 있는데, 대부분 눈이 작고 머리형이 날렵하다. 아래 부리의 붉은 점도 재갈매기에 비해 큰 것이 특징이다. 한국재갈매기와는 달리 민물은 선호하지 않고 대부분 바닷물에서 특히 갯벌에서 많이 보인다. 상당히 늦게 번식깃으로 갈아입는다. 한국재갈매기보다 한 달 이상 늦게 번식한다. 시베리아 최북단에서 번식하고 우리나라 제주도 차귀도~서해안, 동해안 묵호 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001766', '61.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('10', '큰재갈매기', 'Larus schistisagus', 'Stejneger', '1884', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Larus', '갈매기속', 'schistisagus', '큰재갈매기', '도요목 갈매기과의 새로 몸길이 61~66cm가량이다. 재갈매기와 거의 비슷하나 윗등이 흰색이 아니고 어두운 석판색인 점에서 쉽게 구별된다. 날개깃은 검은색이고 바깥쪽 첫째날개깃의 끝부분에는 흰색의 무늬가 있으며 날개에는 회갈색의 띠가 있다. 어린 새는 몸의 윗면이 연한 갈색이고 거무스름한 세로무늬가 있으며 몸의 아랫면은 회색, 턱 밑은 흰색이다. 꽁지는 갈색이고 끝부분은 흰색이며 기부에는 어두운 갈색의 대리석 모양의 무늬가 있다. 흔하지 않은 겨울새이다. 떼 지어 먹이를 찾는다. 둥지는 섬이나 바닷가 부근의 벼랑 위, 해안의 암초 위, 호반의 땅 위, 방파제, 평탄한 땅 위, 모래밭, 풀밭 등에 집단적으로 짓고 산란한다. 알은 5월하순~7월에 3~4개를 낳는다. 개, 고양이, 새 등의 시체, 물오리나 바닷새의 알, 죽은 물고기 등을 먹으며 갑각류, 연체동물, 환형동물, 곤충류 등도 먹는다. 북태평양 서부, 오호츠크 해안, 캄차카, 사할린 등지에 분포한다. 우리나라, 중국, 일본 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001767', '63.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('11', '북극흰갈매기', 'Pagophila eburnea', 'Phipps', '1774', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Pagophila', '북극흰갈매기속', 'eburnea', '북극흰갈매기', '몸 크기는 다른 괭이갈매기에 비해 작은 편이다. 몸은 전체적으로 흰색이며, 부리 끝은 노란색, 부리 기부는 검은색을 띠며, 눈 앞 부분은 검은색이다. 등과 날개 윗면에는 검은색 반점이 산재해 있다. 첫째날개깃에도 검은색 반점이 있다. 유라시아 대륙, 북미 북극권에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001775', '41.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('12', '목테갈매기', 'Xema sabini', 'Sabine', '1819', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Xema', '목테갈매기속', 'sabini', '목테갈매기', '우리나라에서는 미조이다. 몸길이는 33~36cm이다. 여름깃은 머리가 잿빛이며 아랫목 흰색 부위와의 경계에 검은색 목테가 있다. 겨울깃의 이마는 흰색이고 머리의 나머지 부분은 거무스름하다. 윗목에 가로띠가 있다. 첫째날개깃은 검은색이고 둘째날개깃에 흰색의 넓은 삼각형 띠가 있다. 날개 앞가장자리는 검은색이다. 부리는 검은색, 부리 끝은 노란색, 다리는 잿빛이다. 꽁지는 제비꽁지 모양이다. 5월하순~7월상순에 한배에 알 2~3개를 낳는다. 먹이는 주로 곤충을 잡아먹고 연체동물과 물고기도 먹는다. 번식기에는 습한 툰드라에 서식하며, 비번식기에는 대개 트인 바다에서 생활한다. 겨울에는 열대에서 생활한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001777', '34.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('13', '세가락갈매기', 'Rissa tridactyla', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Rissa', '세가락갈매기속', 'tridactyla', '세가락갈매기', '흔하지 않은 겨울새이나 강원도 동해안에 큰 무리가 해마다 찾아와 월동한다. 몸길이는 41cm이다. 날개 끝과 다리는 검은 갈색이다. 뒷발가락이 흔적만 남아 있는 점이 큰 특징이다. 부리는 노란색이고 등은 회색, 아랫면은 흰색이다. 섬 해안 또는 암초의 절벽이나 벼랑에 집단으로 둥지를 튼다. 산란기는 5~6월로 한배에 알을 1~3개 낳는다. 알래스카, 캄차카, 사할린, 쿠릴 열도, 알류샨 열도 등지에서 번식하고 한국, 일본에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001779', '41.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('14', '긴꼬리제비갈매기', 'Sterna dougallii', 'Montagu', '1813', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Sterna', '제비갈매기속', 'dougallii', '긴꼬리제비갈매기', '긴꼬리제비갈매기는 도요목 갈매기과에 속하는 중소형 크기의 새로 몸길이는 약 33-36cm, 날개길이는 67-76cm입니다. 이 새는 특히 여름 깃에서 분홍색을 띄는 몸 아랫면 덕분에 이름을 얻었으며, 부리의 검은색과 붉은색 기초가 특징입니다. 여름철에는 아주 얇고 긴 꼬리 깃과 오렌지색 다리를 가지고 있으며, 겨울철에는 이마가 흰색으로 변합니다.    주로 바다에서 어류를 획득하기 위해 다이빙하며, 드물게 해안 근처의 담수 지역을 방문합니다. 번식지는 주로 해안과 섬의 콜로니에서 운기를 이루며, 때때로 다른 바다새들과 함께 번식합니다. 둥지는 주로 지면의 움푹한 곳에 자리잡고, 보통 1-2개의 알을 낳습니다.    긴꼬리제비갈매기는 일찍이 모피 때문에 수렵되었으나, 최근에는 큰 갈매기류의 경쟁과 포식으로 인해 개체 수가 감소되었습니다. 보호 활동을 통해 어떤 지역에서는 번식률이 상승하고 있습니다. 주요 번식지는 유럽의 아일랜드 Rockabill 섬입니다. 이 새는 현재 국제적으로는 주요 관심 대상이 아니지만, 다양한 국가에서는 멸종위기 또는 위협종으로 분류되어 보호되고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001784', '34.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('15', '제비갈매기', 'Sterna hirundo', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Sterna', '제비갈매기속', 'hirundo', '제비갈매기', '도요목 갈매기과의 새로 몸길이 35.5cm, 날개 편 길이 70~81cm이다. 암, 수가 같은 빛깔이다. 여름깃은 이마와 머리 위, 뒷머리, 옆머리 및 눈 앞의 위쪽 등은 모두 검은색이다. 눈 앞의 아래쪽과 귀덮깃, 뺨, 턱 밑, 목 및 목 옆은 흰색이다. 뒷목의 위쪽은 검은색이다. 아래쪽은 회백색이고 어깨깃과 등 및 허리는 연한 회색이며 가슴과 배겨드랑이, 꽁지밑덮깃은 흰색이다. 주로 호수, 하천 등지에 산다. 철새의 일종으로 겨울새에 속하며 우리나라에서는 해안과 하구에 흔한 나그네새이다. 이동할 때에는 30~100마리씩 떼 지어 이동한다. 둥지는 호수, 하천의 갈대와 왕골 등에 집단적으로 마른 풀줄기를 높게 쌓아 화산 모양으로 짓고 산란한다. 5월상순~8월상순, 2~3개 산란한다. 작은 물고기, 곤충류, 갑각류 등을 먹는다. 시베리아 동부, 캄차카, 사할린, 쿠릴 열도 등지에서 번식한다. 열대 아프리카 동부, 동남아시아, 오스트레일리아 등지에서 월동한다. 우리나라를 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001785', '35.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('16', '구레나룻제비갈매기', 'Chlidonias hybrida', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Chlidonias', '흰죽지제비갈매기속', 'hybrida', '구레나룻제비갈매기', '몸길이는 23~25cm이고, 체중은 100g이다. 암수는 외형이 비슷하다. 수컷이 암컷에 비해 미세하게 부리와 날개가 더 길다. 이마와 정수리, 목덜미는 검은색이고, 눈 밑에 확장된 부분과눈과 윗부리 사이에서부터 귀를 덮는 부분에 좁은 흰 줄무늬가 있다. 늪이나 습지대, 부유식물이 많은 강에 서식한다. 아프리카, 프랑스, 러시아, 중국에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001790', '24.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('17', '흰죽지제비갈매기', 'Chlidonias leucopterus', 'Temminck', '1815', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Chlidonias', '흰죽지제비갈매기속', 'leucopterus', '흰죽지제비갈매기', '도요목 갈매기과의 조류로 몸길이 25cm쯤이다. 머리와 목과 윗등은 검은색이나 겨울에는 흰색이고, 머리 위와 윗목에 검은색의 무늬가 있다. 꽁지위덮깃과 꽁지는 흰색이며, 배 쪽은 검은색이나 겨울에는 흰색이다. 아래 등과 허리는 회색이며, 부리는 여름에는 붉은색이고, 겨울에는 검은색이다. 번식지는 호수, 습지, 섬, 바닷가, 하천 어귀 등지이다. 온대 지역에서 번식한다. 우리나라에서는 드문 나그네새이다. 둥지는 집단으로 짓되, 수면의 수초 위에 갈대, 왕골 등의 줄기와 수초 등을 쌓고 산란한다. 유럽 중남부, 아시아 중부, 아프리카, 유럽 동부, 아무르, 사할린, 구북구 등지에서 번식한다. 오스트레일리아, 뉴질랜드 등지를 거쳐 가서 아프리카, 열대 아시아 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001791', '25.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('18', '검은제비갈매기', 'Chlidonias niger', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Chlidonias', '흰죽지제비갈매기속', 'niger', '검은제비갈매기', '도요목 갈매기과에 속하는 조류이다. 몸길이는 20-60cm이고 수컷이 암컷보다 약간 크다. 부리, 머리와 가슴 부분은 검은색이고, 어깨깃은 흰색에 가깝다. 어깨, 등과 날개덮깃은 회색이고 첫째날개깃은 흰 부분이 있는 진한 회색깃이며 검붉은 다리 색을 가지고 있다. 연안이나 먼바다에 살며 봄과 가을에 지나가는 나그네새이다. 멸치 같은 작은 물고기나 오징어, 새우 등의 갑각류를 먹는다. 둥지는 얕은 물가 나무 위나 튀어나온 바위에 풀줄기를 높게 쌓아 짓는다. 1-3개의 알을 낳는다. 바닷가나 하구에서 항상 무리를 지어 생활한다. 연안 또는 먼 바다에 서식하고 세계적으로는 온대 지역에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001792', '40.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('19', '남극도둑갈매기', 'Stercorarius maccormicki', 'Saunders', '1893', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Stercorariidae', '도둑갈매기과', 'Stercorarius', '도둑갈매기속', 'maccormicki', '남극도둑갈매기', '남극도둑갈매기는 갈매기목도둑갈매기과에 속하는 대형 바닷새입니다. 몸길이는 약 53cm로, 성체는 회갈색의 몸 윗면과 희거나 밀짚색의 머리와 아랫면이 특징적입니다. 남극 해안에서 번식하며 보통 11월과 12월에 두 개의 알을 낳습니다. 겨울에는 태평양, 인도양, 대서양을 따라 이동합니다. 이러한 새는 주로 물고기를 먹으며, 갈매기와 제비갈매기를 괴롭혀 낚아챈 먹이를 빼앗는 경우도 흔합니다. 남극도둑갈매기는 다른 새들과 토끼, 그리고 사체를 먹기도 합니다. 산란기를 제외한 나머지 기간에는 주로 강압적인 방식으로 먹이를 얻습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001794', '53.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('20', '넓적꼬리도둑갈매기', 'Stercorarius pomarinus', 'Temminck', '1815', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Stercorariidae', '도둑갈매기과', 'Stercorarius', '도둑갈매기속', 'pomarinus', '넓적꼬리도둑갈매기', '넓적꼬리도둑갈매기는 겨울철에 비교적 따뜻한 바다에서 월동하는 철새이다. 주요 서식지는 유라시아와 북아메리카의 툰드라 지역으로, 북극의 섬과 툰드라에서 번식하며 풀로 만든 둥지에 2~3개의 알을 낳는다. 몸길이는 약 46~67cm이며 날개 길이는 110~138cm이다. 여름철 번식기의 성체는 길게 늘어진 중앙 꼬리 깃을 지니고 있다. 어두운 갈색 등과 주로 흰색인 하체, 검은 머리와 목의 황백색 빛깔이 특징적이다. 이 새는 주로 물고기, 쓰레기, 기타 새의 먹이를 먹으며 다른 새의 먹이를 빼앗는 행동으로 유명하다. 이들이 다소 공격적으로 둥지를 방어하기 때문에, 사람이나 침입자가 접근할 때 머리 위로 날아드는 행동을 보인다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001797', '56.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('21', '북극도둑갈매기', 'Stercorarius parasiticus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Stercorariidae', '도둑갈매기과', 'Stercorarius', '도둑갈매기속', 'parasiticus', '북극도둑갈매기', '도요목 도둑갈매기과에 속하는 조류이다. 전체 길이는 46cm 정도이다. 중앙 꼬리깃털이 곧고 뾰족하게 나와 있다. 부리는 굵고 짧다. 어미 새는 등이 갈색이며 배는 흰색 또는 약간 거무스름하다. 머리와 다리는 검은색이다. 알은 올리브 갈색 바탕에 어두운 갈색 점이 있다. 여름에는 툰드라 지대의 호수 근처에서 생활하고, 다른 시기에는 바다에서 보낸다. 바위 사이 땅 위에 풀로 엮어 만든 움푹 팬 둥지를 만들어 알을 낳는다. 포항에서 발견된 적이 있으며 세계적으로는 알류샨, 알래스카 북부, 캐나다, 그린란드 남부, 캐나다 중부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001798', '46.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('22', '긴꼬리도둑갈매기', 'Stercorarius longicaudus', 'Vieillot', '1819', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Stercorariidae', '도둑갈매기과', 'Stercorarius', '도둑갈매기속', 'longicaudus', '긴꼬리도둑갈매기', '긴꼬리도둑갈매기는 도둑갈매기과에 속하는 조류로, 북극 지방에서 주로 발견됩니다. 이 새는 성체일 때 긴 꼬리와 회색의 등이 특징적입니다. 몸길이는 38-58cm로, 꼬리가 전체 길이의 상당 부분을 차지하며 최대 29cm까지 자랄 수 있습니다.    주로 바다 위에서 물고기와 작은 먹이를 잡아먹으며, 번식기에는 북극 지역에서 레밍을 주요 먹이로 삼습니다. 다른 바닷새를 쫓아가 먹이를 빼앗는 행동으로도 알려져 있습니다. 흔히 북극제비갈매기와 Sabine 갈매기에게서 먹이를 빼앗습니다.    번식지는 유라시아와 북미의 고위도 지역으로, 건조한 툰드라 지대나 높은 평원에 둥지를 틉니다. 두 개의 올리브 갈색 알을 낳으며, 번식지에서는 크게 울부짖는 소리를 냅니다. 겨울에는 주로 남반구의 대서양과 태평양에서 지내며, 대개에 걸쳐 개방된 바다 위에서 생활합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001799', '48.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('23', '큰부리바다오리', 'Uria lomvia', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Alcidae', '바다오리과', 'Uria', '바다오리속', 'lomvia', '큰부리바다오리', '큰부리바다오리는 바다오리과에 속하는 북반구의 극지 및 아극지 지역에 분포하는 조류입니다. 몸길이는 40~48cm이며 날개 길이는 64~81cm입니다. 주로 바다에서 생활하며, 번식기에는 해안가 절벽에 큰 집단을 이루어 번식합니다. 몸 윗면과 목, 머리는 검은색이며, 아랫면은 흰색입니다. 부리는 두텁고 뾰족합니다. 번식기는 바위 틈새나 절벽 위에 알을 한 개씩 낳으며, 부모가 번갈아가며 알을 품고, 새끼를 기릅니다. 주식은 주로 어류와 갑각류로, 깊은 잠수를 통해 먹이를 사냥합니다. 큰부리바다오리는 기름 유출 및 어획과 같은 인간 활동으로 인해 개체 수에 변동이 있지만 세계적으로는 위협받지 않는 상태입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001802', '44.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('24', '바다오리', 'Uria aalge', 'Pontoppidan', '1763', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Alcidae', '바다오리과', 'Uria', '바다오리속', 'aalge', '바다오리', '도요목 바다오리과에 속하는 조류이다. 여름깃은 머리와 뒷목과 등 쪽이 어두운 회색이고, 목은 갈색이다. 둘째날개깃의 끝부분과가슴 및 배는 흰색이며, 날개의 안쪽도 흰색이다. 겨울깃은 옆머리와 목과 뺨 그리고 옆목이 흰색이고, 부리는 길며 곧고 검은색을 띤다. 어린 새는 어미 새의 겨울깃과 비슷하다. 바닷가의 섬에 있는 암초, 암벽 등에 살며 여름철새이다. 바다 위에서 헤엄도 치고 잠수도 한다. 수백에서 수만 마리씩 큰 떼를 지어 집단으로 번식한다. 둥지를 짓지 않고 암벽이나 암초 위에 직접 알을 낳는다. 물고기와 게, 새우 등의 갑각류, 연체동물, 벼과 식물의 열매를 먹고 산다. 동해 연안에서 관찰되며 세계적으로는 동아시아, 유럽, 북미에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001803', '42.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('25', '알락쇠오리', 'Brachyramphus perdix', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Alcidae', '바다오리과', 'Brachyramphus', '알락쇠오리속', 'perdix', '알락쇠오리', '우리나라에 매우 드물게 오는 겨울새로 몸길이 25cm이다. 주로 먼 바다에 서식하므로 해안에서는 보기 힘들다. 여름깃의 경우, 몸 윗면은 어두운 갈색이며, 아랫면은 갈색과 흰색 얼룩무늬가 많다. 겨울깃은 어깨깃이 흰색이다. 몸 윗면은 검게 보이며, 몸 아랫면은 흰색이다. 번식기는 4~6월로 고산지대 땅 위에다 알 1개를 낳는다. 단독으로 생활하거나 또는 10~15개체의 작은 무리를 짓는다. 동해안 일대의 정치망 어업, 기름유출에 의한 해양오염 등의 외부 위협 요인이 많다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001807', '25.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('26', '바다쇠오리', 'Synthliboramphus antiquus', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Alcidae', '바다오리과', 'Synthliboramphus', '바다쇠오리속', 'antiquus', '바다쇠오리', '우리나라에서는 텃새로 섬에서 번식하는 것이 많다. 몸길이 약 25cm이다. 암수 같은 빛깔이다. 머리 위와 목덜미는 검은색이며, 멱과 가슴, 몸 아랫면은 흰색이다. 바닷가, 육지 연못 등에서 생활한다. 겨울철에는 3~4마리 또는 20~30마리 이상 수천 마리의 큰 떼로 바다 위를 줄지어 떠다니며 잠수도 한다. 산란기는 우리나라에선 3월 중순~4월 중순이나, 다른 지역에서는 6~7월에 산란하며, 산란수는 1~2개이고 때로는 3개인 경우도 있다. 작은 물고기나 갑각류, 연체동물(특히 조개)을 잡아먹는다. 강원도, 경상북도, 전라남도, 제주도 등지에서 볼 수 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001809', '25.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('27', '뿔쇠오리', 'Synthliboramphus wumizusume', 'Temminck', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Alcidae', '바다오리과', 'Synthliboramphus', '바다쇠오리속', 'wumizusume', '뿔쇠오리', '뿔쇠오리는 「멸종위기 야생생물 Ⅱ급」의 바다오리과 조류로, 우리나라의 일부 도서 지역에서 번식하는 텃새이다. 머리의 뿔깃이 특징이며, 주로 갑각류나 작은 물고기 등을 즐겨 먹는다. 일본의 동남부에서 주로 번식하며, 국내 번식 개체군은 매우 적은 것으로 알려져 있다. 먹이 부족, 다른 종에 의한 포식이 종의 감소 원인이 되고 있으며, 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001810', '25.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('28', '작은바다오리', 'Aethia pusilla', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Alcidae', '바다오리과', 'Aethia', '뿔바다오리속', 'pusilla', '작은바다오리', '도요목 바다오리과의 새이다. 몸 크기는 15cm가량으로 참새만 하며 바다오리류 중 가장 작다. 등과 날개는 짙은 갈색에서 검은색 깃털로 덮여 있다. 가슴과 아랫부분에는 하얀 반점이 있다. 여름에는 눈 뒤에 하얀색 솜털이 있다. 부리는 어두운 빨간색이며 눈은 노란색이다. 꽁지는 매우 짧고 다리는 물갈퀴가 있는 회갈색이다. 암수의 외형은 같다. 암벽 해안에 둥지를 만들며 해안에 산다. 물 표면으로 잠수하여 주걱으로 뜨듯 수생 무척추동물을 잡아먹는다. 지면 바위 벽 등의 좁고 깊게 파인 곳에 1개의 알을 낳는다. 한 달 안에 부화하여 한 달 동안 육추한다. 암벽에 무리를 이루고 살며 산란하기 위해 땅에 내려온다. 알래스카 해안에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001812', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('29', '흰수염바다오리', 'Cerorhinca monocerata', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Alcidae', '바다오리과', 'Cerorhinca', '흰수염바다오리속', 'monocerata', '흰수염바다오리', '기러기목 오리과의 여름철새이다. 몸의 각 부위 길이는 날개 172~182mm, 꽁지 45~62mm, 발목 28~32mm, 부리 32~39mm이다. 몸 색은 암수가 같은 빛깔이다. 등 쪽은 검은 갈색이며, 배 쪽은 흰색에 약간의 어두운 회색의 무늬가 있다. 옆얼굴에는 두 줄의 흰 깃이 있다. 턱 밑과 목, 윗가슴 및 겨드랑이는 갈색을 띤 어두운 회색이다. 겨울깃은 가슴과 배가 흰색이며 무늬가 없다. 알은 광택이 없는 회백색 바탕에 연한 갈색이나 어두운 회색의 줄무늬와 점이 약간 산재하여 있다. 바닷가에 있는 섬이나 해양에서 생활한다. 헤엄과 잠수를 하면서 먹이를 찾는다. 알은 4월상순부터 6월상순까지 1개를 낳는다. 한국(평안북도 엽도, 강원도 통천), 알래스카(남부), 캘리포니아, 아무르, 사할린, 일본(북부) 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001814', '39.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('30', '사막꿩', 'Syrrhaptes paradoxus', 'Pallas', '1773', 'Chordata', '척삭동물문', 'Aves', '조강', 'Columbiformes', '비둘기목', 'Pteroclidae', '사막꿩과', 'Syrrhaptes', '사막꿩속', 'paradoxus', '사막꿩', '몸길이 38cm이다. 등은 회색 바탕에 검은 얼룩 반점들이 있다. 머리는 회색이고, 얼굴은 노란색이다. 가슴은 회색이고 가슴 아래 더 짙은 회색 가로띠가 있다. 배에는 검은색의 넓은 가로띠가 있고 그 아래쪽은 회색이다. 암컷은 가슴에 가로띠가 없다. 하천가나 하구 삼각주의 모래 언덕, 평원, 초원 등지에 서식한다. 땅 위에 무리를 지어 4~6m 간격을 두고 둥지를 틀고, 알자리에 마른풀을 깐다. 4월 중순~6월 말에 한배에 2~4개의 알을 낳는다. 우리나라에는 한강 하류와 낙동강 하구에 미조(迷鳥)로 날아온 적이 있다. 중앙아시아의 건조한 스텝 지대에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001818', '38.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('31', '낭비둘기', 'Columba rupestris', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Columbiformes', '비둘기목', 'Columbidae', '비둘기과', 'Columba', '흑비둘기속', 'rupestris', '낭비둘기', '양비둘기는 「멸종위기 야생생물 Ⅱ급」의 비둘기과 조류이며 희귀한 텃새다. 전 세계적으로 집단 규모는 밝혀져 있지 않으며, 집비둘기와의 경쟁 및 교잡으로 감소추세로 추정되나 일부 지역은 여전히 흔한 종으로 간주된다. 국내에서의 감소 원인도 집비둘기와의 교잡으로 추정되고 있다. 낭비둘기, 굴비둘기라고도 한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001821', '32.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('32', '분홍가슴비둘기', 'Columba oenas', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Columbiformes', '비둘기목', 'Columbidae', '비둘기과', 'Columba', '흑비둘기속', 'oenas', '분홍가슴비둘기', '몸 크기는 집비둘기와 비슷하거나 다소 작다. 전체적으로 회색이나, 뒷목은 녹색이며, 멱의 아래쪽에서 가슴 윗부분까지는 탁한 분홍색이다. 날개를 접었을 때 날개 윗면에 짧은 검은색 줄이 2개 있다. 날 때 날개 윗면의 중앙부는 흐린 회색이 뚜렷하게 보이며, 날개 가장자리는 검은색이다. 중국 서북부에서 번식하고, 한국 남부 지역과 일본에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001822', '31.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('33', '흑비둘기', 'Columba janthina', 'Temminck', '1830', 'Chordata', '척삭동물문', 'Aves', '조강', 'Columbiformes', '비둘기목', 'Columbidae', '비둘기과', 'Columba', '흑비둘기속', 'janthina', '흑비둘기', '비둘기목 비둘기과에 속하는 조류이다. 몸길이는 약 40cm로 우리나라의 비둘기류 중에서 가장 크다. 암컷과 수컷이 색이 같고, 햇빛이 드는 곳에서 보면 전체적으로 광택이 있는 보라색으로, 목뒤 쪽은 진줏빛이 나는 녹색이다. 그늘진 곳에서는 검은색으로 보인다. 다리는 붉은색이다. 어린 개체는 목뒤에 녹색 광택이 없다. 텃새이자 여름철새이다. 서남해에서는 2월 말부터 상록활엽수가 있는 숲에 둥지를 만들며, 둥지의 높이는 지면으로부터 평균 3.4m이다. 주변의 나뭇가지를 이용해 조잡한 접시 모양으로 둥지를 만든다. 알은 보통 1개를 낳으며, 알 색깔은 흰색으로타원형이다. 상록활엽수의 열매를 주로 먹으며 보통 마가목, 덧나무, 우묵사스레피, 보리밥나무, 후박나무 열매 등이다. 울릉도 및 남해안 도서에 서식하고 세계적으로는 일본, 러시아 동부, 산둥지방, 타이완에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001823', '40.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('34', '염주비둘기', 'Streptopelia decaocto', 'Frivaldszky', '1838', 'Chordata', '척삭동물문', 'Aves', '조강', 'Columbiformes', '비둘기목', 'Columbidae', '비둘기과', 'Streptopelia', '멧비둘기속', 'decaocto', '염주비둘기', '비둘기목 비둘기과의 새로 몸길이는 33cm쯤이다. 암컷과 수컷의 이마는 다소 장미색을 띤 엷은 잿빛 갈색이고, 머리꼭대기와 뒷머리는 장미색을 띤 엷은 잿빛 갈색이다. 귀깃과 뺨은 장미색을 띤 엷은 잿빛 흰색이며, 턱 밑과 멱은 장미색을 띤 엷은 잿빛이다. 가슴과 배는 장미색을 띤 잿빛이고, 아래허리와 아래꽁지덮깃은 푸른색이 낀 잿빛이다. 부리는 광택이 없는 검은색이고 다리는 붉은색이다. 인가 부근의 소림, 대나무 숲, 건물 주변의 나무 등에서 산다. 소나무, 삼나무, 대나무 위나 건물의 선반처럼 불쑥 나온 곳에 둥지를 튼다. 산란기는 3~7월이며, 한배의 산란수는 2개이다. 식물의 씨나 열매를 즐겨 먹지만 사람이 버린 빵 조각과찌꺼기 등도 먹는다. 그 밖에 야채류, 딸기류, 벚나무 열매 등을 먹으며, 일부 무척추동물과 곤충류 등도 잡아먹는다. 우리나라 서해 도서, 홍도 등 전역에 서식한다. 세계적으로는 유라시아 대륙의 서유럽에서 동아시아에 이르는 넓은 지역에 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001826', '33.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('35', '홍비둘기', 'Streptopelia tranquebarica', 'Hermann', '1804', 'Chordata', '척삭동물문', 'Aves', '조강', 'Columbiformes', '비둘기목', 'Columbidae', '비둘기과', 'Streptopelia', '멧비둘기속', 'tranquebarica', '홍비둘기', '비둘기목 비둘기과의 조류로 몸길이는 23cm 정도이다. 암수 동종이형이다. 수컷은 머리가 녹회색이며 목에 검은 테가 있다. 등 위와 날개, 외피는 짙은 와인 빛을 띤 붉은색이며 아래로 갈수록 엷어진다. 암컷은 수컷의 와인 빛과 회색이 더 엷다. 알은 흰색이다. 정글, 건조한 삼림 지대에서 서식한다. 2개의 알을 낳으며, 14일 동안 부화하고, 2주 동안 양육한다. 인도 북·서부 동쪽-북동 티베트, 중국 남동부 아시아, 남인도, 버마, 샴, 인도차이나 남쪽, 북부 필리핀 군도, 안다만 제도 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001827', '23.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('36', '목점박이비둘기', 'Streptopelia chinensis', 'Scopoli', '1786', 'Chordata', '척삭동물문', 'Aves', '조강', 'Columbiformes', '비둘기목', 'Columbidae', '비둘기과', 'Streptopelia', '멧비둘기속', 'chinensis', '목점박이비둘기', '목점박이비둘기는 크고 가는 꼬리를 가진 작은 비둘기입니다. 인도 아대륙과 동남아시아의 원산지에서 자주 볼 수 있는 텃새입니다. 이 비둘기는 담갈색의 몸에 목 뒤와 옆에 흰색 점이 있는 검은색 목걸이 패턴을 가지고 있습니다. 하얀 꼬리 끝과 연한 갈색의 깃털 끝에 있는 점들이 특징입니다.    주로 숲, 정원, 도시 지역에서 발견되며, 땅 위에서 씨앗과 곡물 등을 먹으며 생활합니다. 날아오를 때는 날갯짓을 하며 빠르게 상승하고, 때로는 활공하기도 합니다. 짝짓기 시기에는 수컷이 비행을 하며 구애를 하고, 주로 작은 목재 둥지에 흰색 알 두 개를 낳습니다. 부모는 둥지를 짓고 알을 품고 새끼를 기릅니다.    한국에서 흔히 볼 수 있는 조류는 아니지만, 특정 지역에서 발견될 수 있습니다. 국내외의 여러 지역에 도입되어 정착한 경우가 많습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001828', '30.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('37', '녹색비둘기', 'Treron sieboldii', 'Temminck', '1835', 'Chordata', '척삭동물문', 'Aves', '조강', 'Columbiformes', '비둘기목', 'Columbidae', '비둘기과', 'Treron', '녹색비둘기속', 'sieboldii', '녹색비둘기', '몸길이 약 33cm이다. 등, 꽁지, 날개는 짙은 녹색이고 가슴은 누런 녹색, 배와 옆구리는 흰색이다. 수컷은 어깨 쪽 날개덮깃이 어두운 보랏빛이지만 암컷은 짙은 녹색이며, 암수 모두 꽁지깃에 검은색 띠가 있다. 주로 울창한 활엽수림이나 혼합림에 산다. 6월경 한배에 2개의 알을 낳는다. 열매나 씨앗을 먹는다. 단독으로 또는 몇 마리씩 작은 무리를 지어 지낸다. 일본, 타이완, 인도차이나 반도 등지에 분포한다. 동아시아 남부 특산종이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001830', '33.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('38', '밤색날개뻐꾸기', 'Clamator coromandus', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Cuculiformes', '두견목', 'Cuculidae', '두견과', 'Clamator', '밤색날개뻐꾸기속', 'coromandus', '밤색날개뻐꾸기', '제주도에서 처음 관찰된 미조이다. 몸길이 45cm 정도이다. 머리에 검은색 뿔깃이 있으며 멱과 앞목은 적갈색이다. 등, 날개덮깃, 꼬리는 길고 검은색이다. 날개는 짧으며 암적갈색이고, 배와 목뒤의 일부는 흰색이다. 부리는 검은색이다. 주로 산림에, 때로는 인가 주변에 서식한다. 단독으로 또는 암수가 함께 생활한다. 다른 새의 둥지에 탁란한다. 인도, 중국 남부, 타이완, 말레이시아, 홍콩, 일본 등에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001834', '45.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('39', '검은등뻐꾸기', 'Cuculus micropterus', 'Gould', '1838', 'Chordata', '척삭동물문', 'Aves', '조강', 'Cuculiformes', '두견목', 'Cuculidae', '두견과', 'Cuculus', '두견속', 'micropterus', '검은등뻐꾸기', '수컷의 머리와 목은 어두운 잿빛이고, 등과 허리는 잿빛 갈색이다. 눈 앞, 턱 밑, 멱, 윗가슴은 잿빛이고, 아랫가슴과 배는 흰색으로 드문드문 검은색의 가로띠가 있다. 아랫배와 아래꼬리덮깃은 흰색이고, 꼬리는 잿빛 갈색인데 끝부분 가까이에 뚜렷한 검은색 띠가 있고, 맨 끝은 흰색이다. 다리는 황색이다. 낙엽수나 낙엽송림 또는 혼효림에서 서식하고 대개 단독 생활을 한다. 다른 새의 둥지에 알을 낳아 위탁하여 부화시키는 습성이 있다. 먹이는 곤충류를 주식으로 한다. 울음소리가 독특하다. 우리나라를 비롯하여 아무르, 우수리, 만주, 인도차이나, 말레이반도, 인도 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001838', '33.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('40', '뻐꾸기', 'Cuculus canorus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Cuculiformes', '두견목', 'Cuculidae', '두견과', 'Cuculus', '두견속', 'canorus', '뻐꾸기', '두견목 두견과에 속하는 조류이다. 몸길이는 32-36cm이다. 암수의 색깔은 비슷하고, 수컷이 암컷보다 약간 크다. 눈과 눈테가 노란색이다. 윗면은 전체적으로 검은색을 띤 회색이며, 날개 끝과 꼬리는 옅은 검은색이다. 아랫면은 머리에서 가슴까지 검은색을 띤 회색이며, 배와 그 아랫부분은 흰색 바탕에 옅은 검은색의 가는 가로줄 무늬가 있다. 낮은 지대의 산지, 넓게 트인 개활지, 농경지, 주택지에 살며, 1년에 10~20개의 알을 낳으며, 다른 종의 둥지에 1개씩 탁란한다. 탁란할 때는 숙주의 알 1개를 먼저 제거하고, 자신의 알을 탁란한다. 한국에 도래하는 뻐꾸기 알의 색깔은 파란색이기 때문에, 붉은머리오목눈이의 흰색 알의 둥지와 파란색 알의 둥지 중, 주로 파란색 알의 둥지에 탁란한다. 전국에 서식하고 세계적으로는 유라시아의 온대-아한대, 아프리카 중부-남부, 동남아시아, 수마트라, 보르네오, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001839', '34.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('41', '두견', 'Cuculus poliocephalus', 'Latham', '1790', 'Chordata', '척삭동물문', 'Aves', '조강', 'Cuculiformes', '두견목', 'Cuculidae', '두견과', 'Cuculus', '두견속', 'poliocephalus', '두견', '두견목 두견과에 속하는 조류이다. 몸길이 28cm로 뻐꾸기류 중에서 크기가 작은 편이다. 가슴의 세로줄 무늬에 약간의 가로줄 무늬도 섞여 있다. 뒷목에 회색 띠가 보이기도 한다. 발가락은 깃털로 덮여 있다. 홍채는 붉은색이다. 뻐꾸기와 비슷하지만 배에 검은색 줄무늬가 굵고 뚜렷하다. 산림, 개활지에 살며 대부분 휘파람새 둥지에 탁란을 하고, 굴뚝새와 산솔새에게도 탁란을 한다. 경기도, 평안남도, 평안북도, 제주도 등에 서식하고 세계적으로는 극동 러시아, 중국, 일본, 동남아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001841', '28.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('42', '검은두견이', 'Surniculus lugubris', 'Horsfield', '1821', 'Chordata', '척삭동물문', 'Aves', '조강', 'Cuculiformes', '두견목', 'Cuculidae', '두견과', 'Surniculus', '검은두견이속', 'lugubris', '검은두견이', '검은두견이는 두견이과에 속하는 조류로, 히말라야부터 동남아시아에 걸쳐 서식한다. 외모는 검은색 딱새와 유사하며, 아래꼬리와 바깥쪽 아래꼬리 깃이 흰색 줄무늬로 구별된다. 날아갈 때는 아래에서 흰색 날개 줄무늬가 보인다. 이 새는 작은 태양조를 대상으로 탁란을 시도하며, 이 모습이 매와 닮아서 탁란에 도움이 될 가능성이 있다. 주요 울음소리는 날카로운 휘파람 소리로, 피치가 올라가며 섬세하게 들린다. 검은두견이는 현재 멸종위기는 아니며, 이전에는 포크타일드 드롱고두견이와 같은 종으로 분류되었으나, 소리와 형태의 차이로 인해 별개의 종으로 나뉘었다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001843', '24.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('43', '검은뻐꾸기', 'Eudynamys scolopacea', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Cuculiformes', '두견목', 'Cuculidae', '두견과', 'Eudynamys', '검은뻐꾸기속', 'scolopacea', '검은뻐꾸기', '검은뻐꾸기는 뻐꾸기과에 속하는 새로, 인도 아대륙, 중국, 동남아시아에 분포한다. 주로 숙주의 둥지에 알을 낳는 탁란 행동을 하며, 까마귀 등의 둥지에 알을 맡겨 키운다. 성체는 주로 과일을 먹으며, 번식기는 3월부터 8월까지이며, 남성은 반복적인 "쿠우우" 소리를 내고, 여성은 날카로운 "킥킥킥" 소리를 낸다. 검은뻐꾸기는 열대 남아시아의 가벼운 숲과 재배지에서 서식하며, 탁란시 주로 까마귀와 찌르레기, 알이 낮게 위치하고 과일나무 가까이에 있는 둥지를 선호한다. 성체는 다양한 과일과 곤충, 작은 척추동물을 섭취한다. 검은뻐꾸기는 인도 시인이 자주 언급하는 상징적인 새로 여러 시와 전설 속에 등장한다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001845', '42.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('44', '작은뻐꾸기사촌', 'Centropus bengalensis', 'Gmelin', '1788', 'Chordata', '척삭동물문', 'Aves', '조강', 'Cuculiformes', '두견목', 'Cuculidae', '두견과', 'Centropus', '작은뻐꾸기사촌속', 'bengalensis', '작은뻐꾸기사촌', '작은뻐꾸기사촌은 뻐꾸기과에 속하는 조류로, 보전 상태는 ''관심 필요''로 분류됩니다. 이 새는 주로 습지대의 풀과 나무가 있는 지역에서 서식하며, 작고 덜 두드러진 부리, 머리와 등 깃털의 연한 줄무늬가 특징입니다. 종종 단독으로 또는 쌍으로 저지대의 덤불 속에서 발견됩니다. 번식기는 주로 5월에서 9월 사이로, 주로 6월 비가 온 후입니다. 둥지는 풀잎으로 만든 돔 형태이며, 한 배에 2~4개의 알을 낳습니다. 수컷과 암컷 모두 알을 품고 새끼를 돌봅니다. 이 조류는 인도아대륙에서부터 동남아시아에 걸쳐 널리 분포합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001847', '42.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('45', '들꿩', 'Tetrastes bonasia', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Galliformes', '닭목', 'Phasianidae', '꿩과', 'Tetrastes', '들꿩속', 'bonasia', '들꿩', '닭목 꿩과에 속하는 조류이다. 흔하게 보이는 텃새로 몸길이 36cm이다. 몸 전체가 얼룩무늬가 있는 갈색을 띤다. 대체로 암수가 비슷하나 수컷은 턱밑에서 멱까지 검은색 반점이 있다. 우거진 숲에서 산다. 숲속 넘어진 나무 밑관목뿌리 아래의 땅 위에 번식한다. 산란기는 5월부터 6월까지이다. 땅 위에서 걸어 다니며 먹이를 찾으며, 놀랐을 때는 푸드덕하고 날아올라 나뭇가지 위에 앉는다. 제주도와 울릉도를 제외한 한반도 전역에 서식하고 세계적으로는 유라시아 북부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001403', '36.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('46', '멧닭', 'Lyrurus tetrix', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Galliformes', '닭목', 'Phasianidae', '꿩과', 'Lyrurus', '멧닭속', 'tetrix', '멧닭', '몸길이 수컷 약 54cm, 암컷 약 41cm이다. 수컷은 검은색이며 머리와 목, 배는 푸른 광택을 띠며 목둘레와 아래쪽 등, 허리는 옅은 녹색 빛이 돈다. 날개에는 흰 띠가 있으며 아랫배와 대퇴부는 회백색이 섞여 있고 날개 아랫덮깃은 흰색이다. 눈 위에는 하나의 붉은색 혹 모양의 볏이 있다. 부리는 검은색이다. 암컷의 배는 암갈색에 회색을 띠며 깃털에는 검은 가로무늬와 얼룩무늬가 있다. 둘째날개깃의 기부와 끝은 흰색이며 꼬리는 두 갈래로 갈라져 있다. 북아메리카, 북유럽, 북아시아 등지에 분포한다. 한국에서 백두산 고산 지대에만 서식하는 아종(T. t. usuriensis)은 우수리, 중국 동북 지방 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001405', '47.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('47', '메추라기', 'Coturnix japonica', 'Temminck & Schlegel', '1849', 'Chordata', '척삭동물문', 'Aves', '조강', 'Galliformes', '닭목', 'Phasianidae', '꿩과', 'Coturnix', '메추라기속', 'japonica', '메추라기', '닭목 꿩과에 속하는 조류이다. 몸길이는 20cm이다. 등에는 황갈색 바탕에 흰색과 검은색 줄무늬가 많다. 흰 눈썹선이 목뒤까지 이어지고 배는 등보다 연한 색을 띤다. 수컷은 이마, 머리꼭대기, 뒷머리가 검은색이며, 각 깃털의 가장자리는 붉은 갈색으로 폭이 넓다. 목, 윗등, 허리, 위꼬리덮깃은 붉게 녹슨 색으로 각 깃털 끝에는 검은색의 삼각형 무늬가 있다. 부리는 잿빛 갈색으로 끝 가까이는 검은색이다. 다리는 엷은 살구색이다. 알은 엷은 황회색 바탕에 어두운 갈색의 얼룩무늬가 있다. 탁 트인 들에 살며 우리나라에서 흔히 월동하는 겨울철새이며, 드물게 번식하는 여름철새이다. 번식기에는 일부일처로 생활한다. 둥지는 지면을 오목하게 파서 내부에 마른풀을 깔아 만든다. 날 때는 짧은 날개를 빠르게 움직여 일직선으로 나는데, 멀리 날지는 못하고 약 50m 정도를 날아간다. 전국에 서식하고 세계적으로는 유라시아 대륙, 아프리카에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001407', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('48', '꿩', 'Phasianus colchicus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Galliformes', '닭목', 'Phasianidae', '꿩과', 'Phasianus', '꿩속', 'colchicus', '꿩', '닭목 꿩과에 속하는 조류이다. 소리는 ‘꿩, 꿩’하고 운다. 5~6월에 한배에서 보통 6~10개 산란한다. 약 21일 만에 새끼가 알에서 깨어난다. 먹이는 나무 열매와 풀씨, 곡물의 낟알, 곤충 등을 먹는다. 한국, 중국 남동부, 일본, 칠레 동북부 등에 분포한다. 꼬리가 길고 뾰족한 지상형 조류로 몸 전체의 길이는 수컷이 80cm, 암컷이 60cm이다. 수컷의 눈 주위에 닭의 벼슬 같은 붉은 피부가 겉으로 드러나 있다. 선명한 흰 목테 위는 금속광택의 어두운 녹색이며 목테 아래로는 갈색과 황갈색의 바탕에 어두운 갈색과 검은색의 반점이 흩어져 있다. 발목 뒤에는 ‘며느리발톱’이라고 불리는 날카로운 발톱이 있어 번식기에 암컷을 차지하기 위한 싸움에 사용된다. 암컷은 몸 전체가 수수한 황갈색에 검은색의 얼룩무늬가 있다. 숲 가장자리의 덤불에 살며 대표적인 사냥새로서 주로 걷거나 뛰어서 도망가며, 접근하면 큰 소리를 내면서 날아오른다. 빠른 날갯짓으로 날아올랐다가 미끄러지듯이 내려앉는다. 겨울에는 무리를 이루는 경우가 많다. 놀랐을 때 매우 빠르게 달리거나 꿩! 하며 날아오른다. 4~6월경에 야산의 덤불, 숲속의 땅 위에 8~12개의 알을 낳는다. 알에서 깨어난 새끼는 암컷을 따라다니며 성장한다. 전국에 서식하고 세계적으로는 흑해 연안, 소아시아, 이란, 시베리아, 우수리, 중국, 타이완에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001409', '70.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('49', '개리', 'Anser cygnoides', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anser', '기러기속', 'cygnoides', '개리', '기러기목 오리과에 속하는 조류로 매우 드문 겨울철새이며 나그네새이다[멸종위기 야생생물 Ⅱ급]. 세계자연보전연맹 적색목록 위기종(EN)으로 분류된 국제보호조이다. 몸길이 약 87cm. 암수의 형태가 유사하다. 부리는 검은색, 다리는 주황색이며, 이마에서 뒷목은 밤색, 뺨과 앞목은 옅은 갈색이고 경계가 뚜렷하다. 가슴과 배는 갈색, 등과 날개 윗면은 암갈색, 아래꼬리덮깃은 흰색이다. 어린 새의 경우 부리 기부의 흰 띠가 없다. 호수, 논, 초습지, 소택지, 간척지, 갯벌에 서식하며, 수생식물의 잎이나 줄기 및 뿌리, 풀, 벼, 보리, 밀 등을 먹는다. 강이나 하천 등지의 땅 위의 움푹 파인 곳에 마른 풀줄기를 깔아 둥지를 만든다. 러시아 극동, 중국 동북부, 중국 흑룡강성, 몽골, 사할린 북부지역에서 번식하며, 한국, 중국 양쯔강, 대만, 일본에서 월동한다. 국내에는 임진강과 한강 하류지역이 월동 이동시기 최대 중간기착지이며, 금강 하구, 서천 갯벌, 영산강 유역, 낙동강 하구, 주남저수지 등 제한된 도래지에서 소수가 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001413', '87.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('50', '큰기러기', 'Anser fabalis', 'Latham', '1787', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anser', '기러기속', 'fabalis', '큰기러기', '기러기목 오리과에 속하는 조류로 비교적 흔한 겨울철새이다[멸종위기 야생생물 II급]. 몸길이 약 85cm. 대형의 기러기로 암수의 형태는 유사하며, 머리, 옆구리, 윗부분은 어두운 갈색 아랫부분은 연한 회갈색이다. 부리는 검은색이고 끝부분에 오렌지색의 띠가 있다. 부리가 두껍고 짧으며 약간 뾰족하다. 날개 끝과 꽁지는 짙은 갈색이고, 꽁지깃의 가장자리에는 흰색 띠가 있다. 위아래꼬리덮깃은 흰색이며, 다리는 주황색을 띠고 있다. 국내에는 전국의 농경지, 호수, 하천, 하구, 해안에 무리 지어 서식하는 겨울철새이다. 월동지에서는 밤에도 잠자리인 호수로 돌아가지 않고 온종일 넓은 논에서 지내는 경우도 많다. 유라시아대륙의 툰드라 지대에서 번식하고, 중국 남부, 중앙아시아, 한국 일본 남부에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001414', '85.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('51', '회색기러기', 'Anser anser', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anser', '기러기속', 'anser', '회색기러기', '기러기목 오리과의 조류로 몸길이는 76~84cm, 대형종이다. 암컷과 수컷 모두 머리는 잿빛 갈색이며 어깨 사이와 어깨깃은 갈색에 각 깃털 끝은 회색을 띤 흰색이다. 등, 허리, 위꽁지덮깃은 흰색이다. 옆구리는 어두운 갈색이며, 각 깃털의 가장자리는 흐린 갈색이다. 늙은 새는 가슴이 흰색이고 가슴과 배에는 검은색 또는 시멘트색의 얼룩무늬가 있다. 부리는 흐린 살구색에서 붉은 오렌지색에 이르기까지 여러 가지이며, 그 끝은 흰색이다. 다리는 살구색이다. 논, 소택지, 초지, 호소 등에서 생활한다. 냉대 및 한대에서 아한대 지역에 걸쳐 번식한다. 낮 동안에는 호반이나 앞이 트인 마른 논에서 무리 지어 먹이를 찾는다. 둥지는 움푹 파인 땅 위에관목의 가지, 마른풀, 이끼 등을 이용하여 접시 모양으로 만든다. 알은 5~6개 낳아 28~29일 동안 포란한다. 먹이는 육지에서 자라는 풀을 주로 먹지만, 수초도 먹는다. 유라시아 대륙에 분포한다. 중국과 일본 등지에서 겨울을 난다. 우리나라 제주도에 드물게 날아오는 미조(迷鳥)이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001415', '80.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('52', '쇠기러기', 'Anser albifrons', 'Scopoli', '1769', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anser', '기러기속', 'albifrons', '쇠기러기', '기러기목 오리과에 속하는 조류로 비교적 흔한 겨울철새이다. 몸길이 약 75cm. 암수의 형태는 유사하다. 머리는 어두운 갈색이며 눈과 부리 사이는 흰색이다. 몸 윗면은 회갈색이며, 각 깃털의 가장자리가 흰색이다. 귀깃, 목, 턱 밑, 멱은 갈색이고, 가슴과 배는 회갈색에서 흰색까지 다양하며, 검은색 얼룩무늬가 있다. 부리와 다리는 붉은색을 띤 살구색부터 옅은 색까지 다양하다. 국내에는 대부분 큰기러기 무리와 함께 섞여 월동하며, 평야지대와 해안 등지의 개방된 습지 또는 수확이 끝난 농경지에서 낟알, 벼 그루터기, 풀잎, 줄기, 뿌리 등을 먹는다. 항상 무리를 이루어 행동하며 경계심이 강하다. 잠을 잘 때는 머리를 등의 깃털에 파묻고 한쪽 다리로만 서 있거나, 배를 땅에 대고 있다. 알은 흰색이며, 산란기는 5~7월, 한 배 산란수는 6~7개이다. 유라시아, 북아메리카 대륙 일부, 그린란드의 북극권에서 번식한다. 국내에는 큰 무리를 이루어 전국의 호수와 저수지, 강, 해안, 간척지 등의 습지와 농경지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001416', '75.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('53', '흰이마기러기', 'Anser erythropus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anser', '기러기속', 'erythropus', '흰이마기러기', '흰이마기러기는 「멸종위기 야생생물 Ⅱ급」의 오리과 조류로 드문 겨울철새이다. 내륙에 규칙적으로 월동하는 기러기류 중 크기가 가장 작은 종이다. 다른 기러기 무리에 섞어 드물게 관찰된다. 서식지와 먹이 감소로 위협받고 있으며, 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001417', '59.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('54', '줄기러기', 'Anser indicus', 'Latham', '1790', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anser', '기러기속', 'indicus', '줄기러기', '줄기러기는 중형의 기러기로 몸길이 71-76cm이며, 무게는 1.87-3.2kg에 이릅니다. 머리에 흑백 줄무늬가 있는 것이 특징입니다. 중앙아시아의 고산 호수 주변에서 대규모 집단으로 번식하며, 남아시아에서 월동합니다. 히말라야 산맥을 넘는 고난도의 이주 능력으로 잘 알려져 있습니다. 주로 보리, 벼와 밀 등의 농작물에서 겨울을 나는 서식지를 찾습니다. 높은 고도에서 비행할 때 산소를 효과적으로 흡수할 수 있도록 체내 적혈구의 산소 친화도가 높습니다. 국내외에서는 제도적 보호를 받고 있지 않지만, 최근에는 개체수가 증가 추세에 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001418', '73.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('55', '흰머리기러기', 'Anser canagicus', 'Sevastianov', '1802', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anser', '기러기속', 'canagicus', '흰머리기러기', '기러기목 오리과의 조류로 몸 전체 길이는 77cm가량이다. 푸른색을 띠는 짙은 회색 몸깃에 머리에서 목뒤까지 흰색을 띠고 있다. 몸집은 쇠기러기보다 약간 작으며 통통하다. 목은 짧고 두꺼운 편이다. 턱과 멱은 검은색으로 짙다. 또한 등에 비늘 모양의 줄무늬가 뚜렷하며 꽁지깃이 흰색인 것이 특징이다. 툰드라 호수나 석호 부근에서 번식한다. 털갈이 시기에 번식하지 않는 개체들은 알래스카의 세인트로렌스섬에서 큰 집단을 이루거나, 시베리아 추코스키 반도의 북부 지역에 모인다. 번식 집단은 번식지 부근에서 털갈이를 한다. 보통 4~6개의 알을 낳고, 대략 24~27일간 부화한다. 알래스카 서부, 시베리아 북동부 해안에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001419', '77.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('56', '흰기러기', 'Anser caerulescens', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anser', '기러기속', 'caerulescens', '흰기러기', '우리나라에서는 매우 드물게 보이는 겨울새로 몸길이 67cm이다. 몸 전체가 흰색이지만 첫 번째 날개깃은 검은색, 부리와 다리는 분홍색이다. 어린 새는 등이 회갈색이고 아랫면이 회색이다. 우리나라의 평야지대 및 간척지의 논과 하구 등에 서식한다. 6월에 알을 4~8개 낳으며, 하루에 1개씩 낳는다. 새끼의 성장 기간은 40~50일이다. 먹이는 주로 식물성으로 낟알이나 씨, 잎을 먹으며 물속 연체동물, 곤충, 조개류 따위도 먹는다. 시베리아 북동부에서 번식하는 집단은 중국 북부와 일본 등지에서 겨울을 나고, 북아메리카의 툰드라 지대에서 번식하는 집단은 멕시코만과 캘리포니아에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001420', '67.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('57', '흑기러기', 'Branta bernicla', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Branta', '흑기러기속', 'bernicla', '흑기러기', '기러기목 오리과 조류로 매우 드문 겨울철새이다[멸종위기 야생생물 Ⅱ급]. 몸길이 약 61cm. 암수의 형태가 유사하며, 몸 윗면은 전체적으로 검은색으로, 목은 짧고 굵으며 흰 목테가 특징적이다. 다른 기러기류와 달리 주로 육지 깊숙이 바다가 들어온 만이나 해안가에서 겨울을 나며, 먹이도 주로 바다에서 해초류를 먹는다. 툰드라 지대의 호수, 갯벌의 강기슭, 강 하구에서 번식한다. 땅 위나 바위 사이에 이끼류를 이용해 접시 모양으로 둥지를 만들고 6월 중순에 회백색의 알을 3~8개 낳는다. 유라시아대륙, 북아메리카, 그린란드의 북극권에서 번식하고, 한국, 일본, 중국 해안, 북미 서부 연안 등지에서 월동한다. 국내에는 동해안과 남해안 지역에서 소수가 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001423', '61.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('58', '혹고니', 'Cygnus olor', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Cygnus', '고니속', 'olor', '혹고니', '기러기목 오리과의 조류로 매우 드문 겨울철새이다[멸종위기 야생생물 Ⅰ급]. 몸길이 152cm. 암수의 형태가 유사하다. 몸은 전체적으로 흰색이며 다리는 검은색, 부리는 오렌지색으로 부리 기부에 검은색 혹이 있다. 수생식물의 뿌리와 줄기를 먹으면서 하구, 저수지, 호수 등지에 서식한다. 우리나라 전역에 도래하나 호수 주변 개발, 수초 제거 등으로 인해 도래 개체 수가 감소하고 있다. 유럽 중·서부, 몽골, 바이칼호 동부, 우수리강 유역에서 번식하고, 소아시아, 북아프리카, 중국 동부, 한국에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001425', '152.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('59', '고니', 'Cygnus columbianus', 'Ord', '1815', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Cygnus', '고니속', 'columbianus', '고니', '기러기목 오리과 조류로 드문 겨울철새이다[멸종위기 야생생물 II급]. 몸길이 120cm. 암수의 형태가 유사하다. 어른 새는 온몸이 흰색이며 어린 새는 회갈색이다. 언뜻 보면 큰고니와 비슷하게 보이지만 큰고니보다는 크기가 작으며 부리의 노란 부분이 좁고, 둥그스름하다. 호수, 강 하구, 해안, 초습지, 농경지, 간척지 등지에서 관찰되며 겨울철새로 물속으로 머리를 넣어 물풀의 잎, 줄기 또는 뿌리 등을 먹으며 가끔은 농경지나 초원에서 먹이를 구한다. 과거 전국적으로 적지 않은 수가 도래하였으나, 현재 크게 감소하여 매우 적은 수가 월동한다. 강원도 동해안 석호, 낙동강 하구, 태화강, 주남저수지, 금강 하구, 천수만, 삽교호 한강 등지에서 월동한다. 전 세계적으로 유라시아대륙 북부, 알래스카, 캐나다 북부에서 번식하고, 유럽 서부, 카스피해 주변, 한국, 중국 동부, 일본에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001426', '120.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('60', '큰고니', 'Cygnus cygnus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Cygnus', '고니속', 'cygnus', '큰고니', '기러기목 오리과에 속하는 조류로 겨울철새이다[멸종위기 야생생물 II급]. 몸길이 약 150cm.  암수의 형태가 유사하다. 어른 새는 모두 순백색이고 어린 새는 회갈색을 띤다. 부리 끝은 검은색, 부리 기부는 노란색이다. 고니와 비슷하나 부리의 노란색 부분이 넓고 부리 앞쪽으로 길게 돌출되어 끝이 삼각형 모양이다. 다리는 검은색이다. 저수지, 물이 고인 논, 호수, 하구, 해안 등 수심이 얕은 수면에 생활한다. 목을 곧게 세우고 헤엄친다. 5월하순에서 6월상순에 걸쳐 한배에 3~7개의 알을 낳는다. 동해안 석호, 낙동강 하구, 천수만, 금강하구, 주남저수지, 한강, 전라남도 진도·해남 등지에서 월동하고 세계적으로는 아이슬란드에서 시베리아에 걸친 툰드라 지대, 지중해, 인도 북부, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001427', '150.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('61', '혹부리오리', 'Tadorna tadorna', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Tadorna', '혹부리오리속', 'tadorna', '혹부리오리', '기러기목 오리과에 속하는 조류로 흔한 겨울철새이다. 몸길이 약 61cm. 몸은 흰색이고, 머리와 어깨깃은 녹색 광택이 있는 검은색, 가슴과 등에 적갈색 띠가 있으며 다리와 부리는 붉은색이다. 주로 연안이나 하구의 갯벌에서 먹이 활동을 한다. 부리가 위로 굽어 있어 부리를 펄에 대고 훑으며 갯벌에 붙어있는 해조류, 갑각류 등을 먹는다. 유럽에서 동쪽으로 몽골, 중국 동북부에서 번식하고 유럽 남부, 북아프리카, 인도 북부, 중국 동부, 한국, 일본에서 월동한다. 국내에는 흔한 겨울철새로 낙동강, 간월호, 금강 하구, 순천만 등지에서 큰 무리를 이루어 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001429', '61.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('62', '황오리', 'Tadorna ferruginea', 'Pallas', '1764', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Tadorna', '혹부리오리속', 'ferruginea', '황오리', '기러기목 오리과의 조류로 드문 겨울철새이다. 몸길이 64cm. 대형 오리류이다. 몸 전체가 선명한 주황색이며, 꼬리와 부리, 다리는 검은색이고 날 때는 날개의 흰색과 검은색의 대조가 선명하다. 수컷은 번식기에는 목에 뚜렷한 검은 띠가 있으나 겨울철에는 폭이 좁고 흐려진다. 암컷의 얼굴은 수컷보다 흰색기가 많이 보인다. 아침과 저녁으로 수확이 끝난 논, 밭 등 다소 건조한 곳에서 무리를 이루어 풀줄기, 낟알을 먹으며, 습지 주변의 개방된 수면에서 휴식을 취한다. 유라시아대륙 중부에서 번식하고, 북아프리카, 남아시아, 중국, 한국, 일본에서 월동한다. 국내에는 한강 하류, 김포평야, 서산 간월호, 금강 중류와 인근의 농경지 등 국지적으로 적은 수가 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001430', '64.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('63', '원앙사촌', 'Tadorna cristata', 'Kuroda', '1917', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Tadorna', '혹부리오리속', 'cristata', '원앙사촌', '기러기목 오리과의 새이다. 몸길이는 약 64cm로 혹부리오리와 크기가 비슷하다. 수컷은 머리가 흰색이며, 머리꼭대기에는 검은색의 긴 댕기가 있다. 윗가슴은 암녹색, 배는 회색이고 아래꽁지덮깃 부근은 오렌지색이다. 등은 회색이며 적갈색을 띤다. 날개에는 넓은 흰색의 띠가 있고 어깨깃은 암녹색이다. 암컷은 갈색이지만 흰머리, 검은 댕기와 검은 눈테가 있다. 부리는 핑크색, 다리는 황색이다. 수컷의 깃은 이마, 머리꼭대기, 뒷머리, 뒷목의 중간까지 검은색이고 어두운 녹색의 금속광택이 있다. 뒷머리와 뒷목의 깃은 댕기 모양으로 늘어지나 그 아랫부분은 흰색이다. 날개덮깃과 겨드랑이깃은 흰색이다. 부리는 수컷이 갈색이고 암컷은 황갈색이다. 다리는 황갈색이다. 우리나라 낙동강 하구 삼각주, 금강 하구 등에 서식한다. 1961년 부산 부근 낙동강 하류에서 1마리가 잡힌 것을 마지막으로 멸종된 것으로 보고 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001431', '64.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('64', '원앙', 'Aix galericulata', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Aix', '원앙속', 'galericulata', '원앙', '기러기목 오리과에 속하는 조류로 비교적 흔한 텃새이자 겨울철새이다. 몸길이 약 45cm. 번식기 수컷의 깃 색이 화려하여 다른 종과 혼동이 없다. 수컷의 부리는 붉은색이며 끝은 흰색이다. 암컷은 전체적으로 어두운 회색 바탕에 몸 아랫면에 흰색 얼룩점이 있으며, 눈 뒤로 흰색의 가는 눈선이 있고 부리는 검은색이다. 수컷의 겨울깃은 암컷과 비슷하지만 부리가 붉은색이다. 산림 주변의 늪지대나 계곡, 냇가 등에서 각종 식물의 열매나 수서곤충, 연체동물, 작은 어류 등을 먹는다. 냇가와 인접한 오래된 나무 위나 바위틈에서 번식한다. 중국 동북부, 한국, 러시아 극동, 사할린, 일본에서 분포한다. 겨울철에는 전국의 호소, 하천, 해안 등 우리나라 전역에서 서식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001433', '45.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('65', '미국오리', 'Anas rubripes', 'Brewster', '1902', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anas', '오리속', 'rubripes', '미국오리', '우리나라에서는 미조이다. 몸길이 48~56cm이다. 암수 모두 온몸이 검은 갈색이며, 둘째날개깃의 하단부는 흰색이다. 부리는 올리브색 또는 녹색이다. 청둥오리 암컷과 비슷하나 부리 색깔과 몸 색깔이 다르다. 알은 녹색 빛이 도는 담갈색이다. 호소와 소택지, 간척지, 하천, 하구, 해안, 연못, 물가의 풀밭 등지에 서식한다. 둥지는 땅에 만들며, 몸깃과 가슴깃을 둥지 밑에 깐다. 알은 9~12개 낳는다. 미국과 캐나다 동부 지역에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001439', '52.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('66', '청둥오리', 'Anas platyrhynchos', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anas', '오리속', 'platyrhynchos', '청둥오리', '기러기목 오리과에 속하는 조류로 매우 흔한 겨울철새이다. 몸길이는 약 59cm. 수컷의 부리는 뚜렷한 노란색이며 머리는 금속광택의 녹색이고 흰색의 가는 목테가 있다. 가슴은 짙은 갈색이며 위꼬리덮깃의 검은색 깃은 위로 말려 있다. 암컷은 전반적으로 갈색이며 흑갈색 줄무늬가 흩어져 있으며, 부리는 오렌지색 바탕에 검은색의 반점이 있다. 낮에는 대부분 물 위, 모래톱, 제방 등지에서 무리 지어 휴식하고, 해가 지면 농경지, 습지 등지로 날아들어 낟알, 식물 줄기 등을 먹는다. 번식기에는 한배에 알을 6~12개 낳는다. 유라시아대륙과 북아메리카대륙의 한 대·온대에 광범위하게 분포한다. 국내에는 전국적으로 가장 흔한 겨울철새이며 일부 지역에서는 번식도 한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001440', '59.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('67', '고방오리', 'Anas acuta', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anas', '오리속', 'acuta', '고방오리', '기러기목 오리과에 속하는 조류로 흔한 겨울철새이다. 몸길이 수컷 약 75cm, 암컷 약 53cm이다. 목이 다른 오리류에 비해 길고 가늘어 보이며 꼬리는 암수 모두 뾰족하다. 수컷은 머리에서 뒷목까지 어두운 밤색이다. 목 아래와 가슴은 흰색이다. 회색의 몸과 검은색인 아래꼬리덮깃의 경계부분은 연한 노란색이며 검은색의 중앙 꼬리깃은 매우 길다. 암컷은 전체적으로 갈색이며 부리가 검은색이다. 강이나 하구, 저수지에 종종 큰 무리를 이루고, 자맥질해 수중의 수초 및 식물의 종자를 먹는다. 유라시아대륙 북부, 북아메리카에서 번식하고, 겨울에는 유라시아대륙과 북아메리카의 온대에서 열대, 북아프리카에서 월동한다. 국내에는 겨울철 전국 습지에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001443', '64.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('68', '쇠오리', 'Anas crecca', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anas', '오리속', 'crecca', '쇠오리', '기러기목 오리과에 속하는 조류로 흔한 겨울철새이다. 몸길이 약 38cm. 수컷은 머리가 적갈색이며 눈에서 뒷목까지 어두운 녹색이다. 적갈색과 녹색의 경계에는 노란색의 얇은 줄이 있다. 옆구리에는 뚜렷한 흰색의 가로줄이 있다. 아래꼬리덮깃은 노란색이며 주위에 검은색 띠가 있다. 암컷은 전체적으로 어두운 갈색에 검은 무늬가 있다. 둘째날개깃은 광택이 나는 녹색이다. 낮 동안은 하천이나 호수, 간척지 등에서 휴식하거나 먹이를 먹고, 밤에는 논, 밭, 초습지에서 먹이를 찾는다. 월동지에서 작은 무리를 형성하며, 불규칙하고 빠르게 난다. 유라시아대륙 북부에서 번식하고, 겨울에는 유럽 남부, 북아프리카, 중동, 남아시아에서 동아시아까지 월동한다. 국내에서는 흔한 겨울철새로 전국 습지, 하천에 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001446', '38.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('69', '붉은부리흰죽지', 'Netta rufina', 'Pallas', '1773', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Netta', '붉은부리흰죽지속', 'rufina', '붉은부리흰죽지', '크기는 원앙과 청둥오리의 중간 정도이다. 수컷은 이마에 붉은 혹이 있고 부리는 분홍색이다. 머리와 목은 검다. 등과 앞가슴은 회색빛을 띤 검은색이며 옆구리와 배는 회색 또는 흰색이다. 꼬리 밑의 엉덩이는 흰색이고 다리는 연한 붉은색이다. 암컷은 부리가 푸르스름한 회색이고 머리와 등은 검은 갈색이며 목, 옆구리, 배는 갈색이다. 초원과 습지대에 서식한다. 작은 무리를 지어 물가에 둥지를 튼다. 5월에 알 6~9개를 낳는다. 연체동물 등을 먹는다. 남아메리카에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001448', '57.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('70', '큰흰죽지', 'Aythya valisineria', 'A.Wilson', '1814', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Aythya', '흰죽지속', 'valisineria', '큰흰죽지', '기러기목 오리과의 조류이다. 몸길이는 50~61cm, 대형의 잠수성 오리이다. 흰죽지와 비슷하지만 길고 검은 부리와 앞이마가 이루는 완만한 각은 독특한 모양을 이룬다. 수컷의 몸 중앙부는 흰색이며, 앞부분은 멀리서 볼 때 검은색으로 보이고 머리는 붉은 고동색이다. 암컷의 머리와 가슴은 갈색, 눈과 멱은 밝은 갈색, 등은 회갈색, 옆구리는 회색이고, 옆모습이 독특하다. 일반적으로 소리를 잘 내지 않지만, 구애 행동 시 수컷은 소리를 낸다. 알은 암갈색이 도는 올리브색이다. 북극권의 숲에 인접한 트인 습지, 식생이 발달한 호수나 초지 등지에 서식한다. 해안가에서 월동하는 겨울철새이다. 식생과 동물상이 풍부하고, 잠수할 만큼 물이 충분히 깊은 곳, 습지 식물이 밀생한 곳에 만수위보다 약간 낮은 곳에 둥지를 만들고, 알을 7~9개 낳는다. 무리를 지어 비행할 때는 V자 형태로 난다. 매우 빠른 새로 최고 시속 116km까지 비행할 수 있다. 서스캐처원, 브리티시 컬럼비아, 앨버타, 마니토바 남부, 오리건, 유타 북부, 콜로라도, 네브래스카, 미네소타 등지에서 번식한다. 북아메리카의 해안, 브리티시 컬럼비아, 매사추세츠 남부 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001450', '55.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('71', '미국흰죽지', 'Aythya americana', 'Eyton', '1838', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Aythya', '흰죽지속', 'americana', '미국흰죽지', '몸길이 50cm, 체중은 수컷 770g, 암컷 680g이다. 크기, 색깔 면에서 성적 동종이형이다. 수컷의 경우 정수리 부분은 흰색, 눈에서 머리 뒤쪽은 넓은 녹색 줄무늬가 있고, 얼굴과 목은 반점이 있는 회색이며, 가슴과 측면은 밝은 밤나무색이며, 등은 밝은 갈색, 익경(翼鏡, speculum)은 녹색, 깃은 끝부분이 검은 파란색이다. 암컷의 경우 머리 부분은 반점이 있는 회색이다. 등은 반점이 있는 갈색이다. 주로 달팽이, 갑각류, 곤충 등의 수서동물, 무척추동물을 먹는다. 일부일처제이며, 초지가 있는 호수나 습지의 움푹 팬 구멍 등에 둥지를 만든다. 9~13개의 알을 낳는다. 미국에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001451', '50.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('72', '흰죽지', 'Aythya ferina', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Aythya', '흰죽지속', 'ferina', '흰죽지', '기러기목 오리과에 속하는 조류로 흔한 겨울철새이다. 몸길이 약 45cm. 잠수성 오리류 중 국내 월동개체수가 가장 많다. 수컷의 머리와 목은 적갈색, 가슴은 검은색, 몸은 회색이다. 부리는 검은색으로 가운데에 굵은 회색 띠가 있다. 변환깃은 가슴과 꼬리의 색이 약간 흐려진다. 암컷은 머리와 가슴은 갈색, 몸은 회갈색이다. 눈을 지나는 황갈색의 둥근 뺨선이 있으며 눈 뒤쪽으로 흐린 흰색 줄무늬가 있다. 저수지, 하천, 강 하구 등지에서 댕기흰죽지나 검은머리흰죽지 등과 함께 무리를 이루어 생활한다. 유럽 동북부, 바이칼호, 사할린섬에서 번식하고, 한국·일본·대만·중국(남부)·미얀마·인도·북아프리카 등지에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001452', '45.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('73', '붉은가슴흰죽지', 'Aythya baeri', 'Radde', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Aythya', '흰죽지속', 'baeri', '붉은가슴흰죽지', '기러기목 오리과의 희귀한 겨울철새이다. 몸길이는 45cm가량이다. 눈은 흰색 또는 흐린 노란색이며, 아래꼬리덮깃의 흰색이 뚜렷하게 보인다. 수컷은 머리가 녹색 광택을 띠며 가슴은 적갈색이다. 암컷은 머리와 가슴이 어두운 밤색이다. 잠수성 오리이며, 먹이나 습성에 대해서는 잘 알려져 있지 않다. 저수지, 강, 강 하구에서 주로 관찰된다. 월동기에는 흰죽지나 댕기흰죽지의 무리에 섞여서 함께 관찰되는 경우가 대부분이다. 전국적으로 관찰되지만 어느 곳에서도 개체수가 많지 않다. 지금까지 관찰된 기록 중 최대 집단은 5개체에 불과하다. 금강 하구와 만경강 하구에 도래한 기록이 있다. 중국 동북 지역, 러시아 극동 지역에서 번식하며, 한국, 일본, 중국 남부 지역에서 월동한다. 동남아시아의 북부 지역에 월동기에 드물게 도래한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001453', '45.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('74', '적갈색흰죽지', 'Aythya nyroca', 'Güldenstädt', '1770', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Aythya', '흰죽지속', 'nyroca', '적갈색흰죽지', '10월에 찾아와 이듬해 2월 말까지 강, 하구, 하천, 해안 등에서 볼 수 있는 겨울철새이다. 수컷은 몸 전체가 적갈색이며 등과 부리는 검은색이다. 배 아랫부분과꼬리 아랫부분이 흰색이다. 눈은 흰색이다. 암컷은 몸 전체가 수컷보다 더 어두운 적갈색이며, 눈도 어두운 갈색이다. 시야가 트인 넓은 공간을 선호하며 흰죽지나 댕기흰죽지 무리에 적은 수가 관찰되는 미조이다. 주로 야간에 먹이활동을 하며, 물속으로 잠수하여 수생식물이나 작은 곤충, 어류 등을 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001454', '43.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('75', '댕기흰죽지', 'Aythya fuligula', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Aythya', '흰죽지속', 'fuligula', '댕기흰죽지', '기러기목 오리과에 속하는 조류로 흔한 겨울철새이다. 몸길이 약 40cm. 수컷의 머리는 광택이 있는 검은색이며, 뒷머리에 긴 댕기가 있다. 가슴과 등은 검은색이며, 옆구리와 배는 흰색이다. 암컷은 몸 전체가 짙은 갈색이며, 댕기가 짧다. 부리와 다리는 푸르스름한 회색이고 끝이 검다. 호수 하구, 항구 등지에서 작은 무리를 이루며 잠수하여 새우, 게 등 갑각류, 수서곤충, 수초를 먹는다. 유라시아대륙 북부에서 번식하고, 유럽, 북아프리카, 인도, 중국 동부와 남부, 한국, 일본, 대만에서 월동한다. 국내에는 겨울철 우리나라 전역의 습지에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001455', '40.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('76', '검은머리흰죽지', 'Aythya marila', 'Linnaeus', '1761', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Aythya', '흰죽지속', 'marila', '검은머리흰죽지', '기러기목 오리과에 속하는 조류로 지역적으로 흔한 겨울철새이다. 몸길이 약 45cm. 수컷은 머리와 목, 가슴이 녹색 광택이 있는 검은색이다. 몸 윗면은 흰색에 가는 검은 물결 모양 줄무늬가 있다. 암컷은 갈색빛이 나며, 부리 기부의 흰 점이 뚜렷하다. 유사종인 댕기흰죽지와 비슷하지만, 암수 모두 뒷머리에 돌출된 깃이 없다. 해안 근처의 호수, 하구 등에서 무리를 이루어 생활하며 잠수하여 조개류, 갑각류, 해초류를 먹는다. 유라시아대륙 북부, 북아메리카 북부에서 번식하고, 유럽, 카스피해, 페르시아만, 우수리, 중국 동부, 한국, 일본, 북아메리카 서부해안과 동부해안에서 월동한다. 국내에는 겨울철 우리나라 전역의 하구, 석호, 만에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001456', '45.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('77', '호사북방오리', 'Somateria spectabilis', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Somateria', '호사북방오리속', 'spectabilis', '호사북방오리', '호사북방오리는 큰 바다오리로, 북반구의 북극 해안에서 번식합니다. 이들은 대부분의 시간을 고위도의 해양 생태계에서 보내며, 6월에서 7월 사이에 북극 툰드라로 이동하여 번식합니다. 주로 바닷가저의 무척추동물을 먹으며 겨울철에는 해안에서 큰 무리를 형성하여 생활합니다. 수컷은 화려한 색깔의 깃털로 잘 알려져 있으며, 몸 대부분이 검은색이고 머리 부분은 여러 색깔로 이루어져 있습니다. 암컷은 갈색을 띠며, 비교적 수수한 외관을 가지고 있습니다. 호사북방오리는 "관심대상"으로 분류되어 있으며, 전세계적으로 넓은 분포를 보입니다. 주요한 포식자는 북극여우와 해양조류들이며, 전통적으로 식용으로 사용되기도 했습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001458', '60.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('78', '흰줄박이오리', 'Histrionicus histrionicus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Histrionicus', '흰줄박이오리속', 'histrionicus', '흰줄박이오리', '기러기목 오리과에 속하는 조류로 드문 겨울철새이다. 몸길이 약 43cm. 수컷은 전체적으로는 금속광택의 어두운 청회색 바탕에 부리 위, 뺨, 귀 뒤쪽, 목, 옆구리에 흰 점 또는 줄무늬가 있다. 옆구리는 적갈색이다. 암컷은 어두운 갈색이며, 눈앞과 귀깃에 흰 점이 있다. 바닷가의 암초가 많은 곳에서 작은 무리를 이루어 생활한다. 잠수해 갑각류와 조개류를 잡아먹으며 해안 암벽에 올라가 쉰다. 시베리아 동부, 캄차카, 알래스카에서 북아메리카 서북부 해안, 쿠릴열도 북부, 그린란드 남부, 아이슬란드, 아메리카 동북 연안에서 번식하고, 겨울에는 번식지 아래 지역에서 월동한다. 국내에는 주로 동해안에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001460', '43.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('79', '검둥오리', 'Melanitta americana', 'Swainson', '1832', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Melanitta', '검둥오리속', 'americana', '검둥오리', '기러기목 오리과에 속하는 조류로 비교적 드문 겨울철새이다. 몸길이 약 48cm. 수컷은 몸 전체가 검은색이고 검은색 부리 기부에 폭넓은 노란색 혹이 있다. 암컷은 전체가 흑갈색이고, 얼굴, 턱 밑, 목 옆은 회갈색이다.  해안, 하구, 먼바다에서 무리를 이루어 생활하며 잠수하여 조개류와 갑각류 등을 먹는다. 이동시 수면 위로 낮게 난다. 번식시기에는 유라시아대륙 북부, 알래스카 서부, 아이슬란드, 뉴펀들랜드섬 등 아한대부터 한대 지역 툰드라나 산림 지대의 담수 못, 호소, 하천 등에서 서식한다. 둥지는 풀숲 땅 위에 풀잎과 줄기를 이용하여 접시 모양으로 만든다. 산란기는 5월하순~6월상순이며, 알은 8~10개를 낳는다. 유럽 연안, 아프리카 북서부 연안, 캄차카, 한국, 중국, 일본, 북미 해안에서 월동한다. 국내에는 겨울철 주로 동해안과 남해안에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001463', '48.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('80', '바다꿩', 'Clangula hyemalis', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Clangula', '바다꿩속', 'hyemalis', '바다꿩', '우리나라에서 작은 무리가 드물게 월동하는 겨울철새이다. 수컷 몸길이 약 57cm, 암컷 몸길이 약 31cm이다. 겨울깃은 짙은 갈색과 흰색이 뚜렷하다. 머리, 목, 아랫면은 흰색이고, 눈 밑에 짙은 갈색 얼룩점이 있다. 가슴, 등, 날개는 검은 갈색, 부리는 검은색이며 부리 끝 가까이에 분홍색 띠가 있다. 가운데 꼬리깃이 긴 것이 이 종의 특징이다. 해안 앞바다나 하구에서 서식한다. 월동지에서는 무리를 지어 해양생활을 하며, 잠수에 능하다. 둥지는 툰드라 지대의 땅 위에 마른풀이나관목가지를 이용하여 만든다. 산란기는 5~7월이다. 알은 6~8개를 낳는다. 먹이는 복족류, 갑각류, 수서곤충, 수생 식물 등이다. 유라시아, 북아메리카 북극해 연안에서 번식하고 우리나라에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001465', '44.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('81', '흰뺨오리', 'Bucephala clangula', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Bucephala', '흰뺨오리속', 'clangula', '흰뺨오리', '기러기목 오리과의 조류로 흔한 겨울철새이다. 몸길이 약 45cm. 수컷은 흰색 몸에 머리는 청록색이며, 노란색 눈 아래 뺨에는 흰 점이 있다. 부리와 등은 검은색이다. 암컷은 어두운 갈색의 머리와 회색의 몸이 흰색의 목테로 명확히 구분된다. 암컷의 검은색 부리 끝에는 노란색 반점이 있다. 날 때는 날개 안쪽에 흰색이 뚜렷하게 보인다. 주로 하구, 호수, 하천 등지에서 작은 무리를 이루어 생활하며, 잠수하여 갑각류, 연체동물, 어류, 수초 등을 먹는다. 번식기에는 호수나 습지 주변의 산림에서 생활한다. 바위틈보다는 숲속에서 번식하는 경우가 더 빈번하다. 한배에 알은 6~15개 낳으며, 암컷이 27~32일간 포란한다. 유라시아대륙과 북미대륙 북부에서 번식하고, 유럽, 페르시아만, 캄차카에서 중국 동부, 한국, 일본, 알래스카에서 미국 중부까지 월동한다. 국내에는 흔한 겨울철새로 우리나라 전역의 습지에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001467', '45.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('82', '북방흰뺨오리', 'Bucephala islandica', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Bucephala', '흰뺨오리속', 'islandica', '북방흰뺨오리', '우리나라에서는 미조로 드물게 관찰된다. 몸길이 42~51cm이다. 몸 윗면은 검은색, 아랫면은 흰색이다. 암컷은 머리가 갈색, 목은 흰색, 몸은 회색이다. 수컷의 눈 앞에 흰 점이 있는 것이 흰뺨오리와 다르다. 날 때 암수 모두 흰뺨오리보다 날개 안쪽에 있는 흰색 부분이 작다. 호숫가의 숲에서 번식한다. 겨울에는 해안에서 월동한다. 나무 구멍에 둥지를 만들며, 알을 9~10개 낳는다. 알래스카 남서부, 캐나다 북동부, 아이슬란드, 그린란드 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001468', '46.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('83', '흰비오리', 'Mergellus albellus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Mergellus', '흰비오리속', 'albellus', '흰비오리', '기러기목 오리과의 조류이다. 몸길이 약 42cm. 수컷은 전체적으로 흰색이며, 눈 주위가 검은색이며, 등, 가슴에 검은색 줄무늬가 있고 머리 위쪽으로 짧은 댕기깃이 있다. 암컷의 몸은 회색이며 머리는 갈색, 뺨과 목 앞은 흰색이다. 전국의 호수, 하천, 하구 등지에 작은 무리를 이루어 생활하며 잠수하여 어류, 조개류, 갑각류 등을 먹는다. 유라시아대륙의 아한대에서 번식하고, 유럽, 카스피해, 인도 북부, 중국 동부, 한국, 일본에서 월동한다. 국내에는 흔한 겨울철새로 우리나라 전역 습지에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001470', '42.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('84', '비오리', 'Mergus merganser', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Mergus', '비오리속', 'merganser', '비오리', '기러기목 오리과의 조류로 흔한 겨울철새이며, 국지적으로 번식하는 드문 텃새이다. 몸길이 약 65cm. 수컷은 머리와 윗목이 녹색을 띠는 검은색이다. 몸은 흰색이고 등은 검은색이다. 암컷은 머리와 윗목이 갈색이며, 턱 밑은 흰색이다. 목에는 흰색의 띠가 있어 머리의 갈색 부분과몸의 회색 부분이 구분된다. 암수 모두 부리는 붉은색에 길고 뾰족하며, 끝부분은 아래로 휘어지고 검은색이다. 암컷은 머리에 뒤로 뻗은 댕기깃이 있다. 다리는 황색을 띤 붉은색이다. 번식기에 둥지는 땅 위의 구멍이나 나무구멍을 이용한다. 산란기는 4~6월이다. 한 배 알은 9~10개 낳으며 32~35일 동안 포란한다. 전국적으로 흔한 겨울철새로 호수, 댐, 하천, 저수지, 하구 등지에서 무리를 이루어 생활하며 잠수해 어류를 사냥한다. 유라시아대륙, 북아메리카대륙의 아한대와 온대에서 번식하고, 유럽, 인도 동부, 미얀마, 중국 동부, 한국, 일본, 북미 남부에서 월동한다. 국내에는 겨울철 우리나라 전역에서 관찰되며, 최근 강원 일부 산간계곡에서 드물게 번식이 확인되었다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001472', '65.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('85', '바다비오리', 'Mergus serrator', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Mergus', '비오리속', 'serrator', '바다비오리', '기러기목 오리과의 조류로 비교적 흔한 겨울철새이다. 몸길이 약 55cm. 암수 모두 뒷머리에 곧고 가는 댕기깃이 많이 있는 것이 특징적이다. 수컷은 머리가 녹색을 띠는 검은색이다. 목에는 흰색의 테가 있고 가슴에는 흑갈색 반점이 흩어져 있다. 등은 검은색이며 몸의 옆면은 흰색과 회색이다. 가슴과 배의 경계에는 검은색에 흰색 반점이 여러 개 있다. 암컷은 머리 부분이 갈색이며 몸의 다른 부위는 회갈색이다. 해안 근처의 해상, 하구 하천 등지에서 작은 무리를 이루며 생활하며 잠수하여 물고기, 수서곤충, 새우 등을 잡아먹는다. 번식기에는 유라시아와 북아메리카의 아한대 지역의관목림이나 덤불이 우거진 바위 주변을 둥지 자리로 삼으며, 간혹 나무 구멍이나 인공 새집을 이용하기도 한다. 유라시아대륙 북부와 영국 북부, 그린란드, 북미 북부에서 번식하고, 유럽, 중국 동부, 북미 서부해안과 동부해안에서 월동한다. 국내에는 겨울철 우리나라 전역 해안과 하구에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001473', '55.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('86', '호사비오리', 'Mergus squamatus', 'Gould', '1864', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Mergus', '비오리속', 'squamatus', '호사비오리', '기러기목 오리과의 조류로 매우 드문 겨울철새이다. [멸종위기 야생생물 Ⅰ급]. 몸길이 약 57cm. 길게 뻗은 뒷머리의 댕기와 옆구리의 뚜렷한 비늘무늬가 특징이다. 수컷은 머리와 목이 녹색 광택이 나는 검은색이며, 몸은 흰색 등은 검은색이다. 암컷은 머리와 목이 연한 갈색이며 수컷에 비해 댕기가 짧고 등은 회색이다. 물 흐름이 빠른 하천, 강이나 호수 등지에서 작은 무리를 이루며 생활하며 잠수하여 물고기를 잡아먹는다. 중국 동북부의 아무르강, 러시아의 우수리강 유역, 백두산 등지 등 매우 제한된 지역에서 번식하고, 중국 남부와 중부, 한국, 일본 등지에서 월동한다. 국내에서는 강원도 철원지역의 하천과 북한강, 한강, 충남 대청호, 춘천 인근 북한강, 경남 진주 남강, 전남 화순 지석천 및 구례 섬진강 인근의 소하천 등에 소수가 도래한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001474', '57.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('87', '아비', 'Gavia stellata', 'Pontoppidan', '1763', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gaviiformes', '아비목', 'Gaviidae', '아비과', 'Gavia', '아비속', 'stellata', '아비', '아비목 아비과에 속하는 조류로 흔한 겨울철새이다. 몸길이 61~67cm. 암수의 형태는 유사하다. 가늘고 위로 약간 휜 부리를 가지고 있다. 여름깃은 머리는 회색, 등은 갈색, 목은 적갈색이다. 겨울에는 등이 흰색의 반점이 있는 갈색이며, 멱과 앞 목, 배는 흰색이다. 겨울철 항만이나 연안에서 가끔 관찰되며, 잠수해서 어류를 잡아먹는다. 번식지에서는 호소 기슭의 식물들의 한가운데를 납작하게 만들고 알을 2개 낳는다. 알은 반점이 있는 올리브색에서 갈색까지 다양하다. 국내에서는 제주도, 남해, 동해 연안에서 주로 월동하고 세계적으로는 유럽, 아시아, 아메리카의 북부, 일본, 사할린, 중국, 타이완 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001478', '64.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('88', '큰회색머리아비', 'Gavia arctica', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gaviiformes', '아비목', 'Gaviidae', '아비과', 'Gavia', '아비속', 'arctica', '큰회색머리아비', '아비목 아비과에 속하는 조류로 흔한 겨울철새이다. 몸길이 72~78cm. 암수의 형태는 유사하다. 부리가 곧고 뾰족하다. 회색머리아비와 형태가 거의 동일하지만 멱에 가늘고 검은 가로줄 무늬가 없다. 옆구리 뒤쪽으로 폭넓은 흰색 깃이 몸 위쪽으로 확장되어 보인다. 11월 초순에 도래하여 3월하순까지 관찰되며 일부 개체는 5월 중순까지 관찰된 기록도 있다. 1마리 또는 작은 무리를 이루어 잠수하며 물고기를 잡아먹는다. 해안가에서 떨어진 먼바다에 많은 수가 서식하며 연안으로는 드물게 강 하류에서도 관찰된다. 회색머리아비보다 월동 개체수가 많으며, 다른 아비류와 마찬가지로 어선에서 버려지는 폐기름에 오염되어 죽거나, 어망에 걸려 질식사하는 등 여러 가지 요인으로 개체수가 감소하고 있는 것으로 추정된다. 유라시아 북부, 알래스카 서북부에서 번식하고, 겨울에는 남쪽으로 이동하여 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001479', '75.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('89', '회색머리아비', 'Gavia pacifica', 'Lawrence', '1858', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gaviiformes', '아비목', 'Gaviidae', '아비과', 'Gavia', '아비속', 'pacifica', '회색머리아비', '아비목 아비과의 조류로 몸길이 69cm쯤인 대형종이다. 여름깃은 암컷과 수컷 모두 머리에서 뒷목까지 쥐색이다. 등, 어깨, 허리, 위꽁지덮깃은 남색의 금속광택을 띠는 검은색이다. 등 양쪽에는 흰색의 굵은 가로무늬가 여러 개 있다. 턱 밑에서 앞목까지는 녹색 광택이 있는 검은색이다. 가슴, 배는 흰색이며, 가슴 옆에는 검은색의 가로선이 여러 개 있다. 부리는 검은 갈색이며, 다리의 바깥쪽은 검은색, 안쪽은 살구색이다. 알은 진한 갈색에 얼룩점이 있다. 한대 지역의 해안 호소, 물가의 초지, 풀이 밀생한 곳에서 번식한다. 우리나라에서는 남해안 일대, 거제도 연안 등지에서 월동하는 겨울철새이다. 월동지에서는 1~2마리 또는 수십 마리가 무리 지어 생활한다. 체형이 유선형이므로 잠수와 수영에 능하다. 경계할 때는 몸을 물속에 잠기고 목만 노출한다. 둥지는 마른풀을 깔아 접시 모양으로 만든다. 알은 6~7월에, 2개 정도 낳아, 20일 동안 품는다. 어류, 갑각류, 연체동물, 극피동물 등을 주로 먹는다. 시베리아 동북부~북아메리카 대륙에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001480', '69.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('90', '느시', 'Otis tarda', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Otidae', '느시과', 'Otis', '느시속', 'tarda', '느시', '느시는 「멸종위기 야생생물 Ⅱ급」의 느시과조류로 매우 드문 겨울철새이다. 몸은 전체적으로 황갈색이고 가슴에 적갈색의 띠가 있다. 긴 목과 다리, 통통하고 큰 체형으로 쉽게 구별된다. 평야, 구릉지, 초원 등에 서식하며, 농경지에서도 서식한다. 5~6월 산란하며, 일부다처제로 생활한다. 과거에는 흔한 겨울철새였으나 불법사냥, 농약사용 등 잇단 개발로 위협받고 있다. 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001626', '102.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('91', '알락뜸부기', 'Coturnicops exquisitus', 'Swinhoe', '1873', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Rallidae', '뜸부기과', 'Coturnicops', '알락뜸부기속', 'exquisitus', '알락뜸부기', '두루미목 뜸부기과의 새이다. 전체 길이 15cm쯤으로 뜸부기류 중 가장 작은 종류에 속한다. 등은 올리브 갈색 바탕에 많은 검은색 반점과 흰색의 반점처럼 보이는 가느다란 횡반(橫斑)이 있다. 가슴은 황색을 띤 갈색이고 나머지 배는 흰색이다. 부리는 암갈색이고, 다리는 엷은 갈색이다. 알은 크림 갈색 바탕에 적갈색의 반점이 산재한다. 소택지, 논, 습지 풀숲, 호소 등에서 서식한다. 우리나라에는 드물게 도래하는 나그네새이다. 풀밭이나 풀 사이의 땅 위에 수초의 잎으로 접시 모양의 둥지를 만든다. 알을 6개 낳는다. 일본, 중국 동부, 한카 호, 바이칼, 우수리 등지에 분포한다. 세계적 희귀종으로 알려져 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001629', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('92', '흰배뜸부기', 'Amaurornis phoenicurus', 'Pennant', '1769', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Rallidae', '뜸부기과', 'Amaurornis', '흰배뜸부기속', 'phoenicurus', '흰배뜸부기', '두루미목 뜸부기과의 조류로 몸길이는 32.5cm가량이다. 수컷의 이마는 순백색이다. 머리꼭대기, 뒷머리, 어깨 사이, 어깨깃, 등, 허리, 옆구리는 어두운 석판 검은색이다. 턱 밑, 눈썹선, 멱, 목 옆, 귀깃 앞 끝, 가슴, 배는 순백색이다. 귀깃 뒤끝에서 몸 옆을 따라 1줄기 검은 선이 지나가고, 몸 윗면의 어두운 검은색과 아랫면의 순백색과의 경계를 이룬다. 등, 허리 양쪽, 아래꽁지덮깃은 밤색이고, 아랫배는 엷은 밤색이다. 부리는 녹색이고 다리는 엷은 황색이다. 논, 호소, 초습지, 도랑 등 갈대밭과 물가의 풀숲에 산다. 갈대나 대나무관목등의 나무 위에 둥지를 틀지만 때로는 물가 풀숲의 땅 위에도 튼다. 갈잎으로 접시 모양의 둥지를 튼다. 우리나라에는 봄과 가을에 서해안을 따라 드물게 통과하는 나그네새이다. 알은 5~7월경, 한배에 4~8개를 산란한다. 곤충류와 패류를 즐겨 먹지만 식물성인 풀씨 낟알도 먹는다. 파키스탄 서부~인도 북부, 미얀마, 중국, 일본에 이르는 지역에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001633', '32.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('93', '뜸부기', 'Gallicrex cinerea', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Rallidae', '뜸부기과', 'Gallicrex', '뜸부기속', 'cinerea', '뜸부기', '두루미목 뜸부기과에 속하는 조류이다. 몸길이는 수컷이 40cm, 암컷이 33cm이다. 수컷의 여름깃은 푸른색을 띤 검은색이며 날개는 갈색이다. 부리는 노란색이며 번식기에는 이마에서 머리꼭대기 위로 붉은 이마판이 있다. 날개와 꼬리는 검은 갈색이다. 겨울깃은 암컷과 유사하나 부리가 더 굵고 노란색이며, 암컷은 황갈색이다. 암컷의 몸은 황갈색이며, 등과 날개에는 진한 갈색 반점이 있다. 다리는 연한 녹색이며, 눈은 붉은 갈색이다. 논이나 습지에 살며 여름철새이다. 아침과 저녁에는 벼가 자라고 있는 논과 논둑에서 활발하게 활동하지만, 낮에는 주변 물가나 구릉의 풀숲 또는 논 부근의 덤불 속에 숨어 지낸다. 곤충류, 어류, 양서류, 풀이나 식물의 종자 등을 먹는다. 둥지는 논에서 벼 포기를 모아 만들거나 주변의 풀숲에서 풀줄기나 잎을 이용해 접시 모양으로 엉성하게 만든다. 6~7월에 번식하며 한 배에 3-5개 알을 낳는다. 강원도, 경기도, 충청남도 일부 지역에 서식하고 세계적으로는 일본, 타이완, 필리핀, 인도, 인도네시아, 말레이반도, 스리랑카 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001639', '36.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('94', '쇠물닭', 'Gallinula chloropus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Rallidae', '뜸부기과', 'Gallinula', '쇠물닭속', 'chloropus', '쇠물닭', '두루미목 뜸부기과에 속하는 조류로 흔한 여름철새이다. 몸길이는 약 32cm. 암수의 형태는 유사하며, 암컷과 수컷의 이마에 선명한 붉은색 이마판이 있다. 몸 전체가 검은색을 띠며, 어깨, 등, 허리, 날개깃은 어두운 갈색이다. 배 중앙과 아랫배의 각 깃털에는 잿빛 흰색 또는 흰색의 가장자리가 있다. 아래꼬리덮깃의 중앙 부분은 검은색이고 양쪽과 끝부분은 흰색이다. 아랫날개덮깃과 겨드랑이깃은 잿빛 갈색으로 흰색의 가장자리가 있다. 부리는 선명한 붉은색이며 다리는 황록색이다. 호소, 소택지, 호반가, 연못가 또는 저수지와 하구, 물 고인 곳에 살며 산란기는 5-8월이며, 한 배에 6-9개의 알을 낳는다. 각종 열매와 곤충, 연체동물, 갑각류, 환형동물 등을 먹는다. 우리나라에서는 중부 이남 전역에 서식하고 세계적으로는 호주, 뉴질랜드를 제외한 전 세계의 온대와 열대에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001641', '32.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('95', '물닭', 'Fulica atra', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Rallidae', '뜸부기과', 'Fulica', '물닭속', 'atra', '물닭', '두루미목 뜸부기과에 속하는 조류로 매우 흔한 겨울철새이자 흔한 텃새이다. 몸길이는 약 39cm. 암수의 형태는 유사하며, 전체적으로 검은색이고 체형은 통통하다. 이마판은 흰색, 부리는 엷은 살구색이다. 홍채는 적갈색, 날 때 둘째날개깃 끝이 흰색으로 보인다. 어린 새는 얼굴과 목 앞이 흰색이고, 부리가 흑갈색이다. 다리는 판족을 가지고 있어 헤엄치는데 적합하며 잠수에도 능하다. 위험할 때는 잠수를 하거나 수면을 박차고 뛰어서 도망간다. 유라시아대륙, 인도, 호주에 폭넓게 분포한다. 국내에는 습지 전역에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001643', '39.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('96', '캐나다두루미', 'Grus canadensis', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Gruidae', '두루미과', 'Grus', '두루미속', 'canadensis', '캐나다두루미', '두루미목 두루미과의 새이다. 몸이 비대하고 목과 다리가 길고 크다. 직립하면 높이 1.2m 정도이고, 날개길이는 2m가량이다. 몸 전체가 균일한 회색빛을 띠고, 뺨은 흰색이며, 정수리는 털이 없고 빨간색이다. 날 때는 목을 쭉 뻗고, 아래로 내려갈 때는 느리고 위로 오를 때는 날갯짓을 빠르게 하는 등으로 다른 새들과 구별된다. 수컷이 암컷보다 약간 큰 것을 제외하고는 암수 생김새가 비슷하다. 대부분 젖은 풀밭, 늪, 소택지 같은 넓은 초원에 보금자리를 튼다. 인가에서 멀리 떨어진 곳을 선호한다. 밤에는 휴식을 취하기 위해 큰 습지에 집합한다. 일부일처제이다. 해마다 함께 남아 번식하고, 구애함으로써 가까운 관계를 유지하고, 조화를 이루어 서로를 부른다. 봄에 이동하는 동안 짝짓기를 한다. 주행성이고, 부분적으로 철새이다. 남부에 사는 종은 연중 내내 머무는 것에 반해 북부에 사는 종은 겨울 동안 남쪽으로 이동한다. 1쌍 또는 가족이 모여 있는 것이 발견된다. 이동 중에는 농경지에서 농작물을 먹는 것을 종종 볼 수 있다. 미국 플로리다, 캐나다 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001649', '108.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('97', '재두루미', 'Grus vipio', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Gruidae', '두루미과', 'Grus', '두루미속', 'vipio', '재두루미', '재두루미는 「멸종위기 야생생물 II급」의 두루미목 두루미과 조류로 드문 겨울철새이다. 몸길이는 약 127cm이며, 암수의 형태가 유사하다. 눈 주위로 붉은색 피부가 노출되어 있다. 머리와 뒷목은 흰색, 목 앞은 회색이다. 몸은 회색이고, 첫째날개깃과 둘째날개깃은 검은색이지만, 날개를 접고 있을 때 흰색의 셋째날개깃이 덮고 있다. 어린 새는 머리꼭대기에서 뒷목까지 황갈색이다. 겨울철에 어미새는 어린 새와 함께 가족군을 형성하며, 강 하구, 갯벌, 개활지, 논, 초습지에서 생활한다. 조개류와 곤충류, 옥수수, 벼의 낱알, 풀씨, 풀뿌리 등을 먹는다. 경계심이 매우 강하며, 사람이 접근하면 주위를 감시하던 개체의 신호에 의해 무리 전체가 목과 머리를 위로 들고 날아갈 준비를 한다. 이동시기에는 여러 가족군이 모여 큰 무리를 이룬다. 극동아시아에서만 분포하는 종으로 몽골 동부, 러시아와 중국 국경지역에서 번식하고, 중국 양쯔강 유역, 한국, 일본 이즈미에서 월동한다. 국내에는 드문 겨울철새로 철원지역에서 가장 많은 개체 수가 관찰되며, 한강 하구, 임진강 유역, 순천만, 구미 해평습지, 주남저수지, 낙동강 하구 등에서도 일부가 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001650', '127.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('98', '검은목두루미', 'Grus grus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Gruidae', '두루미과', 'Grus', '두루미속', 'grus', '검은목두루미', '검은목두루미는 「멸종위기 야생생물 Ⅱ급」의 두루미과 조류이며 드문 겨울철새이다. 몸은 대부분 회색이며, 머리와 목 앞부분이 검은색이다. 우리나라의 습지, 호수, 농경지 등에서 주로 월동한다. 세계적으로 개체 수는 많지만 우리나라에서는 다른 두루미류 무리에 섞여 소수만 도래한다. 서식지의 감소 및 단편화와 먹이 부족으로 위협받고 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001651', '115.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('99', '흑두루미', 'Grus monacha', 'Temminck', '1835', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Gruidae', '두루미과', 'Grus', '두루미속', 'monacha', '흑두루미', '흑두루미는 「멸종위기 야생생물 II급」의 두루미과 조류로 드문 겨울철새이다. 몸길이 약 100cm이며, 암수의 형태가 유사하다. 흰 머리와 목을 제외한 전체가 짙은 회색이다. 이마가 검고 머리꼭대기는 붉은 피부가 노출되어 있다. 날 때 날개 전체가 검게 보인다. 몸은 검고 목의 흰색은 뚜렷하다. 어린 새는 머리와 목이 엷은 황갈색이며 몸은 흑갈색이고 이마에 검은색이 없다. 넓은 초지, 습지, 간척 농경지에서 가족단위로 생활하며, 벼를 비롯한 낟알과 식물의 줄기나 뿌리를 먹는다. 이동시기와 월동지에서는 가족군이 모여 큰 무리를 이룬다. 러시아의 아무르 유역과 중국 북동부에서 번식하고, 중국의 양쯔강 유역과 일본, 한국에서 월동한다. 국내에서는 순천만과 서산 간척지에서 주로 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001652', '100.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('100', '두루미', 'Grus japonensis', 'P.L.S.Müller', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Gruidae', '두루미과', 'Grus', '두루미속', 'japonensis', '두루미', '두루미는 「멸종위기 야생생물 I급」의 두루미과 조류로 드문 겨울철새이다. 몸길이는 약 140cm이며, 암수의 형태가 유사하다. 몸은 흰색이고, 정수리는 붉은색, 눈앞과 멱, 목, 그리고 길게 늘어진 셋째날개깃은 검은색이다. 부리는 노란색을 띤 녹색이며, 다리는 검은색이다. 어린 새는 몸은 흰색이며, 머리와 목은 연한 갈색이다. 월동지인 우리나라에서는 주로 민간인통제지역 일대의 논, 율무밭, 옥수수밭 등에서 먹이활동을 하고, 결빙된 저수지, 강의 모래톱 등을 잠자리로 이용한다. 암컷과 수컷 그리고 그들의 새끼로 형성된 가족 단위로 행동한다. 2~3월에는 암수가 마주 본 상태에서 부리를 하늘로 향하고 반복해 우는 구애 행동을 볼 수 있다. 러시아 동남부 한카호, 중국 동북지방, 일본 홋카이도, 몽골 동부 등지에서 번식하고 한국, 중국 동남부에서 월동한다. 국내에는 강원도 철원지역과 경기도 연천, 파주, 강화 등 주로 비무장지대와 민간인 통제지역 일대가 대표적인 월동지이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001653', '140.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('101', '세가락메추라기', 'Turnix tanki', 'Blyth', '1843', 'Chordata', '척삭동물문', 'Aves', '조강', 'Turniciformes', '세가락메추라기목', 'Turnicidae', '세가락메추라기과', 'Turnix', '세가락메추라기속', 'tanki', '세가락메추라기', '봄, 가을에 드물게 통과하는 나그네새이다. 몸길이는 13cm이다. 외형은 작은 메추라기처럼 보인다. 등은 갈색, 머리는 적갈색이며 흑갈색과 흰색의 반점이 있다. 배는 황갈색이고 가슴 측부와 옆구리에는 둥근 흑갈색의 반점이 많이 있다. 멱은 흰색, 다리는 선명한 황색이다.관목이 있는 개활지, 농경지, 초지, 저지의 구릉에 서식한다. 땅을 움푹하게 파고 둥지를 만든다. 알을 4개 낳는다. 포란은 수컷이 한다. 아무르강에서 황하 하류, 태국, 동남아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001657', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('102', '검은머리물떼새', 'Haematopus ostralegus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Haematopodidae', '검은머리물떼새과', 'Haematopus', '검은머리물떼새속', 'ostralegus', '검은머리물떼새', '도요목 검은머리물떼새과에 속하는 조류로 드문 텃새이다[멸종위기 야생생물 II급]. 몸길이 45cm.  암수의 형태는 유사하다. 여름깃은 등 아래, 허리, 위꼬리덮깃, 가슴 아래, 배가 흰색이며 나머지 몸 깃털은 검은색이다. 부리와 다리는 붉은색이며, 날 때 날개 윗면에 뚜렷한 흰색의 굵은 줄이 보인다. 어린 새의 등과 날개덮깃은 갈색을 띤 검은색이며, 깃 가장자리는 황갈색이다. 부리와 다리의 붉은색은 어미 새보다 흐리다. 알은 황갈색 바탕에 크고 검은 갈색 무늬와 회색 얼룩점이 있다. 도서 지역의 암초가 있는 곳, 하구의 삼각주, 갯벌에서 생활한다. 갯벌에 길고 뾰족한 부리를 깊숙이 넣어 먹이를 잡으며, 조개류의 껍데기 사이에 부리를 넣고 비틀어 연다. 번식기가 되면 수컷이 암컷 앞에 서서 머리를 숙이고 부리를 땅 위에 댄 채 좌우로 흔들어 구애한다. 둥지는 하구의 작은 섬이나 연안 무인도의 땅 위, 암초 위의 오목한 곳에 접시 모양으로 만들고 안에는 풀을 깐다. 4~5월에 알 2~3개를 낳는다. 국내에는 서남해안의 도서 지역에서 번식하고 겨울에는 해안과 갯벌 지역에 서식한다. 세계적으로 유럽, 아프리카, 인도, 일본, 중국 동부, 타이완에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001661', '45.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('103', '장다리물떼새', 'Himantopus himantopus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Recurvirostridae', '장다리물떼새과', 'Himantopus', '장다리물떼새속', 'himantopus', '장다리물떼새', '도요목 장다리물떼새과에 속하는 조류로 드물게 통과하는 나그네새이며, 국지적으로 소수가 번식하는 드문 여름철새이다. 몸길이 약 37cm. 부리는 검은색으로 가늘고 길며, 다리는 길고 분홍색이다. 날개는 검은색이고 몸의 아랫면은 흰색이다. 수컷은 머리꼭대기에서 목의 뒷면에 걸쳐 검은색 또는 회갈색의 무늬가 있으나 개체마다 차이가 있다. 수컷의 몸 윗면은 녹색 광택을 띠며, 암컷은 갈색이다. 어린 새는 머리꼭대기, 뒷목 등이 갈색을 띤다. 1998년 충남 서산 간척지에서 번식하는 것이 처음으로 확인되었으며, 여러 간척지와 습지에서 번식기록이 추가되고 있다. 봄과 가을에 서해안 간척지와 하천 등 습지에 도래한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001664', '37.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('104', '뒷부리장다리물떼새', 'Recurvirostra avosetta', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Recurvirostridae', '장다리물떼새과', 'Recurvirostra', '뒷부리장다리물떼새속', 'avosetta', '뒷부리장다리물떼새', '몸길이 약 43cm이다. 부리는 길고 가늘며, 활처럼 위로 굽었고, 끝은 뾰족하다. 여름깃은 머리 위와 옆머리, 윗목, 뒷목 및 첫째날개깃이 모두 검은색이고 나머지 부분은 흰색이다. 얕은 호수와 논, 하구, 간석지에 도래한다. 짠물과 민물이 섞이는 곳에서 먹이를 찾는데, 부리를 수면과 수평이 되게 유지하면서 좌우로 움직여 갑각류나 수생곤충 따위를 잡아먹는다. 유럽, 아시아, 중국에서 번식한다. 아프리카에는 격리되어 지내는 번식 집단이 있다. 우리나라에서는 미조로, 3회의 관찰 기록밖에 없는 희귀조이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001666', '43.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('105', '댕기물떼새', 'Vanellus vanellus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Charadriidae', '물떼새과', 'Vanellus', '댕기물떼새속', 'vanellus', '댕기물떼새', '도요목 물떼새과에 속하는 조류로 비교적 흔한 겨울철새이다. 몸길이 30cm. 암수의 형태가 유사하다. 여름깃의 이마, 머리꼭대기, 뒷머리는 검은색이며 녹색 금속광택이 있고, 뒷머리의 깃이 위로 솟아 있다. 부리 기부에서 이마 옆과 눈 위를 지나는 흰색 눈썹선이 있다. 윗가슴에는 남색 금속광택이 있는 검은색의 폭넓은 띠가 있다. 어깨깃, 등, 허리는 검은색으로 녹색 금속광택이 있고 빛에 따라 자색 금속광택도 띤다. 아랫가슴, 배, 옆구리는 흰색이다. 위꼬리덮깃은 짙고 붉게 녹슨 색이다. 다리는 갈색을 띤 살구색이다. 논, 밭, 습지, 하천, 하구의 삼각주 등에 도래한다. 초지의 지상에 오목하게 만들고 접시 모양의 둥우리를 튼다. 산란기는 3월하순~5월하순이다. 곤충류, 지렁이, 씨와 열매를 먹는다. 중부 이남에 도래하고 세계적으로는 유라시아, 아프리카 서부, 중국 남부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001669', '30.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('106', '민댕기물떼새', 'Vanellus cinereus', 'Blyth', '1842', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Charadriidae', '물떼새과', 'Vanellus', '댕기물떼새속', 'cinereus', '민댕기물떼새', '우리나라에서는 희귀한 나그네새로 알려져 있다. 몸길이 36cm이다. 머리와 목, 가슴은 회색이다. 몸의 아랫면은 흰색이다. 등과 꼬리날개 윗면은 갈색이며, 이 갈색 면과 몸 아랫면의 흰색과의 경계선은 검은색이다. 부리는 황색으로 끝이 검고 다리는 탁한 황색이다. 논, 밭, 개울가의 풀숲, 모래밭, 초습지 등에 도래한다. 번식기 이외에는 무리를 이루어 생활한다. 풀숲, 잔디, 자갈밭 등 오목한 곳에 둥지를 만든다. 산란기는 3월하순~6월상순이다. 알은 3~4개 낳아 27~30일 동안 포란한다. 곤충류, 지렁이, 파충류, 어류 등을 먹는다. 시베리아 남부, 몽골, 중국, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001670', '36.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('107', '검은가슴물떼새', 'Pluvialis fulva', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Charadriidae', '물떼새과', 'Pluvialis', '개꿩속', 'fulva', '검은가슴물떼새', '도요목 물떼새과에 속하는 조류로 국내에는 봄과 가을 비교적 흔하게 통과하는 나그네새이다. 몸길이 24cm. 암수의 형태가 유사하다. 여름깃의 몸 윗면은 황갈색과 검은색, 흰색의 무늬가 섞여 있다. 이마는 흰색이며 눈 위의 흰색 눈썹선과 연결되고 가슴 양쪽까지 폭넓은 흰색 선이 지나간다. 눈앞, 귀깃, 뺨, 턱 밑, 멱은 검은색이고, 가슴과 배도 검은색이다. 꼬리는 검은 갈색이며 흰색 또는 잿빛 흰색의 가로띠가 여러 개 있다. 부리는 검은색이고 다리는 어두운 갈색이다. 논, 해안 간척지, 염전과 주변의 초습지, 하구, 삼각주 등지에 도래한다. 번식지에서는 6~7월에 알 4개를 낳는다. 곤충, 지렁이, 갑각류, 종자 등을 먹는다. 세계적으로는 시베리아, 알래스카, 남아시아, 오스트레일리아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001672', '24.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('108', '개꿩', 'Pluvialis squatarola', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Charadriidae', '물떼새과', 'Pluvialis', '개꿩속', 'squatarola', '개꿩', '도요목 물떼새과의 조류로 봄과 가을에 한반도를 통과하는 흔한 나그네새이다. 몸길이 29.5cm. 암수의 형태가 유사하다. 몸 윗면은 검은색 바탕에 흰 반점이 줄지어 있고 아랫면은 검은색이다. 머리부터 꼬리까지 몸 옆으로 굵고 흰 선이 있다. 부리는 검은색이고 다리는 짙은 회색을 띤 검은색이다. 어린 새는 어른새 겨울깃과 유사하지만 몸 윗면의 검은색과 흰 반점이 더 뚜렷하며, 가슴과 옆구리의 줄무늬가 뚜렷하다. 날 때 보이는 겨드랑이 위의 큰 검은 반점은 다른 종과 구별되는 특징이다. 해안의 간척지, 사구, 삼각주, 해안가의 초습지 등에 도래한다. 툰드라 지역의 이끼로 덮인 땅 위에 접시 모양의 둥지를 튼다. 산란기는 6월하순~7월상순이며, 한배의 산란 개수는 4개이다. 식성은 동물성인 지렁이, 새우, 패류, 곤충류 등이고 식물의 씨도 먹는다. 구북구 대륙 북부에서 번식하고 겨울에는 남쪽으로 이동하여 남반구 남위 40도까지의 지역의 갯벌이나 강어귀에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001673', '29.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('109', '호사도요', 'Rostratula benghalensis', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Rostratulidae', '호사도요과', 'Rostratula', '호사도요속', 'benghalensis', '호사도요', '희귀한 나그네새이자 겨울철새이다. 몸길이는 약 23.5cm이다. 암컷이 수컷보다 크고 몸 빛깔도 더 선명하다. 암컷의 머리는 갈색이며 넓은 흰색 띠가 중앙선으로 이마에서 뒷머리까지 이어져 있다. 눈 주위의 반달 모양 흰색 무늬가 뚜렷하다. 목과 윗가슴은 밤색, 멱과 몸 아랫면은 흰색이다. 수컷은 목과 윗가슴에 회색 세로무늬가 있다. 등은 암수 모두 녹색을 띤 갈색에 검은색 굵은 체크무늬와 세로무늬가 있다. 부리는 길고 붉은색을 띠며 부리가 시작되는 부위와 다리는 녹색이다. 암컷은 구애와 과시 행동을 한다. 봄에서 가을에 걸쳐 한배에 4~6개의 알을 낳아 수컷이 품는다. 암컷은 새로운 수컷과 교미하여 알을 낳는다. 잡식성으로 곤충, 조개, 지렁이, 화본과 식물 및 그 밖의 풀씨를 먹는다. 아프리카, 아시아, 오스트레일리아에 분포한다. 천연기념물 제449호로 지정되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001685', '23.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('110', '물꿩', 'Hydrophasianus chirurgus', 'Scopoli', '1786', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Jacanidae', '물꿩과', 'Hydrophasianus', '물꿩속', 'chirurgus', '물꿩', '긴 꼬리와 긴 발가락, 긴 목 및 가늘게 빠져나온 첫째날개깃이 특징적이다. 뒷목은 연한 황색이며, 얼굴과 멱, 앞목 및 위쪽 가슴, 접고 있는 날개의 위쪽 부분은 흰색이다. 아래쪽 가슴과 등, 날개는 검은색이다. 나머지 부분은 검은색이다. 저수지 및 가시연꽃 밀생 지역에 서식한다. 분포 지역 내에서 북쪽 번식 집단은 남쪽으로 이동하지만, 남부지방 번식 집단은 텃새이다. 중국(양쯔 강 이남), 일본(규슈, 후쿠오카)에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001688', '48.1');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('111', '멧도요', 'Scolopax rusticola', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Scolopax', '멧도요속', 'rusticola', '멧도요', '봄과 가을에 한반도를 드물게 통과하는 나그네새이나 드물지 않게 월동하기도 한다. 몸길이 약 34cm이다. 다리는 짧고 부리는 곧고 길다. 암수 같은 빛깔이다. 몸 윗면은 검은색과 갈색, 회색의 작고 복잡한 무늬로 덮여 있으며, 아랫면은 연한 갈색 바탕에 어두운 갈색 물결무늬가 있다. 턱 밑은 흰색이다. 단독으로 또는 암수가 함께 생활한다. 야행성이며 낙엽활엽관목이나 잡목 숲에서 번식한다. 나무뿌리 부근의 오목한 곳에 낙엽이나 마른풀로 접시 모양의 둥지를 만든다. 4월상순~6월하순에 한배에 알 3~4개를 낳는다. 모족류, 곤충류, 다족류, 갑각류, 연체동물 등과 아울러 식물의 열매도 먹는다. 유라시아 중북부에서 번식하고 아프리카 북부, 인도, 중국(남부), 한국, 일본 등지에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001691', '34.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('112', '꼬마도요', 'Lymnocryptes minimus', 'Brünnich', '1764', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Lymnocryptes', '꼬마도요속', 'minimus', '꼬마도요', '봄과 가을에 우리나라를 통과하는 나그네새이다. 날개길이 99~110mm, 꼬리길이 42~51mm, 부리 39~47mm이다. 암수가 같은 빛깔이다. 몸은 전체적으로 어두운 갈색이다. 등 가운데로 광택이 있는 검붉은 얼룩무늬가 지나간다. 머리꼭대기 가운데로는 검은 선이 있다. 턱 밑과 멱은 흰색이고 목 옆은 흰색 바탕에 검은 갈색 얼룩무늬가 있다. 가슴과 배와 꼬리밑덮깃은 흰색이다. 꼬리깃은 12장이며 꼬리 모양은 쐐기꼴이다. 바닷가 또는 호수 등의 풀밭이나 논, 하천, 습지 등에서 산다. 주로 단독 생활을 하며 밤에만 활동한다. 산란기는 6월상순~8월상순이고, 알은 4개 낳으며 포란은 암컷만 한다. 주로 곤충류와 환형동물, 작은 물고기, 게, 새우 따위를 잡아먹는다. 구북구 한대와 시베리아에서 번식하고 동남아시아, 남유럽, 아프리카에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001693', '21.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('113', '청도요', 'Gallinago solitaria', 'Hodgson', '1831', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Gallinago', '꺅도요속', 'solitaria', '청도요', '도요목 도요과의 조류로 우리나라에는 드문 겨울철새/드문 나그네새이다. 몸길이 30cm. 암수의 형태가 유사하다. 몸 윗면은 어두운 갈색에 붉은 갈색과 회색의 점무늬가 불규칙하게 있다. 목과 윗가슴은 갈색에 흰색의 무늬가 약간 있으며 배 쪽은 흰색이고 옆 몸과 아랫가슴에는 어두운 갈색의 세로줄이 있다. 날개깃은 갈색이다. 월동기에는 주로 삼림 속의 계류나 습지에서 홀로 생활하는 경우가 많다. 10월경에 우리나라에 도래해 이듬해 4월 중순까지 관찰된다. 번식기에는 산의 고지대에 둥지를 짓고 알을 5개쯤 낳는다. 주로 모족류, 복족류, 곤충류 등을 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001695', '30.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('114', '큰꺅도요', 'Gallinago hardwickii', 'J.E.Gray', '1831', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Gallinago', '꺅도요속', 'hardwickii', '큰꺅도요', '우리나라를 드물게 통과하는 나그네새로 몸길이 27.5~31.5cm이다. 등에는 노란빛이 도는 짙은 갈색 세로줄과 무늬가 있다. 목과 윗가슴은 붉은빛이 도는 갈색이다. 옆구리에는 꺅도요와 달리 가로무늬가 없다. 부리 기부는 노란빛이 도는 녹색이고, 끝부분은 어두운 갈색이다. 다리는 노란빛이 도는 어두운 녹색이다. 산란기는 4월 말~6월로 풀숲에 둥지를 틀고 알 4개를 낳는다. 우리나라의 제주도, 어청도, 흑산도 등 남해안 도서지역 일대와 강릉에서 관찰기록이 있다. 연해주 동부, 사할린 남부, 쿠릴 열도, 홋카이도에서 번식하고 오스트레일리아, 태즈메이니아, 뉴기니 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001696', '29.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('115', '바늘꼬리도요', 'Gallinago stenura', 'Bonaparte', '1830', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Gallinago', '꺅도요속', 'stenura', '바늘꼬리도요', '봄과 가을에 한반도를 통과하는 흔하지 않은 나그네새이다. 깃털은 전체가 적갈색으로 등에 좁은 흰색 선이 있다. 날 때에 둘째날개깃 가장자리에 흰색이 보인다. 꼬리깃은 약 26개이며, 바깥꼬리깃 7쌍은 바늘꼬리로 딱딱하고 좁다. 부리는 곧고 길다. 꺅도요의 무리와 섞이며 주로 논에서 볼 수 있다. 습지나 풀밭 땅 위에 접시 모양으로 둥지를 짓고 마른 풀잎을 깐다. 산란 개수는 4개이다. 산란기는 5월하순경이다. 주로 지렁이나 곤충류를 먹고산다. 러시아 북부에서 번식하고 동남아시아, 인도, 파키스탄 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001697', '26.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('116', '꺅도요사촌', 'Gallinago megala', 'Swinhoe', '1861', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Gallinago', '꺅도요속', 'megala', '꺅도요사촌', '나그네새로서 4월과 10월에 우리나라를 통과한다. 바늘꼬리도요와 비슷하나 바깥쪽 꼬리깃은 그다지 가늘지 않으며 끝부분에 2개의 가로무늬가 있고 꼬리깃의 수가 20개로 되어 있으며 바깥쪽의 5~7개는 바늘꼬리이다. 중앙의 5~6개는 폭이 넓으며 끝부분에 붉은 갈색의 넓은 띠가 있는 점이 다르다. 논이나 호수 습지의 갈대밭이나 풀밭 등에서 생활한다. 단독 또는 2~3마리의 작은 떼로 생활한다. 둥지는 땅 위에 접시 모양으로 짓는다. 산란기는 5월하순경이며 산란 개수는 4개이다. 연체동물, 모족류, 곤충류, 갑각, 물고기, 식물의 열매를 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001698', '28.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('117', '꺅도요', 'Gallinago gallinago', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Gallinago', '꺅도요속', 'gallinago', '꺅도요', '봄과 가을에 흔히 통과하는 나그네새이며 중부 이남에서는 흔히 월동하는 겨울새이다. 몸길이 26cm이다. 암수 같은 빛깔이다. 머리는 검고, 몸 윗면은 짙은 갈색으로 등에 흰 세로 줄무늬가 4개 있다. 배와 아래꼬리덮깃은 흰색이며 앞가슴은 황갈색에 검은 점무늬가 많다. 날개 위·아래에 흰 줄무늬가 있으며 꼬리깃은 적갈색이다. 바닷가 또는 호수 등의 풀밭이나 논 하천 습지 등에서 산다. 주로 밤에 활동한다. 산란기는 3~7월하순이다. 알은 4~5개 낳는다. 연체동물, 모족류, 곤충류, 갑각, 물고기, 식물의 열매를 먹는다. 세계 전역에 분포하며 겨울에 북부의 번식 집단은 온대, 아열대 및 열대 지역으로 이동한다. 남부의 번식 집단은 주로 정주한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001699', '26.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('118', '긴부리도요', 'Limnodromus scolopaceus', 'Say', '1822', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Limnodromus', '긴부리도요속', 'scolopaceus', '긴부리도요', '몸길이는 30cm이다. 부리는 곧고 길며 검은색이고, 기부는 엷은 황갈색을 띠고 있다. 머리와 몸의 윗면은 어두운 회색이고 깃털 가장자리는 엷은 색이다. 얼굴에서 목, 가슴, 겨드랑이는 엷은 회갈색이고, 옆가슴에는 흑갈색의 가로무늬가 있으며, 꼬리 쪽으로 갈수록 색깔이 짙어진다. 몸의 아랫면은 흰색이며 엷은 갈색 무늬가 있다. 다리는 황록색이다. 연안의 개펄과 낮은 내륙 수변, 습지, 강 하구에서 관찰된다. 시베리아, 알래스카, 캐나다 등지에서 번식하고, 서태평양 해안과 일본에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001701', '30.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('119', '큰부리도요', 'Limnodromus semipalmatus', 'Blyth', '1848', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Limnodromus', '긴부리도요속', 'semipalmatus', '큰부리도요', '도요목 도요과의 새이다. 몸길이는 35cm 정도로 큰뒷부리도요와 비슷하지만, 부리는 직선이며 끝부분이 뭉툭하고 전체가 검은색이므로 구별된다. 여름깃은 머리와 몸의 아랫면 전체가 적갈색이다. 겨울깃의 경우 눈썹선은 흰색, 몸의 윗면은 회색을 띤 갈색이며 깃 가장자리는 흰색이다. 어린 새의 경우 목과 옆구리는 황갈색이고 몸의 윗면은 어두운 갈색이며 깃털 가장자리는 흐린 갈색이다. 어두운 눈선이 뚜렷하다. 큰소리로 ''칩-칩''하고 운다. 유사종 흑꼬리도요는 부리의 기부가 분홍색이며 꽁지 끝이 검은색이므로 구별된다. 갯벌, 하구에서 드물게 관찰된다. 우리나라에서는 보기 드문 봄, 가을 나그네새이다. 중국 만주 지역, 러시아 우수리에서 번식한다. 우리나라를 통과하여 동남아 지역에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001702', '35.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('120', '쇠부리도요', 'Numenius minutus', 'Gould', '1841', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Numenius', '마도요속', 'minutus', '쇠부리도요', '도요목 도요과의 희귀한 새이다. 몸길이는 33cm쯤으로 암수가 같은 빛깔이다. 여름깃은 이마와 머리 위 및 뒷머리가 검은 갈색이고 머리의 중앙에는 붉은 회색 줄이 있다. 눈썹선은 연한 노란색이고 눈 밑에는 검은 갈색의 줄이 있다. 귀덮깃과 뺨 뒷목 및 옆목은 연한 노란색이고 각 깃에는 검은 갈색의 작은 축 무늬가 있다. 턱 밑과 목은 노란색이며 어깨깃과 등은 검은 갈색이고 깃의 가장자리는 붉은 회색이다. 꽁지밑덮깃은 연한 노란색이고 갈색의 가로무늬가 있으며, 꽁지는 갈색이고 검은 갈색의 가로무늬가 있다. 우리나라에서 4~5월과 9~10월경에 이동하는 나그네새이다. 해안 갯벌과 하구, 내륙의 물가 개활지에 서식한다. 번식은 확실히 알려져 있지 않다. 주로 갑각류, 복족류, 모족류 등을 먹고 산다. 우리나라 개성, 연안, 용암포 등에서 관찰되었다. 시베리아, 몽골 등의 동아시아에서 번식하고 필리핀, 모로코, 오스트레일리아 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001707', '33.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('121', '중부리도요', 'Numenius phaeopus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Numenius', '마도요속', 'phaeopus', '중부리도요', '도요목 도요과의 조류로 우리나라에는 봄과 가을에 흔하게 통과하는 나그네새이다. 몸길이 42cm. 암수의 형태가 유사하다. 길고 아래로 굽은 부리는 머리 길이의 2배 정도이다. 정면에서 보이는 머리의 중앙선은 백색이며 머리옆선은 암갈색이다. 몸 아랫면은 옅은 갈색이며 암갈색의 줄무늬가 있다. 날 때 꼬리 기부에서 등 중앙까지 이어지는 흰색이 특징적이다. 어린 새는 어른 새에 비해 부리가 짧고 아래로 덜 굽었다. 전국의 갯벌, 강 하구, 습초지, 농경지에서 먹이를 찾는다. 무리 지어 생활하는 경우가 많으며, 갑각류, 조개 등을 잡아먹는다. 유라시아 대륙 북부와 북미 북부에서 번식하고, 아프리카, 중동, 인도, 동남아시아, 호주, 북미 남부, 남미에서 월동한다. 국내에는 흔한 나그네새이며, 소수가 월동을 하기도 한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001708', '42.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('122', '마도요', 'Numenius arquata', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Numenius', '마도요속', 'arquata', '마도요', '도요목 도요새과에 속하는 조류로 국내에는 봄과 가을에 흔하게 통과하는 나그네새이며 일부는 하구와 갯벌에서 월동한다. 몸길이는 50~60cm. 암수의 형태가 유사하다. 부리와 다리가 길다. 몸 전체가 회갈색으로 배의 아랫부분은 흰색이며, 날고 있을 때 아랫날개덮깃 및 허리에서 꼬리까지 흰색이 뚜렷하게 보인다. 부리는 아래쪽으로 길게 구부러져 있으며, 머리 폭의 3배 정도의 길이이다. 유사종인 알락꼬리마도요는 배의 아랫부분과허리가 갈색이다. 해안의 갯벌, 하구에 서식하며 나그네새 또는 겨울철새로 주로 3~6월, 8~9월에 해안의 갯벌이나 수변에서 걸어 다니면서 먹이를 잡는 것이 관찰된다. 갯벌에 긴 부리를 넣고 갑각류 및 소형 수서무척추동물을 잡아먹는다. 서해안과 남해안 갯벌 및 하구에서 관찰되고 세계적으로는 러시아, 중국, 일본, 아프리카, 인도, 동남아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001709', '55.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('123', '알락꼬리마도요', 'Numenius madagascariensis', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Numenius', '마도요속', 'madagascariensis', '알락꼬리마도요', '도요목 도요과에 속하는 조류로 국내에는 봄과 가을 비교적 흔하게 통과하는 나그네새이나 국제적으로는 매우 희귀한 종이다. [멸종위기 야생생물 Ⅱ급] 몸길이 58.5-61.5cm. 마도요와 모습이 비슷하다. 아래로 휘어진 긴 부리는 머리길이의 3배 정도이며, 암컷이 수컷보다 길다. 날 때 등과 허리는 갈색으로 보이며, 날개아랫면은 암갈색의 조밀한 줄무늬가 흩어져 있다. 서남 해안의 갯벌과 강 하구, 물 고인 농경지 습초지에 도래한다. 주로 갯벌과 해안가에서 게를 잡아먹으며, 기타 갑각류와 갯지렁이도 즐겨 먹는다. 세계자연보전연맹의 적색목록에 취약(VU)으로 등재된 국제적인 멸종위기종이다. 전 세계 생존 집단은 32,000개체로 추정되며, 서식지 파괴와 오염으로 인한 개체수의 감소가 심각한 상황이다. 국내의 갯벌 매립으로 중간 기착지 기능이 축소되어 개체군의 위협요인으로 작용하고 있다. 시베리아 동북부, 중국 동북부에서 번식하고, 필리핀, 뉴기니, 호주에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001710', '60.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('124', '학도요', 'Tringa erythropus', 'Pallas', '1764', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Tringa', '삑삑도요속', 'erythropus', '학도요', '도요목 도요과의 새이다. 몸길이는 32.5cm, 날개 15.2~17.0cm, 꽁지 6.2~7.2cm가량이다. 암수가 같은 빛깔이다. 여름깃은 머리와 목과 배 쪽이 암회색이며 턱 밑과 뒷목깃의 끝부분은 흰색이다. 등 쪽은 약간의 금속광택이 나며 흰색의 무늬가 흩어져 있다. 허리는 흰색이며 꽁지위덮깃은 검은색 바탕에 흰색의 가로무늬가 있다. 꽁지는 검은색에 회색의 가로무늬 또는 흰색 무늬가 있으며 겨드랑이와 꽁지밑덮깃에는 흰 세로무늬가 있다. 겨울깃은 머리와 등 쪽이 회갈색이고 무늬가 없으며 옆목에는 연한 회색의 세로무늬가 있다. 바닷가의 간석지 등에서 서식한다. 산란수는 4개이다. 주로 곤충을 먹지만 작은 조개류, 올챙이, 작은 물고기, 갑각류 등도 먹고 산다. 나그네새로서 봄과 가을에 우리나라 서울, 일산, 장단, 개성, 안주, 해주, 만포, 온성, 용암포, 포항 등지를 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001712', '32.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('125', '붉은발도요', 'Tringa totanus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Tringa', '삑삑도요속', 'totanus', '붉은발도요', '도요목 도요과에 속하는 조류로 국내에는 봄과 가을에 흔하게 통과하는 나그네새이며, 매우 적은 수가 서해안과 일부 도서지역에서 번식하는 매우 드문 여름철새이다. 몸길이 27.5cm. 날 때 날개 뒷부분에 흰색 부분이 넓게 보인다. 몸 윗면은 갈색이며 검은색과 회색의 얼룩무늬가 있다. 아랫면은 흰색 바탕에 갈색 세로무늬와 얼룩이 빽빽하다. 다리와 부리가 붉고 부리 끝은 검다. 해안가의 소택지, 간척지, 염전, 해안 가까운 초습지, 하구의 삼각주에 서식하는 나그네새이며 물 고인 논, 염전, 갯벌에서 작은 무리를 이루어 서식한다. 3~5월에 알을 4~5개 정도 낳는다. 주로 곤충류, 연체동물, 갑각류 등을 먹고 산다. 주로 서남부 해안을 통과하며 세계적으로는 알타이-아무르, 히말라야, 일본, 인도, 스리랑카, 미얀마, 말레이 반도, 필리핀에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001713', '27.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('126', '쇠청다리도요', 'Tringa stagnatilis', 'Bechstein', '1803', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Tringa', '삑삑도요속', 'stagnatilis', '쇠청다리도요', '도요목 도요과의 조류로 국내에는 봄과 가을 이동시기에 적은 수가 통과하는 나그네새이다. 몸길이 25cm. 암수의 형태가 유사하다. 어른새는 등 쪽이 어두운 회색이며 각 깃에 검은색의 세로줄과 가로무늬가 있다. 배 쪽은 흰색이고 부리는 어두운 갈색 또는 검은색이다. 몸의 윗면은 회갈색이고 각 깃에는 검은색의 무늬가 있으며 깃의 가장자리는 흰색이다. 몸의 아랫면은 모두 흰색이다. 바닷가, 호수, 습지, 풀밭 등에 서식한다. 단독 또는 4~5마리씩 작은 떼를 지어 생활한다. 5월상순~6월하순에 한배에 4개 정도 알을 낳는다. 수서곤충류나 민물조개 등을 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001714', '25.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('127', '청다리도요', 'Tringa nebularia', 'Gunnerus', '1767', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Tringa', '삑삑도요속', 'nebularia', '청다리도요', '도요목 도요과에 속하는 조류로 국내에는 봄과 가을에 흔하게 통과하는 나그네새이다. 몸길이 35cm. 날개는 17~19cm이다. 여름깃은 머리와 목과 등 쪽이 어두운 회색에 굵은 검은색의 세로무늬가 있다. 몸 윗면은 회갈색에 깃 일부는 검으며, 아래등과 허리와 꼬리위덮깃은 흰색이다. 몸 아랫면은 흰색이다. 겨울깃은 등 쪽이 갈색이고 윗가슴에 있는 무늬는 약해진다. 어린 새는 몸 윗면에 갈색 기운이 강하고 깃의 가장자리는 흰색이며 이마와 옆머리는 흰색인데 검은색의 축무늬가 있다. 바닷가, 하구의 삼각주, 염전, 습지, 호수에 서식하는 흔한 나그네새로서 4~5월, 7~10월경 볼 수 있다. 곤충, 갑각류, 연체동물인 복족류 및 작은 물고기 등을 먹는다. 5월에 4개 정도의 알을 낳는다. 세계적으로는 유라시아대륙 북부에서 번식하고, 아프리카, 인도, 동남아시아, 호주에서 월동한다. 국내에는 봄과 가을 이동시기 전국의 해안과 습지에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001715', '35.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('128', '청다리도요사촌', 'Tringa guttifer', 'Nordmann', '1835', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Tringa', '삑삑도요속', 'guttifer', '청다리도요사촌', '도요목 도요과에 속하는 조류이다. 몸길이는 29~32cm이며, 몸길이에 비해 다리가 짧은 것이 특징이다. 크고 굵은 부리는 길고 위로 휘어지며, 기부가 크고 노란색을 조금 띤다. 다리는 길고 노란색이다. 암수 여름깃은 머리와 가슴에는 검은색의 반점이 뚜렷하고, 몸의 윗면은 검게 보인다. 겨울깃은 머리와 목에 줄무늬가 약하게 있으며, 몸의 윗면은 흐린 회색이다. 부리는 어두운 갈색으로 끝이 검고 기부는 황색이며, 다리는 녹색을 띤 황갈색이다. 날 때 꼬리 뒤로 다리가 약간 나와 있다. 야외에서는 청다리도요보다 뿌옇게 보인다. 알은 검은 갈색 반점이 있는 청록색이다. 봄과 가을에 적은 수가 서해안 일대를 지나가는 매우 드문 나그네새다. 해안 갯벌이나 강 하구에 도래하며, 빠르게 뛰어가 게류를 잡아먹는다. 알은 약 4개 정도를 낳는다. 갑각류, 어류, 연체동물 등을 먹는다. 서해안, 남해안에서 관찰되고 세계적으로는 사할린에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001716', '30.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('129', '큰노랑발도요', 'Tringa melanoleuca', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Tringa', '삑삑도요속', 'melanoleuca', '큰노랑발도요', '캐나다, 남아메리카 남부, 알래스카에 분포하며, 저지대의 습지 주변부터 툰드라 지역, 아고산 지역의관목림 등 다양한 곳에 서식한다. 부리의 길이는 머리길이보다 1.5배 더 길며, 다리는 노란색이다. 물가에서 곤충, 연체동물, 십각류 등을 먹으며, 물가 주변 얕게 파인 곳에 둥지를 짓고 4개의 알을 낳는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001717', '34.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('130', '삑삑도요', 'Tringa ochropus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Tringa', '삑삑도요속', 'ochropus', '삑삑도요', '봄과 가을에 우리나라를 찾아오는 나그네새이자 일부는 겨울에 우리나라에 머무는 겨울철새이다. 몸의 윗면은 녹색이 도는 갈색이며, 눈썹선 앞과 눈 주위가 흰색이다. 꼬리에는 굵은 2-3개의 검은색 줄이 있으며, 날 때 날개의 아랫면은 어두운 갈색으로 보인다. 여름깃은 머리와 가슴에 세로로 갈색 줄무늬가 뚜렷하게 있으며, 겨울깃은 머리와 가슴의 줄무늬가 희미해진다. 하천, 저수지, 논, 강 등의 수심이 얕은 물가에서 볼 수 있으며, 끊임없이 움직이며 물 가장자리를 따라 진흙 속에서 작은 무척추동물을 잡는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001718', '23.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('131', '알락도요', 'Tringa glareola', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Tringa', '삑삑도요속', 'glareola', '알락도요', '도요목 도요과에 속하는 조류로 국내에는 봄과 가을에 흔하게 통과하는 나그네새이다. 몸길이 약 22cm. 뚜렷한 흰색의 눈썹선이 있으며, 다리는 노란색이다. 몸 윗면은 회갈색이며 큰 흰 반점과 검은 반점이 흩어져 있다. 삑삑도요와 유사하지만 서로 서식지가 다르며, 날 때 삑삑도요에 비해 날개 아랫면이 하얗게 보인다. 하천, 호수, 논밭, 바닷가 부근의 습지에 서식하고 곤충류, 갑각류를 주로 먹으며 연체동물인 조개류도 먹는다. 축축한 땅 위에 풀잎, 가느다란 나뭇가지 등을 이용하여 밥공기 모양의 둥지를 짓고 알자리에는 풀잎이나 줄기를 많이 깐다. 알은 4개를 낳고 주로 암컷이 품는다. 전국적으로 물 고인 농경지와 하천 간척지 등 얕은 물이 고인 습지에서 봄철에 많은 수가 관찰된다. 세계적으로는 구북구 툰드라에서 한대, 일부 온대까지 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001719', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('132', '뒷부리도요', 'Xenus cinereus', 'Güldenstädt', '1774', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Xenus', '뒷부리도요속', 'cinereus', '뒷부리도요', '도요목 도요과의 조류로 국내에는 봄과 가을에 흔하게 통과하는 나그네새이다. 몸길이 23cm. 암수의 형태가 유사하다. 부리가 길고 위로 굽어 올라가 있다. 윗면은 연한 회갈색 바탕에 검은 세로무늬가 있다. 몸 아랫면은 흰색이고 목과 윗가슴에 잿빛 갈색 무늬가 있다. 다리는 노란색이다. 간석지, 염전, 논, 하구의 삼각주, 습지의 풀밭에서 서식한다. 무리를 지어 생활한다. 산란기는 5월하순~6월 중순이고 알 4개를 낳는다. 주로 게와 기타 갑각류들을 먹는다. 유라시아대륙 북부에 걸쳐 번식하며 인도, 동남아시아, 호주에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001721', '23.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('133', '깝작도요', 'Actitis hypoleucos', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Actitis', '깝작도요속', 'hypoleucos', '깝작도요', '도요목 도요과의 조류로 국내에는 흔한 나그네새/흔한 여름철새이지만 남부에는 드물게 월동도 한다. 몸길이 20cm. 부리는 곧고 머리 길이와 거의 같다. 몸 윗면은 녹색을 띤 갈색이며, 등과 날개덮깃의 가장자리에 흑갈색의 무늬가 있다. 가슴에는 갈색의 줄무늬가 있으며, 가슴 아래쪽은 날개 앞쪽으로 흰색이 올라와 있다. 날 때 날개에는 흰 선이 뚜렷하며, 허리는 등과 같은 색이며, 다른 도요류와 달리 날개를 몸 아래로 내린 상태에서 빠른 날갯짓을 반복하며 지면이나 물가로 낮게 날아가는 행동이 특징이다. 봄·가을에 전국 각지의 강가나 호숫가·바닷가에 내려앉아 단독으로 지내거나 작은 무리를 이룬다. 북반구의 북부에서 북극권에 걸쳐 널리 분포하며 동남아시아·호주·아프리카까지 내려가 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001723', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('134', '꼬까도요', 'Arenaria interpres', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Arenaria', '꼬까도요속', 'interpres', '꼬까도요', '도요목 도요과의 조류로 봄과 가을에 한반도를 흔하게 통과하는 나그네새이다. 몸길이 22cm. 다리가 짧고 땅딸막한 체형이다. 머리는 검은색 바탕에 눈썹과 눈앞, 이마, 턱 밑, 멱 부분이 흰색이다. 목과 가슴에 넓은 검은색 띠가 있다. 여름깃의 등은 적갈색에 검은색 얼룩무늬가 있다. 몸의 아랫면은 흰색이다. 꼬리는 검은색이다. 다리는 주황색이다. 바닷가 암초, 간석지, 농경지에서 생활한다. 해안의 사력지, 사지, 암초가 있는 곳, 해상의 암초, 해안의 간척지, 하구의 삼각주 등지에 도래한다. 암초의 선반이나 모래밭, 초지의 오목한 곳에 풀로 둥지를 만든다. 산란기는 6월하순~7월하순이며, 한배의 산란수는 3~4개이다. 갑각류, 작은 어류, 곤충류, 거미류, 지렁이와 벼 등을 먹는다. 유라시아대륙 북부, 북미 북부의 툰드라지대에서 번식하고, 아프리카, 남아시아, 호주, 중남미에서 월동한다. 국내에는 봄과 가을에 비교적 흔하게 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001727', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('135', '붉은어깨도요', 'Calidris tenuirostris', 'Horsfield', '1821', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'tenuirostris', '붉은어깨도요', '도요목 도요과에 속하는 조류로 국내에는 봄과 가을에 비교적 흔하게 통과하는 나그네새이다[멸종위기 야생생물 Ⅱ급]. 몸길이 28cm. 부리가 머리 길이보다 길다. 몸 윗면은 흑갈색이며 여름깃에는 어깨에 적갈색 무늬가 있지만 먼 거리에서 잘 보이지는 않는다. 목, 턱 밑, 멱에는 검은색의 작은 무늬가 있다. 등은 검은 갈색이며 어깨깃도 등과 비슷하다. 가슴과 옆구리에 흑색 반점이 있고, 날 때 허리 부분의 흰색이 뚜렷하다. 겨울깃의 몸 윗면은 회색을 띤다. 전국의 갯벌, 하구, 염전 등지에서 관찰된다. 주로 무리를 이루어 행동하며 단독으로 생활하는 경우는 거의 없다. 산악 암석지의 이끼가 있는 오목한 곳에 둥지를 튼다. 한배 산란수는 4개이다. 갯지렁이, 조개류, 갑각류 등을 먹는다. 세계자연보전연맹 적색자료목록에 취약종(VU)으로 분류된 국제보호조다. 과거 서해안 갯벌에 수많은 개체가 기착하였지만, 최근 그 숫자가 크게 줄었다. 전 세계 집단은 290,000여 개체로 추정해 다른 멸종위기종에 비해 비교적 개체수가 많은 편이지만 서식지의 파괴로 인한 개체수 감소가 심각한 상황이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001729', '28.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('136', '붉은가슴도요', 'Calidris canutus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'canutus', '붉은가슴도요', '도요목 도요과에 속하는 조류로 국내에는 봄과 가을에 적은 수가 통과하는 나그네새이다. 몸길이 23.5cm. 여름깃은 몸 윗면에 흑갈색 반점이 있으며 깃 가장자리는 흰색이다. 얼굴에서 배까지 선명한 적갈색이다. 배 아래쪽에서 아래꼬리덮깃까지는 흰색에 흑갈색 반점이 있다. 겨울깃은 몸 윗면이 회갈색이고 깃 가장자리는 흰색, 몸 아랫면은 흰색이며 가슴옆과 옆구리에 반점이 있다. 어린 새는 성조의 겨울깃과 유사하지만 등과 어깨는 깃 가장자리에 흑색 띠가 있고 깃 끝이 백색으로 비늘 무늬처럼 보인다. 유사종인 붉은어깨도요는 몸집이 더 크고 부리가 머리길이보다 약간 길다. 이끼와 선태류 등으로 덮인 건조한 구릉의 움푹한 곳에 둥지를 튼다. 산란기는 6월 중순~7월상순이고 한배의 산란수는 4개이다. 갑각류, 지렁이, 곤충류를 즐겨 먹는다. 구북구 동부, 북극 툰드라, 쿠릴 열도, 팔라우 제도, 일본, 타이완 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001730', '23.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('137', '세가락도요', 'Calidris alba', 'Pallas', '1764', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'alba', '세가락도요', '도요목 도요과의 조류로 봄과 가을에 우리나라를 통과하는 흔한 나그네새이며, 일부는 강 하구나 해안가에서 월동하는 겨울철새이다. 몸길이 20cm. 여름깃은 몸 윗면이 적갈색에 검은 무늬가 있으며, 아랫면은 흰색이다. 겨울깃은 몸 윗면이 엷은 회색이다. 부리와 다리는 검은색이다. 해안, 간척지, 삼각주 등에 도래한다. 풀이 우거진 땅 위의 오목한 곳에 둥지를 튼다. 산란기는 6월 중순~7월 중순이며, 한배의 산란수는 4개이다. 식성은 갑각류, 작은 물고기, 패류, 지렁이, 곤충류 등을 포식하며 그 밖에 식물 종자나 싹 또는 선류, 규조류 등도 먹는다. 북극에 가까운 툰드라 지역, 그린란드에서 번식하고, 동남아시아, 오스트레일리아, 아프리카, 남미 등에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001731', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('138', '좀도요', 'Calidris ruficollis', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'ruficollis', '좀도요', '도요목 도요과의 조류로 국내에는 봄과 가을 흔하게 통과하는 나그네새이다. 몸길이 15cm. 가로로 긴 통통한 체형으로 다리는 검은색이다. 여름깃은 머리, 목, 윗가슴이 적갈색이고 부리 주위는 흰색이다. 등과 어깨는 적갈색이다. 겨울에는 몸 윗면이 회갈색이며 깃 축이 가늘고 검게 보인다. 어린 새의 경우에는 등이 어두운 갈색이고 깃털 가장자리가 적갈색이다. 염전, 논, 갯벌, 강 하구에서 집단을 이루어 생활한다. 보통 민물도요와 혼성하여 행동하는 경우가 많다. 6월하순에서 7월상순에 4개의 알을 낳아 암수가 함께 품는다. 서해안의 염전 지대와 부근의 논, 물웅덩이 등지에서 자주 눈에 띈다. 동아시아의 시베리아 동북부에서 번식하고 가을에 남쪽으로 내려가 인도·미얀마·말레이시아·필리핀·오스트레일리아 등지에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001732', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('139', '작은도요', 'Calidris minuta', 'Leisler', '1812', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'minuta', '작은도요', '도요목 도요과의 새이다. 아름다운 무늬가 있는 새끼는 등 아랫부분의 흰색의 깔끔한 줄무늬로 쉽게 구분할 수 있다. 겨울깃을 가진 새는 작고, 민물도요보다 활동적이다. 배는 깨끗하고 흰색이다. 부리는 짧고 깔끔하며 직선이고 검은색이다. 실제로 여름·겨울깃을 가진 새는 세가락도요와 비슷하지만, 작은도요는 색깔이 희미하지 않다. 땅딸막한 형태, 반점이 있는 깃털, 검은 다리로 근연종과 잘 구분된다. 물 근처, 고지대, 해안에서 번식한다. 겨울에는 개펄과 소택지에서 월동한다. 내륙이나 해안가 모두에서 나타난다. 일반적으로 맑은 물에서 생활한다. 규칙적으로 7~10월의 가을에 이동한다. 주로 9월에 가장 많이 이동한다. 잉글랜드(동부, 노퍽 남부), 노르웨이 북쪽 해안, 지중해 연안 등지에서 번식한다. 지중해 연안에서 월동하고, 유럽 습지대를 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001733', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('140', '흰꼬리좀도요', 'Calidris temminckii', 'Leisler', '1812', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'temminckii', '흰꼬리좀도요', '도요목 도요과의 조류로 몸길이는 14cm쯤이다. 수컷 여름깃의 이마, 머리꼭대기, 뒷머리, 뒷목은 잿빛 갈색으로 각 깃털 끝 가깝게는 검은색의 얼룩무늬가 있고 깃털 끝에는 붉게 녹슨 색의 가장자리가 있다. 목, 멱, 앞목, 목 옆, 가슴은 붉게 녹슨 색을 띤 크림색으로 어두운 갈색의 세로 얼룩무늬가 있고, 턱 밑은 흰색이다. 등, 어깨, 허리는 잿빛 갈색으로 각 깃털 끝 가까이에는 검은색 얼룩무늬가 있다. 부리는 검은색이고 다리는 엷은 갈색이다. 하천 부지, 해안의 초습지, 간척지, 삼각주 등지에 내려앉는다. 봄과 가을에 중부 서해안 해변과 한강 등 물가에서 작은 무리를 흔히 볼 수 있는 나그네새이다. 해안가의 초원, 호소나 호반가의 풀숲, 해안에서 멀리 떨어진 고원 산지의 땅 위에 오목하게 파인 곳에 둥지를 튼다. 알은 6월상순~7월상순, 한배에 4개를 낳는다. 갑각류의 단각목, 환형동물의 지렁이, 곤충류, 그 밖에 해안의 작은 무척추동물을 포식한다. 구북구 전역에서 번식한다. 한국, 일본, 쿠릴 열도, 타이완 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001734', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('141', '종달도요', 'Calidris subminuta', 'Middendorff', '1853', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'subminuta', '종달도요', '도요목 도요과의 새로 몸길이는 15cm가량이다. 암컷과 수컷 여름깃의 머리꼭대기, 뒷머리, 뒷목은 검은색이며 엷고 붉은 갈색 가장자리가 있다. 눈 위에는 갈색을 띤 흰색의 눈썹선이 있다. 눈 앞, 귀깃, 목 옆은 갈색을 띤 흰색으로 갈색의 얼룩무늬가 있다. 턱 밑과 멱은 흰색이다. 어깨깃과 등은 검은색으로 엷고 붉은 갈색 가장자리가 있다. 꽁지의 중앙 1쌍은 검은색이고 그 밖의 꽁지깃은 잿빛 갈색이다. 부리는 검은색이고 다리는 황록색 또는 황갈색이다. 아한대 지역에서 번식한다. 봄과 가을에 한반도를 흔히 통과하는 나그네새이다. 해안가나 내륙의 초원, 지상의 움푹 들어간 곳에 접시 모양의 둥지를 튼다. 5월하순~6월하순, 한배에 4개 산란한다. 작은 조개류나 물고기류, 거미류, 곤충류 등의 동물성 먹이를 주식으로 하나 식물의 열매도 다소 섭취한다. 시베리아 북동부 지역에서 번식한다. 우리나라 경상남도 낙동강 하구, 경기도 시흥 소래 염전, 강원도 속초 청초호 등지, 인천, 남해안 부근 조간대 일원 등지를 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001735', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('142', '아메리카메추라기도요', 'Calidris melanotos', 'Vieillot', '1819', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'melanotos', '아메리카메추라기도요', '도요목 도요과의 보기 드문 새이다. 몸길이는 22cm쯤이다. 암컷과 수컷 여름깃의 이마, 머리꼭대기, 뒷머리, 뒷목은 검은 갈색으로 붉은 갈색의 가장자리가 있다. 눈 위에는 흰색의 눈썹선이 있다. 눈 앞과 귀깃은 붉게 녹슨 색을 띤 흰색으로 어두운 갈색의 가는 가로 얼룩무늬가 있다. 꽁지의 중앙 1쌍은 검은색이며 이외의 꽁지깃은 잿빛 갈색이다. 부리는 검은 갈색이고 다리는 갈색을 띤 녹황색이다. 우리나라에서는 보기 드문 철새이거나 미조(迷鳥)이다. 작은 무리를 짓거나 민물도요와 함께 무리 지어 간척지에 내려 먹이를 찾는다. 단독 또는 암수가 함께 채식할 때가 많다. 작은 도랑가나 물이 고인 부근의 건조한 땅 움푹 파인 곳에 접시 모양의 둥지를 튼다. 6월 중순~7월하순, 한배에 4개 산란한다. 연체동물의 유폐목, 곤충류의 딱정벌레목, 벌목, 파리목, 메뚜기목 등을 즐겨 먹는다. 북극 연안 툰드라 지역에 분포한다. 한국, 일본, 오스트레일리아, 아프리카, 남아메리카, 쿠릴 열도 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001736', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('143', '메추라기도요', 'Calidris acuminata', 'Horsfield', '1821', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'acuminata', '메추라기도요', '도요목 도요과의 조류로 봄과 가을에 흔하게 우리나라를 통과하는 나그네새이다. 몸길이 약 21cm. 여름깃의 이마, 머리꼭대기, 뒷머리, 뒷목은 적갈색이고, 흰 눈썹선이 있다. 몸 윗면은 적갈색에 검은 무늬가 있고, 배는 흰색이고, 옆구리에 검은색 반점, 가슴에는 검은 반점이 있다. 다리는 녹황색이다. 간척지나 염전, 하구, 삼각주, 논 등지에 도래한다. 갑각류와 곤충류, 때로는 식물성 먹이도 먹는다. 시베리아 동북부의 툰드라 지대, 캄차카에서 번식하고, 오스트레일리아, 뉴질랜드 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001737', '21.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('144', '붉은갯도요', 'Calidris ferruginea', 'Pontoppidan', '1763', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'ferruginea', '붉은갯도요', '봄과 가을에 작은 무리가 한반도의 동서 해안을 통과하는 나그네새이다. 몸길이 약 22cm이다. 수컷 여름깃의 이마, 머리, 뒷목은 붉은 갈색, 얼굴과 멱은 밤색, 턱 밑은 흰색이다. 등과 어깨깃은 검은색이고 깃털 가장자리는 밤색이다. 목, 가슴, 배는 밤색이며 깃 가장자리는 흰색이다. 이끼로 덮인 건조한 구릉의 오목한 곳에 둥지를 튼다. 산란기는 6월 중순~7월하순이며, 한배의 산란수는 4개이다. 지렁이, 갑각류, 패류, 곤충류 등을 즐겨 먹는다. 시베리아의 북극 툰드라 지역에서 번식하고, 동남아시아나 오스트레일리아, 아프리카에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001738', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('145', '민물도요', 'Calidris alpina', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'alpina', '민물도요', '도요목 도요과에 속하는 조류로 국내에는 흔하게 통과하는 나그네새이며, 일부는 무리를 이루어 갯벌과 강 하구에서 월동한다. 몸길이 17~21cm. 부리는 검은색이며 약간 아래로 휘어져 있다. 다리는 검은색이다. 여름깃은 등이 갈색이고, 몸 아랫면은 흰색이지만 배 한가운데 큰 검은 반점이 있다. 겨울깃은 등이 회색이고, 배의 검은 반점이 없어지고 흰색이 된다. 갯벌이나 간척지, 염전 등지에 도래하며 우리나라에 겨울새로 도래하는 도요새 중 가장 대표적인 새이다.관목이나 풀뿌리 등의 오목한 곳에 접시 모양의 둥지를 튼다. 산란기는 5월 중순~6월 중순이며, 한배의 산란수는 4개이다. 패류, 달팽이류, 갑각류, 지렁이, 거미류, 곤충류를 먹는다. 우리나라 서남해 연안 및 도서와 일본, 중국에서 월동하며 유라시아, 북아메리카대륙의 한대 지역에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001739', '19.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('146', '큰지느러미발도요', 'Phalaropus tricolor', 'Vieillot', '1819', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Phalaropus', '지느러미발도요속', 'tricolor', '큰지느러미발도요', '도요목 도요과의 새로 몸은 가늘고 중소형이다. 다리는 길다. 부리는 길고 바늘 같다. 암컷은 수컷보다 밝은 색깔을 띤다. 번식깃일 때 정수리는 회색이고, 부리 기부부터 목을 따라 검은색이 도는 포도주 색깔의 줄무늬가 있다. 몸 윗부분은 회색에 몇 개의 포도주색 반점이 있고, 아랫부분은 흰색을 띤다. 수컷은 정수리가 어둡고, 몸 윗부분은 회색을 띠는 갈색이다. 가슴 윗부분은 약간 밤색이 돌며, 몸 아랫부분은 흰색이다. 번식기에는, 암수 모두 다리가 검은색이다. 온화한 내륙 물가에서 번식하고, 호수나 연못에서 월동한다. 대초원이나 다른 광활한 지역에 있는 얕은 민물 늪지대에서 번식하는 것을 좋아한다. 이동 중에는 소금기가 있거나 알칼리성의 호수에서 모인다. 월동할 때에도 소금기가 있는 호수에서 발견된다. 남아메리카 중앙·서부, 미시간, 온타리오 서부, 미네소타, 캘리포니아 동부, 오리건 중부, 워싱턴, 브리티시 컬럼비아, 콜로라도 남부, 애리조나 중부 등지에서 번식한다. 남아메리카(아마존 강 근처 제외), 아르헨티나 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001749', '23.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('147', '지느러미발도요', 'Phalaropus lobatus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Phalaropus', '지느러미발도요속', 'lobatus', '지느러미발도요', '도요목 도요과의 새로 몸길이는 18cm가량이다. 암수에 따라서 그 빛깔이 다르다. 암컷의 여름깃은 등 쪽이 어두운 석판색이고 윗등에는 모래 빛 살구색의 세로무늬가 있으며 아래등과 허리와 꽁지위덮깃은 검은 석판색인데 가장자리는 흰색이다. 수컷의 여름깃은 암컷에 비해 별로 아름답지 않다. 암수의 겨울깃은 등 쪽이 연한 회색이고 앞머리와 배 쪽은 모두 흰색이다. 몸의 윗면은 검은색이고 가장자리는 노란색을 띤 붉은 회색이다. 몸의 아랫면은 흰색이고 목과 가슴은 붉은 회색을 띤 회갈색이다. 개천의 어귀, 바닷가, 큰 바다 등에 사는가 하면 작은 연못, 논, 습지 등에서도 생활한다. 봄과 가을에 우리나라를 통과하는 나그네새이다. 크고 작은 떼를 지어 생활한다. 5월하순~7월상순, 4~7개 산란한다. 주로 물속에서 사는 곤충류, 연체동물, 갑각류 등을 먹는다. 우리나라 성진, 만포 등지를 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001750', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('148', '파랑새', 'Eurystomus orientalis', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Coraciiformes', '파랑새목', 'Coraciidae', '파랑새과', 'Eurystomus', '파랑새속', 'orientalis', '파랑새', '파랑새목 파랑새과에 속하는 조류이다. 몸길이는 29.5cm 정도이다. 수컷 겨울깃의 머리꼭대기와 머리 옆은 검은 갈색이고, 윗등은 푸른색을 띤 갈색으로 점차로 어깨, 아랫등, 허리, 위꼬리덮깃으로 불명확한 푸른 녹색으로 옮아간다. 턱 밑과 멱은 머리꼭대기보다 다소 엷고 검은 갈색이다. 멱 중앙의 깃털은 자색을 띠며 가슴과 옆구리는 등과 같은 색이다. 꼬리의 깃털 기부는 푸른 녹색이고 그 외는 검은색이다. 배, 아래꼬리덮깃, 아랫날개덮깃, 겨드랑이깃은 선명한 푸른 녹색이다. 암컷은 일반적으로 수컷보다 색이 엷다. 부리는 굵고 짧다. 다리는 몸에 비해서 작고 발가락은 붉은색이며, 발톱은 검은색이다. 노거수가 산재하는 활엽수림 또는 혼효림, 도시 공원과 농경지 부근에 산다. 흔하지 않은 여름철새이다. 5-7월 3~5개의 알을 낳는다. 곤충이 주식이며, 딱정벌레, 매미, 나비 등을 잡아먹는다. 전국에 서식하고 세계적으로는 동남아시아, 러시아 동부, 일본, 중국, 오스트레일리아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001888', '29.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('149', '호반새', 'Halcyon coromanda', 'Latham', '1790', 'Chordata', '척삭동물문', 'Aves', '조강', 'Coraciiformes', '파랑새목', 'Alcedinidae', '물총새과', 'Halcyon', '호반새속', 'coromanda', '호반새', '파랑새목 물총새과에 속하는 조류이다. 몸길이는 약 27.5cm이다. 수컷의 머리, 등, 날개, 꼬리를 포함한 등 쪽은 모두 고르게 붉은 살구색으로 이마와 머리 옆 이외에는 붉은 자색 광택을 띠고 있으며, 꼬리는 특히 광택이 강하다. 꼬리는 얕게 둥글다. 턱 밑과 멱의 윗부분은 황갈백색이다. 여름깃과 겨울깃은 같다. 7월쯤에는 대체로 색이 엷어지고 등 쪽의 광택도 줄어든다. 암컷은 수컷과 아주 비슷하다. 부리와 홍채는 어두운 붉은색이다. 드물지 않은 여름새이다. 산간 계곡, 호숫가, 혼효림, 활엽수림 등 우거진 숲속에서 번식한다. 삼림 속의 나무구멍이나 벼랑의 동굴 또는 흙벽이나 썩은 나무 기둥에 스스로 구멍을 파고, 알을 낳으며 암수가 교대로 품는다. 양서류와 갑각류를 즐겨 먹는다. 전국에 서식하고 세계적으로는 일본, 중국, 필리핀에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001891', '27.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('150', '청호반새', 'Halcyon pileata', 'Boddaert', '1783', 'Chordata', '척삭동물문', 'Aves', '조강', 'Coraciiformes', '파랑새목', 'Alcedinidae', '물총새과', 'Halcyon', '호반새속', 'pileata', '청호반새', '파랑새목 물총새과에 속하는 조류이다. 몸길이는 28cm 정도이다. 수컷 겨울깃의 머리는 광택이 없는 진한 검은색이며, 눈 밑에는 가는 흰 선이 있다. 턱 밑, 멱, 가슴, 목띠는 황갈색을 띤 흰색이고 목띠의 아랫부분은 검다. 등, 어깨깃, 허리, 위꼬리덮깃은 광택이 있는 푸른색이고, 꼬리는 푸른색이나 바깥꼬리깃의 안가장자리와 아랫면은 검은색이다. 배의 중앙은 엷은 색이다. 암컷은 수컷과 비슷하지만 가슴의 깃털 가장자리가 항상 옅은 검은색이다. 부리는 길고 뾰족하며, 부리 등은 곧고 밑선만이 약간 굽어 있다. 한국의 전역에 도래하는 비교적 드문 여름새이다. 단독 또는 암수 함께 생활한다. 하천가 또는 산 중턱의 벼랑에 구멍을 파거나 나무 구멍을 이용한다. 4-7월에 4-6개의 알을 낳는다. 갑각류, 파충류, 양서류, 어류, 곤충 등을 먹는다. 전국에 서식하고 세계적으로는 중국, 인도, 스리랑카에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001892', '28.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('151', '물총새', 'Alcedo atthis', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Coraciiformes', '파랑새목', 'Alcedinidae', '물총새과', 'Alcedo', '물총새속', 'atthis', '물총새', '파랑새목 물총새과에 속하는 조류이다. 몸길이는 17cm이다. 머리는 어두운 녹색으로 청백색 반점이 있다. 날개는 전체적으로 어두운 녹색이며, 날개에서 가슴 측면을 따라 부리가 있는 부분까지 청백색 반점이 있다. 등과 허리는 파란색이며, 꼬리는 청록색이다. 윗면은 전반적으로 녹색을 띤 푸른색으로 광택이 있으며, 등은 선명한 파란색이다. 눈의 앞과 뒷부분에는 주황색 반점이 있다. 턱과 목의 측면은 흰색이다. 아랫면은 주황색이고, 다리는 붉은색이다. 수컷은 아랫부리와 윗부리가 검은색이고, 암컷은 윗부리가 검은색이지만 아랫부리는 붉은색을 띤다. 한국 전역에서 흔히 번식하는 여름철새 또는 텃새이다. 주로 물가에 있는 언덕 또는 절벽의 흙 벼랑에 둥지를 튼다. 둥지는 흙 절벽에 옆으로 파서 만들며 깊이 1m에 달한다. 번식기는 4-8월이며, 4~7개의 알을 낳는다. 암수가 교대로 품으며, 야간에는 주로 암컷이 담당한다. 먹이는 주로 물고기이며, 양서류, 수서 곤충류, 갑각류 등도 먹는다. 전국에 서식하고 세계적으로는 중국 북서부 산지를 제외한 유라시아와 아프리카 북부, 일본 등에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001894', '17.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('152', '뿔호반새', 'Megaceryle lugubris', 'Temminck', '1834', 'Chordata', '척삭동물문', 'Aves', '조강', 'Coraciiformes', '파랑새목', 'Alcedinidae', '물총새과', 'Megaceryle', '뿔호반새속', 'lugubris', '뿔호반새', '우리나라에서는 1917년에 1마리 잡힌 이후 자취를 감춘 새이다. 몸길이 37.5cm이다. 머리에 댕기깃이 덥수룩하게 나 있다. 부리는 검은색, 이마, 정수리, 댕기깃, 목덜미는 검은색이다. 눈선은 폭이 넓으며 검은색이다. 눈선의 위, 아래 부분은 흰색이다. 등에서 꼬리로 이어지는 몸의 윗면은 검은색으로 흰색의 가로띠가 있다. 몸의 아랫면은 흰색이다. 계류나 호수, 댐 부근에서 살면서 물가 나뭇가지에 앉아 물속의 물고기를 노린다. 산란기는 5월하순~6월하순이며, 한배의 산란수는 4~7개이다. 일본, 인도, 중국(남부), 동남아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001896', '37.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('153', '후투티', 'Upupa epops', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Coraciiformes', '파랑새목', 'Upupidae', '후투티과', 'Upupa', '후투티속', 'epops', '후투티', '파랑새목 후투티과에 속하는 조류이다. 몸길이는 28cm 정도이다. 수컷 겨울깃의 이마, 머리꼭대기, 뒷목은 엷은 분홍빛 갈색이며 머리꼭대기의 깃털은 크고 길며 여닫이가 자유스러운 훌륭한 모관을 형성한다. 모관의 깃털 끝은 검고 뒷부분의 깃털에서는 검은색 끝에 이어 흰색 띠가 있다. 머리 옆은 분홍빛 갈색이며, 턱 밑과 가슴은 포돗빛 분홍색이다. 턱 밑과 윗멱에는 흰색의 가장자리가 있다. 윗등은 분홍빛 갈색 또는 잿빛 갈색이고 아랫등과 허리의 윗부분은 유백색과 검은 갈색의 띠를 형성한다. 위꼬리덮깃은 검은색이다. 부리는 검은색이다. 홍채는 갈색이며, 다리는 잿빛이다. 농촌이나 농경지 또는 구릉지나 야산 노거수의 구멍에 살며 한국에서 흔하지 않은 여름새이다. 단독 또는 암수가 함께 생활하며 주로 지상에서 생활한다. 4-6월에 5-8개의 알을 낳는다. 곤충류의 유충을 즐겨 먹고, 거미류나 지렁이 등을 먹는다. 중부 이북에 서식하고 세계적으로는 아프리카, 유라시아 전역에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001899', '28.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('154', '개미잡이', 'Jynx torquilla', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Piciformes', '딱다구리목', 'Picidae', '딱다구리과', 'Jynx', '개미잡이속', 'torquilla', '개미잡이', '날개 길이 9cm이다. 빛깔은 등 쪽은 회색에 갈색 무늬가 있고, 위쪽과 등의 가운데 부분, 눈썹 깃에는 검은색의 세로무늬가 있다. 배 쪽은 흰색이다. 꼬리는 노란색을 띤 회색에 갈색의 무늬와 5~6줄의 검은색 가로무늬가 있다. 다리는 엷은 갈색이다.관목림이나 촌락 부근의 수목에서 서식한다. 나무 구멍이나 딱따구리의 묵은 둥지에 산란한다. 산란기는 6~7월이며, 산란수는 6~10개이고 알은 흰색으로 무늬가 없다. 식성은 주로 동물성이고, 곤충류의 딱정벌레목, 벌목, 나비목, 거미류 등을 먹는다. 특히 개미를 즐겨 먹는다. 백두산 지역에서는 여름새이며 그 밖의 지역에서는 매우 드문 나그네새이다. 4~5월에 우리나라에 와 9월까지 머무른다. 한국, 일본, 시베리아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001903', '16.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('155', '쇠딱다구리', 'Dendrocopos kizuki', 'Temminck', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Piciformes', '딱다구리목', 'Picidae', '딱다구리과', 'Dendrocopos', '딱다구리속', 'kizuki', '쇠딱다구리', '딱다구리목 딱다구리과의 조류이다. 수컷의 이마, 머리꼭대기, 뒷머리, 뒷목은 잿빛 갈색이며, 뒷머리의 양쪽에는 붉은색의 깃털이 있다. 눈 앞과 귀깃은 그을린 갈색이고, 눈의 뒷부분에는 흰색의 눈썹선이 있으나 목 옆의 황백색 부분과연결되지 않는다. 꽁지는 검은색이며, 바깥쪽 2쌍의 꽁지깃 끝 가까이에는 갈색을 띤 흰색으로 검은색의 가로띠가 3개 있다. 부리의 털은 검은색으로 끝은 흰색이고, 부리는 납빛 회색의 뿔색이다. 홍채는 붉은색이다. 다리는 납빛 회색이며 경부의 깃은 잿빛 갈색이다. 암컷의 뒷머리 양쪽에는 수컷에 있는 붉은색의 깃털이 없다. 삼림 속의 나무 위에서 생활한다. 한반도 전역에서 흔하게 번식하는 텃새이다. 번식기에는 단독 또는 암수가 함께, 가을과 겨울에는 혼성으로 무리 지어 숲속을 돌아다닌다. 활엽수림 또는 잡목림 속의교목줄기에 스스로 구멍을 파고 둥지를 만든다. 5월상순~6월 중순, 한배에 5~7개 산란한다. 곤충류와 식물의 열매를 먹는다. 우리나라 전역, 중국(북동부), 우수리, 사할린, 쿠릴 열도 남부 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001907', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('156', '아물쇠딱다구리', 'Dendrocopos canicapillus', 'Blyth', '1845', 'Chordata', '척삭동물문', 'Aves', '조강', 'Piciformes', '딱다구리목', 'Picidae', '딱다구리과', 'Dendrocopos', '딱다구리속', 'canicapillus', '아물쇠딱다구리', '딱다구리목 딱다구리과의 조류이다. 수컷 겨울깃은 이마에서 머리꼭대기 뒤쪽까지 갈색을 띤 회색이다. 부리의 털은 크림 흰색이며 각 깃 끝은 검다. 그러나 뒷머리는 아주 검은색이다. 뒷머리의 양쪽에는 붉은색의 작은 세로무늬가 있다. 눈 앞은 갈색을 띤 흰색이며, 위 뺨과 귀깃의 하반은 회갈색이다. 날개는 검은색이고, 날개깃에는 내외 양가장자리에 흰색의 무늬가 있는 것은 같은 속의 다른 종과 같다. 암컷은 뒷머리 양쪽에 붉은 무늬가 없는 것 이외에는 수컷과 비슷하다. 홍채는 어두운 홍색이다. 다리는 다소 가늘지만, 올리브 회색이다. 활엽수림이나 혼효림에서 흔히 눈에 띈다. 과거에는 흔했으나 현재는 비교적 드문 텃새이다. 겨울에는 평지로 내려오지만 여름에는 중부 이상으로 올라가므로 쉽게 찾아볼 수 없다. 한배에 3~5개 산란한다. 갑충과 그 밖의 각종 곤충의 성충과 유충을 포식한다. 우리나라 경기도 광릉, 지리산 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001908', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('157', '쇠오색딱다구리', 'Dendrocopos minor', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Piciformes', '딱다구리목', 'Picidae', '딱다구리과', 'Dendrocopos', '딱다구리속', 'minor', '쇠오색딱다구리', '딱다구리목 딱다구리과의 새다. 날개의 길이는 9.2~9.4cm로 매우 작은 종에 속한다. 수컷은 정수리가 밝은 빨간색이라서 암컷과 구분된다. 공개되어 있는 숲, 잡목 숲, 수림의 초원, 뜰, 과수원 등에서 발견된다. 섬에서는 번식하지 않는다. 높은 곳에 둥지를 짓고, 먹이를 먹는 경향이 있고, 조용하다. 종종 날개를 치며 내는 소리로 인해 위치를 알아낸다. 먹이를 먹을 때, 가지를 따라 기어 다니기도 하고, 가지와 가지 사이를 날기도 하며, 공터를 파도 모양으로 날아다니기도 한다. 작은 가지에 구멍을 뚫고 사는 유충, 거미 등의 곤충을 찾기 위해 종종 나무 꼭대기에 오르기도 한다. 영국, 잉글랜드 북부(요크셔, 랭커셔, 웨일즈) 등지에 분포한다. 영국에 서식하는 딱따구리 3종 중 가장 희귀하다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001909', '15.2');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('158', '큰오색딱다구리', 'Dendrocopos leucotos', 'Bechstein', '1803', 'Chordata', '척삭동물문', 'Aves', '조강', 'Piciformes', '딱다구리목', 'Picidae', '딱다구리과', 'Dendrocopos', '딱다구리속', 'leucotos', '큰오색딱다구리', '딱다구리목 딱다구리과의 새로 몸길이는 25~28cm가량이다. 수컷의 이마는 너비 9mm 정도만 황갈색을 띤 흰색이고, 머리꼭대기와 뒷머리는 홍색으로 각 깃털 기부는 어두운 잿빛이다. 머리 옆을 따라 검은색의 가느다란 선이 있다. 눈 앞, 눈 주위, 귀깃, 눈의 뒤쪽은 황갈색을 띤 흰색이다. 턱 밑, 멱, 목 옆은 황색이 낀 붉게 녹슨 색을 띤 흰색이다. 부리는 납빛 회색이고 부리털은 황갈색을 띤 흰색으로 끝은 검은색이다. 홍채는 붉은 갈색이고 다리는 어두운 납빛 회색이며 경부의 깃털은 엷은 홍색이다. 암컷 머리꼭대기와 뒷머리는 수컷처럼 홍색이 아니고 검은색을 띤다. 낙엽활엽수림 또는 아고산대의 침엽활엽혼효림에 서식한다. 우리나라 전역에서 볼 수 있으나 흔하지 않은 텃새이다. 단독 또는 암수가 함께 생활한다. 나무줄기에 자신이 직접 구멍을 뚫어 둥지를 틀고 산란한다. 알은 4월하순부터, 한배에 3~5개 낳는다. 식성은 곤충류가 주식이나 다족류, 각종 식물의 열매도 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001910', '26.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('159', '오색딱다구리', 'Dendrocopos major', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Piciformes', '딱다구리목', 'Picidae', '딱다구리과', 'Dendrocopos', '딱다구리속', 'major', '오색딱다구리', '딱다구리목 딱다구리과에 속하는 조류이다. 몸길이는 약 24cm이다. 머리꼭대기와 뒷목은 광택이 있는 남빛 검은색이고, 뒷머리에는 너비가 1cm 정도의 홍색 띠가 있다. 눈 주위와 귀깃은 갈색을 띤 흰색이다. 턱 밑, 멱, 목 옆은 갈색빛 흰색 또는 엷은 갈색이다. 아랫부리에서는 검은색의 뺨선이 지나간다. 가슴, 배, 옆구리는 갈색을 띤 흰색 또는 엷은 갈색이고, 아랫배는 장미색이다. 등, 허리, 위꼬리덮깃은 광택이 있는 남빛 검은색이다. 아래꼬리덮깃은 선명한 홍색이다. 꼬리는 검은색이다. 부리는 회색이고, 홍채는 붉은 갈색 또는 붉은색이다. 암컷의 뒷머리는 수컷에 있는 홍색 띠가 없고, 남빛 검은색을 띤다. 흔한 텃새이며 단독 또는 암수가 함께 생활할 때가 많다. 낙엽활엽수림, 잡목림, 아고산대의 삼림 속의 나무 줄기에 구멍을 파고 알을 낳는다. 5-7월에 4-6개의 알을 낳는다. 곤충과 거미, 식물의 열매를 먹는다. 전국에 서식하고 세계적으로는 유럽, 러시아, 북아메리카에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001911', '24.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('160', '세가락딱다구리', 'Picoides tridactylus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Piciformes', '딱다구리목', 'Picidae', '딱다구리과', 'Picoides', '세가락딱다구리속', 'tridactylus', '세가락딱다구리', '몸길이는 20cm이다. 등은 검은색이고, 흰색 사닥다리 무늬가 있다. 머리도 검은색을 띠고, 얼굴에는 흰색 줄무늬가 있다. 수컷은 머리 윗부분이 노란색이다. 날개, 둔부, 꼬리는 검은색이다. 측면에는 줄무늬가 있고, 배는 흰색이다. 주로 침엽수림에서 발견되고, 가끔 혼합림에서도 발견된다. 암수 모두 주로 4개의 알을 14일 동안 품는다. 주로 나무에 구멍을 뚫고 사는 곤충을 먹지만, 거미,장과류, 새싹을 먹기도 한다. 나무나 꺾어진 가지에 구멍을 뚫고 산다. 알래스카, 워싱턴 중앙·남부, 애리조나 중앙부, 뉴멕시코 중남부, 미네소타 북동부, 뉴잉글랜드 북부, 퀘벡 남부에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001913', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('161', '크낙새', 'Dryocopus javensis', 'Horsfield', '1821', 'Chordata', '척삭동물문', 'Aves', '조강', 'Piciformes', '딱다구리목', 'Picidae', '딱다구리과', 'Dryocopus', '까막딱다구리속', 'javensis', '크낙새', '딱다구리목 딱다구리과에 속하는 조류이다. 몸길이는 46cm이고, 수컷은 이마부터 머리꼭대기, 뒷머리까지 진한 붉은색이며, 암컷은 동일한 부위에 붉은색이 없고 검은색이다. 뺨 선은 어두운 붉은색이다. 암컷과 수컷의 아래 등, 허리, 윗꼬리덮깃, 가슴, 윗배는 흰색이며, 그밖에는 모두 검은색이다. 부리는 검은색이며, 다리는 검은색을 띤 회색이다. 형태가 유사한 종인 까막딱다구리는 크낙새와 달리 몸 전체가 검은색으로 쉽게 구별할 수 있다. 전나무, 잣나무, 소나무, 참나무, 밤나무 등의 활엽수와 침엽수가 함께 자라는 숲속에 살며 주로 이른 아침과 저녁에 활동하지만 흐린 날이나 비가 올 때는 낮에도 활동한다. 나무줄기를 나선형으로 기어 올라가 주위의 나무로 옮겨간다. 나무껍질을 벗기고 구멍을 파서 큰 딱정벌레 애벌레를 잡아먹는다. 둥지는 참나무, 전나무, 소나무 등의 나무 구멍을 이용하거나, 직접 구멍을 만든다. 알 낳는 시기는 5-6월이다. 알은 흰색이며, 2-5개 낳아 암수가 교대로 품는다. 경기도에 서식하였지만 현재는 관찰되지 않고 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001915', '46.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('162', '까막딱다구리', 'Dryocopus martius', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Piciformes', '딱다구리목', 'Picidae', '딱다구리과', 'Dryocopus', '까막딱다구리속', 'martius', '까막딱다구리', '딱다구리목 딱다구리과에 속하는 조류이다. 몸길이는 45-57cm이다. 암컷과 수컷 모두 몸 전체 깃털이 검은색이다. 수컷은 이마에서 머리꼭대기를 지나 뒷머리까지 광택이 나는 어두운 붉은색이며, 암컷은 뒷머리만 붉은색이다. 부리는 회백색이며, 부리 등과 끝은 검은색이다. 튼튼한 다리는 회색이다. 수령이 오래된 참나무, 소나무 등 고목이나 노거수가 있는 울창한 숲에 산다. 몸을 수직으로 세워 나무줄기에 붙어 나선형으로 선회하면서 위로 올라가 인근 수목으로 옮겨간다. 부리로 나무줄기를 두들겨 가며 구멍을 파서 곤충 애벌레를 잡아먹는다. 곤충류를 주로 먹으며, 식물의 열매도 먹는다. 둥지는 땅 위에서 4-25m 높이의 나무줄기에 암수가 공동으로 구멍을 파서 만든다. 3-5월에 흰 알 3-6개를 낳는다. 강원도, 경기도, 경상북도, 충청남도, 충청북도에 서식하고 세계적으로는 스칸디나비아, 프랑스, 스페인, 캄차카반도, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001916', '51.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('163', '청딱다구리', 'Picus canus', 'Gmelin', '1788', 'Chordata', '척삭동물문', 'Aves', '조강', 'Piciformes', '딱다구리목', 'Picidae', '딱다구리과', 'Picus', '청딱다구리속', 'canus', '청딱다구리', '딱다구리목 딱다구리과에 속하는 조류이다. 수컷 겨울깃의 앞이마, 눈썹, 뒷머리는 잿빛으로 뒷목을 지나 등 쪽으로는 녹색으로 된다. 뒷머리에는 검은색의 세로 얼룩무늬가 있는 것이 보통이나 없을 때도 있다. 머리꼭대기는 광택이 있는 붉은색이다. 등, 어깨, 허리는 녹색이나 각 깃털 기부는 잿빛이다. 가슴, 배, 아래꼬리덮깃은 녹색을 띤 잿빛이고, 위꼬리덮깃은 황색을 띤 녹색이며 특히 끝은 황금색의 광택이 있다. 암컷의 머리꼭대기는 붉은색이 없고 잿빛으로 검은 세로 얼룩무늬가 있어서 수컷과 구별할 수 있다. 다리는 녹색을 띤 어두운 갈색이다. 삼림에 살며 비교적 흔한 텃새이다. 단독으로 생활할 때가 많고 단단한 꼬리깃을 이용하여 나무줄기에 수직으로 앉아 있거나 나무줄기 주위를 빙빙 돌면서 꼭대기 쪽으로 올라간다. 삼림 속의큰키나무줄기에 구멍을 뚫고 둥지를 튼다. 4-6월에 6-8개의 알을 낳는다. 곤충을 주로 먹으며 개미를 좋아한다. 전국에 서식하고 세계적으로는 유럽-일본의 홋카이도, 타이완 등에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001918', '25.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('164', '팔색조', 'Pitta nympha', 'Temminck & Schlegel', '1850', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Pittidae', '팔색조과', 'Pitta', '팔색조속', 'nympha', '팔색조', '참새목 팔색조과에 속하는 조류이다. 몸길이는 18cm 정도이다. 암컷과 수컷이 동일하며, 모두 머리꼭대기가 갈색이고 검은색 머리 중앙선이 있다. 눈썹 선은 유백색이 도는 황갈색으로 뒷목에까지 이른다. 눈앞에서 뺨, 귀깃을 포함해 뒷목에 이르는 부분은 검은색이다. 등, 어깨, 허리의 윗부분은 푸른색을 띤 녹색이며, 허리 아랫부분은 선명한 푸른색이다. 위꼬리덮깃은 검은색으로 각 깃털의 끝은 푸른색이다. 가슴과 옆구리는 황갈색이며, 목 옆과 가슴 옆은 녹색이고 아랫배 부분은 빨간색이다. 해안과 섬 또는 내륙의 경사지에 있는 잡목림과 활엽수림에 살며 땅 위에서 걸어 다니면서 곤충과 같은 먹이를 찾는다. 경계심이 강해 접근하기가 어렵다. 바위틈을 둥지로 이용하거나 나무줄기 사이에타원형으로 만든다. 5-7월에 유백색에 얼룩무늬가 있는 알 4~6개를 낳는다. 제주도 및 남해안 도서에 서식하고 세계적으로는 일본, 중국 동부, 타이완, 보르네오섬 등에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001922', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('165', '푸른날개팔색조', 'Pitta moluccensis', 'Statius Müller', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Pittidae', '팔색조과', 'Pitta', '팔색조속', 'moluccensis', '푸른날개팔색조', '푸른날개팔색조는 참새목 팔색조과에 속하는 조류로, 몸길이는 약 18~20.5cm입니다. 이 새는 검은 머리와 눈 위에 황갈색 줄무늬가 있으며, 흰색 칼라와 청록색 윗부분, 푸른 날개, 황갈색 아랫부분, 붉은색 배를 가지고 있습니다. 주로 인도에서 필리핀에 이르는 동남아시아에 서식하며, 습한 숲, 공원, 정원 등을 자주 찾습니다. 단, 너무 밀집된 숲은 피하는 경향이 있습니다. 먹이는 주로 곤충과 벌레이며, 번식기에는 둥글고 지저분한 둥지를 나무 뿌리 근처 땅에 짓습니다. 한 번에 4~6개의 알을 낳으며, 부모가 함께 약 15~17일간 알을 품습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001923', '19.2');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('166', '회색숲제비', 'Artamus fuscus', 'Vieillot', '1817', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Artamidae', '숲제비과', 'Artamus', '숲제비속', 'fuscus', '회색숲제비', '회색숲제비는 참새목 제비과에 속하는 조류로, 남아시아 지역에 주로 서식합니다. 몸길이는 약 19cm이며, 짧고 구부러진 부리, 짧은 사각 꼬리, 긴 날개가 특징입니다. 상체는 회색, 머리는 좀 더 어두운 색이며, 하체는 분홍빛 회색입니다. 주로 전선이나 높은 나무에 무리를 지어 앉아있는 모습을 볼 수 있으며, 주로 곤충을 공중에서 사냥합니다. 번식기는 3월에서 6월 사이이며, 둥지는 높게 위치하며 보통 2~3개의 알을 낳습니다. 인도, 방글라데시, 네팔 등지에서 널리 분포합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001925', '19.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('167', '할미새사촌', 'Pericrocotus divaricatus', 'Raffles', '1822', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Campephagidae', '할미새사촌과', 'Pericrocotus', '할미새사촌속', 'divaricatus', '할미새사촌', '참새목 할미새사촌과의 조류로 몸길이는 20cm쯤이다. 수컷의 이마에서 머리꼭대기 1/2까지는 흰색이다. 뒷머리와 뒷목은 검은색이며, 눈 앞 위쪽에는 흰색의 눈썹선이 있다. 윗부리 기부, 눈 앞, 눈 뒤는 검은색이며 눈선을 이룬다. 귀깃은 흰색으로 뒤끝 가까이는 다소 검은색이다. 턱 밑, 멱, 목 옆은 흰색이고, 눈 아래쪽에는 흰색의 가느다란 선이 있다. 꽁지는 중앙의 2쌍이 검은색이고 기타 꽁지깃도 검은색이나 끝에 쐐기 모양의 가늘고 긴 흰색의 얼룩무늬가 있다. 부리는 검은색이고, 홍채는 갈색이다. 다리는 검은색이고 경부는 흰색이다. 낙엽활엽수림 또는 잡목림, 인가 부근의 독립된교목의 꼭대기에도 영소한다. 한반도의 중부 이북과 남한 지역에서는 비교적 흔한 여름새이다. 번식기에는 암수가 함께 생활하나 이동기에는 군서 생활을 한다. 5~6월, 한배에 4~5개 산란한다. 곤충류를 주식으로 한다. 시베리아, 중국 북동부, 일본 등지에서 번식한다. 동남아시아에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001930', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('168', '칡때까치', 'Lanius tigrinus', 'Drapiez', '1828', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Laniidae', '때까치과', 'Lanius', '때까치속', 'tigrinus', '칡때까치', '참새목 때까치과의 새로 몸길이는 약 18.5cm이다. 수컷 겨울깃의 앞이마, 눈 앞, 눈 주위, 귓가, 눈 뒤는 검은색으로 이들이 합쳐서 검은색의 큰 눈선을 형성한다. 머리꼭대기, 뒷목, 목 옆은 잿빛이다. 등, 어깨깃, 허리, 위꽁지덮깃은 붉은 갈색으로 각 깃털에는 2개의 가는 검은색 가로선이 있다. 꽁지깃은 엷은 갈색으로 엷은 색의 가로띠가 있다. 부리는 높고 튼튼하며 쇳빛 검은색을 띤다. 부리의 털은 길며 거의 콧구멍을 덮고 5-6개의 굵고 긴 수염이 있다. 홍채는 갈색이며 다리도 튼튼하고 잿빛이다. 구릉과 산림 및 농촌과 도시 교외 등 도처의관목과교목이 있는 곳에서 눈에 띈다. 우리나라 전역에 드물지 않게 번식하는 여름새이다. 암수 함께 생활한다. 무리를 짓지 않으나 새끼가 이소한 후에는 가족군을 이룰 때가 있다. 번식기에는 일정한 세력권이 있다. 잡목림이나 적송림에 영소한다. 알은 6월부터 7월하순에, 한배에 3-6개 낳는다. 곤충을 주로 먹는다. 3-4월에 털갈이를 한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001933', '18.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('169', '때까치', 'Lanius bucephalus', 'Temminck & Schlegel', '1845', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Laniidae', '때까치과', 'Lanius', '때까치속', 'bucephalus', '때까치', '전체의 길이는 20cm가량이다. 수컷의 머리는 갈색이며 등은 회색이다. 눈줄과 날개깃은 검은색이며 꼬리는 끝부분으로 갈수록 검어진다. 날개의 흰 반점이 특징이며, 이것은 날 때 더욱 뚜렷이 보인다. 멱, 가슴, 배는 흰색이며 옆구리 부분은 연한 적갈색이다. 암컷처럼 수컷의 가슴에도 비늘무늬가 있는 경우가 있으나, 야외에서는 잘 보이지 않는다. 여름깃은 머리와 등이 회색을 띠며 가슴의 적갈색 부분이 좁아진다. 암컷의 머리는 갈색이며 눈줄과 등은 진한 갈색이다. 가슴과 배는 연한 적갈색에 조밀한 비늘무늬가 있다. 눈줄이 수컷보다 좁으며 날개에 흰 점이 없다. 단독 또는 암수가 함께 생활한다. 나뭇가지에 둥지를 틀고 4~7월에 6~7개의 알을 낳는다. 유사종인 칡때까치는 흰색의 눈썹선이 없다. 한국의 중·남부 이남에 서식하며 사할린섬, 중국 북부, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001934', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('170', '노랑때까치', 'Lanius cristatus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Laniidae', '때까치과', 'Lanius', '때까치속', 'cristatus', '노랑때까치', '한국 전역에서 번식하는 여름새이다. 몸길이 20cm이다. 수컷 겨울깃의 윗면은 회갈색, 옆구리와 몸 아랫면은 옅은 황갈색, 부리의 털과 이어지는 눈선은 검은색이며, 눈썹 선은 잿빛 흰색이다. 뺨, 턱 밑, 멱은 흰색이다. 부리, 눈, 다리는 검은색이다. 평지의관목숲에 서식한다. 도시와 교외, 농촌과 구릉, 경작지 등 도처에서 흔히 눈에 띈다. 암수가 함께 생활한다. 번식이 끝나면 가족이 함께 생활한다. 나뭇가지 위에 풀이나 나무껍질로 밥그릇 모양 둥지를 만든다. 산란기는 5월하순~6월이며, 한배의 알은 4~7개이다. 곤충류를 주로 먹는다. 중국(동부, 북동부), 일본, 한국 전역에서 번식하고 동남아시아와 인도에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001935', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('171', '붉은등때까치', 'Lanius collurio', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Laniidae', '때까치과', 'Lanius', '때까치속', 'collurio', '붉은등때까치', '붉은등때까치는 참새목의 육식성 조류로서 때까치과에 속합니다. 몸길이는 16~18cm이며, 이 새는 큰 곤충, 작은 새, 개구리, 설치류, 도마뱀 등을 먹습니다. 사냥한 먹이를 가시나 철망에 꽂아 두는 습성이 있어 ''도살새''라는 별명을 가지고 있습니다.    수컷의 윗면은 붉은색 계통이고, 회색 머리에 눈을 가로지르는 검은 줄무늬가 특징입니다. 암컷과 어린 새의 윗면은 갈색에 세로줄무늬가 있습니다. 주로 유럽에서 번식하며 겨울에는 열대 아프리카로 이동합니다. 전 세계적으로는 ''관심 필요''로 분류되지만, 일부 지역에서는 개체 수가 급감하여 보호가 필요합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001936', '17.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('172', '긴꼬리때까치', 'Lanius schach', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Laniidae', '때까치과', 'Lanius', '때까치속', 'schach', '긴꼬리때까치', '우리나라 남부지방에 서식하는 텃새이다. 머리와 머리꼭대기의 깃털은 진주 빛을 띠는 회색이고 얼굴은 전체적으로 검다. 몸의 아랫면은 흰색이지만 옆구리는 적갈색이다. 꼬리가 길고 적갈색이다. 부리와 다리는 검은색에 가깝다. 서식지는관목이 무성한 곳이나 경작지 등이다. 도마뱀, 설치류, 작은 새, 곤충 등을 잡아서 나뭇가지나 철사에 꽂아두는 습성이 있다. 이렇게 놓아둔 먹이는 잊어버리는 일이 많다. 날 때는 파도처럼 굽이치듯이 날지만 일단 돌진할 때에는 직진한다. 인도에서 뉴기니까지 분포한다. 우리나라에서는 전라남도 하태도와 가거도, 제주도 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001937', '25.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('173', '물때까치', 'Lanius sphenocercus', 'Cabanis', '1873', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Laniidae', '때까치과', 'Lanius', '때까치속', 'sphenocercus', '물때까치', '전국에 걸쳐 적은 수가 도래, 월동하는 드문 겨울새이다. 몸길이 31cm이다. 머리꼭대기와 몸 윗면은 회색, 눈썹선은 흰색, 부리와 눈 아래 폭이 넓은 눈선은 검은색, 멱과 가슴을 포함하여 몸 아랫면과 허리는 흰색이다. 다리는 검은색이다. 나뭇가지 위에 마른 가지와 풀 등으로 밥그릇 모양의 둥지를 만든다. 주로 단독으로 또는 암수가 함께 생활한다. 산란기는 5월상순~6월상순이며, 한배의 산란수는 6~9개이다. 작은 설치류, 양서류(개구리), 파충류, 딱정벌레류, 나비목의 유충, 거미류를 즐겨 먹는다. 중국 중부 및 남부, 한국 전역에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001939', '31.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('174', '꾀꼬리', 'Oriolus chinensis', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Oriolidae', '꾀꼬리과', 'Oriolus', '꾀꼬리속', 'chinensis', '꾀꼬리', '참새목 꾀꼬리과에 속하는 조류이다. 몸길이는 26cm이다. 몸 전체가 선명한 노란색이며, 부리는 붉은색이다. 세력권 내에 들어온 맹금류를 공격하기도 한다. 수컷의 몸 전체는 선명한 노란색이며, 검은색 눈줄의 폭이 넓다. 꼬리는 검은색으로 바깥꼬리깃은 노란색이다. 암컷의 등은 흐린 노란색이며, 눈줄의 폭이 좁다. 어린 새는 암컷과 유사하나 가슴과 배가 흰색이며 검은색 세로줄이 있다. 둥지는교목의 수평으로 뻗어 나온 가지 끝에 나무껍질, 풀잎이나 줄기, 뿌리 등을 밥그릇 모양의 거미줄로 엮어 가지에 매단다. 5~7월에 4개의 알록달록한 알을 낳는다. 소리는 ‘히요, 호호, 호이오’하고 아름답게 울며, 간혹 ‘케엑~’하는 등 다양한 소리를 낸다. 한국에는 4~5월에 찾아와 여러 곳에서 번식한다. 중국, 인도차이나반도, 미얀마, 말레이반도 등지에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001942', '26.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('175', '검은바람까마귀', 'Dicrurus macrocercus', 'Vieillot', '1817', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Dicruridae', '바람까마귀과', 'Dicrurus', '바람까마귀속', 'macrocercus', '검은바람까마귀', '우리나라에서는 충청남도 서산의 도로변에 있는 전주에서 1988년 5월에 1회 관찰된 미조이다. 몸길이는 28cm이다. 온몸은 검은색이며, 꼬리가 길다. 윗목, 등, 그리고 가슴은 광택이 있는 검은색이다. 꼬리는 깊게 팬 모양이고, 날아갈 때는 물결 모양으로 난다. 중국 동남부에서 번식하고 인도차이나에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001945', '28.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('176', '회색바람까마귀', 'Dicrurus leucophaeus', 'Vieillot', '1817', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Dicruridae', '바람까마귀과', 'Dicrurus', '바람까마귀속', 'leucophaeus', '회색바람까마귀', '참새목 바람까마귀과의 조류이다. 수컷은 날개의 길이가 16.8cm가량이다. 색깔은 전체적으로 금속광택이 나는 녹색이고, 가슴에 진한 자줏빛 남색 깃이 있다. 이마에는관모처럼 생긴 털이 몇 가닥 뒤로 늘어져 있다. 암컷은 날개길이가 13.4cm가량이다. 이마에서 등, 허리, 꽁지까지는 어두운 회색이다. 아랫면은 윗면에 비해 색이 조금 연하다. 날개는 광택이 있는 검은색이다. 부리와 다리는 검은색이다. 산림, 덤불, 공원에서 서식한다. 중국의 화남, 각성, 윈난 등에 분포한다. 우리나라 전라북도 어청도, 전라남도 가거도, 평안북도 등지에 도래하는 미조(迷鳥)이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001946', '26.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('177', '바람까마귀', 'Dicrurus hottentottus', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Dicruridae', '바람까마귀과', 'Dicrurus', '바람까마귀속', 'hottentottus', '바람까마귀', '우리나라에서는 미조로만 관찰된 새이다. 몸길이 약 25cm이다. 몸 빛깔은 금속성 광택이 나는 녹색이며 가슴에는 자줏빛이 도는 짙은 쪽빛 깃이 있다. 이마에는관모와 비슷한 몇 개의 털 모양의 깃이 뒤로 늘어져 있다. 꽁지는 긴 제비꽁지 모양이고 바깥꽁지깃의 끝이 넓어져서 바깥쪽으로 젖혀져 있다. 인도에서는 비교적 우거진 숲의 저지대나 무성한관목림 등지에 서식하며, 중국에서는 개활지와 정원 및 인가 부근의 나무에서 볼 수 있다. 중국, 인도, 미얀마, 인도차이나 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001947', '25.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('178', '북방긴꼬리딱새', 'Terpsiphone paradisi', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Monarchidae', '긴꼬리딱새과', 'Terpsiphone', '긴꼬리딱새속', 'paradisi', '북방긴꼬리딱새', '수컷 몸길이 22cm, 꼬리길이 20cm이다. 검은 머리와 돌출한 볏, 부리는 검은색이다. 몸의 윗면과 아랫면은 종에 따라 흰색과 밤색의 두 가지 형이 있다. 흰색형은 등, 허리, 어깨, 위꽁지덮깃이 흰색이지만 가늘고 검은 세로무늬와 가장자리 때문에 온몸이 잿빛으로 보인다. 가슴, 배, 아래꽁지덮깃은 흰색이다. 밤색형은 등과 어깨가 보랏빛 광택이 나는 밤색이고 허리, 위꽁지덮깃 및 꽁지깃은 균일한 밤색이다. 가슴과 옆구리는 회색이며 배와 아래날개덮깃 및 아래꽁지덮깃은 흰색이다. 산지의 숲에서 생활한다. 번식기는 5~7월로 나뭇가지에 둥지를 틀고 한배에 3~4개의 알을 낳아 암수가 함께 품는다. 인도, 동남아시아, 중국 등지에서 번식하고 북부의 번식 집단은 남쪽으로 내려가 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001950', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('179', '긴꼬리딱새', 'Terpsiphone atrocaudata', 'Eyton', '1839', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Monarchidae', '긴꼬리딱새과', 'Terpsiphone', '긴꼬리딱새속', 'atrocaudata', '긴꼬리딱새', '긴꼬리딱새는 「멸종위기 야생생물 Ⅱ급」의 긴꼬리딱새과 조류이며 희귀한 여름 철새이다. 과거 삼광조라고도 불렸다. 부리와 눈테는 푸른색, 배는 흰색이며, 짧은 댕기깃이 있다. 활엽수림 지역을 선호하며 큰 나무에 컵 모양의 둥지를 만든다. 우리나라에서는 전국적으로 분포하며, 제주도와 남부 지역에는 많은 수가 서식한다. 각종 개발과 기온변화로 서식지가 파괴되고 있다. 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001951', '45.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('180', '어치', 'Garrulus glandarius', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Corvidae', '까마귀과', 'Garrulus', '어치속', 'glandarius', '어치', '참새목 까마귀과에 속하는 조류이다. 몸길이는 33cm 정도이다. 등과 배는 분홍빛을 띤 갈색이다. 허리의 흰색과 꽁지의 검은색이 대조적이고, 날개덮깃에는 청색과 검은색 가로띠가 있다. 날개에는 흰색 무늬가 뚜렷하다. 세로무늬가 있는 정수리의 깃을 세워서 낮은 댕기를 이룬다. 폭넓은 검은색의 뺨 선이 있다. 나무 위에서 생활하고 먹이를 숨겨 두는 습성이 있다. 날 때는 날개를 천천히 퍼덕여 날아오른 뒤 대체로 파도 모양을 그리며 난다. 경계할 때에는 맹렬하게 울며 다른 짐승의 소리를 흉내 내기도 한다. 낙엽활엽수림과 혼효림에 둥지를 틀고 4-6월에 5-6개의 알을 낳는다. 북위 40~60°의 유럽과 아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001954', '33.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('181', '물까치', 'Cyanopica cyanus', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Corvidae', '까마귀과', 'Cyanopica', '물까치속', 'cyanus', '물까치', '한반도 전역에 흔한 텃새이다. 몸길이는 37cm이다. 머리는 검은색, 등은 회색, 턱 밑과 뺨, 멱은 흰색, 몸의 아랫면은 엷은 회색, 등 아래쪽의 날개와 꼬리는 엷은 청색이다. 부리와 다리는 검은색이다. 꼬리는 쐐기 모양이다. 산지나 평지의 숲 또는 시가지 공원에서 서식한다. 일반적으로 무리 지어 생활하며 특히 번식기 이외에는 5~10마리의 작은 무리를 지어 행동한다. 산지 또는 마을 부근의 숲에서 2~6m 높이의 나뭇가지에 마른 가지, 이끼류, 풀뿌리, 흙 등으로 밥그릇 모양의 둥지를 만든다. 산란기는 5~7월이며, 한배의 산란수는 6~9개이다. 잡식성으로 동물성과 식물성을 혼식하나, 특히 곤충을 좋아한다. 한국, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001956', '37.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('182', '까치', 'Pica pica', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Corvidae', '까마귀과', 'Pica', '까치속', 'pica', '까치', '참새목 까마귀과에 속하는 조류이다. 몸 전체 길이는 46cm이다. 윗면은 푸른 광택이 있는 검은색이고 배는 흰색이다. 어깨에는 흰색 반점이 있다. 날 때 흰색의 첫째날개깃이 뚜렷하게 보인다. 도시와 농촌 등 평지에 살며 우리나라 대표적인 텃새이다. 나무, 전신주 등에 둥지를 틀고 2월부터 번식을 시작한다. 비번식기에는 무리를 지어 생활한다. ‘꺅 꺅 꺅 꺅’하고 운다. 전국에 서식하고 세계적으로는 유라시아 전역에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001958', '46.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('183', '잣까마귀', 'Nucifraga caryocatactes', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Corvidae', '까마귀과', 'Nucifraga', '잣까마귀속', 'caryocatactes', '잣까마귀', '참새목 까마귀과의 새이다. 몸길이는 34.5cm가량, 까마귀보다 훨씬 작다. 암컷과 수컷의 이마, 머리꼭대기, 뒷머리, 뒷목은 짙은 초콜릿색으로 눈 앞은 더러운 흰색이며 각 깃털의 기부는 갈색이다. 귀깃과 멱은 초콜릿색으로 각 털의 중앙에는 가늘고 긴 물방울 모양의 흰색 얼룩무늬가 있다. 날개는 남빛 녹색의 광택을 띤 검은 갈색이다. 부리는 검은색이며 부리 털은 흰색이고, 기부는 갈색이다. 홍채는 갈색이고, 다리는 검은색이다. 주로 고지대에서 번식하고 겨울에는 평지로 떠돈다. 한반도 전역에서 번식하는 드물지 않은 텃새이다. 작은 무리 또는 단독 혹은 암수가 함께 생활한다. 나무 위나 땅 위에서 먹이를 찾는다. 3~4월에, 연 1회 번식한다. 한배에 2~5개 산란한다. 주로 소나무나 그 밖의 침엽수 종자를 좋아한다. 세계적으로는 우랄, 러시아 동북부~아나디르 지역 서부, 바이칼, 몽골 북부, 알타이, 타르바가타이, 사할린, 아무르, 우수리, 만주 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001960', '34.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('184', '붉은부리까마귀', 'Pyrrhocorax pyrrhocorax', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Corvidae', '까마귀과', 'Pyrrhocorax', '붉은부리까마귀속', 'pyrrhocorax', '붉은부리까마귀', '몸길이 39~40cm이다. 암수는 생김새가 비슷하다. 갈까마귀보다 좀 크다. 깃털은 전체적으로 검은색이다. 다리와 부리는 붉은색이다. 20마리 이상 무리를 지어 날아다니는 것을 볼 수 있다. 낭떠러지에 있는 굴이나 바위틈에 나뭇가지로 둥지를 만든다. 알은 3개를 낳는다. 무척추동물을 먹이로 한다. 영국, 아일랜드, 스페인, 지중해 연안, 서아시아, 중앙아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001962', '39.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('185', '갈까마귀', 'Corvus dauuricus', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Corvidae', '까마귀과', 'Corvus', '까마귀속', 'dauuricus', '갈까마귀', '참새목 까마귀과에 속하는 조류이다. 몸길이는 33cm이고 온몸이 검다. 겨울깃의 머리꼭대기와 뒷머리는 자색의 광택이 있는 검은색이다. 눈 뒤쪽과 귀깃 위쪽의 깃털은 끝이 잿빛 흰색이다. 턱 밑과 멱은 푸른색의 광택이 있는 검은색이다. 폭이 넓은 목띠와 여기에 이어지는 아랫면은 모두 잿빛 흰색이다. 아랫날개덮깃, 겨드랑이깃, 아래꼬리덮깃은 석판 검은색이다. 꼬리와 등 쪽은 광택이 있는 검은색이다. 여름깃은 광택이 적고 검은색은 갈색을 띤다. 암컷의 깃털 색은 수컷과 같고 크기가 작다. 홍채는 잿빛 갈색이며 다리는 모두 검은색이다. 평지 및 산지 숲에 살며 흔하지 않은 겨울철새이다. 언제나 많은 수의 무리를 이룬다. 5-9월에 4-6개의 알을 낳는다. 잡식성이다. 장진호, 풍산에 서식하고 세계적으로는 시베리아, 아무르, 우수리, 만주, 몽골, 중국, 티베트에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001964', '33.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('186', '떼까마귀', 'Corvus frugilegus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Corvidae', '까마귀과', 'Corvus', '까마귀속', 'frugilegus', '떼까마귀', '우리나라 전역에 걸쳐 봄과 가을에 통과하는 흔한 나그네새이며 한반도의 남단 지역에서는 많은 큰 무리가 월동하는 겨울새이다. 수컷의 겨울깃은 온몸이 자색의 광택이 강한 검은색이다. 콧구멍과 부리 주위는 잿빛 피부가 나출되며 적은 양의 솜깃털이 나 있다. 부리는 까마귀보다 더욱 가늘고 검은색이며, 부리의 등은 전체에 걸쳐 천천히 만곡된다. 평지, 개활지, 농경지 부근의 숲, 농촌의 인가와 시가지의 수목에 서식한다.교목위에 집단으로 번식도 한다. 산란기는 3월~5월 중순이고, 한배의 산란수는 3~5개이다. 잡식성으로 동물성과 식물성을 혼식한다. 시베리아 남부, 알타이, 몽골 북부, 아무르, 만주 등지에서 번식하고 한국 남부, 일본 서남부, 타이완 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001965', '45.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('187', '까마귀', 'Corvus corone', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Corvidae', '까마귀과', 'Corvus', '까마귀속', 'corone', '까마귀', '참새목 까마귀과에 속하는 조류이다. 전체 길이는 50cm이다. 암컷은 수컷과 동일한 색깔이지만 수컷보다 약간 작다. 몸 전체가 검은색으로 겨울철에는 녹색 광택을 띠며 여름철에는 약간 갈색을 띤다. 부리는 가늘고 검은색이며, 다리도 검은색이다. 농촌의 인가, 산지, 해변의 침엽수 등의 높은 나무에 살며 흔한 텃새이다. 번식기에는 1-2쌍의 작은 무리를 볼 수 있으나, 번식 후 월동기에는 무리를 짓는다. 주간에는 단독 또는 쌍으로 행동해도 밤에는 집단으로 잠을 자는 경우가 많다. 3-6월에 3-5개의 알을 낳는다. 식성은 잡식성으로 다른 새의 알과 새끼, 소형 포유류, 양서·파충류, 갑각류, 곤충류 등과 도토리, 곡류, 과실 등을 먹는다. 전국에 서식하고 세계적으로는 중국 남부, 일본 남부, 타이완에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001966', '50.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('188', '큰부리까마귀', 'Corvus macrorhynchos', 'Wagler', '1827', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Corvidae', '까마귀과', 'Corvus', '까마귀속', 'macrorhynchos', '큰부리까마귀', '참새목 까마귀과의 새로 몸길이는 57cm쯤이다. 수컷 겨울깃은 머리 깃털이 다른 보통의 깃 모양으로 광택이 적고, 멱의 깃털은 버드나무 잎 모양을 하고 있으나 가슴 깃털은 보통 모양의 깃털이다. 온몸은 광택이 강한 검은색이다. 꽁지는 얕은 둥근꽁지이다. 여름깃은 봄철 털갈이하지 않기 때문에 광택이 감소되고 갈색을 띠게 된다. 암컷은 수컷과 같으나 다소 작다. 한반도의 중부 이북 지역, 주로 북한 지역에서 흔히 번식하나 중부 이남 지역에서는 드물게 번식하는 편이다. 땅 위에서 3~40m의 높은 나뭇가지에 둥지를 튼다. 알은 3월하순~6월하순, 연 1회, 한배에 3~6개 낳는다. 잡초, 곡류, 과실 등 식물성과 작은 포유류, 어류, 양서류와 나비목, 메뚜기목, 딱정벌레목의 곤충류 등의 동물성도 먹는다. 또한 썩은 고기와 찌꺼기 등도 즐겨 먹는다. 한국 전역, 아무르, 우수리, 만주, 쓰시마 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001967', '57.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('189', '큰까마귀', 'Corvus corax', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Corvidae', '까마귀과', 'Corvus', '까마귀속', 'corax', '큰까마귀', '큰까마귀는 북반구에 널리 분포하는 대형의 검은 새로, 까마귀과 중 가장 널리 퍼져 있는 종입니다. 성체는 평균 약 63cm 길이로, 1.47kg 정도의 무게를 지닙니다. 다양한 서식지에 적응하여 활발하게 분포하며, 특히 주로 잡식성으로 먹이를 찾아 다양한 지역에서 활약합니다.    주로 2마리씩 짝을 지어 생활하며, 한 번 짝을 이루면 평생 함께 지내며 영역을 방어합니다. 매우 영리한 새로, 문제 해결 능력이 뛰어나며 인간의 언어를 흉내 내기도 합니다.    큰까마귀는 전 세계적으로 여러 문화에서 신화와 전설의 주제로 등장하며, 특히 영적이고 신적인 존재로 여겨지기도 합니다. 주로 고산지대, 해안, 넓은 개활지를 좋아하며, 때때로 인가 주변에서도 볼 수 있습니다. 그의 생명력과 적응력 덕분에 세계 자연보전연맹(IUCN)에서 ''관심 필요 없음'' 등급으로 분류되며, 안정적으로 서식지를 유지하고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001968', '63.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('190', '황여새', 'Bombycilla garrulus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Bombycillidae', '여새과', 'Bombycilla', '여새속', 'garrulus', '황여새', '참새목 여새과의 조류이다. 몸길이 18~20cm이다. 수컷 겨울깃의 이마에서 머리꼭대기까지는 살구색을 띤 붉은 밤색이다. 머리꼭대기에서 아랫등까지는 홍색을 띤 잿빛 갈색이다. 머리꼭대기에서 뒷머리까지의 깃털은 부드럽고 길며 모관을 형성한다. 꽁지깃의 우축은 대부분이 검은색이나 꽁지 끝부분만은 황색 또는 홍색일 때가 많다. 여름깃은 봄철 털갈이가 없다. 부리는 검은색으로 구각에 가까운 곳만은 흰색이고, 홍채는 어두운 갈색이다. 다리는 검은색이다. 침엽수림, 낙엽활엽수림에서 서식한다. 전국적으로 흔히 도래해 월동하는 겨울새이다. 겨울에는 10~30마리, 때로는 100마리까지 무리를 지어 주로 나무 위에서 생활한다. 6월경, 한배에 4~6개, 보통 5개, 때로는 7개 산란한다. 식물성이 주식이며, 파리목 곤충도 먹는다. 구북구 북부와 신북구 서북부에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001971', '19.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('191', '홍여새', 'Bombycilla japonica', 'Siebold', '1824', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Bombycillidae', '여새과', 'Bombycilla', '여새속', 'japonica', '홍여새', '우리나라에 불규칙하게 도래하는 겨울새로 몸길이 18cm이다. 온몸이 붉은빛이 도는 갈색이나 앞머리와 뺨은 붉은색, 눈선은 검은색, 허리와 배는 청회색이다. 배 중앙은 노란색이다. 꼬리는 검은색이나 끝부분이 붉은색이다. 머리에 댕기는 약 3cm이다. 국내 산림지대 특히 숲 가장자리, 도시의 정원과 공원, 침엽수림 등지에 서식한다. 침엽수에 나뭇가지, 지의류, 풀 등으로 접시 모양의 둥지를 만든다. 옅은 청색에 검은색 반점이 있는 알을 4~6개 낳는다. 주요 번식지는 러시아 동부지역의 산림지대이며, 한국, 중국, 일본 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001972', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('192', '박새', 'Parus major', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Paridae', '박새과', 'Parus', '박새속', 'major', '박새', '참새목 박새과에 속하는 조류이다. 몸길이는 14cm이다. 머리는 검은색이고 뺨은 흰색이다. 윗면은 회색이며 등은 연둣빛을 띤다. 아랫면은 흰색이고 가슴부터 배까지 중앙을 따라 검은색 띠가 있다. 인가부터 산림까지 다양한 곳에 살며 나무 구멍이나 건물 틈에 둥지를 튼다. 4월부터 산란을 시작하고 한 해에 두 번 번식한다. 번식 초반인 3월부터 울음소리를 들을 수 있다. 전국에 서식하는 텃새이고 세계적으로는 유라시아 전역에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001975', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('193', '스윈호오목눈이', 'Remiz pendulinus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Remizidae', '스윈호오목눈이과', 'Remiz', '스윈호오목눈이속', 'pendulinus', '스윈호오목눈이', '참새목 스윈호오목눈이과의 새이다. 앞이마, 눈 앞, 뺨, 귀깃은 갈색을 띤 검은색이다. 머리꼭대기는 잿빛으로 각 깃털에는 어두운 색의 축반이 있다. 눈썹선은 더러운 흰색이다. 뒷목은 잿빛 황갈색이고, 윗등은 밤색 띠가 있으며, 그 띠는 가슴까지 연장된다. 턱 밑과 턱은 흰색이다. 멱, 가슴, 아래꽁지덮깃은 크림 황갈색이다. 등, 허리, 위꽁지덮깃은 잿빛 황갈색이다. 꽁지는 어두운 갈색으로 엷은 황갈색의 가장자리가 있다. 부리는 잿빛인데 부리의 등은 짙다. 홍채는 어두운 갈색이고, 다리는 남청색 또는 잿빛 쥐색이다. 겨울철에는 무리 지어 강가의 갈밭에 서식한다. 여름철에는 강가나 호반의 숲에 살며 번식한다. 봄과 가을에 작은 무리들이 규칙적으로 우리나라를 지나가는 나그네새이다. 둥지는 보통 강가에서 물 위로 늘어진 버드나무 가지 끝에 매달아 만든다. 5월하순~7월 중순, 한배에 5~10개, 보통 7개 산란한다. 곤충의 유충을 주로 먹는다. 새끼는 나비목의 유충을 먹고 성장한다. 겨울철에는 풀씨를 먹는다. 한국의 일부 지역, 만주, 내몽골, 중국 서북부 윈난 성, 일본 등지에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001983', '10.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('194', '갈색제비', 'Riparia riparia', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Hirundinidae', '제비과', 'Riparia', '갈색제비속', 'riparia', '갈색제비', '흔하지 않은 나그네새이다. 암컷과 수컷의 이마, 머리꼭대기, 뒷머리, 눈 앞, 귀깃, 뺨은 흑갈색이고, 턱 밑과 목의 앞은 흰색이다. 등, 어깨깃, 위꼬리덮깃은 머리꼭대기와 같은 색이다. 가슴, 배, 옆구리, 아래꼬리덮깃은 흰색이다. 부리는 검은색이고, 다리는 어두운 갈색이다. 제비 무리에 섞여 이동한다. 배 밭이나 갈대밭을 잠자리로 한다. 수면을 나는 곤충 등을 날면서 잡아먹는다. 호수와 하천의 물가에 있는 모래땅, 흙 벼랑, 경작지의 모래층에 구멍을 파서 둥지로 하며 집단으로 다수가 번식한다. 산란기는 6~7월이며, 한배의 산란 개수는 3~5개이다. 시베리아 동남부, 아무르, 사할린, 쿠릴 열도, 홋카이도에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001986', '12.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('195', '제비', 'Hirundo rustica', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Hirundinidae', '제비과', 'Hirundo', '제비속', 'rustica', '제비', '참새목 제비과에 속하는 조류이다. 몸길이는 대략 18cm이다. 몸 윗면은 광택이 있는 어두운 청색이며, 이마와 멱은 적갈색이다. 멱 아래에는 검은색의 띠가 있다. 배는 엷은 주황색을 띤 흰색이다. 꼬리는 비교적 길고 끝이 V자형으로 패어 있다. 수컷은 암컷보다 바깥꼬리깃이 더 길다. 농경지, 하천에 살며 전국에서 흔히 볼 수 있는 여름철새다. 농경지, 하천 등 개방된 곳에서 빠르게 날아다니며 곤충을 잡는다. 주로 전선줄에 앉아 휴식을 취한다. 시골이나 도시 근처의 인가 또는 건축물의 옥내·외의 적당한 곳이나 다리 등에 둥지를 튼다. 둥지는 흙과 지푸라기 등을 섞어서 짓고 묵은 둥지를 이용하는 때도 있다. 알은 흰색 바탕에 적갈색과 엷은 자색의 작은 점이 흩어져 있다. 5-6개의 알을 낳고, 새끼는 부화 후 20~24일쯤 지나면 둥지를 떠난다. 전국에 서식하고 세계적으로는 유럽, 아시아 및 북미주 전역에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001988', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('196', '바위산제비', 'Ptyonoprogne rupestris', 'Scopoli', '1769', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Hirundinidae', '제비과', 'Ptyonoprogne', '바위산제비속', 'rupestris', '바위산제비', '바위산제비는 참새목 제비과에 속하는 작은 조류로, 몸길이는 약 14cm입니다. 윗면은 회갈색이고 아랫면은 더 밝은 톤을 띠며, 짧고 네모난 꼬리에는 흰 반점이 있습니다. 남유럽, 북서 아프리카와 팔레아크틱의 산악 지역에서 번식합니다. 많은 유럽 개체는 비거주성이며 북부 아프리카, 중동, 인도에서 겨울을 납니다. 바위나 인공 구조물에 진흙으로 만든 반컵 모양의 둥지를 지으며, 암컷이 주로 알을 품고 부모 모두가 새끼를 먹입니다. 번식기가 아니면 무리지어 생활하고, 먹이는 비행 중 곤충을 잡아 먹습니다. 영양은 잠자리의 곤충 다양성에 의존하여 먹이를 주로 절벽이나 계곡 근처에서 잡습니다. 높은 산과 절벽이 있는 지역의 따뜻하고 건조한 환경에서 번식하며, 인간 활동이 만든 구조물도 점점 더 많이 사용하고 있습니다. 보호 상태는 ''관심 필요'' 등급으로 걱정할 수준은 아닙니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001990', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('197', '흰털발제비', 'Delichon dasypus', 'Bonaparte', '1850', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Hirundinidae', '제비과', 'Delichon', '털발제비속', 'dasypus', '흰털발제비', '참새목 제비과의 조류로 몸길이는 14.5cm가량이다. 수컷 겨울깃의 이마, 머리꼭대기, 뒷목, 등, 어깨는 푸른색 광택이 나는 검은색이다. 단, 뒷목 깃털의 기부는 흰색이다. 눈 앞, 뺨, 귀깃은 검은 갈색이고, 턱 밑은 어두운 갈색과 더러운 흰색이다. 꽁지는 오목꽁지이며 꽁지깃의 수는 12개로 검은색이나 다소 푸른색을 띤다. 암컷은 수컷과 구별하기 힘들지만 암컷은 대체로 등 쪽의 광택이 약하고 아랫면의 흰색이 순수하지 않은 것이 많다. 매우 드문 여름새이다. 군서성이 강하고 채식지나 번식지에서 무리를 짓는 수가 많다. 인가의 처마 밑, 절의 천장 등에 영소하며 산지의 암벽, 폭포의 암벽에도 영소한다. 진흙과 마른풀을 이용하여 밥그릇 모양의 둥지를 튼다. 4월하순부터 8월상순까지, 한배에 2~8개를 산란한다. 파리목, 딱정벌레목, 매미목, 벌목 등 날아다니는 곤충류를 주로 먹는다. 오호츠크해 연안, 시베리아 남동부, 아무르, 사할린 등지에서 번식한다. 중국 북동부, 한국(함경남도 장진호반, 부전군 일원, 제주도), 일본 등지를 거쳐 간다. 중국 남부와 타이, 동남아시아에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001993', '14.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('198', '귀제비', 'Cecropis daurica', 'Laxmann', '1769', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Hirundinidae', '제비과', 'Cecropis', '귀제비속', 'daurica', '귀제비', '참새목 제비과에 속하는 조류이다. 수컷 겨울깃의 머리꼭대기, 뒷목, 등, 어깨는 광택 있는 자색을 띤 검은 푸른색이다. 허리는 붉게 녹슨 색이다. 턱 밑에서 배까지의 아랫면은 황갈색 또는 엷고 붉은 갈색이다. 아래꼬리덮깃의 짧은 것은 황갈색이고 긴 것은 푸른색 광택 있는 갈색을 띤 검은색이다. 위꼬리덮깃은 검은 푸른색이고, 꼬리는 깊은 제비꼬리로 바깥쪽 1쌍은 중앙 1쌍의 거의 2.5배나 길다. 암컷은 수컷과 깃털의 차이가 없다. 부리는 검은색이고, 홍채와 다리는 어두운 갈색이다. 도시나 농촌의 인가 주변에 살며 전국적으로 번식하는 흔한 여름새이다. 이동할 때나 번식 초기에는 암수 함께 또는 단독으로 생활하고 번식 후에는 가족과 함께 다니는 것이 많다. 처마 밑, 교량 밑, 지붕 밑, 처마 밑에 집단으로 둥지를 트는 것이 많다. 긴 술병이 옆으로 한 것과 같은 둥지를 튼다. 5-7월에 4-5개의 알을 낳는다. 전국에 서식하며, 세계적으로는 남유럽, 아시아의 온대 지역에서 번식하고 동남아시아, 아프리카, 인도에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001995', '19.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('199', '오목눈이', 'Aegithalos caudatus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Aegithalidae', '오목눈이과', 'Aegithalos', '오목눈이속', 'caudatus', '오목눈이', '참새목 오목눈이과새로 몸길이는 14cm가량이다. 암컷과 수컷의 이마, 머리꼭대기, 뒷머리, 눈 앞, 턱 밑, 멱은 흰색이다. 눈 위에 있는 검은색의 굵은 눈썹선이 뒷목까지 이른다. 날개는 검은 갈색이다. 부리는 검은색이고, 홍채는 어두운 갈색이다. 다리도 어두운 갈색이고, 종아리의 깃털은 엷은 갈색이다. 산림, 낙엽활엽수림, 송림, 잡목림,관목림에서 서식한다. 온대부터 아한대 지역에 걸쳐 널리 번식한다. 우리나라의 산림 전역에서 번식하는 흔한 텃새이다. 번식기에 암수가 함께 생활하며, 기타 시기에는 4~5마리 또는 10마리 정도의 가족군을 형성하는데 때로는 다른 종과 섞여 큰 무리를 지을 때도 있다. 주로 나무 위에서 생활한다. 낙엽활엽수림, 송림, 잡목림,관목림 속에 영소한다. 알은 4~6월, 한배에 7~11개, 때로는 13개 낳는다. 곤충류가 주식이며, 거미류도 먹는다. 식물성으로는 콩과식물의 종자도 먹는다. 한반도 전역, 유라시아 대륙에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001998', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('200', '쇠종다리', 'Calandrella brachydactyla', 'Leisler', '1814', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Alaudidae', '종다리과', 'Calandrella', '쇠종다리속', 'brachydactyla', '쇠종다리', '참새목 종다리과의 새이다. 몸길이는 14cm 정도로 작은 편이다. 수컷의 몸집이 암컷보다 다소 더 크다. 깃털은 다양하지만, 뾰족한 부리와 비교해서 강렬하지 않다. 꽁지는 어두운 갈색이다. 날개는 연한 갈색인데 밑부분은 황토색이다. 가슴과 배는 잿빛을 띠지만 양 가슴 쪽에는 어두운 갈색의 큰 무늬가 있다. 부리는 검은빛이 도는 갈색이나 발가락은 암갈색이다. 땅 위의 풀포기가 우거진 오목한 곳에 둥지를 튼다. 한배에 3~5개 산란한다. 알은 누런 갈색이다. 여름에는 주로 곤충을 잡아먹지만, 겨울에는 씨앗을 먹는다. 월동을 위해 이주한다. 서식지 파괴로 인해 수가 감소하고 있다. 중국, 몽골, 티베트, 이라크, 튀니지, 에티오피아 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002001', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('201', '뿔종다리', 'Galerida cristata', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Alaudidae', '종다리과', 'Galerida', '뿔종다리속', 'cristata', '뿔종다리', '뿔종다리는 「멸종위기 야생생물 Ⅱ급」의 종다리과 조류로, 우리나라에서는 매우 드문 텃새이다. 앉아 있을 때 머리 위에 깃이 뾰족하게 솟아있는 것이 특징이다. 자갈이 많은 건조한 개활지에서 곤충이나 식물의 종자를 주로 먹고 서식하며 우리나라를 포함한 전 세계적으로 개체 수가 감소하고 있다. 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002004', '17.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('202', '해변종다리', 'Eremophila alpestris', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Alaudidae', '종다리과', 'Eremophila', '해변종다리속', 'alpestris', '해변종다리', '해변종다리    해변종다리는 참새목 종다리과의 조류로, 주로 북반구의 열린 지역에 서식합니다. 몸길이는 약 16-20cm이고, 날개길이는 30-34cm에 이릅니다. 이 종은 뿔모양의 검은 머리 깃털로 쉽게 알아볼 수 있습니다. 여름에는 성조 수컷이 검은 ''뿔 깃''과 노란 얼굴 무늬를 자랑하며, 꼬리는 검은색으로 몸과 대조를 이룹니다.    해변종다리는 농경지, 초원, 사막, 해안, 골프장, 비행장 등 다양한 환경에 적응하여 서식합니다. 번식기에는 수컷이 노래하며 구애 시 보통 원을 그리며 날다가 암컷에게 다가가는 행동을 보입니다. 둥지는 암컷이 바닥의 움푹한 곳이나 인공적으로 만든 구멍에 식물 재료로 지으며 2-5개의 알을 낳습니다. 새끼는 양쪽 부모가 돌보며, 부화 후 8-10일 동안 둥지에서 머뭅니다.    최근 개체수는 감소 추세로, 농경지와 서식지의 변화, 인간 활동, 사고 등이 원인으로 지목되고 있습니다. 그러나 아직 멸종 위기 등급에는 미치지 않고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002008', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('203', '개개비사촌', 'Cisticola juncidis', 'Rafinesque', '1810', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Cisticolidae', '개개비사촌과', 'Cisticola', '개개비사촌속', 'juncidis', '개개비사촌', '우리나라에서는 제주도 일대에 서식하는 텃새이다. 몸길이 12.5cm, 날개 길이 약 5.5cm이다. 턱 밑과 멱의 상부, 배는 흰색, 멱의 하부와 가슴, 뒷목은 황갈색, 등은 검은 갈색에 흰무늬가 있다. 초원이나 개울가 풀밭, 보리밭, 하천부지 등에 서식한다. 단독 또는 암수가 같이 생활하며, 생활권이 넓다. 수컷은 번식 기간 중에 여러 개의 둥지를 만들고 여러 마리의 암컷을 유혹하여 각 둥지마다 알을 낳게 한다. 둥지는 화본과 식물의 줄기를 거미줄로 엮어서 컵 모양으로 만들고 4~6개의 알을 낳는다. 산란기는 5~8월이다. 주로 곤충류를 즐겨 먹는다. 유럽 남부와 아프리카, 아시아 남부 및 여러 섬에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002011', '12.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('204', '흰부리아비', 'Gavia adamsii', 'G.R.Gray', '1859', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gaviiformes', '아비목', 'Gaviidae', '아비과', 'Gavia', '아비속', 'adamsii', '흰부리아비', '아비목 아비과의 조류로 몸 전체의 길이는 84~97cm가량이다. 기러기 정도의 크기로 아비류 중 가장 크다. 여름깃의 배는 흰색이고, 옆구리와 등, 어깨 사이에는 흰색의 반점이 줄지어 있다. 목과 머리는 검은색이며, 보랏빛이 도는 녹색의 광택이 난다. 목에는 불완전한 흰색의 줄무늬가 있다. 부리는 뚜렷한 노란색이고 근거리에서 윗부리는 직선이나, 아랫부리는 위로 휜 모양이어서 전체적으로 위로 휜 것처럼 보인다. 알은 올리브 빛이 도는 갈색이다. 툰드라 지역 해안가의 호수, 만(灣)과 강 하구, 겨울에는 주로 만에서 서식한다. 둥지는 작은 섬이나 물가의 땅에 잘 위장하여 만드는데, 물풀 사이에 풀과 이끼를 이용하여 만든다. 알은 2개 낳는다. 전 북구, 북구 연안 툰드라, 북아메리카, 알래스카, 오호츠크 해안 등지에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001481', '90.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('205', '알바트로스', 'Phoebastria albatrus', 'Pallas', '1769', 'Chordata', '척삭동물문', 'Aves', '조강', 'Procellariiformes', '슴새목', 'Diomedeidae', '알바트로스과', 'Phoebastria', '알바트로스속', 'albatrus', '알바트로스', '슴새목 알바트로스과에 속하는 조류이다. 몸 전체 길이는 90cm, 날개 편 길이는 2.1m에 달한다. 몸 색깔은 전체적으로 흰색이다. 첫째날개깃은 검은색이다. 부리는 크고 분홍색이다. 알은 엷은 황갈색을 띤 회백색 바탕에 어두운 갈색의 작은 얼룩점이 있지만 가끔 암갈색이나 흰색으로 얼룩점이 없는 것도 있다. 주로 해안에서 서식하며 거친 땅 위의 움푹 파인 곳이나 맨땅에 알을 낳는다. 알은 1개만 낳으며, 암수가 번갈아 품는다. 우리나라에서는 과거에 관찰되었을 뿐 길잃은새이다. 일본, 태평양 북부 베링해에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001485', '90.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('206', '흰배슴새', 'Pterodroma hypoleuca', 'Salvin', '1888', 'Chordata', '척삭동물문', 'Aves', '조강', 'Procellariiformes', '슴새목', 'Procellariidae', '슴새과', 'Pterodroma', '흰배슴새속', 'hypoleuca', '흰배슴새', '슴배목 슴새과의 해양성 조류이다. 몸 전체 길이는 30.0cm, 날개길이는 22.2cm, 꽁지길이는 100.14mm, 부리는 24.99mm, 부척은 28.71mm이다. 대양의 섬에 서식한다. 전체 개체수는 외딴 섬에만 분포하기 때문에 자세히 조사되어 있지 않다. 태평양의 한 섬(Lisianski Island)에서만 15~25만 쌍이 서식할 정도로 지역적으로는 흔한 종이다. 하와이 제도, 일본 보닌 열도의 외딴 섬 등지에서 번식한다. 북태평양 아열대 지역에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001488', '30.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('207', '슴새', 'Calonectris leucomelas', 'Temminck', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Procellariiformes', '슴새목', 'Procellariidae', '슴새과', 'Calonectris', '슴새속', 'leucomelas', '슴새', '슴새목 슴새과에 속하는 조류이다. 암컷과 수컷은 생김새가 비슷하여 이마에서 뒷머리까지 검은 갈색이며, 각 깃털 끝은 흰색이다. 목과 어깨깃은 어두운 갈색이다. 등, 허리, 위꽁지덮깃은 어두운 갈색이며, 등 중앙의 각 깃털과 위꽁지덮깃의 각 깃털은 가장자리가 흰색이다. 꽁지는 어두운 갈색이다. 알은 흰색이다. 바다에서 무리 지어 생활한다. 우리나라에서 드물게 번식하는 여름철새이다. 날개가 길어서 직접 날아오르지 못하고 나무나 벼랑 위로부터 뛰어내리거나, 달리다가 날아오른다. 둥지는 땅속에 구멍을 파서 만든다. 6-7월에 1개의 알을 낳아 암수가 번갈아 품는다. 어류, 오징어, 낙지, 해조류 등을 먹는다. 울릉도, 제주도에서 관찰되며, 세계적으로 중국 해안에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001490', '48.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('208', '검은슴새', 'Bulweria bulwerii', 'Jardine & Selby', '1828', 'Chordata', '척삭동물문', 'Aves', '조강', 'Procellariiformes', '슴새목', 'Procellariidae', '슴새과', 'Bulweria', '검은슴새속', 'bulwerii', '검은슴새', '검은슴새는 바닷새로, 대서양, 인도양, 태평양의 열대 및 아열대 지역에서 발견됩니다. 몸길이는 25~29cm이며, 주로 갈색 깃털과 긴 꼬리를 지니고 있습니다. 물 위에서 플랑크톤 등의 먹이를 잡으며, 비행은 경쾌하고 비틀거리며 이뤄집니다. 번식기는 4월과 5월에 시작하며, 주로 이전의 둥지와 짝을 유지합니다. 산란은 한 개의 알을 낳으며, 암수 모두 약 42~46일간 교대로 품습니다. 먹이는 주로 작은 물고기와 오징어, 갑각류로 구성됩니다. 북동대서양 등지에서는 야생 고양이와 쥐에 의해 위협받고 있으며, 원주민에 의한 포획과 해양 오염 역시 위협 요인입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001495', '27.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('209', '논병아리', 'Tachybaptus ruficollis', 'Pallas', '1764', 'Chordata', '척삭동물문', 'Aves', '조강', 'Podicipediformes', '논병아리목', 'Podicipedidae', '논병아리과', 'Tachybaptus', '논병아리속', 'ruficollis', '논병아리', '논병아리목 논병아리과 조류로 흔한 텃새이며 겨울철새이다. 몸길이 약 26cm. 국내에 서식하는 논병아리류 중 가장 작다. 암수의 형태는 유사하며, 전체적으로 동그스름한 체형에 머리꼭대기와 등은 어두운 갈색 눈은 노란색이다. 번식깃은 뺨 및 목 앞과 옆이 적갈색이며 검은색 부리 기부에 노란색 부분이 있다. 겨울깃은 뺨에서 앞목까지 황갈색으로 변하며 몸 아랫면도 황갈색이다. 전국의 강, 호수, 저수지, 하천 등 식생이 풍부하게 발달된 습지에서 생활한다. 둥지는 풀줄기 및 뿌리를 이용해 수면 위에 뜨게 만드는 구조로 풀줄기 사이에 만든다. 포란기간은 20~25일이며 새끼는 부화 후 곧바로 둥지를 떠나 어미를 따라다니며 먹이를 받아먹는다. 먹이는 잠수하여 수서곤충이나 소형 어류를 먹는다. 유럽과 아시아의 온대지역, 동남아시아, 인도, 아프리카에 분포한다. 국내에서는 전국의 습지와 저수지에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001501', '26.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('210', '큰논병아리', 'Podiceps grisegena', 'Boddaert', '1783', 'Chordata', '척삭동물문', 'Aves', '조강', 'Podicipediformes', '논병아리목', 'Podicipedidae', '논병아리과', 'Podiceps', '뿔논병아리속', 'grisegena', '큰논병아리', '우리나라를 찾는 겨울철새로 몸길이 약 47cm이다. 우리나라 해안에 주로 분포한다. 여름깃은 머리꼭대기에서 눈 밑까지는 검고 멱과 뺨은 회색, 목은 짙은 밤색이다. 등과 옆구리는 어두운 회색이고 몸 아랫면은 흰색이다. 겨울깃에서는 목의 밤색 부분이 사라진다. 우리나라 동해안과 석호, 간척호수, 서해안의 저수지, 남해안과 제주도 등에 서식한다. 번식지에서는 호수나 습지의 갈대밭에 둥지를 틀고 6월~8월상순에 알 3~6개를 낳는다. 어류, 개구리, 갑각류, 연체동물, 수서곤충 등을 먹는다. 캄차카, 시베리아 동부, 만주, 사할린, 홋카이도, 북아메리카 등에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001503', '47.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('211', '뿔논병아리', 'Podiceps cristatus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Podicipediformes', '논병아리목', 'Podicipedidae', '논병아리과', 'Podiceps', '뿔논병아리속', 'cristatus', '뿔논병아리', '논병아리목 논병아리과 조류로 드문 텃새이자 흔한 겨울철새이다. 몸길이 약 49cm. 암수가 유사하다. 우리나라 논병아리류 중 가장 크다. 목이 길고 부리는 검은색을 띤 분홍색이며, 머리에 검은색의 뿔깃이 있다. 번식기에는 머리 위에 길고 검은 깃이 돌출되며, 뺨은 적갈색 그 아래 머리와 목의 경계는 검은색이다. 번식기에는 물 위에 물풀이나 이끼류를 이용해 물에 뜨는 둥지를 만들며, 둥지를 비울 때는 알을 풀로 덮어 놓는다. 새끼는 부화한 후에 어미를 따라다니며, 때때로 어미의 등에 올라타고 이동하는 것을 볼 수 있다. 강 하구 및 호수나 저수지 해안 등에서 잠수하여 물속의 어류 등을 잡아먹는다. 겨울에는 해안가에서 수백 개체로 구성된 무리를 짓기도 한다. 유라시아대륙 중부, 아프리카, 오스트레일리아, 뉴질랜드에 분포하고, 유라시아에 번식하는 집단은 겨울철에 남하하여 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001504', '49.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('212', '귀뿔논병아리', 'Podiceps auritus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Podicipediformes', '논병아리목', 'Podicipedidae', '논병아리과', 'Podiceps', '뿔논병아리속', 'auritus', '귀뿔논병아리', '우리나라 전역의 하천이나 호소, 저수지 등에서 비교적 드물게 월동하는 겨울새이다. 전체 길이 30~39cm이다. 번식기에는 암수 모두 목과 배는 적갈색 그리고 머리는 검은색이다. 머리에는 담황색 깃이 2개 있다. 겨울에는 등이 검은색이고, 배는 흰색이다. 목은 짧고 가늘다. 뺨과 목은 흰색이 뚜렷하고, 머리꼭대기는 검은색이다. 한대 지역에서 번식한다. 수초를 모아 물에 뜨는 둥지를 만들어 알을 4~5개 낳는다. 암수가 함께 알을 품으며, 알을 품는 기간은 20~24일이다. 2~3쌍 정도가 모여서 번식을 하며 때로는 단독으로 한다. 물고기, 연체동물, 수생곤충 등의 동물성 먹이 외에 수초인 거머리말 따위를 먹는다. 구북구의 온대와 열대에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001505', '34.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('213', '검은목논병아리', 'Podiceps nigricollis', 'C.L.Brehm', '1831', 'Chordata', '척삭동물문', 'Aves', '조강', 'Podicipediformes', '논병아리목', 'Podicipedidae', '논병아리과', 'Podiceps', '뿔논병아리속', 'nigricollis', '검은목논병아리', '논병아리목 논병아리과 조류로 비교적 흔한 겨울철새이다. 몸길이 약 31cm. 암수는 유사하며 부리는 전체적으로 검은색이고 위로 휘어져 있다. 눈은 붉은색이고 정수리가 볼록하다. 강 하류, 해안 호수에서 무리를 이루어 행동한다. 종종 수십 마리가 무리를 지어 차례차례 잠수하여 어류, 갑각류, 복족류, 곤충류를 먹는다. 유럽에서 카자흐스탄, 중국 동북부에서 우수리, 북아메리카 중부, 아프리카 동부 등지에서 번식하고, 유럽, 중동, 동아시아, 중남미, 아프리카 남부에서 월동한다. 국내에는 우리나라 전역에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001506', '31.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('214', '먹황새', 'Ciconia nigra', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ciconiidae', '황새과', 'Ciconia', '황새속', 'nigra', '먹황새', '황새목 황새과에 속하는 조류로 매우 드문 겨울철새이며, 매우 드문 나그네새이다. [멸종위기 야생생물 I급] 몸길이는 약 95cm. 암수의 형태는 유사하다. 몸은 전체적으로 녹색 광택이 있는 검은색이며, 부리와 다리, 눈 주위가 붉고 가슴과 배 부분이 흰색이다. 어린 새는 부리와 눈 주위가 녹색을 띤 회색, 다리는 녹색을 띤 노란색이며, 몸 윗면에 녹색 광택이 거의 없고 목 부분에 작은 흰색 반점이 흩어져 있다. 농경지, 강 하구, 저수지, 하천, 풀이 우거진 습지에 산다. 논과 하천 또는 얕은 물가에 내려앉아 먹이를 찾아 먹는 것 외에는 나무 위나 절벽에서 휴식을 취한다. 먹이는 주로 어류, 양서·파충류이다. 알은 3~5개 낳는다. 세계적으로는 유라시아 대륙의 온대지역과 남아프리카에서 번식하고 인도와 중국 남부에서 월동한다. 국내에서는 매우 드물게 서해안 지역을 통과하며, 겨울철 주남저수지, 내성천, 대동댐 등지에서 관찰된 기록이 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001510', '95.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('215', '황새', 'Ciconia boyciana', 'Swinhoe', '1873', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ciconiidae', '황새과', 'Ciconia', '황새속', 'boyciana', '황새', '황새목 황새과에 속하는 조류로 매우 드문 겨울철새이며, 최근 인공 증식된 개체들이 방사되어 충남 일부 지역에서 번식한 기록이 있다. [멸종위기 야생생물 I급] 몸길이는 약 112cm. 암수의 형태는 유사하다. 날개의 검은색을 제외하고 전체적으로 흰색을 띤다. 부리는 매우 크며 검은색이다. 홍채는 옅은 노란색이고, 눈 주위가 붉은색이다. 다리는 붉은색이다. 논, 하천, 호수에 서식하는 겨울철새로 어류, 개구리, 들쥐 등을 잡아먹는다. 과거 우리나라에서는 전국적으로 번식하는 텃새이자 겨울철새였지만, 현재는 야생 개체는 국내에서 번식하지 않으며 천수만, 해남, 제주도, 순천만, 낙동강 하구 등지에 적은 수가 겨울에 날아온다. 세계적으로는 시베리아, 연해주 남부, 중국 동북부에 분포한다. 최근 국내에서 인공증식 후 방사된 개체 중 일부는 연중 국내에서 관찰되기도 한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001511', '112.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('216', '검은머리흰따오기', 'Threskiornis melanocephalus', 'Latham', '1790', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Threskiornithidae', '저어새과', 'Threskiornis', '검은머리따오기속', 'melanocephalus', '검은머리흰따오기', '우리나라에서는 미조로 알려져 있다. 날개 길이 34~37cm, 부리 길이 13.5~17.5cm이다. 몸 빛깔은 흰색이며 어깨깃과 셋째날개깃은 푸른빛이 도는 잿빛이다. 목둘레의 깃은 길어서 앞쪽에 술 모양으로 늘어져 있다. 따오기와 유사하나 머리와 목 부분에 검은색 피부가 드러나 있는 점이 다르다. 활처럼 굽은 긴 부리와 다리는 검은색이다. 해안이나 늪지, 못가에서 서식한다. 물고기나 개구리, 수서곤충, 조개 등을 먹는다. 우리나라에서는 제주도에서 관찰되었다. 중국 남부와 인도차이나, 인도에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001514', '70.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('217', '따오기', 'Nipponia nippon', 'Temminck', '1835', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Threskiornithidae', '저어새과', 'Nipponia', '따오기속', 'nippon', '따오기', '황새목 저어새과에 속하는 조류이다. 몸길이는 약 75cm이며, 백색 또는 회색이 있다. 백색형 암컷과 수컷의 겨울깃은 몸 전체가 옅은 붉은색을 띤 흰색이다. 부리 뒷부분에서 눈 주위 사이에는 붉은색 피부가 넓게 드러나 있다. 뒷머리와 뒷목의 각 깃털은 10cm 정도의 가늘고 긴 관 모양을 이루고 있으며, 옅은 붉은색을 띤 흰색이다. 부리는 검은색이고, 끝은 붉은색이다. 다리는 갈색을 띤 붉은색이다. 회색형 겨울깃은 뒷머리의 관 모양 깃, 목, 등, 어깨 부분이 회색이며, 기타 부분은 백색형과 비슷하다.관목이나 대나무가 밀생한 소나무 숲에서 서식한다. 번식기에 단독 또는 쌍으로 생활하지만, 그 이외에는 작은 무리를 이루어 생활한다. 둥지는 소나무, 밤나무 등에 나뭇가지와 마른 덩굴을 이용해 접시 모양으로 만든다. 알 낳는 시기는 3월 중순부터 5월까지다. 작은 민물고기, 개구리, 올챙이 등을 주로 먹는다. 비무장 지대, 부산, 대전, 서울, 인천 등에 서식했지만 오랫동안 관찰되지 않고 있다. 세계적으로는 중국에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001516', '75.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('218', '노랑부리저어새', 'Platalea leucorodia', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Threskiornithidae', '저어새과', 'Platalea', '저어새속', 'leucorodia', '노랑부리저어새', '황새목 저어새과에 속하는 조류로 매우 드문 겨울철새이며, 매우 드문 나그네새이다. [멸종위기 야생생물 II급] 몸길이는 약 86cm. 암수의 형태는 유사하고 몸 전체가 흰색이며, 다리는 검고 부리는 평평한 주걱 모양으로 끝부분이 노란색이다. 여름깃은 뒷머리에 긴 다발 모양 노란 장식깃이 있으며, 목 아랫부분에는 노란빛이 도는 갈색 목테가 있다. 겨울깃은 몸이 흰색이고, 눈언저리와 턱 밑, 멱의 중앙은 피부가 드러나 있다. 개활 습지, 얕은 호소, 큰 하천, 하구의 진흙 또는 갯벌, 암석과 모래로 덮인 작은 해안의 섬 등지에 서식하며, 물고기, 개구리, 올챙이, 조개류, 연체동물, 곤충 따위를 잡아먹는다. 서남해안의 강 하구와 습지, 제주도 등지에서 소규모의 무리가 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001518', '86.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('219', '저어새', 'Platalea minor', 'Temminck & Schlegel', '1849', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Threskiornithidae', '저어새과', 'Platalea', '저어새속', 'minor', '저어새', '황새목 저어새과에 속하는 조류로 서해안의 무인도서와 인천 연안 등지에서 번식하는 여름철새이다. [멸종위기 야생생물 I급] 몸길이 약 74cm. 암수의 형태는 유사하다. 몸 전체가 흰색이며, 부리는 전체가 검은색으로 길고 끝이 주걱 모양이며, 다리는 검은색이다, 번식기에는 머리에 장식깃이 생긴다. 어린 새는 부리가 검은빛이 도는 살색이고, 날개 끝이 검은색이다. 번식기가 아닐 때는 강 하구, 양어장, 갯벌 등지에 서식하며, 번식기에는 서해안의 무인도 바위 지역에서 둥지를 틀고 번식하며 한 배 알은 4~6개 낳는다. 갯벌과 하구, 논 등의 얕은 습지에서 부리를 휘저으며 물고기나 새우류를 주로 잡아먹는다. 겨울철에는 주로 제주도와 서남해안의 습지와 하구에서 소수의 무리가 월동한다. 중국과 러시아에 각각 1개씩 있는 번식지를 제외하고는 대부분 우리나라 서해안에서 번식하는데 특히 비무장지대 또는 인근의 무인도를 주요 번식지로 이용하며, 인천의 인공섬에서도 번식한다. 중국, 일본, 타이완, 홍콩 및 베트남에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001519', '74.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('220', '알락해오라기', 'Botaurus stellaris', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Botaurus', '알락해오라기속', 'stellaris', '알락해오라기', '몸길이는 약 76cm다. 암컷과 수컷 모두 이마와 머리꼭대기는 검은색이고, 뒷머리와 뒷목은 검은색으로 깃 가장자리가 황갈색이며 가로무늬가 있다. 뺨과 굵은 턱 선은 어두운 갈색의 가는 턱밑 선이 있고, 그 양쪽은 황갈색으로 가슴에는 검은 갈색의 세로무늬가 있다. 등과 어깨 깃은 검은색으로 가장자리가 황갈색이며, 그 황갈색 부분이 불규칙한톱니모양으로 검은색 부분과서로 엇갈려 있다. 허리와 위꼬리덮깃은 엷은 황갈색으로 불규칙한 엷은 검은색의 가로띠가 있다. 부리는 두텁고 노란색이다. 다리는 짧고 굵은 편이며 노란색을 띤 녹색이다. 겨울철새로 갈대밭, 풀이 우거진 습지, 호수 등에서 혼자 생활한다. 주로 밤에 활동하나 때로는 낮에도 활동한다. 유럽·시베리아·중국(북부) 등지에서 번식하고 한국·일본·중국(남부)·보르네오 섬·말레이시아 등지에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001522', '76.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('221', '덤불해오라기', 'Ixobrychus sinensis', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Ixobrychus', '덤불해오라기속', 'sinensis', '덤불해오라기', '우리나라에서 드물게 번식하는 여름철새이다. 몸길이 37cm이다. 온몸이 일반적으로 갈색이다. 몸 아랫면은 흰색에 가까운 갈색이고, 등은 짙은 갈색, 다리는 엷은 녹황색이다. 뺨과 귀깃은 엷은 홍갈색이다. 턱과 멱의 양쪽은 흰색이다. 꼬리는 검은색이다. 수컷은 이마에서 뒷머리까지가 검은색이나 암컷은 적갈색이다. 갈대밭이나 물가의 풀숲에서 생활한다. 온대에서 열대 지역에 걸쳐 번식한다. 단독으로 또는 암수가 함께 생활한다. 둥지는 물가의 풀숲,관목림의 갈대나 줄풀의 줄기 사이에 접시 모양으로 만든다. 산란기는 5월하순~8월상순이다. 알은 5~6개 낳아 17~19일 포란한다. 어류를 주식으로 하고, 개구리, 갑각류(새우)도 먹는다. 한국, 유라시아 대륙 동남부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001524', '37.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('222', '큰덤불해오라기', 'Ixobrychus eurhythmus', 'Swinhoe', '1873', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Ixobrychus', '덤불해오라기속', 'eurhythmus', '큰덤불해오라기', '황새목 백로과에 속하는 조류이다. 몸길이는 약 38cm이다. 수컷은 이마에서 어깨까지 어두운 밤색이다. 암컷의 이마와 머리꼭대기는 수컷과 같으나 등과 어깨는 어두운 밤색에 작고 둥근 흰색 무늬가 흩어져 있다. 허리와 위꼬리덮깃은 엷은 밤갈색이다. 귀깃, 목의 옆과 뒤는 진하고 붉은 밤색이다. 턱밑과 턱 아래 부위의 양옆은 크림 빛이 도는 흰색이다. 턱 아래 부위의 중앙부터 앞목, 가슴, 배 사이는 황갈색이며 가슴 옆과 옆구리는 갈색이 돌고, 턱밑부터 가슴까지 어두운 갈색 세로줄이 1개 있다. 갈대밭, 작은 물웅덩이, 풀이 우거진 습지에 살며, 우리나라에서는 드문 여름철새이다. 물고기, 개구리, 곤충류를 주로 잡아먹는다. 물가의 갈대 줄기 위 또는 풀이 무성한 땅 위에 풀잎과 줄기를 이용해 둥지를 만들고 5~7월에 알 5~6개를 낳는다. 서남해안에 서식하고 세계적으로는 동아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001525', '38.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('223', '열대붉은해오라기', 'Ixobrychus cinnamomeus', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Ixobrychus', '덤불해오라기속', 'cinnamomeus', '열대붉은해오라기', '황새목 백로과의 새로 몸길이는 40cm, 한쪽 날개길이는 14~15cm이다. 수컷의 깃털은 몸 윗면이 붉은 갈색이고, 암컷은 더 진한 갈색이다. 몸 아랫면은 황색이며, 멱에는 갈색의 세로무늬가 한 줄 있다. 암컷은 분명한 검은 갈색의 세로무늬가 여러 개 있다. 번식 시기에는 수컷의 눈 앞이 붉은색으로 변한다. 논, 갈대밭, 습지에서 단독 또는 암수가 함께 생활한다. 얕은 물속이나 진흙 위를 천천히 거닐면서 채식한다. 둥지는 갈대밭이나 수면 위에 있는 수초의 줄기 사이에 풀잎과 갈대 줄기 등을 이용해서 접시 모양으로 만든다. 5~8월에 5~6개 정도 산란한다. 물고기, 개구리 등을 잡아먹는다. 우리나라 제주도 용담동에 서식한다. 세계적으로는 일본 오키나와를 비롯한 동아시아에 분포한다. 우리나라에서는 미조(迷鳥)로 알려져 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001526', '40.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('224', '붉은해오라기', 'Gorsachius goisagi', 'Temminck', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Gorsachius', '붉은해오라기속', 'goisagi', '붉은해오라기', '황새목 백로과에 속하는 조류이다. 몸길이는 49cm 정도이다. 몸 전체는 붉은색을 띤 갈색이고 부리는 짧고 두툼하다. 눈 앞부분은 노란색을 띠며, 목 앞부분 중앙에 어두운 갈색 줄이 있다. 날 때 날개의 중앙을 가로지르는 검은색 굵은 띠가 특징이다. 다리는 노란색을 띤 녹색이며, 홍채는 노란색이다. 단독으로 생활하기 때문에 쉽게 관찰되지 않으며, 산림이 있는 해안 도서 지역에서 땅속의 벌레나 곤충, 달팽이나 물고기 등을 먹는 모습이 간혹 눈에 띈다. 삼나무나 그 밖의 침엽수 속의 어두운 계곡, 연못 등 물가에 내려 먹이를 찾는다. 그러나 밤에는 먼 거리를 날아다니며, 지상을 조용히 걷기도 한다. 번식은 주로 5월 말에서 6월에 걸쳐 시작되며, 둥지는 지상 7~20m 높이의 나무 위에 만든다. 제주도, 부산, 하태도, 금오도 등 일부 남해안의 해안 도서에 간혹 나타나는 철새이며 세계적으로는 일본, 러시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001530', '49.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('225', '푸른눈테해오라기', 'Gorsachius melanolophus', 'Raffles', '1822', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Gorsachius', '붉은해오라기속', 'melanolophus', '푸른눈테해오라기', '푸른눈테해오라기는 중형 해오라기 종류로, 몸길이 약 48cm이다. 이 새는 남아시아와 동아시아 지역에 분포하며, 숲, 시내, 늪지에서 주로 서식한다. 성체는 목과 가슴이 적갈색이고, 윗부분은 밤색 무늬가 있다. 날개깃은 검정색이며, 머리 윗부분도 검다. 눈은 노란색을 띠고 있으며, 부리는 검은색, 다리는 녹색을 나타낸다. 주로 나무 위에서 쉬며 혼자 생활하는 특징이 있으며, 지렁이, 개구리 등을 먹이로 한다. 한국에서는 매우 드물며, 번식기에는 더 깊은 색의 깃으로 변하기도 한다. 세계적으로는 개체 수가 많으며 멸종 위기 단계는 아니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001531', '48.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('226', '해오라기', 'Nycticorax nycticorax', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Nycticorax', '해오라기속', 'nycticorax', '해오라기', '황새목 백로과의 조류로 흔한 여름철새이고, 드문 텃새이다. 몸길이 약 57cm. 암수의 형태가 유사하다. 머리부터 뒷목까지는 검은색, 등은 녹색을 띤 검은색이다. 짧고 굵은 목과 붉은색의 눈, 노란색의 다리, 검은색의 부리와 머리 뒤로 늘어진 2~3개의 긴 흰색 장식깃이 특징이다. 주로 야행성으로 야간에 주로 활동하지만, 주간에도 먹이사냥을 한다. 다른 백로류와 섞여 집단번식지에서 번식한다. 1990년 이전에는 매우 드문 여름철새로 기록되었으나, 최근 몇십 년 동안 꾸준하게 번식개체수가 증가하는 추세에 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001533', '57.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('227', '검은댕기해오라기', 'Butorides striata', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Butorides', '검은댕기해오라기속', 'striata', '검은댕기해오라기', '황새목 백로과의 조류로 흔한 여름철새이다. 몸길이 약 52cm. 암수의 형태가 유사하다. 머리꼭대기에서 뒷목까지 푸른빛이 도는 검은색이며, 뒷머리에 검은색의 댕기가 돌출되어있다. 몸은 전체적으로 회색이며, 날개 윗면은 진한 회색이다. 날개덮깃 가장자리는 흰색이다. 가슴 중앙 부분에는 흰색과 검은색의 세로줄 무늬가 특징적이다. 다리는 짧고 녹황색을 띠며, 부리는 전체적으로 길고 검은색이다. 4월에 국내에 도래하여 9월까지 관찰된다. 다른 백로류와 다르게 숲속에서 단독으로 번식하며, 나무 위에 접시 모양의 둥지를 짓고 청록색의 알을 4~5개 낳으며, 포란기간은 21~25일이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001535', '52.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('228', '흰날개해오라기', 'Ardeola bacchus', 'Bonaparte', '1855', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Ardeola', '흰날개해오라기속', 'bacchus', '흰날개해오라기', '황새목 백로과의 조류로 드문 나그네새이며, 매우 드문 여름철새이다. 몸길이 약 45cm. 암수의 형태가 유사하다. 부리는 전체적으로 노란색이며, 끝부분은 검은색이다. 다리는 노란색을 띤 녹색이다. 여름깃은 날 때 날개와 꼬리는 흰색으로 어두운 등 색깔과 뚜렷한 대비를 보인다. 머리, 가슴, 뒷목은 적갈색이며, 뒷목에 적갈색의 댕기가 있고, 등은 청회색이다. 겨울에는 등이 갈색으로 변하고 머리, 목, 가슴에 갈색의 세로줄 무늬가 생긴다. 호수, 연못, 논, 해안의 암석지 등에서 서식하고, 작은 척추동물, 곤충류, 물고기 등을 주로 잡아먹는다. 한국과 중국 동북부, 우수리, 베트남 미얀마 등지에서 번식하고, 중국 남부, 대만, 동남아시아 등에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001537', '45.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('229', '황로', 'Bubulcus ibis', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Bubulcus', '황로속', 'ibis', '황로', '황새목 백로과에 속하는 조류로 흔한 여름철새이다. 몸길이 약 50cm. 암수의 형태가 유사하다. 머리는 둥글고, 부리와 목은 다른 백로류보다 짧다. 여름깃은 머리, 목, 등의 장식깃이 오렌지색이며, 번식 초기의 구애시기에는 홍채와 부리는 진한 주황색을 띤다. 겨울깃은 전체적으로 흰색이며, 부리는 어두운 노란색이다. 발은 검은색 또는 짙은 녹색이다. 논, 논둑, 수로, 밭, 하천가, 초지, 목초지에 살며, 한반도 전역에 도래하여 백로과 조류와 집단번식지에서 번식하는 여름철새이다. 황로는 다른 백로류보다 무리를 짓는 일이 많고, 주로 메뚜기, 개구리, 미꾸라지 등을 잡아먹는다. 번식기는 4~8월이다. 전국에 서식하고 세계적으로는 전 대륙의 열대에서 온대에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001539', '50.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('230', '왜가리', 'Ardea cinerea', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Ardea', '왜가리속', 'cinerea', '왜가리', '황새목 백로과에 속하는 조류로 흔한 여름철새이며 흔한 겨울철새이다. 몸길이는 약 93cm. 암수의 형태가 유사하다. 등은 회색이고 아랫면은 흰색, 목 앞 중앙에는 검은 줄무늬가 있다. 머리는 흰색이며 검은 줄이 눈에서 뒷머리까지 이어져 댕기깃을 이룬다. 다리와 부리는 계절에 따라 노란색 또는 분홍색이다. 연못, 저수지, 논, 개울, 강, 하구 등 다양한 습지의 물가에서 단독 또는 2~3마리씩 작은 무리를 지어 행동한다. 4월상순에서 5월 중순에 한배에 3~5개의 알을 낳는다. 먹이는 어류를 비롯하여 개구리·뱀·들쥐·작은 새·새우·곤충 등 다양하다. 유라시아대륙 중남부, 인도, 아프리카, 마다가스카르에 분포한다.   번식시기 국내 전국의 습지에서 관찰되지만 최근 겨울철에도 월동하는 개체수가 증가하는 추세이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001541', '93.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('231', '붉은왜가리', 'Ardea purpurea', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Ardea', '왜가리속', 'purpurea', '붉은왜가리', '우리나라에서는 보기 드문 나그네새이다. 몸길이 79cm이다. 몸의 윗면과 날개는 진한 잿빛이고 어깨 사이에는 기다란 갈색 깃털이 있다. 목은 붉은색을 띠고 검은 줄이 있다. 몸의 아랫면은 붉은 갈색이다. 뒷머리에는 장식깃이 있다. 다습한 초원, 논, 못이나 늪에서 생활한다. 야행성이다. 둥지는 갈대밭이나 초지에 수초를 이용하여 접시 모양으로 만든다. 산란기는 4~6월로 한배에 알 4~5개를 낳는다. 먹이는 어류, 설치류(들쥐), 조류, 양서류, 갑각류, 곤충류 등을 먹는다. 유럽, 아시아, 북아메리카 등지에서 번식하고 열대 지방까지 내려가 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001542', '79.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('232', '중대백로', 'Ardea alba', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Ardea', '왜가리속', 'alba', '중대백로', '황새목 백로과의 조류로 흔한 여름철새이다. 몸길이 약 90cm. 암수의 형태가 유사하다. 여름철 관찰되는 흰색의 대형 백로로, 긴 부리와 다리를 가지고 있다. 번식시기에 부리는 검은색이며, 눈 앞부분은 청록색, 등에는 장식깃이 발달한다. 번식이 끝나면 부리는 노란색으로 바뀌며, 장식깃은 사라진다. 다리는 전체적으로 검은색이다. 다른 백로류와 함께 집단번식지에서 번식하며, 3~4개의 알을 낳으며, 포란기간은 25~28일이다. 논, 하천, 저수지, 양어장, 하구, 갯벌 등지에서 천천히 걸으며, 물고기나, 개구리 등을 잡아먹는다. 남아시아, 동아시아, 호주 등에 분포한다. 아종 대백로는 겨울에 도래한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001543', '90.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('233', '쇠백로', 'Egretta garzetta', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Egretta', '백로속', 'garzetta', '쇠백로', '황새목 백로과의 조류로 흔한 여름철새이다. 몸길이 약 60cm. 암수의 형태가 유사하다. 흰색의 백로류 중 가장 작다. 깃은 균일한 백색이고, 윗목에서 두 개의 길고 흰 장식깃이 자란다. 부리와 다리는 흑색, 발은 노란색이다. 중대백로, 황로 등과 함께 집단 번식하며, 4월하순~8월상순에 알을 3~5개 낳는다. 어류, 개구리, 뱀, 갑각류(새우, 가재) 및 수서곤충 등을 잡아먹는다. 중국, 동남아시아, 인도, 유럽, 아프리카, 뉴기니, 호주에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001546', '60.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('234', '흑로', 'Egretta sacra', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Egretta', '백로속', 'sacra', '흑로', '황새목 백로과의 조류로 드문 텃새이다. 몸길이 약 60cm. 암수의 형태가 유사하다. 온몸이 푸른색을 띤 검은색으로 다른 백로류와 쉽게 구분된다. 다리는 노랑색 또는 녹황색을 띠며, 부리는 어두운 갈색, 눈은 노란색이다. 도서지역이나 해안의 바위에서 서식하는 텃새로 바닷가 바위지대의 웅덩이나 인접 습지에서 물고기, 게, 새우등을 주로 잡아먹는다. 단독이나 소규모 무리가 거리를 두고 생활하며, 번식은 무인도서나 바닷가 절벽 등에서 한다. 국내에는 해안과 도서지역에서 드물게 서식한다. 흑색형과 백색형의 개체가 있지만 국내에는 아직까지 백색형의 기록은 없다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001547', '60.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('235', '노랑부리백로', 'Egretta eulophotes', 'Swinhoe', '1860', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Egretta', '백로속', 'eulophotes', '노랑부리백로', '황새목 백로과에 속하는 드문 여름철새이다[멸종위기 야생생물 I급]. 몸길이 약 68cm. 암수의 형태가 유사하다. 몸은 전체가 흰색이고, 부리와 발은 노란색이다. 번식기에는 다리는 검은색을 띠고 머리에 긴 장식깃들이 생기며, 목과 등에도 장식깃이 발달한다. 겨울에는 장식깃이 없어지고, 부리는 흑갈색, 다리는 녹황색을 띤다. 해안의 만, 하구, 간석지, 갯벌, 논 등에서 서식하는 드문 여름철새이다. 먹이는 주로 갯벌이나 바다가 가까운 습지에서 물고기와 게, 새우 등의 갑각류, 갯지렁이 등을 잡아먹는다. 간조 시 드러나는 갯벌이나 염전, 농경지, 저수지 등에서도 먹이 활동하며, 강화 남단 갯벌, 한강 하구, 안산 도서지역, 안면도, 천수만 등에서 여름철 먹이 활동하는 모습이 종종 관찰된다. 세계자연보호연맹에서 취약종(VU)으로 분류한 국제보호조로 전 세계 생존집단 중 대부분이 서해안의 무인도서 지역에서 번식하고 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001548', '68.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('236', '큰군함조', 'Fregata minor', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Pelecaniformes', '사다새목', 'Fregatidae', '군함조과', 'Fregata', '군함조속', 'minor', '큰군함조', '큰군함조는 대형 해양 조류로, 주로 열대 태평양과 인도양의 여러 섬에 서식합니다. 성체의 몸길이는 최대 105cm에 이르며, 깃털은 대부분 검은색입니다. 수컷은 붉은색의 목 주머니를 부풀려 암컷을 유혹합니다. 암컷이 더 크고 흰 목과 가슴을 가지고 있습니다. 주로 해양에서 나는 물고기, 특히 날치 등을 먹이로 하며, 다른 새로부터 먹이를 빼앗는 행동을 하기도 합니다. 번식지는 주로 섬의 덤불이나 나무이며, 한 번에 하나의 알을 낳고 양 부모가 돌보며 기릅니다. 평균 수명은 대략 37년 이상으로 추정됩니다. IUCN에서 지정한 보전 상태는 ''관심 필요''로 분류되어 있지만, 일부 지역에서는 멸종 위기에 처해 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001552', '52.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('237', '군함조', 'Fregata ariel', 'G.R.Gray', '1845', 'Chordata', '척삭동물문', 'Aves', '조강', 'Pelecaniformes', '사다새목', 'Fregatidae', '군함조과', 'Fregata', '군함조속', 'ariel', '군함조', '사다새목 군함조과에 속하는 조류이다. 몸길이는 1m 이상이다. 수컷은 배 양쪽에 있는 흰색 얼룩무늬를 제외하면 대부분이 검은색 또는 흑갈색이다. 수컷의 턱 밑에는 커다란 붉은색 공기주머니가 달려 있다. 암컷의 뒷목에는 밤색 가로띠가 있고 날개덮깃에는 갈색 띠가 있다. 가슴은 유백색이다. 때론 배를 쫓아다니기도 하며 번식기 이외에는 내륙에서는 보기 힘들다. 낮에는 하늘에서 날다가 급속도로 하강하여 파도 사이에 뛰어오르는 물고기를 포식한다. 다른 바다새를 괴롭혀서 먹이를 토하게 해서 먹이를 훔쳐 먹는다. 섬에 있는 나무에 나뭇가지로 거친 접시 모양의 둥지를 만든다. 어류를 주식으로 한다. 열대 대양에 사는 대형 조류로 간혹 우리나라 바다에 출현하기도 한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001553', '50.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('238', '큰사다새', 'Pelecanus onocrotalus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Pelecaniformes', '사다새목', 'Pelecanidae', '사다새과', 'Pelecanus', '사다새속', 'onocrotalus', '큰사다새', '큰사다새는 주로 유럽 남동부, 아시아 및 아프리카의 습지와 얕은 호수에서 번식하는 조류이다. IUCN 적색 목록에서 최소관심종으로 지정되어 있고, 여러 국제 협약에 의하여 보호받고 있다. 이 새는 몸길이가 약 140~180cm에 달하며, 날개를 펼치면 최대 360cm에 이르는 거대한 날개를 가지고 있다.     성체는 주로 흰색 깃털을 가지고 있으며, 일부는 분홍빛을 띠고 있다. 부리는 크게 발달해 있으며, 성별에 따라 크기와 밝기가 다르다. 수컷은 분홍빛 피부를, 암컷은 주황색 피부를 가지고 있다. 크고 무리를 이루어 생활하는 큰사다새는 주로 물고기를 먹이로 삼으며, 협력해서 사냥하기도 한다. 식량이 부족하면 다른 새의 새끼를 먹기도 한다.    전 세계에 걸쳐 분포하며, 아프리카의 경우 연중 같은 지역에 남아있지만, 유럽과 아시아의 일부 개체는 철새로 이동한다. 둥지는 주로 나뭇가지와 잔디 등 다양한 재료로 땅에 짓는다. 인간의 간섭, 서식지 손실 및 오염 등이 이들의 개체 수에 영향을 미치고 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001556', '160.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('239', '사다새', 'Pelecanus crispus', 'Bruch', '1832', 'Chordata', '척삭동물문', 'Aves', '조강', 'Pelecaniformes', '사다새목', 'Pelecanidae', '사다새과', 'Pelecanus', '사다새속', 'crispus', '사다새', '몸길이 140~178cm이다. 어미 새의 몸빛은 흰색이며 첫째날개깃은 검은색이다. 어린 새는 온몸이 갈색이다. 부리가 크고 아랫부리에 신축성이 있는 큰 주머니가 달려 있다. 이 부리주머니 속에 작은 물고기나 새우 따위를 빨아 삼킨다. 다리는 납빛 회색이다. 알은 흰색이다. 호반이나 습지의 갈밭이나 갯벌에 나뭇가지나 수초 또는 풀을 이용하여 접시 모양의 둥지를 튼다. 산란기는 3~7월로 알은 보통 2~3개를 낳는다. 유럽, 아시아, 페르시아 만, 카스피 해, 몽골, 중국에서 번식하고 중국 남부, 인도 북부, 이집트에서 월동한다. 우리나라에서는 1914년에 인천에서 한 번 채집된 적이 있을 뿐이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001557', '159.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('240', '푸른얼굴얼가니새', 'Sula dactylatra', 'Lesson', '1831', 'Chordata', '척삭동물문', 'Aves', '조강', 'Pelecaniformes', '사다새목', 'Sulidae', '얼가니새과', 'Sula', '얼가니새속', 'dactylatra', '푸른얼굴얼가니새', '사다새목 얼가니새과의 조류이다. 몸길이는 81cm가량의 땅딸막한 바닷새이다. 꽁지 및 날개 끝부분은 검은색이다. 부리는 단단하며 분홍빛 또는 오렌지색이다. 번식기 부리의 색깔은 푸른빛을 띤다. 알은 백악질의 엷은 푸른빛이다. 열대성 바다에서 서식한다. 인가와 가까운 곳에 둥지를 틀며, 2개의 알을 낳는다. 바하마, 대서양, 인도양, 태평양 지역의 섬 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001560', '81.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('241', '붉은발얼가니새', 'Sula sula', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Pelecaniformes', '사다새목', 'Sulidae', '얼가니새과', 'Sula', '얼가니새속', 'sula', '붉은발얼가니새', '붉은발얼가니새는 대형 바다새로, 주로 열대 지방의 해안과 외딴 섬에서 집단 번식하는 특성을 가지고 있습니다. 성체는 항상 붉은 발을 가지고 있으며, 깃털 색깔은 다양한 형태가 있습니다. 이 새는 하늘에서 민첩하게 날지만 이착륙은 어색하게 합니다. 평균 몸길이는 70cm이며 날개 길이는 최대 152cm에 달합니다. 부리와 목은 분홍색과 파란색입니다.    붉은발얼가니새의 주된 서식지는 대서양, 태평양 및 인도양의 열대 지역으로, 카리브 해와 갈라파고스 제도 등에 주로 서식합니다. 나무나 덤불에 둥지를 틀고, 번식기에는 파란색 알을 한 개씩 낳습니다. 먹이는 주로 물고기와 오징어로 이루어져 있으며, 물속으로 다이빙하여 잡습니다. 자연적 또는 인위적 위협은 거의 없으며, 국제 자연 보전 연맹에서는 멸종위기종으로 간주하지 않습니다. 그러나 개체수는 감소하고 있는 것으로 보입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001561', '70.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('242', '갈색얼가니새', 'Sula leucogaster', 'Boddaert', '1783', 'Chordata', '척삭동물문', 'Aves', '조강', 'Pelecaniformes', '사다새목', 'Sulidae', '얼가니새과', 'Sula', '얼가니새속', 'leucogaster', '갈색얼가니새', '몸 전체 길이 71~76cm, 날개 길이 137~145cm이다. 성체는 전체적으로 초콜릿 빛을 띤 갈색이며, 날개 아랫면과 배에 흰색 털이 있다. 얼굴, 부리, 다리 색깔로 성별이 구분된다. 암컷은 이 부분들이 노란색이고, 수컷은 회색을 띤 녹색이며 목은 푸른빛을 띤다. 새끼는 배와 날개 아랫부분의 색이 밝은 갈색인 것을 제외하고는 성체와 비슷하다. 수명은 최대 16년 정도로 알려져 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001562', '73.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('243', '민물가마우지', 'Phalacrocorax carbo', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Pelecaniformes', '사다새목', 'Phalacrocoracidae', '가마우지과', 'Phalacrocorax', '가마우지속', 'carbo', '민물가마우지', '사다새목 가마우지과에 속하는 조류로 매우 흔한 겨울철새고, 드문 텃새이다. 몸길이 약 82cm. 암수의 형태는 유사하며, 몸 전체가 광택이 있는 검은색이다. 부리의 기부는 노란색이며, 흰색 뺨이 만나는 부분은 동그스름하다. 여름깃은 다리 위쪽에 흰색의 반점이 생기며, 뒷머리와 뒷목에 털 모양의 흰색 깃털이 있다. 호수, 강, 바닷가에서 잠수하여 먹이를 찾는다. 이동할 때는 기러기처럼 일정한 대형으로 무리 지어 난다. 유라시아, 아프리카, 오스트레일리아, 북아메리카 등 넓은 지역에 분포한다. 국내에는 매우 흔한 겨울철새로 해안, 하구, 강, 호수 등에서 월동하며 내륙 일부 지역에서는 드물게 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001565', '82.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('244', '가마우지', 'Phalacrocorax capillatus', 'Temminck & Schlegel', '1850', 'Chordata', '척삭동물문', 'Aves', '조강', 'Pelecaniformes', '사다새목', 'Phalacrocoracidae', '가마우지과', 'Phalacrocorax', '가마우지속', 'capillatus', '가마우지', '사다새목 가마우지과에 속하는 조류로 드문 텃새이다. 몸길이 약 84cm. 암수의 형태는 유사하며, 몸의 윗면은 검은색을 띤 녹색이며 금속광택이 있다. 부리 기부의 노란색 부분은 민물가마우지보다 좁고, 눈 뒤쪽의 흰색 부분은 넓으며 그 경계가 각이 져 있다. 꼬리가 짧으며 날 때 날개의 위치는 몸의 중앙보다 약간 뒤쪽에 위치한다. 도서와 해안의 암초나 암벽에서 집단 번식한다. 잠수한 후 바위에 올라가 날개를 펼쳐 햇볕에 말리는 행동을 한다. 둥지는 암초나 바위 절벽의 층을 이룬 오목한 곳에 마른풀이나 해초를 주재료로 해서 접시 모양의 둥우리를 만든다. 산란기는 5월하순에서 7월이며, 한배의 산란 수는 4~5개이다. 러시아 극동, 사할린에서 일본 규수 북부, 한국, 중국 황해 지역에 국지적으로 번식한다. 우리나라에서는 드문 텃새로 울릉도를 비롯하여 서·남해안의 섬 바위 절벽에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001566', '84.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('245', '쇠가마우지', 'Phalacrocorax pelagicus', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Pelecaniformes', '사다새목', 'Phalacrocoracidae', '가마우지과', 'Phalacrocorax', '가마우지속', 'pelagicus', '쇠가마우지', '사다새목 가마우지과에 속하는 조류로 비교적 드문 텃새이다. 몸길이는 약 73cm. 암수의 형태는 유사하며, 몸 전체가 녹색 광택이 있는 검은색이다. 머리와 부리는 다른 가마우리류에 비해 가는 편이다. 여름깃은 정수리와 뒷머리에 돌출된 깃털이 있으며, 눈 주위에 붉은색 나출부가 넓고, 다리 위쪽에 흰색의 반점이 있다. 겨울깃은 머리의 뿔깃과 옆구리의 다리 위쪽 흰점이 없다. 눈 주위 붉은색 나출부도 매우 작아져 얼굴 전체가 검게 보인다. 서해 일부 도서 지역에서는 집단번식하고, 겨울철 해안가 바위나 암벽에서 드물게 볼 수 있다. 한배 산란수는 3~4개이며, 포란기간은 약 31일이다. 둥지는 암벽 위나 오목한 곳에 마른풀과 해초를 이용하여 만든다. 매년 같은 장소를 이용하므로 바위가 배설물로 하얗게 덮이기도 한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001567', '73.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('246', '붉은뺨가마우지', 'Phalacrocorax urile', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Pelecaniformes', '사다새목', 'Phalacrocoracidae', '가마우지과', 'Phalacrocorax', '가마우지속', 'urile', '붉은뺨가마우지', '몸길이는 71~76cm이다. 깃털은 전체적으로 광택이 있는 녹청색이다. 콧등과 눈 주위의 얼굴은 밝은 홍색이며, 부리는 엷은 색이다. 머리에 볏 2개가 있다. 바다 절벽에 둥지를 틀며, 알 3~4개를 낳는다. 알래스카와 북동아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001568', '73.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('247', '황조롱이', 'Falco tinnunculus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Falconidae', '매과', 'Falco', '매속', 'tinnunculus', '황조롱이', '매목 매과에 속하는 조류이다. 몸길이는 수컷이 33cm, 암컷이 38cm이고, 날개 편 길이는 70cm 정도이다. 수컷은 머리와 꼬리가 회색이며 꼬리 끝과 날개 끝은 검은색이다. 등은 갈색 바탕에 검은색의 반점이 있으며, 배는 황갈색 바탕에 흑갈색의 세로줄 무늬가 있다. 암컷은 꼬리를 포함한 몸의 위쪽이 갈색이며 날개 끝과 꼬리 끝만 검은색이다. 맹금류 중에서 비교적 흔한 텃새다. 먹이를 찾기 위해 공중을 빙빙 돌다가 정지비행을 하는 습성이 있다. 작은 새나 들쥐 등을 잡아먹는다. 고층 건물이나 절벽의 틈, 그리고 버려진 까치집 등 다양한 곳에 둥지를 튼다. 알은 흰색 바탕에 어두운 갈색의 무늬가 있으며 5개 정도 낳는다. 전국에 분포하며 세계적으로는 일본, 중국, 티베트, 버마 등에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001572', '35.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('248', '비둘기조롱이', 'Falco amurensis', 'Radde', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Falconidae', '매과', 'Falco', '매속', 'amurensis', '비둘기조롱이', '매우 드문 나그네새다. 몸길이는 29~33cm이다. 수컷의 몸 전체는 어두운 회색이며 아랫배와 아래꼬리덮깃은 붉은색이다. 부리 끝의 납막과 다리는 주황색이다. 날 때 흰색의 아래날개덮깃이 선명하게 보이며, 날개깃과 꼬리 끝은 검은색이다. 암컷은 몸 윗면이 회색이고 뺨은 흰색이며, 몸 아랫면은 흰색 바탕에 검은색 무늬가 빽빽하게 있다. 아랫배의 적갈색이 보다 흐리다. 강 하구, 농경지, 해안가 개활지의 초지에 서식하며, 곤충류, 소형 포유류, 조류, 개구리, 도마뱀 등을 주로 잡아먹는다. 우리나라의 중부 이북지역을 통과하는 것이 관찰된다. 세계적으로는 중국 동북부, 아무르, 우수리, 몽골, 시베리아 북부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001573', '31.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('249', '쇠황조롱이', 'Falco columbarius', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Falconidae', '매과', 'Falco', '매속', 'columbarius', '쇠황조롱이', '매목 매과에 속하는 조류이다. 몸길이는 28~33cm이다. 수컷은 등이 회색이며 꽁지 끝에는 넓은 검정색 띠가 있다. 배는 연한 적갈색의 줄무늬가 있으며 꼬리 끝에는 검은색의 띠가 있다. 암컷은 등이 갈색, 배는 옅은 갈색에 적갈색의 줄무늬가 있으며, 꼬리에는 갈색의 줄무늬가 있다. 스스로 둥지를 틀지 않고 까마귀류나 다른 종의 빈 둥지를 이용하거나 나무구멍, 절벽, 바위 등의 빈틈에 3~6개의 알을 낳는다. 알을 낳는 시기는 5~6월이다. 전국적으로 도래하지만 흔하지 않은 겨울철새이다. 북위 40°에서 북극에 이르는 유럽·아시아·북아메리카에 분포하고 한국·일본·중국·이란·이집트 등지에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001574', '30.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('250', '새호리기', 'Falco subbuteo', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Falconidae', '매과', 'Falco', '매속', 'subbuteo', '새호리기', '새호리기는 「멸종위기 야생생물 Ⅱ급」의 매과의 조류로 여름철새이다. 몸 윗면은 회갈색이며 아랫배와 아래꼬리덮깃은 붉은색이다. 하늘에서 날개를 오므려 급강하하며 작은 새와 같은 먹이를 사냥한다. 번식기에 인간에 의한 까치둥지 소실 등 인간의 간섭을 받고 있다. 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001575', '32.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('251', '헨다손매', 'Falco cherrug', 'J.E.Gray', '1834', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Falconidae', '매과', 'Falco', '매속', 'cherrug', '헨다손매', '소수가 우리나라에서 월동하는 겨울새이다. 몸길이 46cm이다. 흰색을 띠며 갈색 무늬가 있는 머리꼭대기와 윗머리를 제외한 등은 흑갈색이다. 배는 흰색을 띠며 암갈색의 줄무늬가 있다. 꼬리는 암갈색에 담색의 띠가 있다. 서식지는 평야의 개활지나 습지이다. 산란기는 4월경이며, 한배의 산란수는 3개가 보통이다. 알타이, 파미르, 티베트, 히말라야 등지에서 번식하고 우리나라에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001576', '46.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('252', '흰매', 'Falco rusticolus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Falconidae', '매과', 'Falco', '매속', 'rusticolus', '흰매', '흰매는 매과의 조류 중 가장 큰 종으로, 주로 북극 해안과 툰드라에서 번식합니다. 이 새의 깃털 색은 지역에 따라 다양하며, 백색에서 짙은 갈색까지 다양합니다. 암수 차이가 있으며, 암컷은 수컷보다 훨씬 큽니다. 흰매는 주로 뇌조 등의 새를 사냥하며, 때로는 물고기나 작은 포유류도 먹습니다. 일반적으로 절벽에 둥지를 틀며, 다른 새의 버려진 둥지를 사용합니다. 흰매는 툰드라와 고산지대 환경에 잘 적응했으며, 서식지가 파괴되어도 비교적 안정적인 상태를 유지하고 있습니다. 현재 멸종위기종은 아니지만, 환경오염과 같은 요인이 위협이 될 수 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001577', '56.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('253', '매', 'Falco peregrinus', 'Tunstall', '1771', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Falconidae', '매과', 'Falco', '매속', 'peregrinus', '매', '매목 매과에 속하는 조류이다. 몸길이는 34-50cm이고 양쪽 날개를 편 길이는 80~120cm이다. 몸 윗면은 어두운 청회색이며 몸 아랫면은 흰색에 검은 가로줄 무늬가 있다. 암수가 거의 비슷한 색이다. 등, 날개, 꼬리는 푸른색을 띤 회색이고, 가슴과 배는 옅은 노란 갈색에 검은색 가로줄 무늬가 세밀하게 있다. 눈 밑의 검은 무늬는 크고 뚜렷하며, 눈테는 노란색이다. 특히 어린 새의 등은 갈색이며 배에는 굵고 어두운 갈색 세로줄 무늬가 있다. 무인도를 중심으로 한 도서 지역이나 가파른 해안가 절벽에서 번식하며, 꿩, 오리류 등 주로 조류를 주식으로 하지만 육상의 설치류를 잡아먹기도 한다. 나무 꼭대기나 암벽 지대 위 등 지형이 높은 곳에서 휴식을 취하거나 먹이를 찾는데, 먹이를 발견하면 공중에서 빠른 속도로 잡는다. 알은 보통 3-4개를 낳는다. 번식기에는 수컷이 잡은 먹이를 둥지 근처의 공중에서 암컷에게 전달하는 모습을 볼 수 있다. 해안 도서 지역 및 철새도래지를 중심으로 하여 전국에 서식하고 세계적으로는 아이슬란드, 뉴질랜드, 중앙 열대림, 남아메리카를 제외한 전 세계에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001578', '42.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('254', '물수리', 'Pandion haliaetus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Pandion', '물수리속', 'haliaetus', '물수리', '매목 수리과에 속하는 조류이다. 몸길이는 54~64cm이다. 등 쪽은 흑갈색이고 가슴 쪽은 흰색이다. 검은색의 눈선과 목띠가 있다. 날개의 폭은 좁으며 W자 모양으로 날개를 편다. 꼬리는 짧다. 가슴 위쪽으로 갈색 띠가 있다. 제주도와 남해안 일대에 월동하는 겨울철새 또는 나그네새이다. 주로 해안, 호수, 강 하구에서 발견되고 수면 위를 날다가 정지비행 후 재빨리 낙하하여 물고기를 잡아먹는다. 남미를 제외한 전 세계에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001581', '59.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('255', '벌매', 'Pernis ptilorhynchus', 'Temminck', '1821', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Pernis', '벌매속', 'ptilorhynchus', '벌매', '벌매는 「멸종위기 야생생물 Ⅱ급」의 숲 가장자리나 초지에서 볼 수 있는 수리과 조류이다. 봄과 가을 이동 시기에 많은 수가 우리나라를 통과하는 나그네새로, 적은 수의 번식기록이 있다. 땅벌의 유충, 번데기, 성충 등을 잡아먹는다. 주로 까마귀류, 맹금류 등 다른 새의 둥지를 이용하며 산림 훼손에 의해 서식지 감소가 우려되며, 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001583', '58.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('256', '솔개', 'Milvus migrans', 'Boddaert', '1783', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Milvus', '솔개속', 'migrans', '솔개', '매목 수리과에 속하는 조류이다. 몸길이는 58.5~68.5cm이다. 암컷이 조금 더 크다. 몸은 전체적으로 적갈색을 띠며, 밝은 갈색의 세로줄 무늬가 있다. 날 때 날개 아랫면의 중앙에 흰색 점이 있고 꼬리 중앙부가 제비꼬리처럼 안쪽으로 약간 패여 있으나, 꼬리를 펼칠 경우 꼬리 끝이 직선처럼 보이기도 한다. 겨울철새지만 가끔 텃새화하기도 한다. 산림지역, 도서지역, 해안가, 시가지 등의 숲속 나무 위에 나뭇가지로 접시 모양의 둥지를 만든다. 알은 2~3개를 낳으며, 곤충을 비롯한 무척추동물, 죽은 동물, 소형 포유류, 어류 등을 주로 먹는다. 남해와 서해 도서 및 철원에서 발견되며 일본, 중국, 티베트, 히말라야에도 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001585', '63.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('257', '흰꼬리수리', 'Haliaeetus albicilla', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Haliaeetus', '흰꼬리수리속', 'albicilla', '흰꼬리수리', '매목 수리과에 속하는 조류이다. 겨울철새이다. 전국의 해안, 호수, 강 하구 등에서 관찰된다. 연어와 송어를 선호하는 것으로 알려져 있다. 몸길이는 약 84~94cm이며, 날개 편 길이는 199~228cm이다. 전체적으로 갈색을 띤다. 날개깃은 검은색이다. 머리는 황갈색이고 몸의 다른 부위에 비해 색이 엷다. 꼬리는 둥근 마름모꼴로 흰색을 띤다. 부리와 다리는 노란색이지만 어린 새는 흑갈색으로 차이가 있다. 날개 앞쪽과 날개 뒤쪽이 거의 평행선을 이루어 밑에서 보면 직사각형이다. 어린 새는 머리는 암갈색으로 가장 어둡고, 몸은 전체적으로 적갈색이 돌며, 꼬리깃에는 흰색과 검은색이 섞여 있다. 해안, 소택지, 호수, 하구 등 넓은 수면을 낀 지역에 살며 겨울철새이다. 단독으로 생활하며, 때로는 3~4개체 혹은 큰 무리를 형성하기도 한다. 주로 소형 포유류, 조류, 어류 등을 먹는다. 번식기는 3월 중순에서 5월경까지다. 둥지는 산림교목의 가지 위에 나뭇가지를 올려 쌓아 만들거나, 해안가 절벽 등지에 만든다. 산란기에는 둥지에 동물의 깃털 등을 깔기도 한다. 전국에 서식하고 세계적으로는 일본, 중국, 러시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001587', '89.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('258', '참수리', 'Haliaeetus pelagicus', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Haliaeetus', '흰꼬리수리속', 'pelagicus', '참수리', '매목 수리과에 속하는 조류이다. 몸길이는 88~102cm이며, 부리가 노랗다. 몸은 전체적으로 흑갈색이며, 꼬리는 쐐기형으로 길며 흰색이다. 이마와 어깨, 허리와 윗꼬리덮깃은 흰색이다. 이 외의 부분은 어두운 갈색이다. 날 때 날개의 뒤쪽이 둥글게 보이며, 날개 끝 쪽으로 갈수록 넓게 보인다. 어린 새의 몸은 전체적으로 검게 보이며, 꼬리는 흰색이지만 가장자리는 갈색이고 첫째날개깃과 날개덮깃은 부분적으로 흰색을 띤다. 매우 드문 겨울철새이다. 해안가의 절벽이나 언덕, 나무 위에 둥지를 틀며, 먹이는 주로 물고기를 먹으며, 작은 포유류나 오리, 갈매기 등의 조류도 먹는다. 번식기는 4-5월이며, 알은 1~3개를 낳는다. 전국에 서식하고 세계적으로는 동북아시아 전역에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001588', '95.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('259', '수염수리', 'Gypaetus barbatus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Gypaetus', '수염수리속', 'barbatus', '수염수리', '매목 수리과에 속하는 조류이다. 몸길이는 약 110cm이다. 머리는 회색이고 몸 아랫면은 누런 갈색, 몸 윗면과 꽁지는 검은색이다. 눈을 지나는 검은 선은 수염처럼 보인다. 날개는 가늘고 길며 꽁지는 길고 쐐기꼴이다. 고산 지대의 바위 벽의 선반처럼 생긴 곳에 둥우리를 틀고 한배에 1~2개의 알을 낳는데 보통 1개만이 부화한다. 주로 동물의 죽은 고기를 먹는데, 먹이를 높은 곳에서 떨어뜨린 뒤 바스러진 뼈에서 골수를 빼먹는다. 때로는 거북도 같은 방법으로 먹는다. 함경남도, 강원도에서 발견된 기록이 있고 세계적으로는 알타이, 중국, 몽골, 에스파냐, 모로코, 북아프리카, 티베트에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001590', '110.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('260', '고산대머리수리', 'Gyps himalayensis', 'Hume', '1869', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Gyps', '고산대머리수리속', 'himalayensis', '고산대머리수리', '고산대머리수리(히말라야독수리)는 히말라야와 티베트 고원에 서식하는 매우 큰 독수리입니다. 몸길이는 약 1030-1150mm이며, 날개 길이는 2560-3100mm에 달합니다. 몸무게는 보통 8-12kg로, 고산 지역에서 가장 큰 조류 중 하나입니다. 이 독수리는 어두운 갈색의 큰 깃털을 가지고 있으며, 날아다닐 때 밝은 아랫면이 특징적입니다. 주로 썩은 고기를 먹으며, 주로 야크의 사체를 식사로 삼습니다. 그러나 간이나 내장보다는 주로 살코기를 선호합니다. 번식기는 1월경 시작되며, 절벽의 외딴 곳에 둥지를 틀고 한 번에 하나의 알을 낳습니다. 다른 독수리 종류와 달리, 고산대머리수리는 사람의 간섭이나 차량 소음에도 비교적 민감하지 않은 편입니다. 현재 세계자연보전연맹(IUCN)의 적색목록에서 근접위협종으로 분류되어 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001592', '109.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('261', '독수리', 'Aegypius monachus', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Aegypius', '독수리속', 'monachus', '독수리', '매목 수리과에 속하는 조류이다. 몸길이 100~110cm, 날개 편 길이 250~300cm로서 몸 색깔은 전체적으로 검은색을 띠며 나이가 들수록 색이 옅어져 갈색으로 된다. 머리는 피부가 드러나 회갈색으로 보인다. 겨울철새이다. 무리를 이루어 행동하는 경우가 많고 죽은 동물의 사체를 먹고 산다. 날갯짓을 거의 하지 않고 상승기류를 이용해 비행하며 먹이를 찾는다. 우리나라에 찾아오는 수리류 중에 가장 크다. 철원, 연천, 파주 등에서 흔하게 볼 수 있으며, 최근에는 전국에서 관찰된다. 세계적으로는 유럽 남부, 중앙아시아, 티베트, 몽골, 중국 북동부에 서식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001594', '105.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('262', '관수리', 'Spilornis cheela', 'Latham', '1790', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Spilornis', '관수리속', 'cheela', '관수리', '수리과의 전형적인 형태를 지니고 있다. 큰 날개 때문에 하늘을 날기에 불편하고, 공기 중을 활강할 수만 있다. 정수리 부분은 하얀 털로 덮여 있고, 부리는 크고 노란색이다. 고도가 낮은 곳이나 중간 정도에 서식한다. 2~5마리로 이루어진 집단으로 종종 발견된다. 농경지에서 숲 가장자리까지의 광대한 영역에서 먹이를 찾는다. 때로는 작은 새들을 쫓기도 한다. 주로 뱀이나 도마뱀 등 파충류를 포식한다. 주변을 소용돌이치게 하면서 자신의 소유를 증명한다. 타이완을 비롯하여 아열대 및 열대 아시아 지역에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001596', '58.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('263', '개구리매', 'Circus spilonotus', 'Kaup', '1847', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Circus', '개구리매속', 'spilonotus', '개구리매', '주로 개활지에 서식한다. 몸길이는 47~55cm, 체중은 370~780g이다. 수컷은 몸 윗면이 짙은 회갈색이며, 암컷은 줄무늬가 있는 머리 부분을 제외하고는 몸 전체가 밝은 갈색이다. 저습지, 호숫가, 넓은 갈대밭 같은 습지 또는 논, 초지 등 개활지에 살면서 물고기, 뱀, 개구리, 물새, 설치류 따위를 잡아먹으며 갈대밭과 같은 습지의 지상에 둥지를 튼다. 9월부터 이듬해 5월까지 많은 곳에 도래한다. 매년 규칙적으로 도래하진 않지만 천수만, 낙동강 하구 및 해남 간척지에서 비교적 규칙적으로 관찰된다. 시베리아 동남부, 몽골, 우수리, 사할린 지역에서 중국 동북부 및 일본 북부까지 분포한다. 한국, 일본 남부 지역, 중국 남부 및 동남아 등지에서 월동한다. 전 세계적으로 멸종 위협에 처해 있지는 않으나 현존 개체수 및 개체군 동태에 관한 자료가 매우 부족한 실정이다. 비슷한 서식지에 도래하는 잿빛개구리매에 비해 도래 집단의 규모는 작은 편으로 어느 지역에서나 잿빛개구리매에 비해 관찰 개체수는 적은 편이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001598', '51.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('264', '잿빛개구리매', 'Circus cyaneus', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Circus', '개구리매속', 'cyaneus', '잿빛개구리매', '잿빛개구리매는 「멸종위기 야생생물 Ⅱ급」의 수리과 조류로 겨울철새이다. 수컷은 밝은 회색을 띠며, 암컷은 전체적으로 갈색을 띤다. 습지, 농경지 등에서 단독으로 생활하며, 주로 설치류와 조류를 먹는다. 전국에서 관찰되나 적은 개체 수가 도래한다. 세계적으로 진행되는 조류보호운동으로 점차 증가하고 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001599', '46.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('265', '알락개구리매', 'Circus melanoleucos', 'Pennant', '1769', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Circus', '개구리매속', 'melanoleucos', '알락개구리매', '알락개구리매는 「멸종위기 야생생물 Ⅱ급」의 수리과 조류이며 우리나라를 드물게 통과하는 나그네새다. 강 하류, 갈대밭, 습지 등에서 생활하며 천천히 날아다니며 먹이를 사냥한다. 우리나라에서는 일부 지역에서 간헐적으로 관찰되며, 각종 개발로 인해 서식지 감소가 위협요인이 되고 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001600', '45.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('266', '붉은배새매', 'Accipiter soloensis', 'Horsfield', '1821', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Accipiter', '새매속', 'soloensis', '붉은배새매', '붉은배새매는 「멸종위기 야생생물 Ⅱ급」의 수리과 조류이며 여름철새이다. 몸 윗면은 푸른색을 띤 회색이고, 가슴은 흐린 주황색이며 아랫배는 흰색이다. 평지, 농가, 인가 주변의 숲에서 서식한다. 먹이가 되는 개구리 등이 줄어들어 개체에 영향을 주고 있으며, 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001602', '33.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('267', '조롱이', 'Accipiter gularis', 'Temminck and Schlegel', '1844', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Accipiter', '새매속', 'gularis', '조롱이', '조롱이는 「멸종위기 야생생물 Ⅱ급」의 수리과 조류이다. 소형 조류나 곤충 등을 주식으로 하며 울창한 숲속에 둥지를 틀고, 5월경 2개의 알을 낳는다. 산지 훼손에 의한 서식지 파괴가 가장 큰 위협요인으로 작용하며, 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001603', '26.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('268', '새매', 'Accipiter nisus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Accipiter', '새매속', 'nisus', '새매', '매목 수리과에 속하는 조류이다. 수컷의 몸 윗면은 전체적으로 어두운 회색을 띠고 눈 위에는 흰색의 약한 눈썹선이 지나간다. 몸 아랫면은 흰색 바탕에 적갈색의 가는 가로줄 무늬가 있다. 암컷은 전반적으로 갈색을 띠고, 눈 주위는 검은색이며, 턱 밑과 턱 아래 부위는 흰색 바탕에 어두운 갈색 반점이 있다. 그 외의 아랫면은 흰색이며 황갈색과 어두운 갈색 가로띠가 있다. 겨울철새로 번식기에는 약간 높은 산지나 평지의 숲속에 서식하나, 번식기 이외에는 산림, 평지, 도시 인근 농경지 등에서도 관찰된다. 소형 조류나 작은 곤충, 다람쥐 등을 먹는다. 둥지는 보통 잡목림의 4~8m 이상 높은 가지에 가느다란 나뭇가지를 접시 모양으로 쌓아 만든다. 5월경 알 4~5개를 낳는다. 전국에서 관찰되고 세계적으로는 유럽, 아프리카, 러시아, 중국, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001604', '35.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('269', '참매', 'Accipiter gentilis', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Accipiter', '새매속', 'gentilis', '참매', '매목 수리과에 속하는 조류이다. 몸길이는 50~56cm이다. 암수 형태가 유사하며, 흰색 굵은 눈썹선과 두꺼운 검은색 눈선, 노란색 홍채가 특징이다. 머리부터 등, 날개 윗면, 꼬리는 진한 청회색이고 가슴과 배에는 흰색에 흑갈색 가는 가로줄 무늬가 있다. 날개는 짧으며 폭이 넓고, 날 때 보이는 긴 꼬리가 특징이다. 산야, 평지, 하천변 등에서 볼 수 있으며, 번식기에는 비교적 깊은 산림에 서식한다. 암수 함께 생활하거나 단독으로 생활하며 먹이는 주로 꿩, 비둘기, 오리 등과 같이 날아다니는 조류를 추적해 잡아먹으며 토끼, 청설모, 다람쥐 같은 작은 포유류도 먹는다. 큰 나무의 가지에 나뭇가지를 쌓아 접시 모양의 둥지를 지상으로부터 4~8m 높이에 만든다. 5월~6월경 옅은 푸른색의 알을 2~4개 낳는다. 우리나라에서 겨울에 흔하게 관찰되고 번식도 하며, 이동시기에는 서해 도서 지역에서 많이 관찰된다. 세계적으로는 일본, 유럽, 북아메리카, 시베리아, 중국에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001605', '53.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('270', '왕새매', 'Butastur indicus', 'Gmelin', '1788', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Butastur', '왕새매속', 'indicus', '왕새매', '봄과 가을에 한국을 지나가는 나그네새이다. 드문 경우, 번식도 한다. 이마에서 위꼬리덮깃까지는 갈색이다. 뒷머리와 뒷목의 깃털 뒷부분은 흰색이다. 윗가슴은 녹슨 갈색으로 각 깃의 끝은 어두운 흰색이다. 아랫가슴, 배, 옆구리에는 붉은 녹색을 띤 갈색과 흰색으로 된 넓은 가로띠가 있다. 부리의 기부는 황색이고 끝은 검은색이며, 다리는 어두운 황색이다. 숲, 농경지, 개활지에서 관찰된다. 둥지는 큰 나무의 가지 위에 마른 나뭇가지를 이용해 접시 모양으로 만든다. 알 낳는 시기는 5~6월이다. 푸른빛이 도는 흰색의 알 2~4개를 낳는다. 아시아 동부 온대, 아무르 남부, 우수리, 일본, 만주 및 중국에 분포한다. 동남아시아에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001607', '43.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('271', '큰말똥가리', 'Buteo hemilasius', 'Temminck & Schlegel', '1844', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Buteo', '말똥가리속', 'hemilasius', '큰말똥가리', '큰말똥가리는 「멸종위기 야생동물 Ⅱ급」의 수리과 조류로 우리나라의 드문 겨울철새이다. 유연한 날갯짓으로 날면서 쥐, 곤충, 작은 새 등을 먹는다. 농경지, 평지, 간척지, 개활지 등에 서식한다. 세계적으로 분포하는 개체 수에 대한 구체적인 자료는 없으나 주식인 설치류의 수와 서식 개체의 연관성이 있을 것으로 보고 있으며, 티베트, 러시아, 몽골 등지에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001610', '64.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('272', '털발말똥가리', 'Buteo lagopus', 'Pontoppidan', '1763', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Buteo', '말똥가리속', 'lagopus', '털발말똥가리', '한국 전역에서 월동하는 드문 겨울철새이다. 몸길이는 50~60cm다. 깃털의 색과 무늬는 지역 개체군에 따라 매우 다양하다. 한국에 도래하는 아종은 머리와 가슴 부분이 흰색이며, 날개는 암갈색, 꼬리는 흰색이며 꼬리 끝에 암갈색의 굵은 줄이 있다. 앞이 트인 평원의 암석·말뚝 따위에 앉아 쉬면서 부근에 있는 쥐 따위 먹이를 찾는다. 때로는 나무 위에 앉아 먹이를 찾을 때도 있다. 바닷가 암벽 위나 툰드라의 구릉 지대, 나무 위에 둥지를 짓고 5~6월상순에 걸쳐 3~4개의 알을 낳는다. 유라시아 대륙과 북미의 북극권에서 번식하며, 한국·중국·일본 등지에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001611', '55.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('273', '초원수리', 'Aquila nipalensis', 'Hodgson', '1833', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Aquila', '검독수리속', 'nipalensis', '초원수리', '매목 수리과의 새로 몸길이는 62~74cm 정도이고, 날개길이는 165~190cm이다. 몸 윗부분은 갈색이고, 날개와 꽁지는 검은색이다. 황갈색독수리(tawny eagle)보다 크고 어두우며, 동종에는 없는 가냘픈 목이 있다. 새끼는 성체와 약간 차이가 나긴 하지만, 둘 다 몸 색깔이 다양하다. 사막, 반사막, 스텝 지대, 대초원 등 건조한 환경을 좋아한다. 나무 위에 나뭇가지로 지은 둥지에 1~3개의 알을 낳는다. 모든 종류의 갓 죽은 고기를 먹지만, 설치류와 토끼 크기까지의 작은 포유류와 메추라기 크기까지의 새들을 잡아먹기도 한다. 또한 다른 동물로부터 먹을 것을 훔치기도 한다. 울음소리는 까마귀와 비슷하지만, 자신을 과시할 때를 제외하고는 조용하다. 루마니아 동부, 러시아 남부, 중앙아시아, 몽골 등지에서 번식한다. 아프리카(유럽, 중앙아시아 번식종), 인도(동부 번식종) 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001614', '68.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('274', '흰죽지수리', 'Aquila heliaca', 'Savigny', '1809', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Aquila', '검독수리속', 'heliaca', '흰죽지수리', '흰죽지수리는 「멸종위기 야생생물 Ⅱ급」의 수리과 조류로 드문 겨울철새이다. 전국에 국지적으로 하구, 간척지, 저수지 등에 도래한다. 유럽과 러시아 일대에 대부분 분포하며, 각종 개발로 인해 종 유지에 위협이 되고 있다. 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001615', '79.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('275', '검독수리', 'Aquila chrysaetos', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Aquila', '검독수리속', 'chrysaetos', '검독수리', '매목 수리과에 속하는 조류이다. 몸길이는 약 75~90cm이며 암컷이 더 크다. 양쪽 날개를 편 길이는 190~227cm이다. 전체적으로 암갈색이며 뒷머리에서 목에 걸쳐 황갈색이며, 몸 아랫면은 암색이다. 날개가 길고, 발가락을 제외하고 발목까지는 모두 깃털로 덮여있다. 활동할 때 날개를 위로 약간 들어 올려 밋밋한 V자 모양을 이룬다. 어린 새는 첫째 날개깃의 기부가 흰색이고, 흰색의 꼬리 끝에는 폭넓은 검은색 띠가 있다. 산악지대의 암벽에서 번식하고 겨울에는 하천, 평야, 해안이나 평지에서 생활한다. 겨울철새이다. 토끼, 다람쥐, 꿩, 오리류 등을 먹는다. 3-4월하순에 번식하며 둥지는 암벽의 오목한 곳이나교목의 나뭇가지 위에 마른 나뭇가지를 쌓고 벼과 식물의 잎이나 줄기 등을 깔아 알자리를 만든다. 푸른색을 띤 흰색 알을 2개 낳는다. 전국에 도래하고 세계적으로는 유라시아와 북아메리카 북부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001616', '82.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('276', '흰점어깨수리', 'Hieraaetus pennatus', 'Gmelin', '1788', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Hieraaetus', '흰점어깨수리속', 'pennatus', '흰점어깨수리', '흰점어깨수리는 중형의 맹금류로, 주로 팔레아르크틱 및 남아시아에 분포하며, 겨울에는 아프리카와 아시아의 열대 지역에서 겨울을 보냅니다. 이 새는 주로 홀로 생활하거나 쌍으로 보이며, 날카로운 소리를 냅니다. 몸길이는 약 40cm에 날개 길이는 110-132cm에 이릅니다. 깃털은 두 가지 형태로, 밝은 회색과 중간 갈색 모양을 가집니다.    흰점어깨수리는 다양한 서식지에서 번식하며, 특히 남아프리카와 나미비아에 작은 번식 집단이 있습니다. 번식 시기는 8월에 시작되며, 나무나 절벽에 둥지를 틉니다. 주로 작은 포유류, 파충류, 조류를 먹이로 하며, 남아프리카에서는 특히 조류가 주된 먹잇감입니다.    이 새는 보전 상태가 양호하지만, 서식지 환경과 관련하여 다양한 연구가 필요합니다. 특히, 남아프리카 지역에서는 변화하는 기후와 환경 요인 때문에 새로운 번식지가 형성된 것으로 보입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001620', '40.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('277', '큰소쩍새', 'Otus bakkamoena', 'Pennant', '1769', 'Chordata', '척삭동물문', 'Aves', '조강', 'Strigiformes', '올빼미목', 'Strigidae', '올빼미과', 'Otus', '소쩍새속', 'bakkamoena', '큰소쩍새', '올빼미목 올빼미과에 속하는 조류이다. 몸길이 24cm이다. 온몸이 회갈색으로 검은 세로줄무늬가 불규칙하게 있다. 황갈색인 몸 아랫면에는 검은 세로줄무늬가 뚜렷하다. 귀깃은 크다. 발가락은 깃털로 덮여 있으며, 뒷목에 회색 띠가 보이기도 한다. 홍채는 붉은색이며, 드물지만 눈동자가 노란 개체도 있다. 민가 부근, 산림 또는 사찰 주변의 숲, 침엽수림 등지에 살며 흔하지 않은 텃새이다. 둥지는 노거수의 나무 구멍을 이용하지만 때로는 건물 처마 밑을 이용한다. 번식기는 5-6월이고 3-5개의 알을 낳는다. 야행성이고 작은 포유류, 조류, 파충류, 양서류, 곤충 등을 먹는다. 전국에 서식하는 것으로 추정되며 세계적으로는 동아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001854', '24.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('278', '소쩍새', 'Otus sunia', 'Hodgson', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Strigiformes', '올빼미목', 'Strigidae', '올빼미과', 'Otus', '소쩍새속', 'sunia', '소쩍새', '올빼미목 올빼미과에 속하는 조류이다. 몸길이는 20cm 정도이며 몸 전체가 회갈색이며 갈색이 흩어져 있다. 온몸이 회색빛이 도는 갈색인 회색형과 붉은빛이 도는 갈색인 적색형이 있다. 긴 귀깃이 있으며 발가락에는 깃털이 없다. 민가 주변의 야산, 공원, 산림, 사찰 등지에 사는 흔하지 않은 여름철새이다. 번식기인 5-6월에는 해질 무렵부터 새벽에 이르기까지 밤새도록 울며 흐린 날씨에는 낮에도 운다. 나무 구멍에 3-4개의 흰색 알을 낳는다. 주로 곤충을 잡아먹지만 작은 조류나 쥐도 잡아먹는다. 우리나라 전역에 서식하고 세계적으로는 에스파니아, 일본, 아프리카, 동남아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001855', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('279', '꼬리치레', 'Rhopophilus pekinensis', 'Swinhoe', '1868', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Cisticolidae', '개개비사촌과', 'Rhopophilus', '꼬리치레속', 'pekinensis', '꼬리치레', '날개길이 6~6.5cm, 꼬리길이 8~10.5cm이다. 몸의 윗면은 잿빛이 도는 갈색이고, 꼬리위덮깃 이외는 모두 짙은 갈색 세로무늬가 있다. 뺨에는 검은 띠가 있다. 가운데꼬리깃은 등과 같은 잿빛이 도는 갈색이고 바깥쪽 깃은 검은 갈색, 꼬리 끝은 잿빛이 도는 흰색이다. 아랫면은 흰색이다. 옆구리에는 붉은 갈색의 아름다운 세로무늬가 있다. 키가 큰 풀이 있는 초지,관목림, 풀에 덮인 개활지, 산허리나 구릉의 숲속에 서식한다. 둥지는 숲속의 땅 위에 만든다. 한배의 산란수는 5개이다. 번식 후 겨울에는 작은 무리를 이룬다. 곤충과 씨앗이나 열매를 먹는다. 한국 북부지방과 중국 북부, 북동부에서 번식하고 한국 중부 이남에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002013', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('280', '검은이마직박구리', 'Pycnonotus sinensis', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Pycnonotidae', '직박구리과', 'Pycnonotus', '검은이마직박구리속', 'sinensis', '검은이마직박구리', '평지의 산림, 과수원, 농경지 주변에 서식하며, 나무 위에서 열매, 곤충, 거미류를 잡아먹는다. 암수가 같은 색을 띤다. 몸 윗면은 어두운 녹회색이고, 날개와 꼬리의 일부는 녹색을 띠는 황색이다. 앞이마에서 정수리까지는 검은색, 정수리에서 뒷머리까지는 흰색이다. 뒷목에 검은색과 흑갈색 무늬가 있다. 귀깃은 흑갈색이며 작은 흰색 반점이 있다. 앞가슴은 회갈색, 배는 탁한 흰색이다. 비상시 파도 모양으로 난다. 비교적 경계심이 적은 편이고, 나뭇가지에 앉아 “깩- 깩”하는 울음소리를 낸다. 세계적으로 대만, 중국 남부, 하이난섬, 베트남 북부에 분포한다. 우리나라에서는 2002년 10월 전라북도 어청도에서 처음 확인된 이후 전라남도 하태도 및 홍도 등지에서도 관찰되는 등 관찰기록이 증가하고 있는 추세다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002016', '19.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('281', '직박구리', 'Microscelis amaurotis', 'Temminck', '1830', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Pycnonotidae', '직박구리과', 'Microscelis', '직박구리속', 'amaurotis', '직박구리', '참새목 직박구리과에 속하는 조류이다. 몸길이는 약 28cm이다. 머리와 등은 푸른색을 띤 회색이며, 날개는 회갈색이다. 눈 뒤로 밤색의 반점이 있다. 배에서 꼬리 쪽으로 갈수록 흰색 반점이 많아진다. 알은 엷은 장밋빛 바탕에 붉은 갈색의 얼룩무늬가 있다. 평지에서 산지에 이르기까지 수목이 있는 곳에 살며 흔히 볼 수 있는 텃새이다. 여름철에는 암수가 함께 생활하며, 대부분 나무 위에서 생활하여 지상에 내려오는 일은 거의 없다. 나뭇가지에 밥그릇 모양으로 둥지를 만든다. 5-6월에 4~5개의 알을 낳는다. 먹이는 식물의 열매, 곤충류이다. 전국에 서식하고 세계적으로는 일본, 타이완, 필리핀 등에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002018', '28.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('282', '큰개개비', 'Megalurus pryeri', 'Seebohm', '1884', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Megalurus', '큰개개비속', 'pryeri', '큰개개비', '참새목 휘파람새과의 새로 몸길이는 12.5cm가량이다. 수컷 겨울깃의 이마에서 위꽁지덮깃까지의 등 쪽은 황갈색이다. 머리꼭대기, 등, 어깨는 다소 넓고, 뒷목과 허리는 폭이 좁은 검은색 축반이 있다. 눈썹선은 엷은 색이나 선명하지 못하며, 눈 앞과 귀깃은 엷은 갈색이다. 턱 밑, 멱, 가슴의 중앙, 배의 중앙은 흰색이고, 가슴 옆과 배 옆은 황갈색이다. 부리는 가늘고 부리 등과 밑선은 모두 둥근 기가 있다. 부리는 갈색으로 부리 등은 어두운 갈색이다. 홍채는 짙은 갈색이고, 다리는 엷은 갈색이다. 벼과 식물이 밀생하는 초습지에 영소한다. 갈대가 밀생하는 숲에 잠입하여 잎과 줄기 사이를 여기저기 걸어 다니면서 먹이를 찾는다. 둥지는 땅 위에서 27~35cm 높이의 풀잎이나 줄기 사이에 위치한다. 알은 7월하순~8월상순, 한배에 5~6개 낳는다. 나비목과 메뚜기목의 곤충류를 즐겨 먹는다. 북부의 번식 집단은 겨울에 남하 이동한다. 구북구 동부, 일본, 만주 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002021', '12.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('283', '숲새', 'Urosphena squameiceps', 'Swinhoe', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Urosphena', '숲새속', 'squameiceps', '숲새', '참새목 휘파람새과의 여름새이다. 몸길이는 9cm가량이다. 수컷 겨울깃의 머리꼭대기 깃털은 어두운 갈색으로 각 깃털의 가장자리는 어두운색이다. 눈썹선은 현저한 갈색을 띤 크림색이다. 뺨과 귀깃은 크림 황갈색으로 각 깃은 어두운색의 가장자리가 있다. 턱 밑과 멱은 엷은 황갈색이다. 가슴은 다소 갈색을 띤다. 배의 중앙은 광택이 있는 흰색이며, 옆구리는 올리브 갈색으로 배 옆은 황갈색이 풍부하다. 꽁지깃은 엷고 어두운 갈색이다. 윗부리는 갈색 또는 뿔색이며 아랫부리의 기부는 어두운 살구색 또는 엷은 황갈색이다. 홍채는 어두운 갈색이고, 다리는 살구색을 띤 흰색이다.관목림이나 낙엽활엽수림에서 서식한다. 우리나라 전역에서 여름에 흔히 번식한다. 단독 또는 암수 함께 생활한다. 땅 위에 영소한다. 5~7월에 한배에 5~7개 산란한다. 주로 딱정벌레목, 매미목, 메뚜기목, 파리목, 벌목 등의 곤충류를 먹으며, 기타 다족류도 먹는다. 구북구 동부, 우수리, 만주 동부, 한국, 일본 등지에서 번식한다. 중국 남부, 타이완, 하이난섬, 인도네시아, 미얀마 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002023', '9.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('284', '쥐발귀개개비', 'Locustella lanceolata', 'Temminck', '1840', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Locustella', '붉은허리개개비속', 'lanceolata', '쥐발귀개개비', '참새목 휘파람새과의 새로 몸길이는 11cm가량이다. 수컷 겨울깃의 등은 올리브색을 약간 띤 갈색으로 각 깃은 넓고 검은 갈색의 세로 얼룩무늬가 있다. 눈썹선은 좁고 선명하지 않은 황갈색을 띤 흰색이다. 눈 앞은 황갈색을 띤 흰색과 암갈색이 섞여 있다. 귀깃은 올리브 갈색과 황갈색이 섞인다. 턱 밑, 멱, 가슴은 황색을 띤 흰색으로 삼각형인 검은 갈색의 작은 얼룩점이 산재한다. 배는 황색을 띤 흰색이다. 습지나 소택지의 풀 또는 버드나무와관목, 물가의 갈대밭 등지에서 생활한다. 우리나라 전역에 걸쳐 봄과 가을에 작은 무리들이 통과하는 나그네새이다. 숨어서 모습을 잘 나타내지 않는다. 둥지는 풀 밑에 때로는 땅 위에서 30cm 높이의 숲속에 지으며 주위는 흔히 풀로 가려져 있다. 6월 중순~7월하순, 한배에 3~5개 산란한다. 주로 곤충류를 먹는다. 한국, 구북구 시베리아 동부, 캄차카, 바이칼, 알타이, 우랄 서부, 만주, 아무르, 사할린, 홋카이도, 혼슈 등지에서 번식한다. 인도, 미얀마, 수마트라, 보르네오, 자바 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002029', '11.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('285', '북방개개비', 'Locustella certhiola', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Locustella', '붉은허리개개비속', 'certhiola', '북방개개비', '한반도 전역에 걸쳐 작은 무리들이 통과하는 흔하지 않은 나그네새이다. 몸길이 약 13cm이다. 몸의 윗면은 누런 갈색이며 검은색 세로무늬가 많다. 아랫면은 잿빛을 띤 흰색이다. 하상이나 또는 강가의 초지에 서식하나 특히 키가 큰 풀숲이나관목림에 산다. 초습지의 풀 사이 땅 위에 둥지를 만든다. 산란기는 6월이고, 한배의 산란수는 4~6개이다. 작은 곤충과 유충을 먹는다. 시베리아와 동아시아 북부에서 번식하고 중국, 인도차이나, 미얀마, 말레이 반도 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002030', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('286', '알락꼬리쥐발귀', 'Locustella ochotensis', 'Middendorff', '1853', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Locustella', '붉은허리개개비속', 'ochotensis', '알락꼬리쥐발귀', '참새목 휘파람새과의 조류이다. 등 쪽은 모두 녹슨 색을 띤 올리브 갈색으로 머리꼭대기는 조금 어두운 색이다. 눈썹선은 잿빛 흰색 또는 황갈색으로 짧고 선명하지 않으며, 눈 앞은 잿빛 흰색이다. 뺨과 귀깃은 올리브 황갈색으로 각 깃털 끝은 어두운 갈색이며 아랫면은 황갈색을 띤 흰색으로 가슴은 갈색을 띤다. 부리는 측압되며 머리 길이의 1/2 정도이다. 윗부리는 뿔빛 갈색이고, 아랫부리는 올리브 잿빛과 누런빛을 띤 뿔색 또는 자색을 띤 홍색이다. 홍채는 적갈색 또는 어두운 황갈색이다. 다리는 엷은 황갈색 또는 살구색이다. 봄과 가을에 한반도를 드물게 통과하는 나그네새이다. 풀 사이를 여기저기 잠입하여 먹이를 구하며 때때로 풀줄기에 앉아 지저귄다. 6월하순~7월상순에 한배에 5~6개, 보통 5개 산란한다. 곤충류를 주로 먹는다. 오호츠크해 서안, 샨타르섬, 캄차카, 사할린, 쿠릴 열도, 일본 홋카이도 등지에서 번식한다. 중국 동부, 일본, 타이완, 필리핀, 보르네오, 셀레베스 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002031', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('287', '섬개개비', 'Locustella pleskei', 'Taczanowski', '1890', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Locustella', '붉은허리개개비속', 'pleskei', '섬개개비', '참새목 휘파람새과에 속하는 조류이다. 몸길이는 약 14cm이다. 몸 윗면은 올리브색을 띤 갈색이고 허리와 위 꼬리덮깃은 황갈색이다. 바깥 꼬리깃 끝에 회색빛이 도는 흰색 띠가 있고 아랫면은 흰색이다. 대나무 숲이나관목또는 잡목 숲에 살며 가끔 밭이나 인가 주변까지 내려와 먹이를 찾는다. 먹이는 곤충이 주식이며, 복족류 연체동물도 잡아먹는다. 대나무 줄기 사이나 나뭇가지 사이에 둥지를 틀고 5-6월 알 4개를 낳는다. 서해안, 남해안 및 동해안 도서에 서식하고 세계적으로는 일본, 중국 동부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002032', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('288', '붉은허리개개비', 'Locustella fasciolata', 'G.R.Gray', '1860', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Locustella', '붉은허리개개비속', 'fasciolata', '붉은허리개개비', '봄과 가을에 한반도를 통과하는 드물지 않은 나그네새이다. 몸길이 약 17cm이다. 몸의 윗면은 갈색이고, 아랫멱과 가슴은 잿빛이다. 배는 흰색이고, 옆구리는 잿빛 황갈색이다. 숲속에서 생활한다. 강가나 하구 등에 있는 버드나무와 작은관목이 산재하는 초지에 둥지를 튼다. 산란기는 6월상순~7월하순이고, 한배의 산란수는 3~5개이다. 주로 곤충류로 딱정벌레, 매미를 즐겨 먹는다. 구북구 동부, 시베리아 동남부, 만주 북부 등지에서 번식하고, 필리핀과 뉴기니 등에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002033', '17.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('289', '연노랑솔새', 'Phylloscopus trochilus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'trochilus', '연노랑솔새', '연노랑솔새는 참새목 솔새과에 속하는 매우 흔한 종이다. 유럽 북부와 온대 지역, 그리고 팔레아르크틱 전역에서 번식하며, 아일랜드에서 동시베리아의 아나디르 강 유역까지 분포한다. 이 새는 철저한 철새로, 대부분의 개체가 사하라 이남 아프리카에서 겨울을 난다. 다양한 나무와 땅식물이 있는 개방된 숲에서 주로 서식하며, 종종 낮은 식생에 둥지를 짓는다. 나뭇잎을 찾으며 먹이를 구하는 곤충성 조류로, 이른 봄 북부 유럽으로 돌아오는 첫 솔새류 중 하나이다. 연노랑솔새는 긴 이주 경로를 통해 동시베리아에서 남아프리카까지 이동하며, 여름철 번식과 겨울철 월동 시기가 구분된다. 국내에서는 주요 번식기가 4월 말에서 8월 사이이며, 주로 벌목, 곤충류를 먹이로 삼는다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002041', '11.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('290', '검은다리솔새', 'Phylloscopus collybita', 'Vieillot', '1817', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'collybita', '검은다리솔새', '검은다리솔새는 유럽과 아시아, 북아프리카에 분포하는 작은 참새목의 철새로, 일반적으로 널리 퍼져 있으며, 여름에는 북유럽과 온대 유럽에서 번식합니다. 남유럽, 남아시아, 북아프리카에서 겨울을 납니다. 이 새는 나뭇잎 사이를 날아다니며 곤충을 잡아먹습니다. 몸 윗면은 녹갈색이며, 아래쪽은 흰색을 띠고 있습니다. 둥근 둥지를 땅 위나 근처에 짓고, 암컷이 둥지를 책임집니다. 천적은 주로 고양이 같은 포유류와 매 등의 맹금류입니다. 검은다리솔새는 생존에 위협이 거의 없는 상태지만 일부 아종은 멸종되었습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002042', '11.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('291', '솔새사촌', 'Phylloscopus fuscatus', 'Blyth', '1842', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'fuscatus', '솔새사촌', '참새목 휘파람새과에 속하는 조류이다. 몸 전체의 평균 길이는 134mm이다. 몸무게는 평균 10.3kg이다. 알은 흰색이다. 자작나무 숲, 버드나무 숲, 낙엽침엽수혼합림, 풀이 무성한 툰드라 지대를 선호한다. 5월 말에 수컷의 지저귐이 절정에 달하며 2m 정도의 분비나무 또는 신갈나무 정상부에서 지저귀는 것이 자주 관찰되는 나그네새이다. 둥지는 주로 벼과 식물의 줄기 아랫부분, 눈잣나무나 매발톱나무 줄기에 틀며, 긴 공 모양으로 마른 풀줄기를 엮어 엉성하게 만들고 옆으로 작은 구멍을 낸다. 둥지의 바깥지름은 평균 11.5~12.0mm, 깊이는 6.5~9.0mm, 입구는 3.3~4.0mm이다. 알래스카 북부·서부에서 번식한다. 월동은 아시아에서 한다. 우리나라에서는 주로 이동할 때 발견할 수 있고, 북부 지역에서는 적은 수가 번식하기도 한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002043', '13.4');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('292', '노랑배솔새사촌', 'Phylloscopus affinis', 'Tickell', '1833', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'affinis', '노랑배솔새사촌', '노랑배솔새사촌은 주로 방글라데시, 부탄, 중국, 인도, 미얀마, 네팔, 파키스탄, 태국 등 아시아에 서식하는 여름철새입니다. 몸의 아랫면과 눈썹선이 노란색이며, 날개띠가 없어 쉽게 구분됩니다. 주로 곤충을 먹고, 나뭇잎 밑면과 잔가지를 탐색하며 활발하게 움직이는 모습이 특징입니다. 번식기는 5월에서 8월 사이로, 메마른 풀과 식물 섬유로 둥지를 만듭니다. 주로 바위와 낮은 관목이 있는 산악 지역에서 번식합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002044', '10.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('293', '긴다리솔새사촌', 'Phylloscopus schwarzi', 'Radde', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'schwarzi', '긴다리솔새사촌', '백두산과 묘향산의 고지대에서는 드물지 않게 번식하는 여름새이며, 중부 이북을 통과하는 나그네새이기도 하다. 가슴과 배는 흰 크림색, 그리고 눈 위에서 옆머리를 가로지르는 띠무늬는 노란빛이 도는 흰색이고, 다리는 황색을 띤 살구색이다. 나머지 부분은 농담과 배색의 차이는 있지만 대체로 갈색 내지 황갈색이다. 침엽수림 속이나 숲 가장자리 혹은 숲 가까이의 초지에 둥지를 만들며, 특히관목이 무성한 초지에 산다. 번식기에는 암수가 함께 생활한다. 산란기는 6월상순~7월하순이고, 한배의 산란수는 4~5개이다. 곤충류와 거미류를 즐겨 먹는다. 시베리아, 사할린, 우수리, 만주, 한국 북부 등지에서 번식하고 중국, 인도차이나에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002045', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('294', '노랑허리솔새', 'Phylloscopus proregulus', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'proregulus', '노랑허리솔새', '우리나라 중부 이남에서는 볼 수 없으나 북부 지역에서는 봄과 가을에 드물지 않게 통과하는 나그네새이다. 몸길이 약 9cm로 솔새 중에서 가장 작은 종이다. 암수 같은 빛깔이다. 몸의 윗면은 진한 올리브색이다. 머리 가운데에 노란색 선이 1줄, 날개에는 노란색 띠가 2줄 있다. 몸의 아랫면은 노란색을 띤 흰색이다. 산지 숲에 서식한다. 주로 침엽수나 낙엽활엽수의 꼭대기 가까이에 앉는다. 산란기는 5월 중순~7월상순이고, 한배의 산란수는 5~6개, 때로는 7개이다. 곤충류를 주로 먹는다. 사할린, 아무르, 우수리, 바이칼호, 알타이, 만주 북부, 몽골 북부 등지에서 번식하고, 중국 남부, 인도차이나, 하이난섬 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002046', '9.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('295', '노랑눈썹솔새', 'Phylloscopus inornatus', 'Blyth', '1842', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'inornatus', '노랑눈썹솔새', '봄과 가을에 한반도를 드물지 않게 통과하는 나그네새이다. 몸길이 약 10cm이다. 수컷 겨울깃의 등은 모두 잿빛을 띤 올리브 녹색으로 날개에 흰색 가로띠 무늬가 2개 있다. 허리와 겨드랑이 깃은 다소 노란색이다. 배는 흰색이다. 눈썹선은 노란색이다. 다리는 가늘고 약하며, 올리브색 또는 잿빛 갈색이다. 산지 숲에서 주로 단독으로 생활한다. 숲속의 땅 위나관목의 낮은 가지 위에 둥지를 짓는다. 산란기는 6월 중순~7월 중순이며, 한배의 산란수는 5~7개이다. 주로 곤충류를 잡아먹는다. 시베리아, 바이칼호, 아무르, 몽골 북부, 만주, 우수리에서 번식하고, 중국 남부와 말레이시아에 내려가 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002047', '10.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('296', '연노랑눈썹솔새', 'Phylloscopus humei', 'Brooks', '1878', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'humei', '연노랑눈썹솔새', '연노랑눈썹솔새는 작은 참새목의 조류로, 주로 내륙 아시아의 산악 지역에서 번식합니다. 이 새는 겨울철에 주로 인도에서 월동하며, 대개 산림의 고지대에서 발견됩니다. 몸 윗면은 녹색을 띠며 아랫면은 희미한 흰색입니다. 긴 눈썹선과 날개의 노란 테두리는 특징적입니다. 번식기는 산둥지에서 이루어지며, 곤충을 주로 먹고 이동 중에는 서유럽까지도 관찰되곤 합니다. 이 종은 일반적으로 멸종위기는 아니며, 다른 노랑눈썹솔새와 구별되는 점은 노래의 소리와 형태학적 차이입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002048', '10.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('297', '쇠솔새', 'Phylloscopus borealis', 'Blasius', '1858', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'borealis', '쇠솔새', '몸길이 약 13cm이다. 이마에서 위꼬리덮깃까지는 어두운 올리브색이며, 눈썹 선은 황백색이고, 귀깃은 올리브색과 엷은 황색이 섞여 있다. 턱밑, 턱 아래 부위, 가슴의 중앙, 배, 아래꼬리덮깃은 엷은 황색이고, 가슴 옆과 옆구리는 잿빛을 띤 올리브색이다. 윗부리는 어두운 갈색이고, 아랫부리는 황갈색이다. 다리는 황색을 띤 엷은 갈색이다. 아고산대의 침엽수림에서 서식하는 나그네새이다. 주로 나무 위에서 생활하며, 알을 낳는 시기는 6~8월이다. 알은 흰색 바탕에 붉은 갈색과 엷은 자색의 얼룩점이 있으며 4~5개 낳는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002049', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('298', '버들솔새', 'Phylloscopus plumbeitarsus', 'Swinhoe', '1861', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'plumbeitarsus', '버들솔새', '참새목 휘파람새과에 속하는 조류이다. 몸길이는 약 11cm이다. 등 쪽은 갈색을 띤 올리브 녹색이나 머리꼭대기는 조금 어두운 잿빛을 띤다. 귀깃은 엷은 황색과 어두운 갈색이 섞여 있다. 멱과 가슴은 다소 황색을 띠고, 목 옆과 옆구리는 어두운 올리브색을 띠고 있다. 위꼬리덮깃은 갈색 기가 강하다. 꼬리깃은 엷고 어두운 갈색으로 엷은 가로띠를 나타내며 바깥 가장자리는 등과 같은 올리브 녹색이다. 윗부리는 어두운 갈색이고 아랫부리는 황갈색이다. 홍채는 어두운 갈색이다. 봄과 가을에 드물게 통과하는 나그네새이다. 여름철 산지의 침엽수림 속이나 강가의 숲속에 살며 번식도 한다. 6-7월에 5-6개의 알을 낳는다. 주로 곤충을 먹는다. 중부 이북에 서식하고 세계적으로는 시베리아, 몽골, 아무르, 우수리, 중국, 하 인도차이나 등에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002050', '11.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('299', '되솔새', 'Phylloscopus tenellipes', 'Swinhoe', '1860', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'tenellipes', '되솔새', '참새목 휘파람새과에 속하는 조류이다. 몸길이는 12cm이다. 눈썹선은 흰색 또는 유백색이다. 귀깃은 황갈백색과 어두운 올리브색이 혼합되어 있다. 이마에서 위꼬리덮깃에 이르는 등 쪽은 약간 녹색을 띤 올리브 갈색이나 허리와 위꼬리덮깃은 다소 붉은 갈색 기를 띤다. 머리꼭대기는 다른 것보다 어두운색이다. 꼬리깃은 엷고 어두운 갈색으로 바깥 가장자리는 녹색을 띤 올리브 갈색이며, 안가장자리는 좁고 엷은 색 부분으로 싸여 있다. 홍채는 어두운 갈색이고, 다리는 엷은 살구색 또는 엷은 갈색이다. 침엽수림 속의 흙 언덕 깊은 동굴이나 낙엽활엽수림 속의 언덕에 살며 전국에 걸쳐 봄과 가을에 통과하는 나그네새이다. 암수가 함께 생활하는 수가 많다. 이동할 때도 무리를 짓지는 않는다. 6월경에 4-6개의 알을 낳는다. 주로 곤충류를 먹는다. 전국에 서식하고 세계적으로는 구북구 동부, 사할린, 우수리, 쿠릴 열도, 홋카이도, 혼슈, 만주에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002051', '12.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('300', '사할린되솔새', 'Phylloscopus borealoides', 'Portenko', '1950', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'borealoides', '사할린되솔새', '사할린되솔새는 참새목 되솔새과에 속하는 새로, 사할린, 쿠릴 열도, 일본에 분포하며 아마미와 오키나와에서 겨울을 보낸다. 주로 온대림에 서식한다. 눈 위로 긴 흰색 선과 짙은 갈색의 눈선을 가지고 있으며, 머리와 윗부분, 부리 또한 짙은 갈색이다. 뺨은 녹갈색이고 귓깃은 얼룩무늬를 가지고 있으며, 부리는 밑부분이 분홍색이다. 다리는 옅은 분홍색을 띤다. 비슷한 종인 연파랑솔새와 헷갈리기 쉽지만, 윗부분이 약간 더 녹색빛을 띤다. 번식기는 관목에서 이루어진다. 구별 포인트로는 더 낮은 음조의 소리가 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002052', '11.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('301', '산솔새', 'Phylloscopus coronatus', 'Temminck & Schlegel', '1847', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'coronatus', '산솔새', '산지 저지대의 낙엽활엽수림에 서식하는 여름철새다. 혼자 또는 암수가 함께 생활하며, 무리를 형성하지 않는다. 머리꼭대기는 갈색을 띤 어두운 올리브색이며, 몸 윗면은 올리브색이고, 턱밑, 턱 아래 부위, 가슴, 배는 어두운 흰색,. 배의 중앙은 노란색이다. 아래꼬리덮깃은 연한 황색, 윗부리는 어두운 갈색이며 아랫부리는 연한 황색, 다리는 갈색이다. 이마에서 정수리까지 갈색 선이 나 있어 다른 솔새류와 구별된다. 둥지는 낙엽활엽수림 또는관목림의 풀뿌리, 땅 위, 절벽의 움푹 파인 곳에 이끼류, 나무껍질, 낙엽 등을 이용해서 둥근 모양으로 만든다. 산란 시기는 5~6월이며, 알은 흰색, 4~6개씩 낳는다. 우리나라에서 흔한 나그네새이며 드물게는 번식도 하는 여름새이다. 히말라야에서 중앙아시아를 거쳐 중국 북동부와 일본에 이르는 지역에 분포하고 남쪽으로 내려가 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002053', '11.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('302', '수염오목눈이', 'Panurus biarmicus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Timaliidae', '붉은머리오목눈이과', 'Panurus', '수염오목눈이속', 'biarmicus', '수염오목눈이', '참새목 붉은머리오목눈이과의 새이다. 몸길이는 16.5cm 정도이다. 성별에 따라 외형이 다르다. 수컷의 머리는 회색이 도는 파란색이고, 몸은 적갈색 또는 갈색이다. 날개는 황갈색, 검은색, 적갈색의 줄무늬가 길게 나 있다. 항문은 검은색이다. 암컷은 약간 흐린 황갈색과 적갈색이다. 암수 모두 눈이 노랗다. 민물 습지대에 서식한다. 온대 지역에 부분적으로 분포한다. 혀로 털을 정리한다. 조용하게 ‘칭- 칭-’ 우는 등 다양한 울음소리를 낸다. 대부분 일부일처이다. 위로 오르기 위한 수직의 활동 영역이 있어야 한다. 날아다니는 곤충을 찾기 위해 거꾸로 매달려서 머리를 빙빙 돌린다. 가장 긴 수염을 가지고 있는 우두머리 수컷에게 먹을 것을 양보한다. 겨울에는 씨를 먹고, 여름에는 곤충을 먹는다. 되새류의 알, 활짝 핀 애기부들, 알껍데기, 모기의 유충 등을 먹는다. 영국 남동부, 아시아, 태평양 북부 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002059', '16.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('303', '상모솔새', 'Regulus regulus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Regulidae', '상모솔새과', 'Regulus', '상모솔새속', 'regulus', '상모솔새', '우리나라 전역에서 월동하는 흔한 겨울새이다. 몸길이 9cm이다. 온몸이 녹갈색이나 아랫면은 옅은 황백색을 띤다. 머리꼭대기에 노란색 넓은 선이 있다. 수컷은 머리 중앙에 붉은 선이 있다. 날개에 흰색 띠가 2줄 있다. 눈테두리는 흰색이다. 아고산대의 침엽수림에 둥지를 튼다. 보통 암수가 함께 생활한다. 산란기는 6~7월이고, 한배의 산란수는 5~8개이다. 곤충류를 주식으로 하며 거미류도 먹는다. 식물성으로는 솔씨를 즐겨 먹는다. 아무르, 만주 북부, 우수리, 사할린, 쿠릴 열도 남부에서 번식하며, 만주 남부, 한국 전역, 일본, 중국 동부, 류큐, 타이완 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002068', '9.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('304', '굴뚝새', 'Troglodytes troglodytes', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Troglodytidae', '굴뚝새과', 'Troglodytes', '굴뚝새속', 'troglodytes', '굴뚝새', '참새목 굴뚝새과에 속하는 조류이다. 몸길이는 10cm 정도의 작은 새이다. 전체적으로 적갈색을 띠며 얼룩무늬가 있다. 부리가 짧고 뾰족하며 꼬리도 짧다. 산림, 숲 가장자리, 개활지에 살며 흔히 발견되는 텃새이다.관목의 숲 사이, 계류의 바위 위, 벼랑, 인가의 처마 밑에서 먹이를 찾는다. 5-8월에 4-6개의 알을 낳는다. 곤충류를 주식으로 하고 거미류도 먹는다. 전국에 서식하고 세계적으로는 유라시아 전역에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002071', '10.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('305', '동고비', 'Sitta europaea', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sittidae', '동고비과', 'Sitta', '동고비속', 'europaea', '동고비', '참새목 동고비과에 속하는 조류이다. 겨울깃의 앞이마와 접하는 부분은 그을린 붉은색을 띤 흰색이다. 뚜렷한 검은색의 눈선이 있고, 턱 밑, 뺨, 귀깃, 멱, 가슴은 흰색이나 개체에 따라서는 가슴에 다소의 붉은 황색을 띤다. 배는 황갈색이다. 배 옆에 갈색 및 밤색의 얼룩점이 있다. 머리꼭대기로부터 위꼬리덮깃까지의 등 쪽은 전부 청색을 띤 잿빛이다. 여름깃은 깃털이 마모되어 색이 전체적으로 퇴색한다. 부리는 길어서 머리 길이와 같고 곧다. 잡목림, 침엽수, 혼효림에 사는 흔한 텃새이다. 단독 또는 암수가 함께 생활하며 번식이 끝나면 다른 새들과 섞여 숲속을 돌아다니며 먹이를 찾는다. 4-6월에 7개의 알을 낳는다. 곤충이나 거미, 각종 종자와 열매를 먹는다. 전국에 서식하고 세계적으로는 아무르 남부, 우수리, 만주, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002074', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('306', '쇠동고비', 'Sitta villosa', 'Verreaux', '1865', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sittidae', '동고비과', 'Sitta', '동고비속', 'villosa', '쇠동고비', '참새목 동고비과에 속하는 새이다. 몸길이는 12cm가량이다. 수컷 겨울깃의 앞이마에 있는 약한 눈썹선은 흰색이고, 머리꼭대기는 광택이 있는 검은색이다. 눈 앞에서 목 옆에 이르는 검은색의 긴 눈선이 있다. 턱 밑, 멱, 귀깃은 크림색이나, 턱 밑과 멱의 중앙은 거의 흰색에 가깝다. 가슴 이하의 아랫면은 엷은 크림색이다. 가슴, 배, 아래꽁지덮깃은 엷은 크림 황갈색을 띤다. 뒷목, 등, 허리, 위꽁지덮깃은 고르게 어두운 잿빛 푸른색이다. 중앙꽁지깃은 등과 같은 색이며 가장 바깥쪽 꽁지깃 기부의 2/3는 검은색이다. 날개깃은 어두운 잿빛 갈색으로 푸른 잿빛의 좁은 가장자리가 있다. 셋째날개깃과 날개덮깃은 어두운 잿빛 푸른색이다. 산지의 침엽수림에 서식한다. 백두산 지역에서는 흔한 텃새로 민첩하게 나무 사이를 건너다닌다. 마른나무 줄기에 스스로 구멍을 파고 영소하나, 딱따구리의 옛 둥지를 이용하기도 한다. 한배에 5~6개 산란한다. 중국 북부, 한국(백두산)에 서식한다. 백두산 지역에서는 흔한 텃새이나 중부 이남 지역에서는 매우 드물다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002075', '12.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('307', '나무발발이', 'Certhia familiaris', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Certhiidae', '나무발발이과', 'Certhia', '나무발발이속', 'familiaris', '나무발발이', '우리나라 북부지방에서는 텃새, 중부 이남에서는 흔하지 않은 겨울새이다. 몸길이 약 13cm이다. 이마와 머리꼭대기, 뒷머리, 뒷목, 등은 검은 갈색과 황갈색이 혼합된 색으로 각 깃털 끝에는 흰색의 얼룩무늬가 있다. 허리와 위꼬리덮깃은 황갈색이다. 눈썹선과 턱, 목 아랫부분, 가슴, 배는 흰색이다. 부리는 뾰족하고 아래로 굽어 있다. 침엽수림 또는 혼합림에서 서식한다. 동작이 빠르고 나무줄기를 나선형으로 빙빙 돌면서 올라간다. 사람에게 발각되면 움직이지 않고 한곳에 정지한다. 숲의 고목나무 구멍이나 나무 틈에 둥지를 튼다. 산란기는 3월상순~5월하순이고, 한배의 산란수는 4~5개이다. 곤충류와 거미류 등을 먹는다. 한국 북부, 만주, 홋카이도, 사할린, 아무르, 우수리, 오호츠크 해 연안 등에서 번식하고 한국 중부 이남, 일본에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002078', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('308', '북방쇠찌르레기', 'Sturnus sturninus', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sturnidae', '찌르레기과', 'Sturnus', '찌르레기속', 'sturninus', '북방쇠찌르레기', '참새목 찌르레기과에 속하는 조류이다. 몸길이는 18cm이다. 이마에서 뒷목까지는 잿빛이고, 뒷머리에는 검은 자색의 큰 얼룩무늬가 있다. 귀깃은 잿빛이다. 턱 밑과 멱 윗부분은 갈색을 띤 잿빛이다. 머리꼭대기에서 뒷목까지의 깃털은 길고 찢어져서 다소 털 모양으로 나타난다. 부리는 다소 짧고 검은색이며, 다리는 잿빛 갈색이다. 도시공원과 정원, 교외의 노거수, 소림 등지에 살며 여름철새이다. 번식기에는 암수가 생활하나 번식 후에는 큰 무리를 이룬다. 둥지는 매우 조잡한 접시 모양이다. 5월하순에 3-8개의 알을 낳는다. 어미 새는 딱총나무, 뽕나무, 벚나무 등의 열매와 딱정벌레와 파리, 나비 유충 등을 즐겨 먹는다. 우리나라에서 번식하고 중국 남부와 동남아시아에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002081', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('309', '잿빛쇠찌르레기', 'Sturnus sinensis', 'Gmelin', '1788', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sturnidae', '찌르레기과', 'Sturnus', '찌르레기속', 'sinensis', '잿빛쇠찌르레기', '참새목 찌르레기과의 새로 몸길이는 18cm가량이다. 수컷 겨울깃의 얼굴은 조금 갈색을 띠고, 멱은 잿빛 흰색이다. 가슴은 엷은 잿빛이며, 배와 아래꽁지덮깃은 흰색이다. 머리, 허리, 위꽁지덮깃은 잿빛 흰색이고, 등은 잿빛이다. 꽁지는 검은색으로 깃 가장자리는 크림색이다. 날개깃, 큰날개덮깃, 꽁지는 어두운 검은색으로 청동색 광택이 있다. 부리는 쇠찌르레기와 비슷하여 색깔은 잿빛이고 기부는 청색이다. 홍채는 푸른빛 백색이며, 다리는 청색이다. 봄철의 도래지에는 짝을 짓고 번식기에는 암수가 함께 생활한다. 번식이 끝나면 무리를 지어 산다. 주로 나무 위에서 생활한다. 큰 나무의 밑동이 패어 생긴 굴이나 건물의 틈 사이에 영소한다. 때로는 여러 쌍이 한 건축물에 영소하기도 한다. 5~6월, 한배에 4~5개 산란한다. 식성은 쇠찌르레기와 흡사하다. 동양구, 중국 남부, 인도차이나 북부에서 번식한다. 인도차이나, 타이, 말레이 반도 등지, 하이난 섬, 타이완 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002083', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('310', '분홍찌르레기', 'Sturnus roseus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sturnidae', '찌르레기과', 'Sturnus', '찌르레기속', 'roseus', '분홍찌르레기', '분홍찌르레기는 찌르레기과에 속하는 조류로, 아시아와 동유럽의 초원, 반사막, 사막 지역에서 번식합니다. 여름에는 성체 수컷이 핑크색 몸과 윤기 나는 검은색 머리, 날개, 꼬리를 가지고 있으며, 암컷은 짙은 색을 띠고 짧은 깃털을 가집니다. 비번식기에는 남아시아와 인도에서 겨울을 보내며 높은 사회성을 보이고 큰 무리를 형성합니다. 주로 곤충을 먹으며 특히 메뚜기와 풀무치를 선호해 농민들에게 이롭습니다. 번식기는 5~6월로 짧으며, 그 시기에 먹이 부족이 발생하면 많은 어린 새가 굶주리기도 합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002084', '20.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('311', '붉은부리찌르레기', 'Sturnus sericeus', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sturnidae', '찌르레기과', 'Sturnus', '찌르레기속', 'sericeus', '붉은부리찌르레기', '농경지, 초지, 인가 주변에서 생활한다. 나무 위에서 열매나 곤충을 먹거나 초지, 농경지에서 곤충을 잡아먹는다. 부리는 붉은색이며 끝부분은 검은색이고, 다리는 주황색이다. 첫째날개깃 기부에 흰색 반점이 있다. 허리와 아래꼬리덮깃은 회색이다. 홍채는 암갈색이다. 수컷의 머리는 엷은 황갈색 또는 황백색으로 개체에 따라 차이가 있다. 몸 윗면은 청회색이다. 날개와 꼬리는 어두운 녹색 광택이 있는 검은색이다. 멱과 윗가슴은 머리와 같이 탁한 황갈색 또는 황백색이다. 앞가슴과 가슴옆은 청회색이며 배는 탁한 흰색이다. 암컷은 전체적으로 갈색을 띤다. 등 부분이 수컷과 달리 갈색이다. 첫째날개깃 기부에 흰색 반점이 있다. 턱선은 불명확한 갈색이며, 부리는 수컷보다 색이 엷다. 중국 남동부에서 서식하는 텃새이며, 일부는 베트남 북부에서 월동한다. 우리나라에서는 2000년 4월 강화도에서 수컷 1개체가 처음으로 관찰된 이래, 제주도 및 경기도 파주, 의왕, 전라북도 군산 등지에서 서식 및 번식이 확인되었다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002085', '24.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('312', '찌르레기', 'Sturnus cineraceus', 'Temminck', '1835', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sturnidae', '찌르레기과', 'Sturnus', '찌르레기속', 'cineraceus', '찌르레기', '참새목 찌르레기과의 새로 몸길이는 24cm가량이다. 수컷 겨울깃의 머리와 뒷목은 다소 녹색의 광택이 있는 검은색이며, 머리와 멱의 깃털은 버드나무 잎 모양이다. 이마와 머리 옆은 검은색과 흰색의 세로 얼룩무늬가 서로 혼합된다. 여름깃은 봄철 털갈이를 하지 않기 때문에 깃털은 퇴색되어 대체로 엷은 색으로 되고 청동색의 광택을 잃는다. 홍채는 갈색이고, 다리는 황색이다. 우리나라 전역에서 번식하는 흔한 여름새이나 중부 이남에서는 월동도 하는 겨울새이다. 수동(樹洞), 건물의 지붕 또는 틈 사이, 돌담의 틈, 딱따구리의 옛 둥지 등에서 영소한다. 번식기에는 암수가 함께 생활하지만 그 이외의 시기에는 대개 무리를 지어 생활한다. 알은 3월하순~7월경, 한배에 4~6개 낳는다. 식성은 잡식성이다. 유라시아 대륙 동부, 일본 등지에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002086', '24.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('313', '흰점찌르레기', 'Sturnus vulgaris', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sturnidae', '찌르레기과', 'Sturnus', '찌르레기속', 'vulgaris', '흰점찌르레기', '참새목 찌르레기과의 조류로 몸길이는 22cm가량이다. 암수 모두 등, 목덜미, 가슴 등은 광택이 나는 무지갯빛 녹색 깃털로 덮여 있다. 날개는 검은색이지만 녹색 또는 자주색으로 보이기도 한다. 부리 빛깔은 평소에는 검은색이나 번식기에는 노란색으로 변한다. 다리는 붉은 갈색, 홍채는 흑갈색이다. 수컷의 가슴에는 가늘고 긴 깃털이 있으나, 암컷은 짧고 맵시 있는 깃털이 있다. 수컷은 부리 기부에 파란색 반점이 있으나, 암컷은 분홍색 얼룩이 있다. 주로 산이 없는 저지대, 황야, 바닷물이 드나드는 늪지 등에서 무리 지어 산다. 곤충, 씨앗, 열매 등 아무것이나 잘 먹는다. 수명은 15년 정도이다. 번식기에는 나무, 건물, 옥상 등의 구멍이나 갈라진 틈에 둥지를 짓는다. 간혹 다른 새들이 지은 둥지를 빌리기도 한다. 우리나라 천수만, 해남, 제주도 등지에서 관찰되는 미조(迷鳥)이다. 겨울이 되면 깃털의 끝부분이 가슴부터 흰색 또는 크림색으로 변해 흐린 검은색 바탕에 얼룩 반점이 있는 것처럼 보여서 흰점찌르레기라는 이름이 붙었다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002087', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('314', '호랑지빠귀', 'Zoothera aurea', 'Holandre', '1825', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Zoothera', '호랑지빠귀속', 'aurea', '호랑지빠귀', '비교적 흔히 볼 수 있는 여름철새다. 특히 일부는 텃새가 되기도 한다. 몸길이는 29.5cm 정도다. 몸의 윗면은 황갈색이고 멱과 가슴, 배는 흰색이며 몸 전체에 검은색과 진한 갈색의 얼룩무늬가 있다. 꼬리의 가장자리는 검은색이며 끝은 흰색이다. 날 때 날개 아랫면에 있는 검은색과 흰색의 띠가 특징적이다. 주로 새벽녘과 늦은 밤에 ‘히이~, 호오~’하고 가느다란 피리 소리를 낸다. 낙엽활엽수림, 잡목림 속에서 서식한다. 둥우리는교목의 가지 위에 다량의 이끼류와 소량의 마른 가지와 낙엽 등을 이용해서 밥그릇 모양으로 만든다. 한국·일본·중국(동북부)·우수리·시베리아(남부)에서 번식하고, 겨울에는 중국 남부나 대만·필리핀·동남아시아 등지로 내려가 겨울을 난다. 4월~7월에 4~5개의 알을 낳는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002092', '29.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('315', '되지빠귀', 'Turdus hortulorum', 'Sclater', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'hortulorum', '되지빠귀', '봄과 가을철 이동 중 우리나라의 숲에 머물렀다 떠나는 여름 철새이다. 몸길이는 약 23cm, 날개 길이는 10cm가량이다. 수컷의 머리, 등, 멱 및 윗가슴은 회색이며 아랫가슴과 배는 흰색이다. 옆구리는 주황색이며 날개와 꼬리는 어두운 회색이다. 암컷의 등은 갈색이며 배는 흰색이다. 옆구리는 주황색이며 가슴과 옆구리에는 검은색 반점이 있다. 숲속 참나무 등의 가지에 밥그릇 모양의 둥지를 만든다. 5~6월에 녹청색에 적갈색 반점이 있는 알을 6~7개 낳는다. 흰배지빠귀와 비슷한 소리로 지저귄다. 유사종인 흰눈썹붉은배지빠귀는 흰색의 눈썹선이 선명하여 이 종과 구별된다. 한국, 중국, 러시아 등에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002094', '23.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('316', '검은지빠귀', 'Turdus cardis', 'Temminck', '1831', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'cardis', '검은지빠귀', '작은 무리를 이루어 한반도를 드물게 통과하는 나그네새이다. 몸길이 21.5cm, 날개 길이 10.5~12cm이다. 머리, 등, 날개에서 꼬리까지 몸의 윗면과 멱과 가슴은 검은색이고 흰색의 배에는 불규칙한 검은색 반점이 있다. 암컷은 윗면이 갈색이고 아랫면은 희다. 가슴과 배에 갈색 얼룩무늬가 있다. 암수 모두 부리와 다리는 노란색이다. 산지 낙엽활엽수림에서 서식한다. 잡목림, 낙엽활엽수림,관목숲속에 둥지를 짓는다. 산란기는 5월 초순~7월하순이고, 한배의 산란 개수는 3~4개이다. 주로 곤충류를 먹으나 지네나 지렁이,장미과와 포도과의 열매도 먹는다. 일본과 중국 중부에서 번식하고 일본 서남부, 중국 동부 및 남부, 인도차이나반도 등지에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002095', '21.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('317', '흰눈썹붉은배지빠귀', 'Turdus obscurus', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'obscurus', '흰눈썹붉은배지빠귀', '참새목 지빠귀과의 조류로 몸길이 22cm가량이다. 수컷 겨울깃의 머리꼭대기는 잿빛을 띤 어두운 올리브 갈색으로 각 깃털의 중앙은 다소 검은색이다. 꽁지는 정확히 모난꽁지이다. 여름철이 되면 등 이하의 올리브색은 감소되고 잿빛이 많아진다. 암컷 겨울깃은 등 이하의 색이 수컷과 같으나 머리꼭대기와 뒷목이 수컷과는 달리 등과 같은 올리브 갈색으로 단지 각 깃털 중앙이 다소 어두운색일 따름이다. 가슴과 옆구리는 수컷보다 다소 엷은 황색을 띤 녹슨 갈색이고 기타는 수컷과 같다. 홍채는 갈색이고, 다리는 황색을 띤 잿빛 황갈색 또는 살구색을 띤 갈색이다. 소나무, 일본잎갈나무 숲 등에 영소한다. 봄과 가을에 한반도를 통과하는 비교적 드문 나그네새이다. 여름철에는 암수가 함께 생활한다. 알은 6~7월, 한배에 4~6개, 보통 4개 낳는다. 곤충류, 복족류, 다족류의 진각목 등을 주로 먹으며,장미과의 열매도 즐겨 먹는다. 동북아시아에 분포한다. 중국 남부와 동남아시아 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002097', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('318', '흰배지빠귀', 'Turdus pallidus', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'pallidus', '흰배지빠귀', '참새목 지빠귀과의 조류로 몸길이 약 23cm이다. 수컷 겨울깃의 머리꼭대기는 올리브색을 띤 석판 잿빛이다. 눈 앞부분과눈 가장자리 부분은 어두운 잿빛이며, 턱 밑은 흰색이다. 뺨, 귀깃, 멱은 잿빛이고 윗가슴은 올리브 잿빛 갈색이다. 옆구리는 엷은 홍색을 띤 갈색이다. 배 옆은 올리브 잿빛 갈색이며 배의 중앙은 흰색이다. 꽁지는 모가 나 있다. 여름깃은 봄철 털갈이가 없다. 암컷 겨울깃의 머리꼭대기에서 위꽁지덮깃에 이르는 등 쪽은 수컷보다 다소 붉은색 기가 적은 올리브 갈색으로 머리와 등의 차이는 없다. 꽁지는 수컷과 같다. 윗부리 전부와 아랫부리 끝은 갈색이며 아랫부리 기부는 황색이다. 홍채는 갈색이고, 다리는 황색이다. 숲속에서 번식한다. 봄과 가을에 한반도 전역을 지나가는 나그네새이다. 여름에는 암수가 함께 생활한다. 이동할 때는 많은 수가 군서 생활을 하나 겨울에는 단독일 때가 많다. 땅 위에서 그리 높지 않은 나뭇가지 위에 영소한다. 알은 6월경, 한배에 4~5개를 낳는다.장미과, 포도과, 기타 식물의 씨앗과 곤충류, 거미류, 다족류 등을 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002098', '23.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('319', '붉은배지빠귀', 'Turdus chrysolaus', 'Temminck', '1832', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'chrysolaus', '붉은배지빠귀', '봄과 가을에 한반도를 지나가는 드문 나그네새이다. 몸길이 24cm이다. 수컷은 몸 윗면과 머리와 목이 어두운 회갈색이며, 가슴과 옆구리는 적갈색, 배는 흰색이다. 암컷은 수컷보다 아랫면이 옅은 색이며, 멱에 흰 줄무늬가 조금 있다. 우거진 숲속에서 생활한다. 산란기는 5월상순~8월상순이고, 한배의 산란수는 3~5개이다. 곤충류와 기타 식물의 열매를 즐겨 먹는다. 사할린, 일본 중북부에서 번식하고 일본 남부와 중국 남부, 필리핀에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002099', '24.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('320', '검은목지빠귀', 'Turdus atrogularis', 'Jarocki', '1819', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'atrogularis', '검은목지빠귀', '우리나라에서는 1989년 9월에 광주 무등산에서 5개체가 관찰된 기록이 있는 미조이다. 몸길이 23~25cm이다. 수컷은 머리 위와 몸 윗면, 날개 및 가운데꼬리깃이 녹회색이고 나머지 꼬리깃은 갈색을 띤다. 머리 옆, 턱, 멱 및 윗가슴은 검은색이고 아랫가슴과 배는 흰색이다. 보통 평지 및 구릉의 숲과 덤불에서 서식한다. 유럽 동북부, 러시아 시베리아 서부 바이칼호에서 번식하고 시베리아 서부 알타이 지역, 중앙아시아, 이란에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002100', '24.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('321', '붉은목지빠귀', 'Turdus ruficollis', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'ruficollis', '붉은목지빠귀', '붉은목지빠귀는 참새목 지빠귀과에 속하는 조류로, 시베리아 동부에서 북만주까지 번식하며, 중국 서부, 미얀마, 인도 북동부로 이동해 겨울을 납니다. 수컷 성체는 붉은색 목을 가지고 있으며, 일반적으로 회색 등과 붉은 아랫날개를 특징으로 합니다. 암컷과 어린 새는 목의 붉은 깃이 없고, 검은 줄무늬가 있는 아랫면을 가지고 있습니다. 이 새는 서유럽에서는 매우 드물게 관찰되는 방랑자입니다. IUCN에서 ''관심 필요''로 분류되어 있으며, 특별히 멸종 위기에 처한 것은 아닙니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002101', '25.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('322', '노랑지빠귀', 'Turdus naumanni', 'Temminck', '1820', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'naumanni', '노랑지빠귀', '참새목 지빠귀과에 속하는 조류이다. 몸길이는 23cm이다. 머리꼭대기 깃털은 갈색을 띤 잿빛이다. 뒷목과 등의 깃털은 대부분 붉은 살구색이나 엷은 갈색을 띤 잿빛의 깃 가장자리가 있기 때문에 붉은 살구색 부분은 그 밑에 있으므로 보이지 않는다. 귀깃은 잿빛 갈색이다. 배는 흰색이다. 아래꼬리덮깃은 흰색으로 각 깃털의 기부는 녹슨 색이다. 홍채는 갈색이고, 다리는 엷은 갈색이다. 흔한 겨울철새이다. 10월 초에 도래하여 4월하순까지 체류하는 개체도 있다. 곤충 또는 식물의 종자나 열매를 먹는 잡식성이다. 평지 또는 산지 숲에서 서식한다. 농경지, 도시, 교외, 구릉, 산지 등 도처에서 눈에 띈다. 곤충류 또는 식물의 종자나 열매를 먹는다. 시베리아에서 번식하고 만주 남부, 한국 전역, 일본 서북부, 중국 북부, 타이완 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002102', '23.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('323', '개똥지빠귀', 'Turdus eunomus', 'Temminck', '1831', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'eunomus', '개똥지빠귀', '참새목 지빠귀과에 속하는 조류이다. 몸길이는 23cm이며 날개 길이는 12cm 정도이다. 머리꼭대기부터 꼬리까지 몸의 윗면은 어두운 갈색이며, 눈썹선과 멱은 노란색이 도는 흰색이다. 흰색의 가슴과 배, 옆구리에는 검은색의 반점이 있으며 날개깃은 적갈색을 띠지만 개체에 따라서 차이가 크다. 다양한 중간형이 있다. 평지 또는 산지 숲속에 산다. 5-6월에 나뭇가지 위에 잔 모양의 보금자리를 틀고 4-7개의 알을 낳는다. 주로 식물의 열매를 먹는데 지렁이, 벌레도 좋아한다. 시베리아 북부와 캄차카반도에서 번식하고, 우리나라에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002103', '23.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('324', '붉은날개지빠귀', 'Turdus iliacus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'iliacus', '붉은날개지빠귀', '붉은날개지빠귀는 참새목 지빠귀과의 조류로, 유럽과 북극권 지역에 서식합니다. 몸 길이는 20~24cm, 날개 길이는 33~34.5cm입니다. 붉은 옆구리와 날개 아래, 그리고 눈 위의 크림색 줄무늬가 특징적입니다. 이 새는 주로 북유럽의 침엽수와 자작나무 숲에서 번식하며, 남서 아시아와 북서 아프리카로 겨울을 나러 이동하는 철새입니다. 무리 생활을 하며, 철 따라 곤충과 딸기를 먹습니다. 개체 수가 감소해 IUCN에서는 붉은날개지빠귀를 "준위협" 등급으로 지정하고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002104', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('325', '큰점지빠귀', 'Turdus mupinensis', 'Laubmann', '1920', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'mupinensis', '큰점지빠귀', '큰점지빠귀는 참새목 지빠귀과에 속하는 조류로, 주로 중국과 베트남 북부 지역의 온대 및 아열대 산림에 서식한다. 이 새는 유럽의 개똥지빠귀와 노랑지빠귀와 유사한 깃털을 가지고 있으며, 이들과 가까운 친척 관계이다. 서식지의 특성상 일반적으로 숲에서 발견된다. IUCN에 의해 보전 상태는 ''관심 필요 없음''으로 평가되고 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002105', '23.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('326', '꼬까울새', 'Erithacus rubecula', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Erithacus', '꼬까울새속', 'rubecula', '꼬까울새', '꼬까울새는 참새목 파랑새과에 속하는 작은 조류로, 주로 유럽, 서부 시베리아 및 북아프리카에 분포합니다. 몸길이는 12.5~14cm이며, 암수 모두 주황색 가슴과 얼굴, 갈색 등, 흰색 배를 가지고 있습니다. 어린 새는 갈색 점무늬를 띠며, 성장하면서 주황색 깃털이 나타납니다.    이 새는 번식기에 플루트 소리 같은 노래를 부르며, 겨울에도 각각의 영토를 지킵니다. 낮에 주로 활동하며, 잡식성으로 곤충, 열매, 씨앗 등을 먹습니다. 매우 영리하고 사람을 두려워하지 않으며, 정원사들의 친구로 알려져 있습니다.    꼬까울새는 한국에서는 매우 드문 위치에 있으며, 주로 남서 유럽에서 월동합니다. 이 새는 매우 영리하고 기존 영국 및 아일랜드의 전통에서는 크리스마스와 연관되어 많은 우화에 등장합니다. 개인 영토를 지키고 다른 새들과의 경쟁에서 격렬하게 행동하기도 합니다. 대략 1.1년의 생존 기간을 가지며, 드물게 19년까지 살기도 합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002108', '13.2');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('327', '흰눈썹울새', 'Luscinia svecica', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Luscinia', '진홍가슴속', 'svecica', '흰눈썹울새', '매우 드문 나그네새이다. 수컷은 봄철에 깃털의 이마, 머리꼭대기, 뒷머리, 뒷목은 세피아 갈색으로 각 깃털 중앙은 어두운 색을 나타내고 가장자리는 엷은 색이다. 눈 위에는 크림 황백색의 눈썹 선이 있다. 눈앞은 검은색을 띠며 귀깃은 붉게 녹슨색을 띤 갈색이다. 턱밑, 멱, 앞목은 푸른색으로 그 중앙보다 다소 아랫부분에 짙고 붉게 녹슨색의 큰 얼룩무늬가 있다. 부리는 검은색이고, 다리는 어두운 갈색이다. 암컷 몸의 윗면은 수컷과 같다. 턱 밑과 멱은 흰색으로 다소 붉게 녹슨색의 얼룩점이 있고 아랫부분에는 검은색과 흰색 순으로 2개의 띠가 있으며 다소 푸른색을 띤다. 가슴에는 얼룩무늬가 있다.관목이 산재하고 기복이 심한 습지에 서식한다. 겨울철에는 단독으로 여름철에는 암수가 함께 주로 땅 위에서 생활한다. 산란기는 5~7월이고, 한배의 산란수는 5~7개, 때로는 8~9개이다. 동물성인 곤충류, 환형동물, 연체동물의 복족류와 각종 식물의 열매도 먹는다. 동남아시아, 러시아, 중국, 알래스카 서부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002111', '13.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('328', '검은머리딱새', 'Phoenicurus ochruros', 'S.G.Gmelin', '1774', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Phoenicurus', '딱새속', 'ochruros', '검은머리딱새', '우리나라에서는 보기 드문 미조이다. 날개 길이 8.6cm, 꼬리 길이 6.5cm 정도이다. 수컷은 머리와 목, 등, 가슴이 검은색이고, 이마와 눈 부근은 회색이다. 배와 허리, 꼬리덮깃은 붉은빛 갈색이다. 암컷은 대체로 갈색이고, 목과 아랫배는 회색이다. 습성은 딱새와 비슷하다. 한국, 몽골(남부), 중국(북부), 티베트, 인도, 미얀마 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002117', '13.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('329', '딱새', 'Phoenicurus auroreus', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Phoenicurus', '딱새속', 'auroreus', '딱새', '참새목 솔딱새과에 속하는 조류이다. 몸길이 14cm이다. 수컷은 아랫면이 짙은 주황색이고 얼굴은 검은색이다. 머리는 명암이 있는 회색이고 등과 날개는 흑갈색이다. 수컷의 날개에 있는 흰색 반점이 뚜렷하게 보인다. 암컷은 전체적으로 황갈색을 띠며, 날개와 꼬리는 짙은 갈색이다. 허리는 짙은 주황색이고 날개에는 수컷보다 작은 흰색 반점이 있다. 새끼는 회갈색으로 얼룩무늬가 있다. 인가 주변부터 산림 가장자리까지 다양한 곳에 사는 텃새이다. 나무 구멍, 쓰러진 나무 밑, 돌 틈, 건물 틈에서 번식한다. 3월부터 울기 시작하며 산란기는 5월이다. 나무꼭대기와 전깃줄 등 개활지에 앉아 울기 때문에 쉽게 눈에 띈다. 제주도와 울릉도를 제외한 전국에 서식하고 세계적으로는 러시아, 중국, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002118', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('330', '부채꼬리바위딱새', 'Rhyacornis fuliginosa', 'Vigors', '1831', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Rhyacornis', '부채꼬리바위딱새속', 'fuliginosa', '부채꼬리바위딱새', '부채꼬리바위딱새는 참새목 딱새과에 속하는 조류로, 남아시아, 동남아시아, 중국에 분포한다. 수컷은 청회색이고 암컷은 회색을 띠며, 주로 빠르게 흐르는 강과 계곡 주변에 서식한다. 몸길이는 약 14cm에 이르며, 수컷은 검붉은 꼬리를 가지고 있다. 해발 2,000~4,100미터의 높은 지역에 서식하지만 겨울에는 더 낮은 고도로 내려온다. 매우 영토를 보호하는 성향이 강하며, 침입자에게 공격적인 행동을 보인다. 주요 서식지로는 히말라야 지역과 중국 남부, 동남아의 강 주변이 포함된다. 부채꼬리바위딱새는 설상 러시아 적색목록에 ‘관심 필요’(Least Concern) 등급으로 분류되어 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002120', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('331', '흰머리바위딱새', 'Chaimarrornis leucocephalus', 'Vigors', '1831', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Chaimarrornis', '흰머리바위딱새속', 'leucocephalus', '흰머리바위딱새', '흰머리바위딱새는 참새목 딱새과에 속하는 조류로, 주로 인도 아대륙, 동남아시아, 중국 일부 지역, 중앙아시아에서 발견된다. 수컷과 암컷 모두 검은색 몸에 붉은 하체와 흰색 머리 꼭대기를 가지고 있으며, 수컷의 경우 머리 위 흰색 무늬가 더 크고 날개 아래에 갈색 또는 붉은 반점이 있다. 이들은 온대림을 자연 서식지로 삼고 있으며, 2010년에 분자 계통학 연구 결과에 따라.Chaimarrornis라는 독립된 속에서 Phoenicurus 속으로 분류 변경되었다. 흰머리바위딱새는 보호 상태가 ''관심 필요''로 분류되며, 아프가니스탄, 방글라데시, 부탄, 캄보디아, 인도, 라오스, 미얀마, 네팔, 타지키스탄, 태국, 티베트, 베트남 등지에 분포한다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002122', '18.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('332', '검은딱새', 'Saxicola torquatus', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Saxicola', '검은딱새속', 'torquatus', '검은딱새', '여름새로 몸길이는 13cm이다. 수컷은 머리와 등, 날개와 꼬리가 검은색, 가슴은 황갈색이며, 배와 옆구리는 흰색에 가까운 회백색, 허리는 흰색이고 옆목과 어깨에 흰색 반점이 있다. 겨울에는 몸의 대부분이 황갈색으로 변한다. 암컷은 윗면이 황갈색에 암회색 세로 줄무늬가 있으며 가슴과 옆구리는 적황색, 배는 회색이 도는 연노란색이다. 전국적으로 농경지, 초지, 구릉, 산록에 서식한다. 암수가 단독으로 생활한다. 초지 또는관목이 산재하는 초지에 둥지를 짓는다. 산란기는 5~7월이고, 한배의 산란수는 5~7개이다. 곤충류를 먹는다. 한국, 일본, 중국(북부), 아무르, 사할린, 시베리아(동부)에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002124', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('333', '검은뺨딱새', 'Saxicola ferreus', 'J.E.& G.R.Gray', '1846', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Saxicola', '검은딱새속', 'ferreus', '검은뺨딱새', '수컷의 이마와 등은 회색으로 검은색 세로 줄무늬가 많다. 아랫면은 흰색이다. 눈썹선은 흰색이고 뺨은 검은색이다. 날개에는 흰 줄이 있다. 암컷은 멱과 배를 제외한 전체가 갈색이다. 해안의 경작지나 초지에 서식한다. 동남아시아, 인도, 방글라데시, 파키스탄 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002125', '14.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('334', '긴다리사막딱새', 'Oenanthe isabellina', 'Temminck', '1829', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Oenanthe', '사막딱새속', 'isabellina', '긴다리사막딱새', '긴다리사막딱새는 참새목 딱새과에 속하는 조류로, 작은 곤충을 먹으며 이주하는 특성이 있습니다. 남부 러시아와 중앙아시아에서 번식하며 아프리카와 북서부 인도에서 월동합니다. 이 조류는 스텝 지역과 개활지에 서식하며, 몸길이가 15~16.5cm로 북방딱새 암컷과 유사하지만 더 크고 꼿꼿하며 황갈색이 도드라집니다. 꼬리와 날개의 검은색이 특징적입니다. 번식기는 주로 3월 말에서 5월 사이로, 주로 땅 속 굴에 둥지를 틉니다. 알은 4~6개를 낳으며, 주로 작은 곤충을 먹이고 새끼는 2주 간 부모의 보살핌을 받습니다. 이 종은 보전 상태에서 ''관심 대상''으로 평가되고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002127', '15.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('335', '사막딱새', 'Oenanthe oenanthe', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Oenanthe', '사막딱새속', 'oenanthe', '사막딱새', '사막딱새는 참새목에 속하는 작은 조류로, 이전에는 지빠귀과로 분류되었으나 현재는 구세계 딱새과로 여겨진다. 이 종은 유럽과 중앙아시아, 북아메리카 등의 암석이 많은 지역에서 번식하는 이주성 조류로, 주로 곤충을 먹는다. 사막딱새는 암수가 모두 흰색 엉덩이와 꼬리를 가지고 있으며, 꼬리 끝에는 검은색 T자 모양의 무늬가 있다. 수컷의 여름 깃은 회색 윗면과 검은 날개를 가지며, 암컷은 연한 갈색을 띤다. 무리를 이루어 대서양을 횡단하며 긴 여행을 하는 것으로 유명하다. 번식기는 암컷이 주도하여 바위 틈이나 토끼굴에 둥지를 트는 것이 일반적이다. 이들은 약 4~7개의 알을 낳으며, 부모가 함께 새끼를 돌본다. 사막딱새는 아프리카에서 겨울을 보내며, 전 세계에 널리 분포하여 멸종 위협은 받지 않는다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002128', '15.2');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('336', '검은등사막딱새', 'Oenanthe pleschanka', 'Lepechin', '1770', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Oenanthe', '사막딱새속', 'pleschanka', '검은등사막딱새', '우리나라에서는 드물게 관찰되는 미조이다. 전체 길이는 14.5cm이다. 수컷 여름깃의 머리와 뒷목은 흰색이다. 얼굴과 멱은 검은색으로 어깨 사이와 날개의 검은색과 만난다. 암수 모두 허리와 꼬리는 흰색이고, 꼬리에는 검은색의 T자형의 무늬가 있다. 개활지의 모래땅, 경작지 및 해안가에 서식한다. 혼자 지내는 동물이고, 영역성을 가지나 대부분의 지역에서 영토는 매우 작다. 횃대는관목과 낮은 가지에 있어 땅으로 빠르게 돌격할 수 있기 때문에 주 먹이인 곤충을 쉽게 잡을 수 있다. 유럽 동부, 중국 서부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002129', '14.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('337', '검은꼬리사막딱새', 'Oenanthe deserti', 'Temminck', '1825', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Oenanthe', '사막딱새속', 'deserti', '검은꼬리사막딱새', '검은꼬리사막딱새는 14.5~15cm 크기의 작은 참새목 조류로, 곤충을 먹이로 삼는 철새입니다. 주로 사하라와 아라비아반도 북부 지역에서 서식하며, 중앙아시아에서는 저지대 반건조 지대에서 발견됩니다. 수컷의 여름깃은 윗면이 담황색이고 아랫면은 흰색에 가까우며, 얼굴과 목 부분이 검은색입니다. 암컷은 전체적으로 회색빛을 띱니다. 검은 꼬리가 대표적 특성으로, 암수 모두 전체 꼬리깃이 검습니다. 번식은 주로 늦봄에 이뤄지며, 바위 틈이나 풀숲에 둥지를 틉니다. 북아프리카, 중앙아시아에서 주로 번식하며, 겨울에는 남아시아, 북동 아프리카로 이동하여 월동합니다. 주요 먹이는 땅에서 잡는 곤충입니다. IUCN 적색목록에서는 "관심 필요 없음" 등급으로 분류됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002130', '14.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('338', '바다직박구리', 'Monticola solitarius', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Monticola', '바다직박구리속', 'solitarius', '바다직박구리', '참새목 솔딱새과에 속하는 조류이다. 몸길이는 약 25cm이다. 수컷의 머리와 멱, 윗가슴, 등은 남청색, 아랫가슴과 날개, 꼬리는 청흑색, 배는 적갈색이다. 암컷의 등은 회갈색, 꼬리는 흑갈색, 배는 어두운 황갈색이다. 부리는 수컷이 검은빛이고, 암컷은 갈색빛이다. 홍채는 갈색이며, 다리는 어두운 갈색이다. 해안 및 내륙 바위 절벽에 사는 흔한 텃새이다. 기후 조건에 따라 이동하거나 한곳에 머물러 산다. 암수가 단독으로 생활하며 일정한 세력권을 점유한다. 암벽이 갈라진 곳, 암초의 틈, 벼랑의 동공 또는 건축물의 틈에 산다. 5-6월에 5-6개의 알을 낳는다. 곤충류와 갑각류의 이각목, 십각목, 연체동물의 복족류 등을 먹는다. 전역에 서식하고 세계적으로는 일본, 에스파냐에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002132', '25.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('339', '꼬까직박구리', 'Monticola gularis', 'Swinhoe', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Monticola', '바다직박구리속', 'gularis', '꼬까직박구리', '머리꼭대기와 뒷목, 작은날개덮깃은 파란색이며, 등과 날개, 꼬리는 검은색이다. 턱 아래 부위는 흰색이고 뺨과 배, 허리는 적갈색이다. 날개에 흰색 반점이 있는 것이 특징이다. 암컷은 수컷과 달리 머리와 등, 꼬리는 회갈색이며 턱 아래 부위와 가슴, 배는 흰색이다. 부리는 수컷이 갈색을 띤 검은색이고, 암컷은 갈색으로 기부는 엷은 색이다. 다리는 갈색을 띤 살구색이다. 삼림 속에서 생활하며 곤충을 주로 먹는다. 우리나라에서는 여름에 백두산 고지대에서 작은 무리들이 번식하고, 남한 지역에서는 이동 시기에 한하여 볼 수 있는 희귀한 나그네새이다. 한국, 러시아, 중국, 일본, 동남아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002133', '17.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('340', '제비딱새', 'Muscicapa griseisticta', 'Swinhoe', '1861', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Muscicapa', '솔딱새속', 'griseisticta', '제비딱새', '참새목 솔딱새과의 새로 몸길이는 13cm 정도이다. 수컷 겨울깃의 이마와 머리꼭대기는 어두운 갈색을 띤 잿빛으로 어두운색의 축반이 있다. 등 이하의 등 쪽은 어두운 갈색을 띤 잿빛이다. 눈 앞은 엷은 잿빛이며, 귀깃과 뺨은 엷은 잿빛과 갈색을 띤 잿빛이 섞인다. 꽁지는 어두운 갈색이다. 날개는 어두운 갈색이다. 암컷은 수컷과 아주 비슷하여 깃털로 구별할 수는 없다. 부리를 위에서 보면 이등변삼각형이다. 윗부리는 검은 갈색이고, 아랫부리도 검은 갈색으로 기부만이 엷은 색이다. 홍채는 갈색이다. 다리는 검은 갈색이며 작으나 발톱은 크다. 봄과 가을에 한반도 전역에서 드물지 않게 통과하는 나그네새이다. 단독 또는 암수가 함께 생활한다. 나뭇가지나 꼭대기의 잎이 적은 가지 또는 마른 가지에 몸을 꼿꼿하게 세우고 앉는다. 언제나 나무 위에서 생활한다. 딱정벌레목, 벌목, 파리목, 매미목, 메뚜기목 등의 곤충류를 주식으로 한다. 구북구 동부, 캄차카, 쿠릴 열도, 사할린, 아무르 하류, 우수리, 만주 등지에서 번식한다. 필리핀, 셀레베스, 몰루카, 뉴기니 서북부 등지에서 월동한다. 한국 전역, 일본, 중국 동부 등지를 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002135', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('341', '솔딱새', 'Muscicapa sibirica', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Muscicapa', '솔딱새속', 'sibirica', '솔딱새', '참새목 솔딱새과에 속하는 조류이다. 수컷 겨울깃의 머리꼭대기는 어두운 흐린 잿빛이다. 눈 앞은 엷은 잿빛이며 누런 갈색을 띤 흰색의 깃털이 있다. 뺨과 귀깃은 흐린 잿빛이다. 턱, 가슴, 옆구리, 배 옆에는 흐린 잿빛의 넓은 세로 얼룩무늬가 있다. 특히 가슴은 전체에 흐린 잿빛에 가깝다. 턱 밑, 멱의 중앙, 아랫배, 아래꽁지덮깃은 흰색이다. 등, 허리, 위꽁지덮깃은 흐린 잿빛이고, 꽁지는 어두운 갈색이다. 여름깃은 봄철 털갈이가 없다. 암컷은 수컷과 구별하기 어렵다. 부리는 검은 갈색으로 아랫부리 기부는 엷은 색이다. 홍채는 갈색이며, 다리는 가늘고 잿빛 쥐색 또는 갈색이다. 아고산대의 숲속에 영소한다. 봄과 가을에 한반도의 전역에 걸쳐 드물지 않게 통과하는 나그네새이다. 단독 또는 암수 함께 주로 나무 위에서 생활하며 땅 위에 내려오는 일은 드물다. 한배에 3~5개 산란한다. 식성은 주로 날아다니는 곤충류, 거미류를 먹는다. 시베리아 남부, 알타이 동부, 몽골 북부, 만주, 아무르, 우수리, 사할린, 일본, 캄차카, 쿠릴 열도 등지에서 번식한다. 한국, 일본, 중국 동·남부, 타이완, 하이난 섬, 인도차이나, 태국 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002136', '13.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('342', '쇠솔딱새', 'Muscicapa dauurica', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Muscicapa', '솔딱새속', 'dauurica', '쇠솔딱새', '참새목 솔딱새과의 조류로 몸길이는 13cm가량이다. 수컷 겨울깃의 눈 앞은 잿빛 흰색이며 눈꺼풀에는 흰색의 깃털이 있고, 흰색 고리 모양이 현저하다. 뺨, 귀깃, 목 옆, 가슴 옆, 옆구리는 갈색을 띤 잿빛이다. 꽁지는 어두운 잿빛 갈색으로 바깥가장자리는 엷은 색이다. 부리는 편평하고 위에서 보면 이등변삼각형을 이룬다. 윗부리와 아랫부리 끝은 검은 갈색이고, 아랫부리의 대부분은 흐린 잿빛 또는 황갈색이다. 홍채는 갈색이고, 다리는 검은 갈색이다. 삼나무, 소나무 숲, 촌락, 인가 부근의 독립수에 영소한다. 봄과 가을에 전국적으로 흔히 통과하는 나그네새이다. 단독 또는 암수가 함께 생활한다. 이동할 때에도 군서 생활을 하지는 않는다. 나무 위의 생활이 주가 된다. 번식 후에는 어린 새를 동반하여 가족군을 형성한다. 5~6월, 한배에 4~5개 산란한다. 폭이 넓은 부리로 날면서 주로 곤충을 잡아먹는다. 구북구 동부, 몽골 북부, 아무르, 우수리, 만주, 사할린, 일본, 쿠릴 열도, 인도, 히말라야, 중국 서부 등지에서 번식한다. 중국 남부, 하이난섬, 인도차이나, 미얀마, 인도, 말레이반도, 필리핀, 보르네오 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002137', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('343', '회색머리딱새', 'Muscicapa ferruginea', 'Hodgson', '1845', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Muscicapa', '솔딱새속', 'ferruginea', '회색머리딱새', '회색머리딱새는 참새목 딱새과에 속하는 조류로, 주로 동부 히말라야, 팟카이, 중국 중부와 남부에 서식한다. 겨울철에는 하이난과 베트남 남부로 이동한다. 이들은 아열대 또는 열대 습윤 산지림에서 자연 서식한다. 보전 상태는 ''관심 필요''로 비교적 안정적인 편이다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002138', '12.75');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('344', '흰눈썹황금새', 'Ficedula zanthopygia', 'Hay', '1845', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Ficedula', '황금새속', 'zanthopygia', '흰눈썹황금새', '전국적으로 번식하는 흔하지 않는 여름철새로 몸길이 13cm이다. 수컷은 몸 윗면이 검은색이다. 눈썹선은 흰색, 몸 아랫면은 선명한 노란색, 아래꼬리덮깃은 흰색이다. 부리는 검은색이며, 다리는 어두운 갈색이다. 암컷의 윗면은 녹회색이며, 아랫면은 흰색에 가까운 옅은 노란색이고, 눈썹선은 짧고 선명하지 않다. 도시의 공원, 정원, 평지와 구릉의 소림이나 활엽수 및 혼효림이 있는 지역에 서식한다. 둥지는 주로 전나무 가지 위에 만들거나 나무 구멍, 인공 둥지 상자를 이용한다. 알 낳는 시기는 5월경이며, 알 5~6개를 낳는다. 곤충이나 거미를 잡아먹는다. 구북구 동부, 만주, 중국 동부에서 번식하고, 말레이 반도, 수마트라 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002140', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('345', '황금새', 'Ficedula narcissina', 'Temminck', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Ficedula', '황금새속', 'narcissina', '황금새', '참새목 솔딱새과의 조류로 몸길이는 11cm쯤이다. 수컷 겨울깃의 이마, 머리꼭대기, 뒷머리 눈 앞, 뺨, 귀깃, 뒷목, 목 옆, 가슴 옆, 등, 어깨깃은 다소 갈색기가 있는 검은색이다. 눈썹선은 선명한 황색이다. 턱 밑과 멱은 등황색 또는 짙은 오렌지색이다. 가슴과 배는 황색이며, 아랫배는 엷은 색이다. 부리는 위에서 보면 이등변삼각형이다. 홍채는 갈색이고, 다리는 어두운 갈색이다. 낙엽활엽수림, 침엽활엽혼효림, 아고산대의 숲에 영소한다. 우리나라 전역에 걸쳐 드물게나마 통과하는 나그네새이다. 단독 또는 암수가 함께 생활하며 번식이 끝나면 가족군으로관목숲 사이나교목의 높은 꼭대기에 앉아 생활한다. 알은 5~7월에, 한배에 4~5개 낳는다. 여름에는 곤충류를 주식으로 하고 거미류도 먹는다. 가을에는 콩과식물의 열매를 즐겨 먹는다. 사할린, 일본 등지에서 번식한다. 인도차이나, 보르네오, 필리핀, 우수리 등지에서 월동한다. 한국, 중국 동부, 일본, 타이완 등지를 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002141', '11.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('346', '노랑딱새', 'Ficedula mugimaki', 'Temminck', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Ficedula', '황금새속', 'mugimaki', '노랑딱새', '봄과 가을에 우리나라 전역을 통과하는 흔한 나그네새이다. 몸길이 13cm이다. 수컷 겨울깃의 턱 밑, 멱, 가슴은 오렌지빛 갈색이다. 배와 아래꼬리덮깃은 흰색이다. 이마, 눈 앞, 뺨, 귀깃, 머리꼭대기, 뒷목, 목 옆, 등, 허리, 위꼬리덮깃은 검은색이나, 깃 가장자리가 잿빛이라 회흑색으로 보인다. 눈 뒤에는 흰색의 눈썹선이 있다. 단독으로 또는 암수가 함께 생활한다. 침엽수 가지 위에 이끼나 마른 잎으로 사발 모양의 둥지를 만든다. 산란기는 6월상순~7월이고, 한배의 산란수는 4~8개이다. 먹이는 곤충류를 주식으로 한다. 구북구 동부, 시베리아 알타이 북동부, 아무르, 사할린, 우수리, 만주 동북부에서 번식하고 중국 남부, 동남아시아에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002142', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('347', '붉은가슴흰꼬리딱새', 'Ficedula parva', 'Bechstein', '1792', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Ficedula', '황금새속', 'parva', '붉은가슴흰꼬리딱새', '참새목 솔딱새과의 조류로 몸길이는 11cm가량이다. 수컷 겨울깃은 이마에서 머리꼭대기와 등을 지나 허리까지는 고른 잿빛 갈색이다. 귀깃과 목 옆은 잿빛 갈색이다. 턱 밑과 멱은 오렌지 황갈색이고, 눈 앞과 뺨에서 가슴과 옆구리로 이어지는 곳은 잿빛이다. 날개는 어두운 갈색으로 각 깃털에는 엷은 색의 폭이 좁은 가장자리가 있다. 눈 주위는 더러운 흰색이다. 암컷 겨울깃의 등 쪽은 수컷 겨울깃과 같으나 다소 갈색기가 많은 경향이 있다. 부리를 위쪽에서 보면 이등변삼각형을 이룬다. 부리는 검은 갈색이고, 홍채는 어두운 갈색이다. 다리는 어두운 갈색으로 가늘다. 숲속에서 번식한다. 한반도의 중부 이북 지역을 봄과 가을에 드물게 통과하는 나그네새이다. 단독 또는 암수가 함께 생활한다. 알은 5월 중순~6월, 연 1회, 한배에 4~7개를 낳는다. 곤충류를 주식으로 하지만장미과의 열매도 먹는다. 시베리아, 알타이, 몽골 북부, 아무르, 우수리에서 번식한다. 중국 남부, 하이난섬, 인도차이나, 인도, 말레이반도 등지에서 월동한다. 한국 중부 이북, 쿠릴 열도, 만주, 중국 북부·중부·동부, 티베트 등지를 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002143', '11.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('348', '큰유리새', 'Cyanoptila cyanomelana', 'Temminck', '1829', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Cyanoptila', '큰유리새속', 'cyanomelana', '큰유리새', '우리나라 전역에서 자주 볼 수 있는 여름철새로 몸길이는 16.5cm다. 수컷은 이마와 머리꼭대기부터 등까지는 광택이 있는 파란색이다. 얼굴과 가슴 옆구리는 검은색, 배는 흰색, 꼬리는 푸른색이다. 암컷은 등은 연한 녹색을 띤 갈색, 가슴과 멱은 회갈색, 배는 흰색이다. 어린 수컷은 암컷과 비슷하나 날개와 허리가 파란색을 띠지만 정도가 다양하다. 둥지는 계곡 근처의 낙엽활엽수림이 있는 절벽 또는 바위 등에 이끼류와 낙엽으로 만들고 흰색 또는 흰색에 가까운 갈색의 알을 3~5개 낳는다. 산란기는 5~7월이다. 한국, 중국, 일본, 러시아에서 여름을 보내고 동남아시아에서 겨울을 보낸다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002145', '16.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('349', '파랑딱새', 'Eumyias thalassinus', 'Swainson', '1838', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Eumyias', '파랑딱새속', 'thalassinus', '파랑딱새', '참새목 솔딱새과의 조류이다. 몸길이는 17cm 정도이다. 몸 색깔은 아랫면이 윗면보다 뚜렷하게 엷은 색이다. 아래꽁지덮깃에 있는 검은 무늬는 뚜렷하고 폭이 매우 넓다. 산림 지대에 서식한다. 나무 사이를 빠르게 날아다니며 곤충을 잡아먹는다. 우리나라에서는 최근에 흑산도와 가거도에서 처음으로 발견된 미조(迷鳥)이다. 히말라야, 중국 남동부, 동남아시아 등지에 분포한다. 수컷의 온몸이 파란색이라서 우리말 이름이 붙여졌다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002147', '17.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('350', '붉은가슴딱새', 'Niltava davidi', 'La Touche', '1907', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Niltava', '붉은가슴딱새속', 'davidi', '붉은가슴딱새', '붉은가슴딱새는 참새목 딱새과에 속하는 조류입니다. 캄보디아, 중국 남부(홍콩 포함), 라오스, 태국, 베트남에 서식합니다. 주로 아열대 또는 열대의 습윤 저지대와 산악림에서 발견됩니다. IUCN 등급은 "관심 대상 종"으로, 멸종 위협이 적은 편에 속합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002149', '16.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('351', '물까마귀', 'Cinclus pallasii', 'Temminck', '1820', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Cinclidae', '물까마귀과', 'Cinclus', '물까마귀속', 'pallasii', '물까마귀', '참새목 물까마귀과에 속하는 조류이다. 몸길이는 22cm이다. 암수의 깃털 색은 거의 비슷하다. 눈꺼풀에 흰색의 가는 털이 섞여 있으나 그 이외의 안쪽 면과 아랫면은 모두 어두운 갈색이다. 배 옆은 녹슨 색이 많다. 등 쪽은 모두 검은 갈색이다. 허리와 위꼬리덮깃은 잿빛이다. 부리는 가늘고 길며 부리 등은 콧구멍 부근에서 조금 오목한 모양으로 굽었다. 홍채는 갈색이다. 다리는 잿빛이며 튼튼하다. 낮은 산지 계곡 물가, 폭포, 계류지에 살며 흔한 텃새이다. 계류의 물가, 흐르는 물속의 돌 사이, 얕은 계류의 물속에 잠수하면서 먹이를 찾고, 단독 또는 암수가 생활한다. 3-6월에 4-5개의 알을 낳는다. 물에 사는 곤충류를 주식으로 한다. 전국에 서식하고 세계적으로는 아프가니스탄, 히말라야, 중국, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002152', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('352', '집참새', 'Passer domesticus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Passeridae', '참새과', 'Passer', '참새속', 'domesticus', '집참새', '집참새는 참새과에 속하는 조류로, 전 세계적으로 널리 분포하고 있습니다. 몸길이는 약 16cm, 무게는 24-39.5g입니다. 수컷은 검은색과 흰색 무늬가 특징적이며, 암컷과 어린 새들은 연한 갈색과 회색을 띱니다. 집참새는 주로 인간 거주지 근처에 서식하며, 도시나 농촌 지역에 쉽게 적응합니다. 집참새는 곡물의 씨앗과 잡초를 먹지만, 기회주의적 잡식성이어서 곤충류도 잡아먹습니다. 이 새는 인간의 생활과 깊이 연관되어 많은 문화적 중요성을 갖지만, 때로는 농업에 해를 끼친다고 여겨지기도 합니다. 최근 일부 지역에서 집참새의 수가 감소하고 있으며, 도심에서는 서식지가 줄어드는 것이 주요 요인으로 여겨집니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002155', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('353', '참새', 'Passer montanus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Passeridae', '참새과', 'Passer', '참새속', 'montanus', '참새', '참새목 참새과에 속하는 조류이다. 몸길이는 14.5cm이다. 몸의 윗면 전체가 밤색이나 머리가 더 진하다. 등은 갈색 바탕에 검은 줄무늬가 있으며 가슴과 배는 흰색, 옆구리는 옅은 황갈색이다. 턱밑과 턱 아래 부위, 눈앞은 검은색이고 뺨은 흰색 바탕에 짙은 검은색 반점이 있고 뒷목은 흰색의 띠로 몸과 경계를 이룬다. 겨울에는 부리 아랫부분에 노란빛이 나타난다. 흔한 텃새이다. 시가지, 인가, 갈대밭, 풀밭과 농경지 등에서 관찰되며 번식기에는 주로 곤충류를 먹지만 비번식기에는 벼나 풀 씨 등 식물성 먹이를 주로 먹는다. 돌 틈, 오래된 나무 구멍, 처마 밑 등에서 번식한다. 둥지는 주로 마른풀을 이용하여 만들고 알은 4~8개 정도 낳는다. 전국에 서식하며 유라시아의 온대와 아열대에 널리 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002157', '14.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('354', '얼룩무늬납부리새', 'Lonchura punctulata', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Estrildidae', '납부리새과', 'Lonchura', '납부리새속', 'punctulata', '얼룩무늬납부리새', '얼룩무늬납부리새는 참새목에 속하는 소형 조류로 주로 아시아 남부와 동남아시아의 열대 초원과 평야에서 서식한다. 몸길이는 약 11~12cm이며, 성체는 어두운 갈색 머리와 짧고 어두운 부리를 가지고 있다. 가슴과 배에는 비늘 모양의 얼룩무늬가 특징이다. 수컷이 암컷보다 더 진한 무늬를 가지고 있다. 이 새는 주로 풀씨와 작은 열매, 곤충을 먹으며, 무리를 지어 생활하며 부드러운 소리로 소통한다. 여름철에 번식을 하며, 둥근 모양의 둥지를 짓는다. 여러 지역에 도입되어 퍼지기도 했으며, 현재 전 세계적으로 ''관심 필요 없음''으로 분류되어 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002160', '11.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('355', '바위종다리', 'Prunella collaris', 'Scopoli', '1769', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Prunellidae', '바위종다리과', 'Prunella', '바위종다리속', 'collaris', '바위종다리', '참새목 바위종다리과에 속하는 조류이다. 몸길이는 18cm이다. 머리와 목은 잿빛 쥐색이다. 등은 잿빛 갈색으로, 깃에는 검은 갈색 무늬가 있다. 가슴은 갈색을 띤 쥐색이다. 아래꼬리덮깃은 어두운 밤색으로, 가장자리는 흰색이다. 배는 흰색 바탕에 적갈색 세로줄 무늬가 많다. 남부 유럽에서 아시아 동부까지 분포한다. 몸길이는 18cm이다. 머리와 목은 잿빛 쥐색이다. 등은 잿빛 갈색으로, 깃에는 검은 갈색 무늬가 있다. 가슴은 갈색을 띤 쥐색이다. 아래꼬리덮깃은 어두운 밤색으로, 가장자리는 흰색이다. 배는 흰색 바탕에 적갈색 세로줄 무늬가 많다. 돌이 많은 산지의 풀밭에 살며 북부 지역에서는 텃새이나 중부 이남 지역에서는 늦가을에 남하하여 월동하는 드문 겨울철새이다. 돌이 많은 산지의 풀밭에서 생활한다. 둥지는 암석 틈에 벼과 식물의 이삭과줄기, 잎 또는 이끼류를 사용해서 밥그릇 모양으로 만든다. 6-7월에 3-4개의 알을 낳는다. 곤충류를 주로 먹지만, 열매나 씨도 먹는다. 북부지방, 경상북도, 경기도에 서식하고 세계적으로는 남부 유럽-아시아 동부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002163', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('356', '멧종다리', 'Prunella montanella', 'pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Prunellidae', '바위종다리과', 'Prunella', '바위종다리속', 'montanella', '멧종다리', '전국적으로 흔한 겨울새이다. 몸길이 15cm이다. 수컷 겨울깃의 머리는 검은색이며 콧등에서 시작하여 정수리를 지나 목덜미에 이르는 굵고 진한 황갈색 눈썹선이 있다. 등은 갈색 바탕에 흑갈색 세로 줄무늬가 있고, 몸 아래는 황갈색이며 옆구리에 밤색 세로 줄무늬가 있다. 산지의 숲이나 농경지에서 서식한다. 보통 단독으로 또는 암수가 함께 생활한다. 나무 밑이나 풀숲에 잔가지와 풀줄기로 밥그릇 모양의 둥지를 만든다. 한배의 산란수는 4~6개이다. 화본과의 씨를 즐겨 먹으며, 여름철에는 곤충류도 즐겨 먹는다. 시베리아 툰드라 일대에서 번식하고 중국 동북 지방, 한국에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002164', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('357', '물레새', 'Dendronanthus indicus', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Dendronanthus', '물레새속', 'indicus', '물레새', '참새목 할미새과에 속하는 조류이다. 몸길이는 16cm이다. 몸 윗면은 연한 녹색을 띤 갈색, 아랫면은 흰색, 가슴에 T자 모양의 검은 띠가 있으며, 날개는 흰색 띠와 검은색 띠 2줄이 서로 교대하고 있다. 윗부리는 어두운 갈색이고, 아랫부리는 살구색이다. 다리는 엷은 갈색이다. 산지 숲, 계곡, 낙엽활엽수림에 살며 전국에서 번식하는 흔치 않은 여름철새이다. 암수 함께 생활할 때가 많다. 5-7월에 4개의 알을 낳는다. 전역에 서식하고 세계적으로는 일본, 사할린, 중국에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002167', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('358', '노랑머리할미새', 'Motacilla citreola', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'citreola', '노랑머리할미새', '전체 길이 16.5cm이고, 날개길이 76~92.5mm, 꼬리길이 67~85mm 정도이다. 등에서 허리까지 몸 윗부분은 대부분 회색이고, 눈에 띄는 흰색 날개선이 두 줄 있다. 머리 전체와 몸 아랫부분은 진한 노란색이고, 목에서 어깨까지는 검은색을 띠고 있다. 비번식기의 수컷은 이마와 눈썹에만 노란색이 남아 있을 뿐이며, 이마 뒤쪽과 뺨은 암컷과 비슷한 회갈색으로 색이 엷어지고, 목에서 어깨까지의 검은 선도 대부분 없어진다. 양어장 근처의 초지에서 갯지렁이나 곤충을 잡아먹는다. 러시아, 서유럽, 오스트레일리아, 일본에서 번식하고 인도, 동남아시아에서 월동한다. 서남아시아에서는 드문 겨울철새 또는 통과조로 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002170', '16.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('359', '노랑할미새', 'Motacilla cinerea', 'Tunstall', '1771', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'cinerea', '노랑할미새', '참새목 할미새과에 속하는 조류이다. 몸길이는 20cm이다. 몸의 윗면은 회색을 띠나, 야외에서는 아랫면의 노란색이 더 눈에 띈다. 부리는 짙은 회색으로 길고, 다리는 분홍색이다. 수컷은 암컷보다 전체적으로 짙은 색을 띠며 아랫면의 노란색도 더 선명하다. 수컷 턱밑과 멱에 있는 검은색 반점은 암컷에는 나타나지 않는다. 다른 할미새류에 비해서 꼬리가 더 긴 편이다. 계곡, 하천, 연못 등 습지 주변에 살며 우리나라에서 흔히 번식하는 여름철새이다. 돌 틈이나 건물 틈에 둥지를 틀며, 가는 줄기와 뿌리로 둥지를 튼다. 땅 위에서 걸을 때는 다른 할미새류와 마찬가지로 꼬리를 까딱까딱 위아래로 흔드는 행동이 특징적이며, 날 때는 물결 모양을 그리며 난다. 나무 꼭대기, 전깃줄, 지붕이나 바위 위에 앉아 운다. 전국에 서식하고 세계적으로는 러시아, 중국, 한반도, 일본, 인도차이나에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002171', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('360', '알락할미새', 'Motacilla alba', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'alba', '알락할미새', '참새목 할미새과의 흔한 새이다. 몸길이는 21cm가량이다. 수컷 겨울깃의 이마, 머리 옆, 목 옆, 턱 밑, 목의 앞부분은 흰색이다. 목 앞의 아랫부분과가슴은 등과 같은 검은색이나 깃 가장자리는 흰색이다. 머리꼭대기에서 위꽁지덮깃까지의 등 쪽은 모두 검은색이다. 날개깃은 갈색을 띤 검은색으로 명확한 흰색이며, 경부의 깃털은 검은 갈색으로 끝은 흰색이다. 부리, 다리, 홍채는 백할미새와 같으나 부리는 한층 가늘다. 도시와 농촌의 인가의 지붕 위나 건물 옥상, 냇가나 하천 또는 농경지와 구릉 등 도처에서 발견된다. 암석이 쌓인 틈, 인가의 건물 틈 사이에 영소한다. 우리나라에 도래하는 여름새 중 첫 도래 아종으로서 3월 초순에는 이미 서울 근교에 도착한다. 쉽게 눈에 띈다. 산란기는 4월하순-5월이다. 거미류와 곤충류를 주식으로 한다. 우리나라 서울 지역에 서식한다. 유라시아 대륙 전역에 걸쳐 번식한다. 남쪽의 온대, 아열대 및 열대 지역에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002172', '21.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('361', '검은등할미새', 'Motacilla grandis', 'Sharpe', '1885', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'grandis', '검은등할미새', '한반도 남부 지역에서는 드물지 않게 월동하는 겨울새이다. 몸길이 21cm이다. 몸 윗면과 가슴, 꼬리는 검은색이다. 이마와 이마에 이어지는 눈썹선, 턱 밑, 아랫가슴, 배, 아래꼬리덮깃은 흰색이다. 암수가 함께 생활하며, 번식 후에는 어린 새를 동반한 작은 무리를 짓는다. 둥지는 물가 돌담이나 풀숲, 지붕 틈새에 튼다. 수생곤충이 많아지는 이른 봄부터 번식에 들어간다. 산란기는 3월 중순~7월이며, 한배의 산란수는 4~6개이다. 주로 갑충, 파리, 나비, 거미, 조개, 가재 따위를 잡아먹는다. 농작물의 낟알도 먹는다. 일본과 우리나라 일부 내륙 지방에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002173', '21.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('362', '큰밭종다리', 'Anthus richardi', 'Vieillot', '1818', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Anthus', '밭종다리속', 'richardi', '큰밭종다리', '참새목 할미새과의 새로 몸길이는 18cm가량이다. 꽁지와 다리가 길다. 수컷 겨울깃의 눈썹선과 귀깃은 황갈색이다. 턱 밑, 멱, 배는 황갈 백색이며, 가슴과 옆구리는 황갈색이고, 턱선 부분과가슴에는 어두운 황갈색으로 다소 선명하지 않은 짧은 세로 얼룩무늬가 있다. 등 쪽은 모두 황갈색이다. 꽁지는 얕은 오목꽁지이다. 날개의 각 깃털도 어두운 갈색으로 어두운 황갈색의 가장자리가 있다. 여름깃은 3~5월에 몸깃 전부, 날개덮깃, 셋째날개깃은 털갈이를 한다. 암컷은 수컷과 깃털 색은 다르지 않으나 조금 작다. 부리는 가늘고 긴 원추형으로 어두운 갈색이고 아랫부리 기반부는 엷은 색이다. 물가, 하천, 소택지 등지에서 가까운 초지의 지상이나 고산 초지에 영소한다. 적은 무리나마 봄과 가을에 규칙적으로 한반도를 통과하는 나그네새이다. 언제나 암수 함께 생활하고, 겨울철은 작은 무리로 있다. 지상의 먹이를 찾을 때가 많다. 알은 5월하순~7월상순, 연 2회, 한배에 4~6개 낳는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002175', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('363', '쇠밭종다리', 'Anthus godlewskii', 'Taczanowski', '1876', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Anthus', '밭종다리속', 'godlewskii', '쇠밭종다리', '참새목 할미새과에 속하는 조류이다. 수컷 겨울깃의 머리꼭대기와 등은 황갈색으로 각 깃털에는 검은색 세로 얼룩무늬가 있고 종다리와 같은 느낌을 준다. 눈 앞부분은 흰색이며, 눈썹선은 황갈색이다. 귀깃과 뺨은 황갈색이다. 뺨과 귀깃 사이에는 어두운 빛의 선이 있다. 등 쪽은 황갈색이나, 멱, 아랫배, 아래꽁지덮깃은 엷은 색이다. 턱선 부분에는 불연속적인 어두운 검은색 선이 있다. 아랫멱과 가슴에는 삼각형의 갈색 얼룩무늬가 있다. 윗부리는 갈색이며 아랫부리는 황색으로 끝만 어두운색이다. 홍채는 갈색이다. 뒷발가락의 발톱은 뒷발가락과 비슷하다. 초원에서 번식한다. 소수가 봄과 가을에 한반도를 통과한다. 단독 또는 암수가 지상 생활을 주로 한다. 돌 밑, 어린 침엽수, 숲의 나무뿌리 근처, 차 바퀴의 깊이 파인 곳 등에 둥지를 튼다. 산란기는 5월하순~7월상순이고, 한배에 4~5개, 때로는 6개 산란한다. 에스파냐에서 몽골에 이르는 유라시아 대륙에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002176', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('364', '풀밭종다리', 'Anthus pratensis', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Anthus', '밭종다리속', 'pratensis', '풀밭종다리', '풀밭종다리는 참새목에 속하는 작은 조류로, 몸길이는 약 14.5~15cm이다. 주로 유라시아 대륙의 북쪽에서 번식하며 남부 유럽, 북아프리카, 남서 아시아 등지에서 겨울을 난다. 외형은 대체로 위쪽이 갈색이고 아래쪽은 담황색이며, 날개와 꼬리에는 어두운 줄무늬가 있다. 얇은 부리와 연한 분홍색 다리를 지니며, 뒷발톱이 길다. "찬겨울새"로 분류되며, 우리나라에서는 보통 겨울에 관찰된다. 풀밭종다리는 주로 풀밭, 황무지, 저강도 농업 지역에서 서식하며, 주로 지상에서 먹이를 찾고 관목이나 울타리, 전선 위에 앉아 주변을 경계한다. 주요 먹이는 작은 곤충과 무척추동물이며, 일부 씨앗과 열매도 섭취한다. 각 해에 두 번의 번식을 하며, 둥지는 지상에 짓고, 타조둥지(뻐꾸기의 기생)를 흔히 볼 수 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002177', '14.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('365', '나무밭종다리', 'Anthus trivialis', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Anthus', '밭종다리속', 'trivialis', '나무밭종다리', '나무밭종다리는 작은 참새목 새로 유럽과 팔레아르크 전역에서 번식하며, 동시베리아 산맥까지 분포한다. 긴 거리의 철새로 겨울에는 아프리카와 남아시아로 이동한다. 이 새는 주로 개방된 삼림과 덤불에서 서식하며, 둥지는 땅에 짓고 보통 4-6개의 알을 낳는다. 곤충을 주로 먹지만 식물의 씨앗도 먹는다. 중형 나무와 낮게 자라는 덤불을 선호하며, 가볍게 또는 중간 정도로 목초지를 방목하는 환경을 선호한다. 나무밭종다리는 나무에 앉아 있을 때가 많고, 소리는 강하며 비행 중 노래는 독특하다. 주요 번식기는 5월부터 8월까지이며, 가을이 되면 다시 사하라 남부로 이동한다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002178', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('366', '힝둥새', 'Anthus hodgsoni', 'Richmond', '1907', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Anthus', '밭종다리속', 'hodgsoni', '힝둥새', '참새목 할미새과의 조류로 몸길이 15.5cm가량이다. 수컷 겨울깃의 등 쪽은 녹갈색으로 선명하지 않은 어두운색의 세로 얼룩무늬가 있다. 머리꼭대기는 황록색을 띤다. 눈썹선은 황갈색이고, 귀깃은 황갈색 및 올리브 갈색이다. 턱 밑, 멱, 가슴은 황갈색이며, 배와 아래꽁지덮깃은 황갈색이고, 배 옆은 황갈색 또는 갈색이다. 이른 봄에서부터 5월경까지 털갈이하여 여름깃이 되지만 새로 생긴 깃털은 겨울깃과 다름이 없다. 암컷과 수컷은 비슷하다. 부리는 가늘고 길며 부리 등은 가운데가 다소 굽고 갈색이며 아랫부리는 엷은 색이다. 홍채는 갈색이다. 잡목림, 초원, 고산 지대의 풀밭, 논 부근의 초지에 서식한다. 중부 이남에서는 봄과 가을에 흔히 통과하는 나그네새이다. 남부지방에서는 겨울에도 떠돌아다니는 것을 간혹 볼 수 있다. 4월과 9~10월의 이동기에는 논과 부근의 초지에서 쉽게 눈에 띈다. 여름철에는 암수가 함께 생활한다. 번식기의 수컷은 일정한 서식 영역이 있으며, 봄과 가을철에는 군서 생활을 할 때가 많다. 지상에서는 먹이를 찾을 때가 많다. 알은 5월 중순~8월, 한배에 3~5개를 낳는다. 일본, 중국 동북부, 몽골, 시베리아, 캄차카, 티베트 등지에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002179', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('367', '흰등밭종다리', 'Anthus gustavi', 'Swinhoe', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Anthus', '밭종다리속', 'gustavi', '흰등밭종다리', '참새목 할미새과의 조류로 몸길이는 14cm가량이다. 수컷 겨울깃의 눈썹선은 명확하지 않다. 귀깃은 갈색으로 어두운색의 세로 얼룩무늬가 있다. 턱 밑 이하 아랫면은 황갈색을 띤 흰색으로 턱선 부분에는 검은 갈색의 작은 얼룩무늬가 있다. 가슴에는 갈색을 띤 검은색의 세로 얼룩무늬가 있다. 몸 옆에는 긴 세로 얼룩무늬가 있다. 등 쪽은 갈색으로 폭이 넓고 명확한 검은 갈색의 세로 얼룩무늬가 있다. 날개의 각 깃털에는 갈색으로 깃 가장자리는 올리브 갈색이다. 여름깃은 겨울깃과 다름이 없다. 암컷은 수컷보다 작고 깃털 색은 같다. 부리는 갈색으로 아랫부리 기부만이 엷은 갈색이다. 습지나 버드나무류가 산재하는 초지의 지상에 영소한다. 한반도 전역에 걸쳐 봄과 가을에 드물지 않게 통과하는 나그네새이다. 알은 6월하순~7월상순, 한배에 5개를 낳는다. 딱정벌레목, 메뚜기목, 파리목 등의 곤충류를 주식으로 먹고, 거미류도 먹는다. 유럽 동부와 시베리아 북부 등지에서 번식한다. 한국, 중국, 일본 등을 통과하여, 동남아시아 열대에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002180', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('368', '한국밭종다리', 'Anthus roseatus', 'Blyth', '1847', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Anthus', '밭종다리속', 'roseatus', '한국밭종다리', '참새목 할미새과의 조류로 몸길이는 15cm 정도이다. 수컷 겨울깃의 눈 앞과 귀깃은 엷은 검은색이며, 귀깃에는 황색을 띤 세로 얼룩무늬가 있다. 눈썹선은 다소 황색과 붉은색을 띤 황갈색이다. 턱 밑, 멱, 가슴은 엷고 붉은 포도색으로 멱과 가슴에는 검은 갈색의 세로 얼룩무늬가 있다. 기타 아랫면은 황색을 띤 흰색으로 배 옆에는 세로 얼룩무늬가 있다. 등 쪽은 올리브 갈색으로 검은 갈색의 세로 얼룩무늬가 있다. 꽁지깃은 어두운 갈색으로 올리브색의 가장자리가 있다. 날개의 각 깃털은 어두운 갈색으로 올리브 황갈색의 가장자리가 있다. 암컷은 수컷과 깃털 색이 거의 같으나 다소 작다. 부리는 뿔빛 검은색으로 가을철에는 아랫부리의 기부가 살구색이 도는 황색을 띤다. 홍채는 어두운 갈색이다. 고산에 서식하는 새이며, 특히 6~7월 번식 시기에는 2,400~4,600m 고지에서만 서식한다. 300m 이하의 지역에서는 드물게 번식한다. 알은 보통 한배에 3~4개, 드물게는 5개까지 낳는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002181', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('369', '붉은가슴밭종다리', 'Anthus cervinus', 'pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Anthus', '밭종다리속', 'cervinus', '붉은가슴밭종다리', '봄과 가을에 우리나라를 드물지 않게 통과하는 나그네새이다. 몸길이 15cm이다. 여름깃은 몸 윗면이 황갈색 바탕에 검은색 세로무늬가 있고, 아랫면은 연한 황백색 바탕에 옆구리에 검은색 줄무늬가 있다. 습지 및 농경지, 해안 부근, 습윤한 초지의 약간 파인 곳에 둥지를 튼다. 여름철에는 암수 함께 생활하며 겨울에는 무리를 짓는다. 산란기는 6~7월이다. 한배의 산란수는 5~6개이다. 주로 동물성인 곤충류를 먹으나 겨울철에는 풀씨를 먹는다. 스칸디나비아 반도, 동아시아 툰드라 지대에서 번식하고 동남아시아에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002182', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('370', '밭종다리', 'Anthus rubescens', 'Tunstall', '1771', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Anthus', '밭종다리속', 'rubescens', '밭종다리', '월동하거나 통과하는 겨울철새이다. 몸은 길이 16cm 정도로서 몸집이 작고 날씬하며, 꼬리와 다리가 길다. 겨울철 몸의 윗면은 갈색이며 줄무늬가 있고, 아랫면은 흰색이며 검은 줄무늬가 있다. 부리 아랫면의 기부는 분홍색을 띠며, 다리는 붉은색을 띠는 살구색이다. 머리와 몸 윗면에 희미한 줄무늬가 있다. 턱선은 뚜렷하다. 눈앞은 주변에 비해 색이 연하다. 논, 초지, 해안, 하천에서 생활하며 무리를 지어 식물 씨앗이나 곤충을 먹는다. 북반구의 고위도 지방에서 번식하고, 겨울에 우리나라를 비롯하여 중국 동남부, 일본, 미국 남부, 멕시코 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002183', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('371', '옅은밭종다리', 'Anthus spinoletta', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Anthus', '밭종다리속', 'spinoletta', '옅은밭종다리', '참새목 할미새과에 속하는 조류이다. 몸길이는 16cm 정도이다. 여름에는 몸의 윗면이 회갈색이며, 턱선과 가슴의 검은색 줄무늬가 약하다. 가슴과 배는 살색을 띠며 윗가슴의 양측에 약간의 검은색 세로줄 무늬가 있다. 겨울깃은 몸의 윗면이 갈색을 띤 녹색이다. 가슴과 배는 흰색이며 턱선과 가슴의 줄무늬는 뚜렷하다. 눈썹선은 희미하다. 유사종 붉은가슴밭종다리는 몸의 윗면에 뚜렷한 검은색 줄무늬가 있어 구별된다. 수확이 끝난 논, 해안 평지, 물이 빠진 못, 갯벌, 하구에 살며 흔한 나그네새이자 겨울철새이다. 여름철에는 암수 함께 생활하고, 이동할 때나 겨울철에는 10마리 내외로 무리를 지을 때가 많다. 둥지 주위는 풀로 덮여 있을 때가 많다. 6-7월에 4~5개의 알을 낳는다. 겨울에는 주로 식물성을 먹지만, 여름에는 곤충류를 먹는다. 거제도, 진도에 서식하고 세계적으로는 유럽, 아시아, 북아메리카의 온대, 툰드라 지대에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002184', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('372', '되새', 'Fringilla montifringilla', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Fringilla', '되새속', 'montifringilla', '되새', '참새목 되새과에 속하는 조류이다. 수컷의 여름깃은 머리와 등은 검은색이다. 날 때 허리는 흰색이고 멱, 가슴, 어깨는 황갈색이며 배는 흰색이다. 날개에는 두 줄의 흰 띠가 있다. 부리는 검은색이다. 수컷의 겨울깃은 머리는 연한 흑갈색에 두 줄의 회색 줄무늬가 있다. 등은 황갈색에 검은색의 반점이 있다. 부리는 연한 노란색이다. 암컷은 수컷의 겨울깃과 비슷하다. 머리는 연한 갈색이고 멱, 가슴, 어깨는 황갈색인데 수컷보다 연하다. 황갈색의 등에는 수컷과 같은 검은색의 반점이 있다. 자작나무, 전나무, 소나무 숲에 살며 흔한 겨울철새이다. 5-6월 6-7개의 알을 낳는다. 주로 식물성을 먹으며, 여름철에는 곤충류의 성충과 유충을 먹는다. 전국에 서식하고 세계적으로는 구북구 전역에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002187', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('373', '갈색양진이', 'Leucosticte arctoa', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Leucosticte', '갈색양진이속', 'arctoa', '갈색양진이', '수컷 겨울깃의 깃털은 몸의 대부분에서 잿빛이나 뒷머리, 뒷목의 깃털 끝부분은 엷은 갈색이다. 등의 깃털은 어두운 갈색으로 끝이 황갈색이고, 허리의 깃털은 어두운 갈색으로 끝이 장미색이다. 배의 깃털 끝부분, 아래꼬리덮깃의 깃털 끝부분도 장미색이다. 꼬리깃은 검은 갈색이다. 다리는 검은색이다. 암컷은 수컷보다 색이 연하며 배 부분과허리는 은회색에 연한 푸른색을 띤다. 산지의 벼랑, 비탈, 하천 부지에서 서식한다. 겨울에는 10마리에서 수백 마리의 큰 무리로 생활한다. 화본과, 여뀌과, 질경이과, 콩과의 종자 등을 먹는다. 시베리아 동부 산악 지대, 쿠릴 열도 북부, 홋카이도 등지에서 번식하고, 사할린, 쿠릴 열도, 일본 북부와 중부, 우수리, 한국 북부 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002194', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('374', '긴꼬리홍양진이', 'Uragus sibiricus', 'Pallas', '1773', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Uragus', '홍양진이속', 'sibiricus', '긴꼬리홍양진이', '우리나라 북부 지역에서 흔히 번식하는 텃새이나 중부 이남에서는 불규칙적으로 도래하는 겨울새이다. 몸길이 15cm이다. 수컷은 전체적으로 붉은색을 띠며 깃 가장자리는 흰색 또는 잿빛 흰색이다. 날개는 검은 갈색이다. 암컷은 전체적으로 갈색으로 검은색 세로 줄무늬가 있다. 낮은 산지의 숲 가장자리, 평지의 초원, 강가 버드나무 숲에서 서식한다. 여름에는 암수가 함께 생활하나 겨울철에는 흔히 작은 무리를 이룬다. 바닷가 또는 평지의관목림이나 강가의 버드나무 숲에 둥지를 만든다. 산란기는 5월하순~7월하순이다. 한배의 산란수는 3~4개이다. 여름에는 작은 곤충류를 먹이로 하나, 가을과 겨울에는 주로 식물성을 먹는다. 우수리, 만주, 한국 북부에서 번식하고 한국 중부 이남, 중국 북부에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002196', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('375', '붉은양진이', 'Carpodacus erythrinus', 'Pallas', '1770', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Carpodacus', '양진이속', 'erythrinus', '붉은양진이', '참새목 되새과의 새로 몸길이는 14cm가량이다. 수컷 겨울깃의 이마에서 뒷머리까지는 진한 다홍색으로 폭이 좁은 갈색의 가장자리가 있고, 눈 앞은 갈색이며, 귀깃은 붉은 갈색이다. 뺨, 멱, 윗가슴은 선명한 붉은 장미색이며, 가슴 이하는 장미색으로 아래쪽일수록 엷어져서 아래꽁지덮깃은 거의 더러운 흰색이다. 여름깃은 마모에 의해서 등 쪽은 모두 어두운 갈색을 띠고 또 아랫면의 바탕색은 거의 더러운 흰색으로 된다. 부리는 굵고 조금 둥근 모양을 한 원추형이며 잿빛 갈색으로 아랫부리는 엷은 색이다. 홍채와 다리는 갈색이다. 한국의 북부 고산 지대에서 작은 무리가 번식하나, 중부 이남에서는 봄과 가을에 작은 무리가 통과하는 드문 나그네새이다. 습지, 물가, 언덕에 있는관목속, 작은 나무 숲속의 땅 위에 흔히 영소하고, 버드나무 나뭇가지 위에 영소하는 경우도 있다. 산란기는 5월하순~7월이고, 연 1회 번식하며, 한배의 산란수는 4~6개이다. 식성은 주로 식물성을 먹는다. 동물성으로는 딱정벌레목을 먹는다. 시베리아 동부, 오호츠크 해 연안, 러시아 알타이, 몽골 북부, 아무르, 우수리, 사할린 북부 등지에서 번식한다. 사할린 중부·동부 등지에서 월동한다. 일본, 한국 중부 이남을 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002198', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('376', '양진이', 'Carpodacus roseus', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Carpodacus', '양진이속', 'roseus', '양진이', '참새목 되새과의 조류이다. 몸길이는 17.5cm가량이다. 수컷 겨울깃의 눈 앞과 윗부리 주위는 어두운 붉은색이다. 이마와 머리꼭대기의 깃털은 비늘 모양이며 잿빛 은색으로 뒷머리에서 점차로 뒷목의 어두운 붉은색으로 옮아간다. 등의 깃털은 붉은색으로 폭이 좁은 검은색 축반이 있고 깃 가장자리는 엷은 황갈색이다. 멱의 깃털은 뾰족한 비늘 조각 모양으로 붉은색을 띤 은색이고, 목 옆과 가슴 이하의 아랫면은 모두 붉은 장미색이다. 날개깃은 전부 검은 갈색이다. 부리는 적원자보다 만곡이 적으며 원추형에 가깝고 어두운 갈색이다. 홍채는 갈색빛을 띤 밤색이고, 다리는 어두운 갈색이다. 구릉, 경지 주변의 혼효림, 낙엽활엽수림, 침엽수림 등 도처의 숲에서 쉽게 찾아볼 수 있다. 우리나라의 전역에 도래 월동하는 흔한 겨울새이다. 가을철에는 작은 무리 또는 큰 무리로 도래하여 숲 사이의 풀 속이나관목숲속의 땅 위를 뛰어다닌다. 겨울철에는 주로 식물성을 섭취한다. 곤충류의 딱정벌레목도 잡아먹는다. 시베리아 동부, 사할린 등지에서 번식하고 우리나라 전역에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002199', '17.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('377', '솔양진이', 'Pinicola enucleator', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Pinicola', '솔양진이속', 'enucleator', '솔양진이', '참새목 되새과에 속하는 조류이다. 날개의 길이는 10cm가량이다. 몸은 불룩하고, 꽁지는 길다. 수컷은 장밋빛을 띤 붉은색이다. 색깔과 3개의 흰 줄무늬가 있는 것이 근연종과 비슷하지만, 부리의 특징으로 구분할 수 있다. 새끼와 암컷은 수컷보다 훨씬 회색을 띠고, 머리, 둔부, 몸 아랫면은 엷은 황갈색을 띤다. 소나무 숲을 선호한다. 해바라기 씨를 좋아한다. 단풍나무, 자작나무, 느릅나무의 싹을 먹기도 한다. 길이 잘 들고, 수동적인 새이다. 비슷한 크기의 새들보다 먹는 데에 훨씬 오랜 시간을 보낸다. 그 이유는 목에 작은 주머니가 있어서 좋은 먹이를 발견하면 주머니에 채워 넣기 때문이다. 아마도 이것이 아무것도 하지 않으면서 오랜 시간 동안 있는 이유도 될 것이다. 세계적으로는 아시아 북부, 유럽 북부, 북아메리카 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002201', '22.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('378', '솔잣새', 'Loxia curvirostra', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Loxia', '솔잣새속', 'curvirostra', '솔잣새', '참새목 되새과에 속하는 조류이다. 수컷 겨울깃의 부리털은 갈색이고, 이마에서 뒷목까지는 어두운 붉은색이다. 등은 갈색을 띤 어두운 붉은색이다. 눈 앞, 눈선, 귀깃의 윗부분은 엷은 갈색이고, 아랫면은 모두 둔한 붉은색이다. 배의 중앙과 아래꽁지덮깃은 흰색으로 아래꽁지덮깃에는 어두운 갈색의 축반이 있다. 꼬리는 어두운 갈색으로 누런 가장자리가 있다. 날개는 어두운 갈색이나 작은날개덮깃은 붉은 갈색을 띠고 있다. 부리는 어두운 갈색으로 아랫부리 옆은 엷은 색이다. 홍채는 갈색이고, 다리는 어두운 갈색이다. 흔히 송림 가장자리에 서식한다. 우리나라 전역에 흔하게 월동하는 겨울새이다. 번식기 이외에는 군서 생활을 한다. 땅 위에 잘 내리지 않으며 주로 나무 위에서 생활한다. 둥지는 지상에서 5m 이상 되는교목의 가지에 튼다. 3~4월에 한배에 3~5개, 보통 4개 산란한다. 식성은 소나무과의 종자를 즐겨 먹으며장미과, 국화과의 종자도 먹고 기타 나무의 눈을 먹는다. 동물성으로는 나비목의 유충, 딱정벌레목, 파리목 등의 곤충을 먹는다. 아무르, 우수리, 일본, 중국 북부, 사할린, 쿠릴 열도 등지에서 번식한다. 한국, 일본 남부, 중국 동부 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002203', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('379', '흰죽지솔잣새', 'Loxia leucoptera', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Loxia', '솔잣새속', 'leucoptera', '흰죽지솔잣새', '참새목 되새과의 조류로 몸길이는 15cm가량이다. 수컷 겨울깃은 솔잣새와 흡사하다. 큰날개덮깃과 가운데날개덮깃의 끝에 5~15mm의 흰색 부분이 있어 날개에 2줄의 흰색 선으로 나타나기 때문에 쉽게 구별된다. 이외에도 바깥꽁지깃과 날개깃 끝에도 흰색의 가는 가장자리가 있다. 몸깃의 붉은색은 솔잣새보다 엷은 장밋빛을 띤다. 암컷의 날개에는 수컷과 같은 2줄의 흰색 띠가 있다. 그 외는 솔잣새 암컷과 아주 닮았으나 대체로 솔잣새의 암컷보다 바탕색이 엷고 또한 등과 아랫면의 세로무늬는 명확하다. 부리는 어두운 갈색으로 아랫부리 옆은 엷은 색이다. 홍채와 다리는 어두운 갈색이다. 솔잣새와 흡사하게 소나무 숲에 서식한다. 침엽수림에 영소하며, 둥지는 솔잣새의 것과 비슷하다. 알은 3월하순~6월하순, 한배에 3~4개, 드물게는 5개를 낳는다. 스웨덴 북부, 시베리아 동부, 아무르, 만주 북부 등지에서 번식한다. 한국, 일본, 중국 북부, 만주, 우수리, 사할린, 유럽 서부·중부·동부 지역 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002204', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('380', '멋쟁이새', 'Pyrrhula pyrrhula', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Pyrrhula', '멋쟁이새속', 'pyrrhula', '멋쟁이새', '우리나라 전역에 걸쳐 불규칙적으로 도래하는 흔하지 않은 겨울새이다. 몸길이 15cm이다. 머리는 검은색, 뺨과 멱, 윗가슴은 붉은색, 등은 회색, 배는 장밋빛을 띤 옅은 회색, 윗날개 끝과 꼬리는 검은색이다. 여름철에는 암수가 함께 생활하고, 가을철과 겨울철에는 작은 무리를 이룬다. 나무 위에서 주로 생활한다.관목가지에 나무껍질, 풀뿌리 등으로 컵 모양 둥지를 만든다. 산란기는 5~7월이고, 연 2회 번식하며, 한배의 산란수는 4~6개이다. 식물의 씨앗이나 어린잎, 곤충 등을 먹는다. 아무르강 하류, 우수리, 사할린 등지에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002206', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('381', '콩새', 'Coccothraustes coccothraustes', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Coccothraustes', '콩새속', 'coccothraustes', '콩새', '참새목 되새과에 속하는 조류이다. 몸길이는 18cm 정도이며, 부리가 두껍고 목이 굵다. 꼬리는 검은색이나 끝은 흰색이다. 몸깃은 분홍색을 띤 갈색이며 날개는 청흑색, 윗목은 회색이다. 부리는 봄에는 금속 청색, 겨울에는 청백색을 띠며 부리 기부에는 약간의 검은색 부분이 있다. 숲 또는 나무가 있는 정원에 살며 흔한 겨울철새이다. 무리 지어 다니며 각종 낙엽활엽수의 종자를 따 먹으며 월동하고 번식기에는 곤충류를 먹는다. 5-6월에 3-6개의 알을 낳는다. 전국에 서식하고 세계적으로는 유라시아 대륙에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002208', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('382', '밀화부리', 'Eophona migratoria', 'Hartert', '1903', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Eophona', '밀화부리속', 'migratoria', '밀화부리', '참새목 되새과에 속하는 조류이다. 몸길이는 17cm이다. 부리는 엷은 주황색이며 끝이 검은색이다. 수컷의 머리와 뺨은 광택이 있는 검은색이며, 목뒤와 등은 회갈색이고 날개는 광택이 있는 검은색이다. 날개깃 끝과 첫째날개덮깃은 흰색이다. 가슴과 배는 담황색, 허리는 엷은 회색, 꼬리는 검은색이다. 암컷의 머리와 등은 회갈색이다. 흔한 여름철새로 공원이나 산림의교목에 만들며 비교적 높지 않은 가지 위에 밥그릇 모양의 둥지를 튼다. 옅은 푸른색에 어두운 갈색 반점이 있는 알을 3-5개 낳는다. 먹이는교목과관목의 다양한 씨앗과 열매를 주로 먹으며, 곤충을 먹기도 한다. 전국에서 관찰되고 세계적으로는 중국, 홍콩, 일본, 라오스, 미얀마, 러시아, 타이완, 태국, 베트남에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002210', '17.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('383', '큰부리밀화부리', 'Eophona personata', 'Temminck & Schlegel', '1848', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Eophona', '밀화부리속', 'personata', '큰부리밀화부리', '참새목 되새과의 새로 몸길이는 23cm가량이다. 제주밀화부리보다 몸집이 크며 특히 부리가 크고 높으며 부리 등이 굽어져 있다. 또한 등은 순잿빛이며 날개의 흰색 무늬는 다소 폭이 좁다. 산지, 숲속에서 번식한다. 평원에서 월동한다. 우리나라 북부지방 일부 지역에서는 번식도 하는, 드물지 않은 여름새이다. 여름에는 노거수가 우거진 강가의 숲에서 떼를 지어 다닌다. 무거운 몸짓으로 힘차게 직선으로 난다. 우리나라 북부에 서식하고, 우수리, 만주 등지에서 번식한다. 만주, 중국 동부 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002211', '23.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('384', '노랑멧새', 'Emberiza citrinella', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'citrinella', '노랑멧새', '노랑멧새는 참새목 멧새과의 조류로 유라시아에 서식하며, 뉴질랜드와 호주에 도입된 적이 있습니다. 주로 유라시아의 열린 지역에 서식하며, 일부는 남쪽으로 이동합니다. 수컷은 밝은 노란색 머리와 갈색의 줄무늬가 있는 등이 특징입니다. 번식기는 4월에서 5월이며 암컷은 땅 가까이 둥지를 만들고 3~5개의 알을 낳습니다. 주로 씨앗을 먹고 번식기에는 무척추동물도 섭취합니다. 유럽에서는 농업 변화로 인해 개체 수가 감소하고 있지만, 아직은 ''관심 필요'' 등급입니다. 노랑멧새는 시와 음악에서도 영감을 준 새로, 그 노래는 지역적인 변화를 보입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002214', '16.2');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('385', '흰머리멧새', 'Emberiza leucocephalos', 'S.G.Gmelin', '1771', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'leucocephalos', '흰머리멧새', '보기 드문 겨울철새로 몸길이는 17cm 정도다. 수컷은 여름에 머리꼭대기와 뺨선이 흰색, 눈썹선과 멱은 밤색, 배는 흰색이며 가슴을 가로지르는 밤색 줄무늬가 있다. 겨울에는 이마와 머리꼭대기는 갈색으로 흰색 줄무늬가 있으며 밤색은 거의 없다. 암컷은 머리가 회갈색이며 검은색의 줄무늬가 있다. 가슴은 연한 갈색으로 검은색을 띤 줄무늬가 있다. 유럽 동부 및 동아시아의 북위 50~67° 지역에서 번식하고, 몽골·중국(북부)·우수리(남부)·중앙아시아·인도(북부)·이라크 등지에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002215', '17.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('386', '멧새', 'Emberiza cioides', 'Brandt', '1843', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'cioides', '멧새', '참새목 멧새과에 속하는 조류이다. 흔한 텃새이다. 주로 저지대의관목림이나 농경지, 초지 등의 개활지에서 서식하기 때문에 인가 주변에서 종종 관찰된다. 멧새는 언뜻 보면 크기나 색깔이 참새와 비슷하지만, 자세히 보면 참새보다 꼬리가 길고, 흰색의 눈썹선과 꼬리 양쪽의 흰색 깃이 눈에 띈다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002216', '15.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('387', '점박이멧새', 'Emberiza jankowskii', 'Taczanowski', '1888', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'jankowskii', '점박이멧새', '참새목 멧새과의 조류이다. 배에 반점이 있다. 탁 트인 지대의 작은 둔덕, 산기슭의떨기나무숲, 낮은 산간 계곡, 강기슭의 풀숲에 산다. 4월에 날아와 5월하순에 산란한다. 우리나라 두만강 유역에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002217', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('388', '회색머리멧새', 'Emberiza hortulana', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'hortulana', '회색머리멧새', '회색머리멧새는 유라시아에 서식하는 조류로, 멧새과에 속한다. 몸길이는 약 16~17cm이고, 날개 길이는 23~29cm이다. 이 종은 주로 농경지와 그 주변에서 서식하며, 유럽 전역과 서아시아, 스칸디나비아 북부까지 분포한다. 회색머리멧새는 씨앗을 주로 먹고, 번식기에는 딱정벌레와 같은 곤충을 먹여 새끼를 기른다. 프랑스에서는 이러한 새를 요리로 먹는 것이 전통이었지만, 남획으로 인해 법적 보호를 받고 있다. IUCN에 따르면 회색머리멧새는 ''관심 필요''로 분류되어 있으나, 일부 연구에서는 개체 수 감소가 우려되고 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002218', '16.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('389', '흰배멧새', 'Emberiza tristrami', 'Swinhoe', '1870', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'tristrami', '흰배멧새', '참새목 멧새과의 조류로 몸길이는 13~14cm쯤이다. 수컷 겨울깃의 머리 중앙선은 황갈색을 띤다. 머리 옆, 턱 밑, 멱에는 갈색을 띤 황갈색의 깃 가장자리가 있다. 여름깃은 봄철 털갈이 유무가 불명확하다. 머리꼭대기는 검은색이고, 명확한 흰색의 머리 중앙선이 있다. 뒷머리와 등은 올리브 갈색이고, 등에 검은 갈색의 축반이 있다. 눈썹선과 턱선은 지저분한 흰색이다. 가슴은 황갈색이며, 겨드랑이깃은 어두운 황갈색으로 어두운색의 명확하지 못한 세로무늬가 있다. 암컷 겨울깃과 불명확한 여름깃의 머리꼭대기는 검은 갈색이고 황갈색의 머리 중앙선이 있다. 턱 밑과 멱은 엷은 황갈색이다. 홍채는 갈색이고, 다리는 살구색이다. 숲속에서 서식한다. 봄과 가을에 한반도 전역을 통과하는 흔한 나그네새이다. 숲속의 땅 위에서 먹이를 찾을 때가 많으나 나무 위에서 먹이를 찾을 때도 있다. 알은 6월경 한배에 3개 정도 산란한다. 곤충류와 잡초류의 씨를 즐겨 먹는다. 구북구 동부, 아무르, 우수리, 만주 북·동부 등지에서 번식한다. 중국 남부, 윈난 지방, 일본, 미얀마 등지에서 월동한다. 한국, 만주, 중국 북부 등지를 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002219', '13.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('390', '붉은뺨멧새', 'Emberiza fucata', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'fucata', '붉은뺨멧새', '참새목 멧새과에 속하는 조류이다. 수컷은 머리에서 뒷목까지 회색이며 검은색 줄무늬가 있다. 뺨은 적갈색이다. 멱은 흰색이며 가슴에는 검은색과 밤색 띠 2개가 있다. 나머지 배는 흰색이다. 암컷은 전체적으로 색이 흐리고, 가슴의 띠도 뚜렷하지 않다. 숲, 개활지, 농경지 등지에 살며 가는 줄기와 뿌리, 칡 등으로 밥그릇 모양의 둥지를 만든다. 엷은 푸른색에 갈색 반점이 있는 알을 3-6개 낳는다. 우리나라 전 지역에서 번식하는 흔한 여름철새이며 적은 수가 겨울철에 보이기도 한다. 전국에 서식하고 세계적으로는 중국, 일본, 동남아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002220', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('391', '쇠붉은뺨멧새', 'Emberiza pusilla', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'pusilla', '쇠붉은뺨멧새', '참새목 멧새과의 조류이다. 수컷 겨울깃의 머리꼭대기는 검은색으로 폭넓은 밤색의 머리 중앙선이 있고 각 깃털은 황갈색의 깃 가장자리가 있다. 눈 앞, 눈썹선, 귀깃은 붉은 밤색이다. 턱 밑과 멱은 황갈색을 띠는 백색으로 불완전한 검은색의 턱선이 있다. 귀깃의 아래쪽과 뒤에도 검은색의 얼룩점이 있다. 가슴과 옆구리는 황갈색으로 검은색의 짧은 세로무늬가 많다. 배와 아래꽁지덮깃은 흰색이다. 부리는 쑥새와 비슷하고, 홍채는 어두운 갈색이고, 다리는 갈색이다. 습지의 버드나무 소림이나 숲 사이의 공터 또는 툰드라 지대의 버드나무관목이 산재하는 초원에서 영소(營巢)한다. 봄과 가을에 작은 무리들이 한반도를 통과하는 나그네새이자 일부는 우리나라 남부 지역에서 월동도 하는 비교적 드문 겨울새이다. 여름에는 암수가 함께 생활하며 겨울에는 크고 작은 무리를 이룬다. 6월 중순~7월, 한배에 4~5개, 드물게는 6개 산란한다. 딱정벌레목 곤충류나 잡초의 씨를 먹는다. 구북구 동부, 러시아 북부, 바이칼호, 야쿠츠크 남부 등지에 분포한다. 한국 남부, 중국 동부 윈난성, 인도차이나, 히말라야 동부, 네팔 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002221', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('392', '노랑눈썹멧새', 'Emberiza chrysophrys', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'chrysophrys', '노랑눈썹멧새', '흔치 않은 나그네새로 5월과 9~11월에 작은 무리들이 한반도를 지나간다. 몸길이 14cm이다. 수컷의 여름깃은 머리는 검은색, 눈썹선은 노란색, 턱 밑은 흰색, 등은 흑갈색 세로 줄무늬가 있는 갈색이다. 배는 흰색, 옆구리는 옅은 갈색이며 가슴에서 옆구리로 갈색 줄무늬가 나 있다. 암컷은 수컷보다 색깔이 옅다.관목림, 덤불, 잡목림에서 서식한다. 봄과 가을의 이동 시기에는 농경지나 개활지의관목이나 덤불 또는 잡목림에서 눈에 띈다. 곤충이나 식물 씨앗을 먹는다. 구북구 동부, 바이칼, 스타노보이 산맥 등지에서 번식하고 중국 남부에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002222', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('393', '쑥새', 'Emberiza rustica', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'rustica', '쑥새', '참새목 멧새과에 속하는 조류이다. 몸길이는 약 15cm다. 몸의 윗면은 붉은빛이 도는 갈색 바탕에 검은 세로무늬가 있고 아랫면은 잿빛을 띤 흰색 바탕에 드문드문 갈색 세로무늬가 있다. 눈썹선과 멱은 흰색이며 가슴을 가로질러 갈색 띠가 지난다. 여름깃은 머리가 검고 겨울깃은 머리가 갈색이다. 겨울깃의 빛깔이 더 연하다. 암컷은 머리꼭대기와 뺨의 색이 탁하며, 아랫면의 줄무늬도 더 연하다. 경지 주변과 구릉, 소림, 낙엽활엽수, 침엽수, 혼효림에 살며 흔한 겨울철새이다. 5-7월에 4-6개의 알을 낳는다. 겨울에는 주로 풀씨를 먹고 여름에는 곤충의 유충과 성충을 먹는다. 전국에 서식하고 세계적으로는 캄차카, 일본, 중국 동부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002223', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('394', '노랑턱멧새', 'Emberiza elegans', 'Temminck', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'elegans', '노랑턱멧새', '참새목 멧새과에 속하는 조류이다. 몸길이는 16cm이다. 몸 전체가 갈색이고 부리가 도톰하고 짧으며 바깥꼬리깃이 흰색인 멧새류의 특징을 가진다. 수컷은 턱밑과 눈 위부터 뒷머리가 짙은 노란색을 띠며, 대조적으로 눈선과 윗가슴은 검은색을 띤다. 얼굴 부분이 노란색과 검은색으로 알록달록하게 보인다. 암컷은 윗가슴과 눈선의 검은색이 없고, 노란색 부분이 흐릿하다. 머리 중앙의 깃털은 가끔 뿔처럼 세워지기도 한다. 산림, 경작지, 습지 주변의관목및 덤불에 살며 텃새이다. 산란기는 5월이다. 세력권을 형성하는 번식 시기에는 분산되어 분포하지만 번식이 끝난 후에는 수십 마리까지도 무리를 지어 생활한다. 보통 나무꼭대기나 전깃줄 등 노출된 곳에서 울기 때문에 울음소리가 나는 곳을 확인하면 쉽게 관찰할 수 있다. 전국에 서식하고 세계적으로는 연해주, 중국, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002224', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('395', '검은머리촉새', 'Emberiza aureola', 'Pallas', '1773', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'aureola', '검은머리촉새', '검은머리촉새는 「멸종위기 야생생물 Ⅱ급」의 멧새과 조류이며 봄과 가을에 우리나라를 지나는 드문 나그네새이다. 기온변화와 각종 개발로 서식지가 파괴되고, 식용 등의 목적으로 대규모로 포획되어 개체 수 감소에 심각한 영향을 미치고 있다. 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002225', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('396', '꼬까참새', 'Emberiza rutila', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'rutila', '꼬까참새', '봄과 가을에 우리나라를 지나가는 나그네새이다. 몸길이 14cm이다. 수컷은 겨울깃의 머리에서 위꼬리덮깃까지의 등 쪽 전부와 멱, 윗가슴은 적갈색이다. 배는 노란색이다. 옆구리는 노란색이며 흑갈색 줄무늬가 있다. 암컷은 머리와 윗면이 갈색이며 검은색 줄무늬가 많다. 허리에 적갈색 띠가 넓게 보이고 배는 노란색에 갈색 줄무늬가 있다. 강변 갈대숲이나 산기슭관목숲, 아카시아 숲, 과수원, 논밭 등지에 서식한다. 초지나관목숲의 땅 위에 밥그릇 모양 둥지를 만든다. 산란기는 5~6월상순이고, 한배의 산란수는 3~4개이다. 화본과 식물과 같은 잡초의 종자와 곤충을 먹이로 한다. 바이칼호, 오호츠크해 연안, 아무르, 시베리아 남부, 만주 북부, 우수리에서 번식하고 중국 남부와 동남아시아에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002226', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('397', '검은머리멧새', 'Emberiza melanocephala', 'Scopoli', '1769', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'melanocephala', '검은머리멧새', '검은머리멧새는 참새목 멧새과에 속하는 조류로 몸길이는 약 15cm이다. 성체 수컷은 밝은 노란색의 하체와 밤색 등의 상체, 검은 머리를 특징으로 한다. 암컷은 수컷보다 색이 옅다. 이 새는 주로 여름에 동남부 유럽에서 이란까지의 개활지와 농경지에서 번식하며, 겨울에는 주로 인도로 이동한다. 큰 무리를 지어 곡물과 씨앗을 찾는데, 둥지는 낮은 덤불이나 땅 위에 지어지며 건조한 풀로 만든 컵 모양을 하고 있다. 알은 한 번에 4~6개를 낳아 약 13일 후 부화하며, 새끼는 약 10일 후 둥지를 떠난다. 주로 곤충과 씨앗을 먹으며, IUCN에서 "관심 필요 없음" 등급으로 평가받고 있다. 한국에서는 매우 드문 겨울철새로 간혹 관찰되기도 한다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002227', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('398', '붉은머리멧새', 'Emberiza bruniceps', 'Brandt', '1841', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'bruniceps', '붉은머리멧새', '아시아, 인도에 분포하며,관목림, 농경지 주변에 서식한다. 몸길이는 17cm이다. 수컷의 복부는 노란색, 등은 녹색을 띠며, 머리와 가슴은 적갈색이다. 암컷은 수컷에 비해 비교적 엷은 색을 띠고 등은 회갈색, 머리는 갈색이다. 둥지는 나무나관목림 내 덤불 속에 만들며, 3~5개의 알을 낳는다. 먹이는 주로 식물의 씨앗을 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002228', '17.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('399', '무당새', 'Emberiza sulphurata', 'Temminck & Schlegel', '1848', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'sulphurata', '무당새', '무당새는 「멸종위기 야생생물 Ⅱ급」의 멧새과 조류이며 우리나라에서는 봄과 가을에 서남해안을 지나가는 나그네새이다. 등은 잿빛 녹색, 또는 갈색을 띤 녹색으로 검은색의 축반이 있다. 곤충류와 씨앗을 즐겨 먹는다. 과도한 살충제 사용, 불법 포획 등이 개체의 위협요인이 되고 있으며, 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002229', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('400', '촉새', 'Emberiza spodocephala', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'spodocephala', '촉새', '참새목 멧새과에 속하는 조류이다. 몸길이는 16cm 정도이다. 수컷의 겨울깃은 이마에서 뒷목까지가 녹회색이다. 깃 끝은 약간 갈색을 띤다. 어깨 사이는 황갈색이다. 눈 앞, 눈 밑, 턱 밑은 그을린 검은색이다. 배와 옆구리는 올리브 갈색을 띤 어두운 갈색의 세로무늬가 있다. 윗부리는 어두운 뿔색이고, 아랫부리는 엷은 갈색이며 끝만 어두운 색이다. 홍채는 어두운 갈색이고, 다리는 엷은 갈색이다. 습한 풀밭에 살며 습한 풀밭에서 번식한다. 우리나라에서 일부 번식하나 대부분 봄과 가을에 한반도를 통과하는 흔한 나그네새이다. 6월경에 보통 5개의 알을 낳는다. 잡초 씨, 조와 벼 등의 낟알, 곤충의 성충과 유충 등을 섭식한다. 전국에 서식하고 세계적으로는 시베리아 남부, 러시안 알타이, 트란스바이칼리아, 아무르, 우수리, 만주에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002230', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('401', '검은멧새', 'Emberiza variabilis', 'Temminck', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'variabilis', '검은멧새', '우리나라에서는 남해 도서에 나타나는 미조이다. 수컷 겨울깃의 머리부터 위꼬리덮깃까지의 몸 윗면은 대체로 슬레이트 잿빛으로 깃털 끝은 약한 올리브색을 띤다. 머리 옆과 아랫면 전체는 등 쪽보다 다소 엷은 석판 잿빛이고, 머리 중앙은 거의 흰색이다. 등은 석판 잿빛으로 명확한 검은색 축반과 올리브 황갈색의 깃 가장자리가 있다. 날개깃과 날개덮깃은 검은 갈색이다. 아고산대 산지, 조릿대 숲에서 서식한다. 여름철에는 암수가 함께 생활하며 일정한 세력권을 갖는다. 산란기는 6월경이고, 한배의 산란수는 5개이다. 식물로는 주로 화본과와 꿀풀과의 씨를 먹고, 동물로는 곤충류를 먹는다. 구북구 동부, 캄차카 등 아한대 지역에서 번식하고 혼슈 중부, 류큐 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002231', '16.75');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('402', '북방검은머리쑥새', 'Emberiza pallasi', 'Cabanis', '1851', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'pallasi', '북방검은머리쑥새', '우리나라 전역에 도래하는 겨울새이다. 몸길이 16cm이다. 수컷 겨울깃의 머리, 목덜미, 등은 황갈색으로 보인다. 눈 앞과 눈 주위는 엷은 갈색이고 폭넓은 뺨선은 황갈백색이다. 허리와 위꼬리덮깃, 배는 황갈백색이다. 암컷은 수컷보다 연한 색이고 갈색 머리에 흰 눈썹선과 뺨선이 있다. 등에는 검은색 세로 줄무늬가 있다. 못가나 습지, 갈대밭, 풀밭에 서식한다. 시베리아에서 번식하고 한국, 만주, 중국 남부, 일본 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002232', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('403', '쇠검은머리쑥새', 'Emberiza yessoensis', 'Swinhoe', '1874', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'yessoensis', '쇠검은머리쑥새', '쇠검은머리쑥새는 「멸종위기 야생생물 Ⅱ급」의 멧새과 조류이다.관목이 있는 초원에 서식한다. 우리나라 전국에서 분포하며 주로 대규모 간척사업으로 만들어진 호수, 만 등에 분포하여 간척지 개발과 하천 정비 등으로 서식지가 점차 감소하고 있다. 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002233', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('404', '긴발톱멧새', 'Calcarius lapponicus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Calcarius', '긴발톱멧새속', 'lapponicus', '긴발톱멧새', '참새목 멧새과에 속하는 조류이다. 몸길이는 15cm이다. 겨울 깃털 색은 머리, 뺨, 턱은 검은색, 가슴 이하의 아랫면은 황갈색을 띤 흰색으로 옆구리에 검은색의 굵은 세로 얼룩무늬가 있다. 등, 허리, 위꼬리덮깃은 검은 갈색이다. 여름에는 검게 보이던 부분이 옅어지거나 엷은 갈색으로 바뀐다. 잔디밭, 구릉,관목림에 살며 봄과 가을에 한국을 통과하는 나그네새이며 또한 월동도 하는 겨울철새이다. 5-6월에 4-6개의 알을 낳는다. 식물 씨와 열매를 주로 먹고, 여름철에는 곤충을 먹는다. 전국에 서식하고 세계적으로는 캄차카, 오호츠크해, 사할린, 쿠릴 열도, 만주, 중국, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002235', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('405', '흰멧새', 'Plectrophenax nivalis', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Plectrophenax', '흰멧새속', 'nivalis', '흰멧새', '참새목 멧새과의 조류로 몸길이 약 16cm이다. 수컷 겨울깃의 머리꼭대기와 뒷목은 흰색이나 각 깃털 끝은 폭넓은 황갈색이기 때문에 황갈색으로 보인다. 특히 머리꼭대기 중앙은 길다. 눈 앞과 눈썹은 황갈색이고, 귀깃은 다갈색이며 아랫면은 흰색으로 윗가슴 깃털 끝은 다갈색이고 다른 부분은 조금 황갈색을 띤다. 암컷 겨울깃은 겉으로 보아 수컷 겨울깃과 흡사하나 머리꼭대기의 깃털 기부는 어두운 갈색이다. 여름깃은 수컷과 같은 부분을 털갈이한다. 부리는 작으나 측압되어 높다. 홍채는 갈색이고, 다리는 짧고 희미한 검은색이다. 산지의 자갈밭, 해안의 암석지, 용암지 등에서 서식한다. 번식기 이외는 흔히 작은 무리로 생활한다. 알은 5월 중순~7월 중순, 한배에 4~7개, 드물게는 8개 낳는다. 여름철에는 곤충류를 많이 먹고, 종자나 열매 등도 즐겨 먹는다. 북극 시베리아, 아나디르 지역 연안, 캄차카 동부 등지에서 번식한다. 시베리아 초원, 알타이, 몽골, 만주, 아무르, 우수리, 사할린, 쿠릴 열도, 중국 허베이성, 일본 북부, 한국 등지에서 월동한다. 우리나라에서는 겨울철 미조(迷鳥)이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002237', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('406', '흰정수리북미멧새', 'Zonotrichia leucophrys', 'Forster', '1772', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Zonotrichia', '흰정수리북미멧새속', 'leucophrys', '흰정수리북미멧새', '흰정수리북미멧새는 참새목 조류로, 북미 지역에 서식한다. 몸길이는 약 15~16cm이며, 체중은 25~28g 정도이다. 이 새는 머리 위에 검정과 흰색 띠가 특징적이며, 회색 얼굴, 갈색의 줄무늬가 있는 윗부분과 긴 꼬리를 가지고 있다. 분홍색이나 노란색 부리를 지니고 있다.    흰정수리북미멧새는 북미 대륙의 북부, 타이가와 툰드라 지역의 덤불에서 번식한다. 이들의 일부 집단은 정착 생활을 하며, 북부 집단은 남쪽으로 이주하여 멕시코 중부까지 겨울을 난다.    흰정수리북미멧새는 주로 땅이나 낮은 식물에서 먹이를 찾으며, 종종 무리를 이루어 이동한다. 씨앗, 식물 일부, 곤충 등을 먹는다. 번식기에는 덤불 아래나 나무 낮게에 둥지를 짓고, 3~5개의 알을 낳는다.    이 새는 인간 활동과 소음에 의해 영향을 받을 수 있으며, 긴 이주 기간 동안 반수면 상태를 유지할 수 있는 것으로 알려져 연구되었다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002239', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('407', '초원멧새', 'Passerculus sandwichensis', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Passerculus', '초원멧새속', 'sandwichensis', '초원멧새', '초원멧새는 참새목에 속하는 작은 조류로, 주로 북미의 열린 초원에서 서식합니다. 이 새는 매우 다양한 외모를 가지고 있으며, 약 17개의 아종이 존재합니다. 초원멧새는 여름철에는 캐나다와 미국 북부에서 번식하고 겨울에는 멕시코와 미국 남부로 이동합니다. 이 조류는 두 성별이 유사한 외모를 가지며, 종종 작은 무리로 생활합니다. 몸길이는 약 11~17cm 정도이며, 날개 길이는 18~25cm에 이릅니다. 초원멧새는 주로 씨앗을 먹지만 번식기에는 곤충도 섭취합니다. 겨울철에는 가족 단위로 무리를 짓고, 이주는 무리를 이루어 진행합니다. IUCN에 의하면, 초원멧새는 현재 멸종위기종은 아니지만, 개체 수 변화에 지속적인 관심이 필요합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002241', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('408', '미국쇠오리', 'Anas carolinensis', 'Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anas', '오리속', 'carolinensis', '미국쇠오리', '우리나라에서는 미조이다. 몸길이 32~39cm 정도의 소형종으로 쇠오리와 형태, 색깔이 비슷하다. 가슴과 옆구리의 경계부에 있는 흰색 줄이 쇠오리와 구별할 수 있는 가장 큰 특징이다. 알은 흰색에서 올리브색까지 다양한 색을 가지고 있다. 호소, 소택지, 간척지, 하천, 하구, 해안, 연못, 물가의 풀밭 등지에 서식한다. 번식기가 되면 물가에 풀을 엮어 둥지를 만들며, 알을 10개 정도 낳는다. 미국과 캐나다에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002242', '35.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('409', '검은머리쑥새', 'Emberiza schoeniclus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'schoeniclus', '검은머리쑥새', '작은 무리들이 우리나라에 찾아오는 드문 겨울새이다. 몸길이 16cm이다. 수컷은 머리가 검은색이며 뺨에는 흰색이 뚜렷하다. 등은 적갈색 바탕에 검은색 세로 줄무늬가 있다. 아랫면은 흰색이다. 암컷은 수컷보다 색이 옅으며, 머리는 갈색 바탕에 흰 눈썹선과 뺨선이 있다. 부리, 다리, 홍채는 갈색이다. 물가 풀밭, 갈대밭, 습한 초원에서 서식한다. 초지의 땅 위에 밥그릇 모양의 둥지를 만든다. 산란기는 5월하순~7월 중순이며, 연 2회 번식하고 한 배의 산란수는 4~5개이다. 먹이는 식물성이 주가 되나 곤충류도 잘 먹는다. 캄차카, 사할린, 아무르, 우수리, 홋카이도 등지에서 번식하고 한국, 일본, 만주 남부, 중국 남부에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000002244', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('410', '올빼미', 'Strix aluco', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Strigiformes', '올빼미목', 'Strigidae', '올빼미과', 'Strix', '올빼미속', 'aluco', '올빼미', '올빼미목 올빼미과에 속하는 조류이다. 몸길이는 37~39cm로 머리가 둥글다. 몸은 회색이며, 귀깃이 없다. 배와 등에는 세로줄 무늬에 가로줄이 섞여 있다. 눈은 검은색이며, 부리는 푸른빛을 띤 회색이다. 평지나 산지의 침엽수림, 활엽수림, 혼효림 등에서 단독으로 생활한다. 낮에는 나뭇가지에 앉아 움직이지 않고, 밤에 활동한다. 청각을 이용해 먹이를 잡기 때문에 비가 오거나 바람이 강한 날에는 낮에도 먹이를 잡는다. 날개에 솜털이 많기 때문에 비행할 때 소리가 나지 않는다. 둥지는 까마귀나 매의 낡은 둥지와 나무 구멍 등을 이용한다. 알은 흰색이며, 3월에 3-5일 간격으로 4개를 낳는다. 전국에 서식하고 세계적으로는 독일, 영국, 이탈리아, 오스트리아, 일본, 중국, 몽골 등에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001861', '38.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('411', '긴점박이올빼미', 'Strix uralensis', 'Pallas', '1771', 'Chordata', '척삭동물문', 'Aves', '조강', 'Strigiformes', '올빼미목', 'Strigidae', '올빼미과', 'Strix', '올빼미속', 'uralensis', '긴점박이올빼미', '긴점박이올빼미는 「멸종위기 야생생물 Ⅱ급」의 올빼미과 야행성 텃새다. 엷은 황갈색의 깃털에 군데군데 흰색 얼룩무늬가 깃털 끝에 있다. 백두산 고산 지대에 서식하는 것으로 알려졌으며, 강원도와 경기도에서 관찰 빈도가 높고 충청도에서 관찰되기도 하였다. 산림개발에 의한 서식지의 감소 및 단편화로 위협을 받고 있으며, 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001862', '57.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('412', '긴꼬리올빼미', 'Surnia ulula', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Strigiformes', '올빼미목', 'Strigidae', '올빼미과', 'Surnia', '긴꼬리올빼미속', 'ulula', '긴꼬리올빼미', '날개 길이 23cm, 꼬리 길이 18cm, 몸무게 290g이다. 이마와 머리 위, 뒷머리는 흰색이고 그 가장자리는 누런 갈색을 띤다. 어두운 갈색 바탕에 진한 갈색 세로무늬가 깃털의 대부분을 차지하며, 등은 갈색 바탕에 흰색 가로무늬가 있다. 가슴 아래쪽의 바탕색은 황갈색 또는 암황갈색으로, 진한 갈색 세로무늬 외에 각 깃털에는 2가닥의 엷은 갈색 가로띠가 있다. 눈은 노란색이다. 산지 혼합림에서 서식한다. 낮에 활동하며 작은 새나 곤충, 쥐, 두더지 등을 잡아먹는다. 딱따구리나 매의 묵은 둥지를 이용하여 둥지를 틀고 알을 낳는 자리에는 가슴의 털을 약간 뽑아 깐다. 알은 3~5월에 3~10개를 낳는다. 한국, 일본, 만주, 시베리아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001864', '40.4');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('413', '금눈쇠올빼미', 'Athene noctua', 'Scopoli', '1769', 'Chordata', '척삭동물문', 'Aves', '조강', 'Strigiformes', '올빼미목', 'Strigidae', '올빼미과', 'Athene', '금눈쇠올빼미속', 'noctua', '금눈쇠올빼미', '올빼미 중에서 가장 작은 종이다. 날개 길이 14.8~15.8cm이다. 암컷이 수컷보다 약간 크다. 수컷 겨울깃의 등 쪽은 잿빛을 띤 황갈색이고 머리꼭대기에 황갈색의 작은 얼룩무늬가 있다. 턱 밑은 희고 목 옆, 가슴, 옆구리, 배는 황갈백색 바탕에 짙은 황갈색 세로 얼룩무늬가 있다. 부리는 노랗고 발톱은 어두운 갈색이다. 깊은 숲속에서는 살지 않으며 촌락 부근이나 성벽, 벼랑, 강가의 무너진 흙더미, 고목 부근에 서식한다. 한배에 알 3~7개를 낳아 26~28일 동안 품는다. 우는 소리는 고양이 소리 또는 갓난아이 우는 소리와 비슷하다. 작은 포유류나 조류, 파충류, 양서류, 곤충 따위를 잡아먹는다. 한국 중부 이북, 중국, 몽골에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001866', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('414', '솔부엉이', 'Ninox scutulata', 'Raffles', '1822', 'Chordata', '척삭동물문', 'Aves', '조강', 'Strigiformes', '올빼미목', 'Strigidae', '올빼미과', 'Ninox', '솔부엉이속', 'scutulata', '솔부엉이', '솔부엉이는 흔한 여름철새로 평지에서 1,000m의 높은 산지에 이르기까지 침엽수, 낙엽활엽수, 인가 부근 숲, 도시공원, 정원 등에 서식한다. 야행성이기 때문에 주간에는 나뭇가지에 앉아서 지낸다. 솔부엉이는 도시화가 진행되는 지역에도 가로등에 모여 날아다니는 곤충을 잡아먹는다. 최근에는 번식지에서 영소할 수 있는 고사목의 벌채와 월동지인 동남아시아에서 산림개발 등으로 서식 환경이 악화되고 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001868', '32.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('415', '칡부엉이', 'Asio otus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Strigiformes', '올빼미목', 'Strigidae', '올빼미과', 'Asio', '칡부엉이속', 'otus', '칡부엉이', '우리나라에는 드문 겨울철새로 몸길이 약 38cm이다. 귀깃이 길며, 눈은 주황색이다. 몸의 윗면은 누런 갈색과 짙은 갈색으로 얼룩져 있고, 아랫배에는 가로줄이 섞인 굵은 세로 줄무늬가 있다. 야행성 조류이고 주로 소나무 숲에서 무리 지어 겨울을 난다. 번식기는 5월 중순에서하순이며 둥지는 잡목 숲속에 있는 매나 말똥가리 등의 옛 둥지를 이용한다. 한배 산란 수는 4-6개이다. 포란은 암컷이 하며, 수컷은 암컷에게 먹이를 공급한다. 들쥐, 작은 들새, 곤충을 먹는다. 유럽, 시베리아 남부, 내몽골, 중국 북동부, 일본 등지의 구북구 한대와 온대에 분포하며 겨울에는 중국 남부나 인도 북부, 이란 등지로 내려가 겨울을 난다. 천연기념물 제324호로 지정되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001870', '38.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('416', '쇠부엉이', 'Asio flammeus', 'Pontoppidan', '1763', 'Chordata', '척삭동물문', 'Aves', '조강', 'Strigiformes', '올빼미목', 'Strigidae', '올빼미과', 'Asio', '칡부엉이속', 'flammeus', '쇠부엉이', '우리나라에서 드물게 관찰되는 겨울철새로 몸길이 33~43cm이다. 귀깃이 눈에 잘 보이지 않을 정도로 매우 짧다. 온몸은 황갈색 바탕에 굵은 갈색 세로줄무늬가 많다. 날개 아랫면의 끝부분이 검은색이며, 눈은 노란색이다. 서식지로 습지, 초지, 대초원, 저지대 산림지역 등을 선호한다. 산란기는 4월하순~5월상순이고 한배의 산란수는 4~8개이다. 포란 기간은 24~28일이며, 육추 기간은 12~17일이다. 들쥐, 작은 들새, 곤충 등을 먹는다. 구북구 일원의 한대와 온대 유럽, 시베리아, 북극의 툰드라, 우수리, 아무르, 캄차카, 키르기스 초원, 몽골 북부, 중국 동북지방에 걸쳐 광범위하게 분포한다. 천연기념물 제324호이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001871', '38.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('417', '작은칼새', 'Aerodramus brevirostris', 'Horsfield', '1840', 'Chordata', '척삭동물문', 'Aves', '조강', 'Apodiformes', '칼새목', 'Apodidae', '칼새과', 'Aerodramus', '작은칼새속', 'brevirostris', '작은칼새', '작은칼새는 히말라야와 동남아시아에 서식하는 작은 칼새로, 주요 서식지는 히말라야 지역이다. 몸길이는 13~14cm이며, 좁고 긴 날개와 갈라진 꼬리를 가졌다. 주로 공중에서 곤충을 잡아먹으며 살고, 쌍을 이루어 식사와 번식을 다 함께 돌본다. 이 새는 야간에 동굴 속에서 쉬고, 낮에는 먹이를 찾는다. 작은칼새는 전형적인 칼새로 주로 높은 고도에서 번식하고, 4,500m까지 서식지가 확장되기도 한다. 주로 어두운 색을 띠며, 비행 시 주로 활공하는 특성을 보인다. 집을 만드는 데 타액을 사용하며, 둥지는 동굴 벽에 붙는다. 이 종은 일반적으로 50마리 정도의 무리를 지어 생활하며, 높은 곳에서 살지만 먹이를 찾아 낮은 곳으로 내려오기도 한다. 작은칼새는 그 수가 많고 널리 분포되어 있어 보전 상태는 양호한 편이다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001879', '13.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('418', '바늘꼬리칼새', 'Hirundapus caudacutus', 'Latham', '1801', 'Chordata', '척삭동물문', 'Aves', '조강', 'Apodiformes', '칼새목', 'Apodidae', '칼새과', 'Hirundapus', '바늘꼬리칼새속', 'caudacutus', '바늘꼬리칼새', '바늘꼬리칼새는 칼새과에 속하는 조류로, 대형 새로 몸길이는 약 20cm이고 무게는 110~120g 정도입니다. 한국에서는 드물게 관찰되며, 하전 여름에는 중앙아시아와 시베리아 남부 등지에서 번식하고, 겨울철에는 인도 아대륙, 동남아시아, 호주 등에 걸쳐 월동합니다. 바늘처럼 뾰족한 꼬리가 특징이며, 대부분의 시간을 공중에서 보내고 작은 날벌레를 주로 먹습니다. 이 새는 날기의 속도가 매우 빠른 것으로 알려져 있습니다. 바위 틈이나 속이 빈 나무에 둥지를 트고 지상에 앉는 것을 꺼려합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001881', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('419', '칼새', 'Apus pacificus', 'Latham', '1801', 'Chordata', '척삭동물문', 'Aves', '조강', 'Apodiformes', '칼새목', 'Apodidae', '칼새과', 'Apus', '칼새속', 'pacificus', '칼새', '칼새목 칼새과의 여름새로 몸길이는 20cm, 날개길이는 17-18cm 정도이다. 수컷 여름깃의 눈 앞, 뺨, 귀깃, 목 옆은 엷은 어두운 갈색이다. 턱 밑과 멱 중앙은 흰색으로 각 깃털의 축은 갈색이며 아랫면은 검은 갈색이다. 꽁지의 깃털은 갈색을 띤 검은색으로 윗면은 다소 푸른색 광택이 있다. 암컷과 수컷은 구별하기 어렵다. 부리 기부는 아주 넓으나 끝은 작고 또한 좁다. 발톱은 매우 튼튼하고 굽어 있다. 부리와 다리는 모두 검은색이고, 홍채는 갈색이다. 해안 도서 암벽과 고산 암석 굴 또는 오래된 높은 건물 등에 서식한다. 우리나라에서 드물지 않게 번식하는 여름새이다. 고공을 날면서 먹이를 찾고 단독일 때도 있으나 대개 큰 무리를 이룬다. 공중에서 교미한다. 6-7월에 알을 2-3개 낳는다. 파리목, 딱정벌레목, 벌목, 매미목 등 날아다니는 곤충을 주로 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001883', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('420', '쇠칼새', 'Apus nipalensis', 'Hodgson', '1837', 'Chordata', '척삭동물문', 'Aves', '조강', 'Apodiformes', '칼새목', 'Apodidae', '칼새과', 'Apus', '칼새속', 'nipalensis', '쇠칼새', '칼새목 칼새과의 작은 새이다. 몸 전체 길이는 13cm 정도이며, 색깔은 검은색이다. 제비와 비슷하나 꽁지가 팬 모양으로 짧다. 등, 허리, 위꽁지덮깃은 흰색이다. 해안 절벽, 동굴 및 바위섬에서 서식하고, 개방된 지역에서 번식한다. 국지적으로 흔한 여름철새이다. 우리나라 제주도 서귀포 부근에 서식한다. 세계적으로는 아프리카, 중국, 타이완, 필리핀 등 동남아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001884', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('421', '수리부엉이', 'Bubo bubo', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Strigiformes', '올빼미목', 'Strigidae', '올빼미과', 'Bubo', '수리부엉이속', 'bubo', '수리부엉이', '올빼미목 올빼미과에 속하는 조류이다. 몸길이는 약 70cm이며 올빼미과의 조류 중 몸이 가장 크고 귀깃도 길다. 눈은 주황색을 띤 노란색이다. 머리꼭대기에서 목뒤까지 그리고 등과 날개덮깃은 황갈색 바탕에 검은색과 진한 갈색 세로줄무늬가 있다. 가슴과 배의 세로줄 무늬는 아래로 내려갈수록 점점 좁아진다. 다리와 발가락은 연한 갈색 깃털로 덮여 있다. 산림만으로 이루어진 산지보다는 개활지 및 수변이 인접한 암벽 지대나 바위산에서 주로 서식한다. 무리를 짓지 않고 단독으로 생활하며, 야행성이지만 낮에도 활동한다. 둥지를 만들지 않고 암벽의 선반처럼 생긴 곳, 바위의 평평한 곳, 바위틈 사이에 2-3개의 흰색 알을 낳는다. 암컷이 알을 품고 수컷은 암컷에게 먹이를 공급한다. 쥐, 두더지, 토끼, 개구리, 뱀, 비둘기, 꿩 등을 먹는다. 전국에 서식하고 세계적으로는 구북구 전역, 북아프리카에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000001859', '70.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('422', '백할미새', 'Motacilla alba lugens', 'Gloger', '1829', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'alba', '알락할미새', '겨울새이다. 몸길이 21cm이다. 이마에서 머리꼭대기까지는 흰색이며, 뒷머리와 뒷목은 검은색이다. 흰색 눈썹선이 있다. 턱과 멱, 뺨은 흰색이다. 가슴을 가로지르는 폭 1cm의 검은색 띠가 있다. 아랫가슴 이하 아랫면은 모두 흰색이다. 나머지 부분은 잿빛이다. 꼬리깃은 가늘고 길다. 농경지, 물가, 강 하류에서 생활한다. 언제나 암수가 함께 생활하며 겨울철에도 2~3마리씩 작은 무리를 이룬다. 돌담, 바위 사이, 물가 벼랑의 파인 곳, 풀숲의 땅 위에 밥그릇 모양의 둥지를 만든다. 산란기는 5월하순~7월이고, 한배의 산란수는 4~5개이다. 우수리, 사할린 등 동아시아 북부 지역에서 번식하고 중국 북동부, 일본, 한국에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000076289', '21.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('423', '알락할미새', 'Motacilla alba leucopsis', 'Gould', '1838', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'alba', '알락할미새', '알락할미새는 참새목 할미새과에 속하는 소형 조류입니다. 이 새는 유라시아와 아프리카, 그리고 북미 일부 지역에서 번식하며, 온대 지역에서는 이주하지 않고 머무르기도 합니다. 몸길이는 16.5-21cm로 길고 가늘며, 꼬리를 자주 흔드는 특징을 갖고 있습니다. 먹이는 주로 곤충이며, 탁 트인 지역에서 먹이를 찾습니다. 주로 돌담과 같은 인공 구조물의 틈에 둥지를 트는 습성을 가지고 있습니다. 알은 하나의 둥지에 3~8개 정도 낳으며, 두 세대째는 암컷이 혼자 둥지를 짓곤 합니다. 번식기는 보통 4월부터 8월입니다. 알락할미새는 도시 환경에 잘 적응하여, 주차장 같은 포장된 지역에서도 먹이를 찾는 모습을 보입니다. 현재 IUCN 적색 목록에서는 ''관심 필요 없음''으로 분류되고 있으며, 여러 나라의 우표에 등장하기도 합니다. 라트비아의 국조로도 지정되어 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000076290', '18.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('424', '검은턱할미새', 'Motacilla alba ocularis', 'Swinhoe', '1860', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'alba', '알락할미새', '우리나라에서는 가을과 봄에 지나가는 나그네새이다. 수컷의 겨울깃은 백할미새의 겨울깃과 비슷하다. 머리꼭대기는 검고 등은 잿빛이다. 턱 밑에 작은 흰색 무늬가 있는 것 이외에는 목 전체가 검은색이다. 겨울깃의 둘째날개깃 바깥쪽이 대부분 어두운 갈색이거나 검은색이며, 흰색이 없는 점이 백할미새와 다르다. 몸의 아랫면은 흰색이다. 강 하류, 바닷가에서 서식한다. 시베리아 동북부, 북극해 연안 등지에서 번식하고 중국 남부에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000076291', '17.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('425', '큰기러기', 'Anser fabalis serrirostris', 'Gould', '1852', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anser', '기러기속', 'fabalis', '큰기러기', '큰기러기는 기러기목 오리과에 속하는 조류로, 북유럽과 아시아에서 번식하는 겨울철새입니다. 이 새는 남쪽으로 이동하여 유럽 및 아시아의 온대 지역에서 겨울을 보냅니다. 몸길이는 68~90cm, 날개 길이는 140~174cm이며, 몸무게는 1.7~4kg입니다. 부리는 검정색으로 기초와 끝부분이 검고 중간에 주황색 띠가 있습니다. 또한 밝은 주황색의 다리와 발을 가지고 있고, 윗날개 덮깃은 어두운 갈색입니다. 국내에서는 매우 드문 겨울철새로, 주요 월동지는 동아시아 지역입니다. 이 새는 호밍 소리가 크며, 철새의 이동 경로 보존을 위한 국제 협약의 대상이 됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000076292', '79.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('426', '한국동박새', 'Zosterops erythropleurus', 'Swinhoe', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Zosteropidae', '동박새과', 'Zosterops', '동박새속', 'erythropleurus', '한국동박새', '참새목 동박새과의 조류이다. 몸길이는 10.5cm가량이다. 수컷 겨울깃의 눈 앞은 검은색이고, 눈까풀의 흰색 둥근 고리 모양은 현저하다. 턱 밑과 멱의 황색은 동박새의 것보다 더 선명하다. 가슴은 잿빛 흰색이며 가슴 양쪽은 진하여 거의 잿빛에 가깝다. 배는 흰색이며, 옆구리에 붉은 밤색의 큰 얼룩점이 있다. 부리는 흐린 잿빛으로 아랫부리의 기부는 엷은 색이다. 홍채는 갈색이고, 다리는 흐린 잿빛이다. 흔한 새이나 민첩하고 겁이 많은 새이기 때문에 사람의 눈에 잘 띄지 않는다. 봄철에는 작은 무리를 지어 나뭇가지를 오가며 곤충류를 잡아먹는다. 주로 딱정벌레목, 나비목 곤충의 유충과 성충을 잡아먹는다. 아무르 동부, 하바롭스크, 우수리 남부, 만주 동부 등지에서 번식한다. 인도차이나, 캄보디아, 미얀마 동부 등지에서 월동한다. 우리나라 북부, 평안도 지역에 서식한다. 세계적으로는 중국 동부·중부, 만주 등을 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000072194', '10.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('427', '동박새', 'Zosterops japonicus', 'Temminck & Schlegel', '1845', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Zosteropidae', '동박새과', 'Zosterops', '동박새속', 'japonicus', '동박새', '참새목 동박새과에 속하는 조류이다. 몸길이는 12cm이다. 암수는 비슷한 색깔을 가지며, 암컷은 수컷보다 약간 작다. 윗면은 황록색이고 목은 노란색이다. 아랫부분은 회백색이고 옆구리는 담갈색이다. 눈의 주위가 흰색의 가는 털이 모여 링 모양을 하고 있다. 부리는 흑갈색으로 끝이 뾰족하고 아래로 다소 구부러져 있으며, 다리는 어두운 회갈색이다. 상록활엽수림에 살며 둥지는 잡목림, 적송림,관목림의 가지나교목의 아랫가지 또는교목에 감겨 있는 덩굴식물 등에 사발 모양으로 만든다. 알 색깔은 흰색 또는 담청색이며, 4-7월에 3~5개를 낳는다. 암수가 교대로 알을 품는다. 새끼의 먹이는 곤충의 유충이 가장 많고, 거미, 나비, 파리 등의 성충도 있다. 둥지를 떠난 새끼는 수일 동안 어미로부터 먹이를 받아먹는다. 경상남도와 전라남도 해안 및 도서지방, 제주도에 서식하고 세계적으로는 중국, 타이완, 인도지나 반도 북부, 필리핀 북부, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000072228', '12.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('428', '시베리아알락할미새', 'Motacilla alba baicalensis', 'Swinhoe', '1871', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'alba', '알락할미새', '시베리아알락할미새는 참새목 할미새과에 속하는 작은 조류로, 몸길이는 16.5~19cm입니다. 주로 유라시아의 팔레아크틱 지역에서 번식하며, 유럽과 아프리카 등 따뜻한 지역에서는 텃새로 남습니다. 벌판이나 물가 근처의 탁 트인 지역을 선호하여, 도시에서는 주차장 등에서 먹이를 찾기도 합니다. 곤충을 주로 먹으며 둥지는 돌담이나 자연, 인공 구조물의 틈에 짓습니다. 꼬리를 끊임없이 위아래로 흔드는 특징이 있습니다. IUCN 적색 목록에서 ''관심 필요 없음'' 등급으로 평가되며, 겨울에는 인도, 동남아시아로 이동해 월동합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000080366', '17.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('429', '우는뻐꾸기', 'Cacomantis merulinus', 'Scopoli', '1786', 'Chordata', '척삭동물문', 'Aves', '조강', 'Cuculiformes', '두견목', 'Cuculidae', '두견과', 'Cacomantis', '우는뻐꾸기속', 'merulinus', '우는뻐꾸기', '우는뻐꾸기는 뻐꾸기과에 속하는 작은 조류로, 몸길이는 약 21–24cm입니다. 성조 수컷은 윗면이 회갈색이며 아랫면이 주황색이고, 머리와 목, 가슴 윗부분이 회색입니다. 꼬리 깃은 흰색 끝을 가지고 있습니다. 다리와 발은 노란색이며, 눈은 빨간색입니다. 암컷은 수컷과 유사하거나 간혹 간흑 빛깔을 띠는 변형이 나타나며, 윗면이 적갈색에 짙은 줄무늬가 있습니다. 주로 숲 가장자리, 초원, 경작지, 공원 같은 열린 공간에 서식하며, 무척추동물을 먹습니다. 번식기에는 다른 새들의 둥지에 알을 낳는 탁란 습성을 보입니다. 우는뻐꾸기는 인도, 네팔, 중국에서부터 인도네시아에 이르기까지 아시아 전역에 분포합니다. 종소리와 같은 휘파람 소리를 내며, 주로 독립적으로 생활합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000081763', '22.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('430', '집까마귀', 'Corvus splendens', 'Vieillot', '1817', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Corvidae', '까마귀과', 'Corvus', '까마귀속', 'splendens', '집까마귀', '집까마귀는 아시아 원산의 까마귀로 몸길이 약 40cm이며, 검은색의 이마, 목, 날개, 꼬리와 회갈색의 목과 가슴을 가지고 있습니다. 이 새는 매우 적응력이 뛰어나고 인간 거주지와 가까운 곳에서 흔히 발견됩니다. 집까마귀는 쓰레기, 작은 파충류, 포유류, 곤충, 알 등 다양한 것을 먹으며, 시장이나 쓰레기장 주변에서 자주 볼 수 있습니다. 나무가 번식에 필요하지만 가끔 통신탑에도 둥지를 짓습니다. 번식기는 4월에서 7월이며 한 번에 3~5개의 알을 낳습니다. 사람 활동이 많은 도시 지역에서 떼를 지어 잠을 자며, 목소리는 "카우-카우"라는 소리를 냅니다. 이 새는 다양한 지역에 침입하여 번식하고 있어 관리가 필요한 침입종으로 간주되고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000082978', '40.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('431', '검은머리갈색찌르레기', 'Acridotheres tristis', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sturnidae', '찌르레기과', 'Acridotheres', '뿔찌르레기속', 'tristis', '검은머리갈색찌르레기', '검은머리갈색찌르레기는 참새목 찌르레기과에 속하며 아시아 원산지의 조류입니다. 몸 길이는 약 23cm로, 갈색 몸통과 검은 머리, 노란 부리와 다리로 쉽게 식별할 수 있습니다. 강한 영토 본능을 가지고 있으며, 도시 환경에 잘 적응했습니다.    이 종은 급속히 퍼져 세계 최악의 침입종 100개 중 하나로 지정되었습니다. 생태계, 농업, 인간의 이익에 위협을 주며, 특히 호주에서는 심각한 생태계 위협이 되고 있습니다. 주로 개활지와 인근 서식지에서 서식하며, 나무나 벽에 구멍을 뚫어 둥지를 짓고, 연중 여러 번 번식합니다. 잡식성으로 곤충, 과일, 곡물 등을 먹습니다.    검은머리갈색찌르레기는 특유의 목소리로 높이 평가받으며, 노래와 말하는 능력 때문에 애완조로 선호되기도 합니다. 그러나 이들의 침입적 성격과 공격적인 번식 행동으로 인해 많은 지역에서 해충으로 간주되고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000085486', '23.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('432', '검은해오라기', 'Ixobrychus flavicollis', 'Latham', '1790', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Ixobrychus', '덤불해오라기속', 'flavicollis', '검은해오라기', '몸길이 58cm의 큰 종으로 긴 목과 노란색의 긴 부리를 가지고 있다. 위쪽은 균일한 검은색을 띠고, 목 옆은 노란색이다. 아랫부분은 흰색이고, 빽빽하게 갈색의 줄무늬가 있다. 새끼는 성조와 비슷한 색이지만 어두운 갈색을 띤다. 갈대밭이나 나무 위에 보금자리를 짓는다. 3~5개의 알을 낳는다. 숨어 지내는 생활 습관과 주거 환경 때문에 보기 힘들 수도 있지만, 종종 날아다니는 것을 볼 수 있다. 곤충, 물고기, 양서류를 먹는다. 열대 지방에서 번식한다. 유럽, 아시아, 인도, 스리랑카 동부, 중국, 인도네시아, 오스트레일리아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000085544', '58.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('433', '큰부리큰기러기', 'Anser fabalis middendorffi', 'Severtsov', '1873', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anser', '기러기속', 'fabalis', '큰기러기', '큰부리큰기러기는 북유럽과 아시아에서 번식하는 기러기류로, 겨울에는 남쪽 유럽과 아시아로 이동합니다. 몸길이는 68~90cm이며, 날개 길이는 140~174cm입니다. 몸무게는 1.7~4kg로, 수컷 평균은 3.2kg, 암컷 평균은 2.84kg입니다. 부리는 검은색으로, 가운데는 주황색 띠가 있고, 다리와 발은 밝은 주황색입니다. 주로 스칸디나비아에서 우랄 지역까지 분포하고, 겨울철에는 덴마크에서 폴란드 그리고 스코틀랜드, 프랑스 북부까지 이동합니다. 서부 시베리아에서 동아시아까지 겨울에는 중앙 아시아에서 한국, 일본까지 이동합니다. 큰부리큰기러기는 밝은 상태로 IUCN에 등재되어 있으며, 아프리카-유라시아 이동성 물새 보존 협정의 대상 종 중 하나입니다. 과거에 북해와 동유럽 등지에서 자주 발견되었으나 현재는 그 수가 줄어들고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000087255', '79.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('434', '한국재갈매기', 'Larus cachinnans', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Larus', '갈매기속', 'cachinnans', '한국재갈매기', '10월부터 이듬해 3월 사이에 다른 갈매기류 무리에 섞여 강, 하구, 해안, 간척지 등에서 관찰되는 흔하지 않은 겨울철새이다. 재갈매기와 유사하나 머리와 목에 갈색 줄무늬가 없고, 몸은 전체적으로 흰색이며, 등과 날개덮깃은 재갈매기보다 진한 회색이다. 다리는 분홍색이며, 눈 테두리가 붉은색이다. 부리는 노란색이며 아래쪽에 빨간색 점이 있다. 주로 재갈매기 무리에 섞여 강, 하구, 해안, 간척지 등에서 드물게 관찰된다. 물고기, 게, 수서곤충 등을 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000086214', '62.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('435', '멧비둘기', 'Streptopelia orientalis', '(Latham)', '1790', 'Chordata', '척삭동물문', 'Aves', '조강', 'Columbiformes', '비둘기목', 'Columbidae', '비둘기과', 'Streptopelia', '멧비둘기속', 'orientalis', '멧비둘기', '비둘기목 비둘기과에 속하는 조류이다. 몸길이는 33cm가량이다. 우리나라의 대표적인 비둘기로서 머리와 목, 몸의 아랫면은 회갈색이다. 진한 회색의 날개깃 가장자리는 적갈색으로, 날개를 접었을 때 얼룩무늬로 보인다. 첫째날개깃과 꼬리는 검은색이며, 꼬리 끝에는 흰 띠가 있다. 목 옆에는 회색과 검은색의 줄무늬로 이루어진 반점이 있다. 눈과 다리는 붉은색이다. 인가, 농경지, 초지 주변의 산림에 살며 번식기에는 날개를 수평으로 한 채 활공하기도 한다. 정원, 산림, 나무가 있는 곳에서 흔하게 번식하며 작은 나뭇가지로 엉성하게 둥지를 만들고 2개의 알을 낳는다. 낮고 탁하게 ‘구, 구, 쿠-, 쿠-’하는 소리로 우는데, 뒤의 두 음절이 더 높고 빠르다. 전국에 서식하고 세계적으로는 시베리아 남부, 사할린, 일본, 중국, 히말라야에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000086224', '33.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('436', '흰꼬리딱새', 'Ficedula albicilla', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Ficedula', '황금새속', 'albicilla', '흰꼬리딱새', '흰꼬리딱새는 참새목 딱새과에 속하는 철새이다. 암컷은 갈색의 윗면과 검은 꼬리를 가진다. 가슴은 연한 갈색이며 아랫면은 주로 흰색이다. 수컷은 번식기에 귀덮개와 목 옆이 푸른빛을 띠며, 목 부분에 주황색이 나타난다. 북아시아의 타이가 숲에서 번식하고, 겨울에는 남아시아와 동남아시아로 이동한다. 이들의 자연 서식지는 타이가 숲이며, 서유럽에는 매우 드물다. 이전에는 붉은가슴딱새의 아종으로 분류되었다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000087324', '12.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('437', '큰제비갈매기', 'Thalasseus bergii', 'M.H.C.Lichtenstein', '1823', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Thalasseus', '뿔제비갈매기속', 'bergii', '큰제비갈매기', '도요목 갈매기과의 새이다. 몸길이는 46cm쯤 되는 대형종에 속한다. 머리는 검고 등은 회색빛을 띤 갈색이다. 얼굴과 몸의 아래쪽은 희고, 부리는 누런색이며 다리는 검은색이다. 섬이나 바닷가에서 서식한다. 열대 지방에 분포한다. 바다 위를 천천히 날다가 먹이를 보면 쏜살같이 내려가 챈다. 우리나라에서는 서해 무인도에서 한 번 잡힌 기록이 있을 뿐인 미조(迷鳥)이다. 타이완 등의 동아시아, 남아프리카 열대 지방, 오스트레일리아 등지의 연안에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000081107', '46.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('438', '붉은배오색딱다구리', 'Dendrocopos hyperythrus', 'Vigors', '1831', 'Chordata', '척삭동물문', 'Aves', '조강', 'Piciformes', '딱다구리목', 'Picidae', '딱다구리과', 'Dendrocopos', '딱다구리속', 'hyperythrus', '붉은배오색딱다구리', '우리나라에서는 주로 백두산 고지대에서 번식하는 드문 텃새이지만, 남한 지역에서는 경기도에서 2회 채집한 기록이 있는 미조이다. 몸길이 약 24cm이다. 정수리는 수컷은 붉은색, 암컷은 검은색, 얼굴과 뺨은 흰색, 뒷머리와 목 전체, 턱, 가슴, 배는 황갈색, 등과 윗날개는 검은색 바탕에 흰색 가로무늬로 얼룩져 있다. 꼬리위덮깃은 검은색이다. 부리 윗면은 검은색이고 밑면은 황갈색이다. 침엽수림에 서식한다. 밀림 속의 나무 구멍에 둥지를 튼다. 산란기는 3~5월이며, 한배의 산란수는 3~4개이다. 곤충류를 주식으로 한다. 히말라야에서 만주에 이르는 동아시아를 비롯하여 미얀마 북부와 타이에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000090055', '24.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('439', '큰부리제비갈매기', 'Gelochelidon nilotica', 'J.F.Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Gelochelidon', '큰부리제비갈매기속', 'nilotica', '큰부리제비갈매기', '도요목 갈매기과의 새로 몸길이는 36cm이며, 날개 편 길이는 86cm이다. 몸 빛깔은 대체로 흰색에서 위로 갈수록 밝은 회색을 띤다. 부리와 다리, 발가락은 검은색이다. 번식기에는 정수리와 목덜미가 검다. 월동기에는 머리 위, 머리, 등, 날개, 꽁지가 회색이 된다. 크고 두꺼운 부리와 깊게톱니모양으로 파인 꽁지가 특징이다. 모래, 자갈, 해변의 초지에서 번식한다. 번식기는 5월 초~8월 초이다. 한 지역에 여러 개의 둥지를 튼다. 암컷은 보통 2~3개의 알을 낳으며 부화 기간은 22~23일이다. 새끼는 부화 후 23~35일 동안 어미에게 의존한다. 미국 동부, 남쪽 해안에서 번식한다. 미국 남쪽 해안에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000088662', '36.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('440', '큰부리바람까마귀', 'Dicrurus annectans', 'Hodgson', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Dicruridae', '바람까마귀과', 'Dicrurus', '바람까마귀속', 'annectans', '큰부리바람까마귀', '큰부리바람까마귀는 동남아시아의 습윤 열대림에 서식하는 조류로, 몸 전체가 검고 낮게 갈라진 꼬리를 가지고 있다. 흑색 바람까마귀와 유사한 외형을 지닌다. 번식기는 4월에서 6월 사이로, 수컷과 암컷이 함께 나뭇가지 포크에 풀과 거미줄로 만든 컵 모양의 둥지를 짓고, 암컷이 알을 품는다. 이 새는 일반적으로 곤충과 작은 동물을 먹으며, 국제자연보전연맹(IUCN)에 의해 ''관심 불필요'' 등급으로 분류된다. 주요 서식지는 방글라데시, 부탄, 브루나이, 캄보디아, 중국, 인도, 인도네시아, 라오스, 말레이시아, 미얀마, 네팔, 필리핀, 싱가포르, 태국 및 베트남이다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000083573', '29.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('441', '왜가리', 'Ardea cinerea jouyi', 'A.H.Clark', '1907', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Ardea', '왜가리속', 'cinerea', '왜가리', '왜가리는 기러기목 해오라기과에 속하는 조류로, 전 세계의 습지에서 주로 서식하는 대형 도요새입니다. 키는 약 1미터에 달하며, 몸무게는 1~2kg 정도입니다. 몸은 회색이며, 머리와 목은 하얗고 눈에서부터 머리뒷부분까지 검은 띠가 나 있습니다. 부리는 분홍빛 노란색, 다리는 갈색입니다.    이 새는 호수, 강, 연못, 습지, 해안 등지에서 물고기, 양서류, 갑각류 등을 먹으며, 물가에서 먹이를 기다렸다가 빠르게 찌르는 방식으로 사냥합니다. 번식기는 봄이며, 나무에 집단으로 둥지를 틀고 알을 낳습니다. 알은 청회색이며, 보통 3~5개의 알을 낳습니다. 두 마리가 번갈아 알을 품으며 약 25일 후 부화합니다.    왜가리는 유럽과 아시아 전역, 아프리카의 일부 지역에 걸쳐 광범위하게 분포하며, 일부 북부 지역의 개체군은 겨울에 남쪽으로 이동합니다. 주로 일몰과 일출 무렵에 활동하며, 도심에서도 적응력을 보입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000094851', '36.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('442', '대백로', 'Ardea alba alba', 'Linneaus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Ardea', '왜가리속', 'alba', '중대백로', '중대백로의 아종으로 생김새는 중대백로와 유사하지만 겨울에 우리나라를 찾아오는 겨울철새이다. 몸 전체가 흰색이며, 다리는 검은색이지만 경부가 연한 노란색 또는 주황색이다. 부리와 눈앞의 피부가 노란색이다. 왜가리보다 크고, 목이 상당히 길어 날거나 먹이를 먹을 때 긴 목이 눈에 띈다. 일부 월동하는 다른 백로류와 함께 관찰되며, 논, 강, 하천, 호수, 저수지, 갯벌, 해안 등에서 주로 볼 수 있다. 초지나 수심이 얕은 곳에서 물고기, 작은 양서류, 곤충, 수서생물 등을 주로 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000094853', '92.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('443', '중대백로', 'Ardea alba modesta', 'J.E.Grey', '1831', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Ardea', '왜가리속', 'alba', '중대백로', '중대백로는 백로과에 속하는 대형 새로, 몸길이는 약 83-103cm이며 몸무게는 0.7-1.2kg입니다. 전체적으로 순백색의 깃털을 가지고 있으며, 번식기에는 부리가 검은색, 번식기가 아닌 때에는 노란색입니다. 다리는 붉거나 검은색입니다. 번식지는 아시아와 오세아니아 전역에 분포하며, 한국, 중국, 일본 등 여러 나라에서 발견됩니다.    주로 물고기, 개구리, 작은 파충류와 새, 곤충 등을 먹으며, 얕은 물에서 먹이를 기다리거나 걸어다니며 사냥합니다. 높은 나무 위에 둥지를 틀며, 한 번에 2-6개의 하늘색 알을 낳습니다. 번식지에서는 다른 백로, 왜가리들과 함께 집단으로 번식합니다.    중대백로는 보호종으로, 특정 지역에서는 서식지 감소 등의 위협을 받고 있습니다. 뉴질랜드에서는 "Kōtuku"라 불리며, 중요한 문화적 상징으로 여겨집니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000094854', '93.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('444', '참매', 'Accipiter gentilis schvedowi', 'Menzbier', '1882', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Accipiter', '새매속', 'gentilis', '참매', '매목 수리과에 속하는 조류이다. 몸길이는 50~56cm이다. 암수 형태가 유사하며, 흰색 굵은 눈썹선과 두꺼운 검은색 눈선, 노란색 홍채가 특징이다. 머리부터 등, 날개 윗면, 꼬리는 진한 청회색이고 가슴과 배에는 흰색에 흑갈색 가는 가로줄 무늬가 있다. 날개는 짧으며 폭이 넓고, 날 때 보이는 긴 꼬리가 특징이다. 산야, 평지, 하천변 등에서 볼 수 있으며, 번식기에는 비교적 깊은 산림에 서식한다. 암수 함께 생활하거나 단독으로 생활하며 먹이는 주로 꿩, 비둘기, 오리 등과 같이 날아다니는 조류를 추적해 잡아먹으며 토끼, 청설모, 다람쥐 같은 작은 포유류도 먹는다. 큰 나무의 가지에 나뭇가지를 쌓아 접시 모양의 둥지를 지상으로부터 4~8m 높이에 만든다. 5월~6월경 옅은 푸른색의 알을 2~4개 낳는다. 우리나라에서 겨울에 흔하게 관찰되고 번식도 하며, 이동시기에는 서해 도서 지역에서 많이 관찰된다. 세계적으로는 일본, 유럽, 북아메리카, 시베리아, 중국에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000094903', '53.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('445', '매', 'Falco peregrinus japonensis', 'J.F.Gmelin', '1788', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Falconidae', '매과', 'Falco', '매속', 'peregrinus', '매', '매는 전 세계에 널리 분포하는 맹금류로 날쌘 사냥꾼으로 유명합니다. 몸길이는 34~58cm, 날개 길이는 74~120cm로, 등은 청회색, 반점이 있는 흰색의 아랫면, 그리고 검은 머리를 가지고 있습니다. 매는 속도의 대명사로, 사냥 중 급강하할 때 시속 320km 이상의 속도를 자랑하며, 이는 동물 중 가장 빠릅니다. 암컷이 수컷보다 크며, 주로 중간 크기의 새를 잡아먹고 때로는 작은 포유류도 사냥합니다. 매는 높은 절벽이나 높은 건물에 둥지를 틀며, 평생 한 짝과 함께 살고 매년 같은 장소에 알을 낳습니다. DDT와 같은 화학물질의 사용으로 개체수가 감소했으나, 보호 노력 덕분에 회복되었습니다. 도시에서는 높이 솟은 건물을 둥지로 사용하고 비둘기와 오리 등을 먹이로 삼습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000094920', '46.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('446', '중백로', 'Ardea intermedia', 'Wagler', '1829', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Ardeidae', '백로과', 'Ardea', '왜가리속', 'intermedia', '중백로', '황새목 백로과의 조류로 드문 여름철새이다. 크기는 약 65cm. 암수의 형태가 유사하다. 온몸은 흰색이며, 다리는 계절에 상관없이 검은색이다. 부리의 끝부분은 검은색이며, 뒤쪽은 부분적으로 노란색을 띠지만, 범위는 개체에 따라 차이가 있다. 여름철에는 가슴과 등 쪽에 실가닥과 같은 가는 장식깃이 생긴다. 중대백로와 유사하지만 입꼬리끝(구각)과 머리의 형태가 차이가 난다. 다양한 습지의 얕은 물가에서 먹이를 사냥하며, 다른 백로류의 번식집단과 섞여 집단번식한다.   한국, 일본, 중국 남부, 동남아, 호주, 인도, 아프리카에 걸쳐 분포한다. 국내에는 드문 여름철새로 4월에 도래하여 번식하고 9월하순에 월동지로 떠난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000154940', '65.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('447', '흰배줄무늬수리', 'Aquila fasciata', 'Vieillot', '1822', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Aquila', '검독수리속', 'fasciata', '흰배줄무늬수리', '매우 큰 맹금류로 참수리속(Aquila)에 속하며, 다리까지 깃털로 덮인 부엉이수리아과(Aquilinae)의 일원이다. 몸길이 약 55~74cm, 암컷이 수컷보다 약 10% 크다. 암컷 날개 길이는 156~180cm, 수컷은 143~163cm이며, 몸무게는 암컷 2.1~3.03kg, 수컷 1.4~2.24kg 정도이다. 몸은 근육질이고 다리가 길며 발톱이 크고 강력하다. 등은 어두운 갈색이며, 성조는 어깨 부근에 흰 반점이 있다. 아랫면은 크림색 바탕에 검은 갈색의 줄무늬가 있다. 미성숙한 개체는 더 밝고 적갈색을 띠며 줄무늬는 적다. 분포는 남유럽, 북아프리카, 중동에서 인도 아대륙, 동남아시아, 인도네시아의 일부 섬까지 이르며 대부분 텃새로 생활한다. 해발 1,500m 이하의 산악 지대와 암벽, 벼랑, 삼림과 관목 지대 등에서 서식하며, 겨울에는 더 개방된 반사막이나 습지 주변에서도 관찰된다. 한국에는 번식 및 월동 기록은 없으며, 매우 드문 미조(vagrant)로서 관찰될 가능성이 있다. 주로 단독 또는 짝으로 생활하며, 먹이는 토끼, 꿩류, 비둘기, 까마귀, 물새 등 다양한 중소형 조류와 포유류이다. 유럽에서는 유럽토끼가 주요 먹이이나, 개체 수 감소 시 비둘기 등 다른 조류로 먹이를 전환한다. 평균 먹이 크기는 약 400~900g으로, 체중의 20~45% 수준이다. 짝이 함께 사냥하기도 하며, 이는 사회적 유대 강화의 역할이 크다. 번식기는 1월~7월 사이이며, 주로 벼랑 또는 나무 위에 큰 둥지를 튼다. 보통 2개의 알을 낳고, 부화에는 약 37~45일이 걸린다. 새끼는 약 8~11주간 부모에게 의존하며, 이 시기 후 수백 km까지 분산하여 정착지를 찾아간다. 형제간 경쟁으로 인해 하나만 성장하는 경우가 많으나, 20% 정도는 두 마리 모두 성장한다. IUCN 평가상 ''Least Concern(관심 필요 없음)''으로 분류되나, 유럽을 중심으로 개체 수가 급감하고 있으며, 서식지 파괴, 송전탑 감전, 불법 사냥 등의 위협으로 일부 지역에서는 국소 멸종 위기에 처해 있다. 스페인, 프랑스, 이탈리아, 그리스, 터키 등지에서 보호 및 복원 프로젝트가 진행 중이다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155008', '64.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('448', '뿔매', 'Nisaetus nipalensis', 'Hodgson', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Nisaetus', '뿔매속', 'nipalensis', '뿔매', '뿔매는 아시아에 서식하는 대형 맹금류입니다. 이 새는 주로 산악 지역과 숲에서 서식하며, 다양한 작은 포유류, 조류, 파충류를 먹이로 삼습니다. 뿔매는 비교적 큰 날개와 인상적인 부리를 갖추고 있어 울창한 숲에서도 기민한 날갯짓으로 사냥을 합니다.    길이는 69~84cm이며, 날개 길이는 134~175cm에 이릅니다. 암컷이 수컷보다 일반적으로 크고, 몸 색깔은 주로 어두운 갈색입니다. 두 개의 아종이 있으며, 일본에 서식하는 아종은 다른 아종보다 약간 더 큽니다. 번식기는 봄에 시작하며, 높은 나무에 큰 둥지를 틀고 한 번에 1~3개의 알을 낳습니다.    뿔매는 서식지 파괴와 삼림 벌채로 인해 점차 서식지가 줄어들면서 개체 수가 감소하고 있습니다. IUCN에 의해 ''취약(Vulnerable)'' 등급으로 분류되고 있으며, 특히 일본 아종은 감소가 두드러집니다. 뿔매는 보호가 필요한 종으로, 관리가 필요합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155011', '76.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('449', '흰눈썹뜸부기', 'Rallus indicus', 'Blyth', '1849', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Rallidae', '뜸부기과', 'Rallus', '흰눈썹뜸부기속', 'indicus', '흰눈썹뜸부기', '두루미목 뜸부기과의 조류로 몸길이는 29cm가량이다. 암컷과 수컷의 이마, 머리꼭대기, 뒷머리는 검은색으로 각 깃털에 엷은 갈색 가장자리가 있고, 앞이마의 깃털은 센털과 같다. 눈 위에는 석판 잿빛의 폭넓은 눈썹선이 있다. 눈 앞에서 귀깃까지는 어두운 갈색의 눈선이 있다. 뺨은 석판 잿빛이고, 턱 밑과 멱은 흰색이다. 가슴과 배는 엷은 잿빛이다. 꽁지는 검은색으로 엷은 갈색의 폭이 아주 넓은 가장자리가 있다. 부리는 검은 갈색이고 다리는 살구색을 띤 갈색이다. 줄과 갈대가 우거진 호소나 소택지 또는 하구의 물가에서 산다. 온대 지역에서 번식한다. 우리나라 중서부 지역을 봄과 가을에 통과한다. 일부 작은 집단이나마 경상남도 낙동강 하구, 창녕 주남저수지에서 월동한다. 남부 낙동강 유역에서 월동하기도 한다. 갈대밭 땅 위나 갈대가 쌓인 위에 갈잎이나 줄기로 접시 모양의 둥지를 튼다. 알은 5~6월경, 한배에 6~7개를 낳는다. 어류, 새우류, 복족류, 곤충류 등 동물성과 각종 식물의 종자를 채식한다. 유라시아 대륙, 우수리, 사할린 등지에서 번식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155015', '29.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('450', '흰뺨검둥오리', 'Anas zonorhyncha', 'Swinhoe', '1866', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Anas', '오리속', 'zonorhyncha', '흰뺨검둥오리', '기러기목 오리과에 속하는 조류로 흔한 겨울철새이며, 흔한 텃새이다. 몸길이 약 61cm. 암수가 비슷하나 수컷의 위꼬리덮깃과 아래꼬리덮깃이 암컷보다 어두운 흑갈색이며, 수컷의 뺨은 암컷보다 더 밝게 보인다. 황갈색의 뺨을 제외한 몸 전체가 균일한 어두운 갈색이다. 날 때 청색의 둘째날개깃과 흰색의 셋째날개깃은 먼 거리에서도 잘 보인다. 검은색 부리 끝은 노란색이며, 다리는 주황색이다. 과거에는 흔한 겨울철새였으며, 1960년대 이후 전국적으로 흔히 번식하는 텃새로 자리 잡았다. 전국의 야산이나 풀밭 해안가 도서 지역 등 다양한 서식지에서 흔히 번식하며 겨울에는 북쪽에서 남하한 무리가 집단으로 월동한다. 시베리아 동남부, 몽골, 중국, 인도, 동남아, 한국, 대만 일본에 서식한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000154877', '61.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('451', '흰죽지꼬마물떼새', 'Charadrius hiaticula', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Charadriidae', '물떼새과', 'Charadrius', '물떼새속', 'hiaticula', '흰죽지꼬마물떼새', '흰죽지꼬마물떼새는 북유라시아와 그린란드 등지에서 번식하는 조류로, 몸길이는 약 17–19.5cm이다. 등과 날개는 회갈색이고 배는 흰색이며, 검은 목띠를 가진다. 부리는 짧고 주황색과 검은색이 섞여 있으며 다리는 주황색을 띤다. 주로 해안가와 평지에서 살아가며, 겨울에는 아프리카 남쪽 해안으로 이동한다. 수명은 1년차에 번식이 시작되며, 홀로 둥지를 틀고 3~4개의 알을 낳는다. 먹이는 주로 곤충, 갑각류, 벌레로 이루어져 있다. IUCN에서는 흰죽지꼬마물떼새를 "관심대상"으로 분류하고 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155042', '18.2');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('452', '흰목물떼새', 'Charadrius placidus', 'J.E.Gray & G.R.Gray', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Charadriidae', '물떼새과', 'Charadrius', '물떼새속', 'placidus', '흰목물떼새', '도요목 물떼새과에 속하는 조류로 국지적으로 번식하는 드문 텃새이다. 몸길이 20.5cm. 꼬마물떼새와 비슷하지만, 부리와 다리가 더 길고 전체적으로 크기가 더 크다. 부리는 가늘고 길며, 아랫부리 기부가 엷다. 눈테는 황색이고 꼬마물떼새와 비교해 가늘고 약해 보인다. 눈앞의 흑색은 흰물떼새보다 흐리다. 머리 위, 귀깃에 흑색이며, 가슴의 흑색 줄무늬는 중앙부에서 가늘어진다. 꼬마물떼새와 유사한 환경에서 서식하지만, 자갈이 보다 많은 하천이나 강가에 서식한다. 단독 또는 작은 무리를 이루어 생활하며, 둥지는 자갈 사이에 땅을 오목하게 파서 만들고 4개의 알을 낳는다. 포란기간은 28~29일이다. 세계적으로 우수리 지방, 중국 동북부, 우리나라에서 번식하고, 중국 남부와 인도 북부에서 월동한다. 국내에는 국지적으로 강가의 자갈밭이나 모래밭에서 소수가 번식하는 드문 텃새이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155046', '20.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('453', '꼬마물떼새', 'Charadrius dubius', 'Scopoli', '1786', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Charadriidae', '물떼새과', 'Charadrius', '물떼새속', 'dubius', '꼬마물떼새', '도요목 물떼새과의 조류로 국내에는 흔한 여름철새이다. 몸길이 16cm. 황색 눈테가 뚜렷해 다른 종과 구별된다. 부리는 흰목물떼새보다 짧으며, 아랫부리기부는 폭 좁은 주황색을 띤다. 눈앞, 머리 위, 귀깃, 가슴에 검은 무늬가 있다. 수컷은 눈앞과 귀깃이 검은색이며, 암컷은 흑갈색이다. 어린 새는 성조의 겨울깃과 비슷하지만 몸 윗면에 비늘 무늬가 있고 머리는 등과 거의 같은 색이며, 깃 끝은 색이 연하다. 어린 새의 이마는 흰색 바탕에 옅은 황갈색이 섞여 있으며, 눈썹선은 거의 보이지 않는다. 3월 중순에 국내에 도래해 하천, 자갈밭, 매립지의 풀이 적고 모래와 자갈이 많은 곳에서 서식하며, 주로 곤충을 먹는다. 종종걸음으로 빠르게 달리다 갑작스럽게 멈주고 먹이를 잡아먹는 행동을 한다. 둥지는 자갈밭에 만들고 4개의 알을 낳는다. 포란기간은 24~25일이다. 둥지 근처에 침입자가 나타나면 날개를 늘어뜨리고 소리를 지르며, 다친 것처럼 행동하여, 포식자를 관심을 돌리고 다른 곳으로 유인한다. 세계적으로 북반부의 온대, 아한대, 열대와 뉴기니에서 번식하고, 아프리카, 인도, 동남아시아에서 월동한다. 우리나라는 전국에 흔하게 찾아와 번식하는 여름철새이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155047', '16.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('454', '흰물떼새', 'Charadrius alexandrinus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Charadriidae', '물떼새과', 'Charadrius', '물떼새속', 'alexandrinus', '흰물떼새', '도요목 물떼새과의 조류로 국내에는 흔한 나그네새이며, 일부 집단은 해안 모래밭이나 휴경지, 염전 주변, 모래가 발달한 강 하구 등에서 지역적으로 번식하며, 일부는 월동도 한다. 몸길이 17.5cm. 수컷의 머리 위는 적갈색이며 이마 위에 흑색 무늬가 있다. 머리 위적갈색의 밝기는 개체 또는 계절적으로 차이가 심하다. 윗가슴 옆의 흑색 무늬는 앞가슴까지 연결되지 않고 중간에 끊긴다. 흑색 눈선은 다른 종보다 폭이 좁다. 다리는 분홍색이 도는 흑색이다. 해안의 모래밭이나 염전, 갯벌 등지에 서식한다. 빠르게 뛰다가 멈추어 갯지렁이나 소형 갑각류 등을 잡아먹고, 다시 빠르게 달려가 먹이를 잡는 행동을 반복한다. 둥지는 모래땅을 오목하게 파고 옅은 갈색에 흑색 무늬가 있는 알 3개를 낳는다. 암수가 교대로 포란하며, 포란기간은 24~26일이다. 북반부의 온대지역에서 번식하고, 겨울에는 남쪽으로 이동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155049', '17.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('455', '왕눈물떼새', 'Charadrius mongolus', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Charadriidae', '물떼새과', 'Charadrius', '물떼새속', 'mongolus', '왕눈물떼새', '도요목 물떼새과의 조류로 국내에서는 봄과 가을 비교적 흔하게 통과하는 나그네새이다. 몸길이 19.5cm.  큰왕눈물떼새와 매우 비슷하다. 부리는 전체가 검은색이며, 큰왕눈물떼새에 비해 짧다. 다리는 어두운 녹색이나 회색으로 보인다. 성조의 여름깃은 이마 위와 눈선, 귀깃이 검고 뒷목에서 가슴은 적갈색이다. 성조의 겨울깃은 얼굴이 흑갈색으로 변하며, 흰물떼새의 암컷과 비슷하지만 목 뒤의 흰색이 없다. 어린 새는 성조의 겨울깃과 비슷하지만, 몸 윗면에 비늘 무늬가 있으며, 얼굴과 가슴에 황갈색 기운이 있다. 해안의 갯벌, 염전, 강 하구, 해안 사구 등에서 작은 무리를 이루어 다른 물떼새류와 섞여 생활하며, 갯지렁이를 주로 먹는다. 파미르, 티베트, 캄차카, 추코트반도에서 번식하고, 아프리카 동부, 인도, 동남아시아, 뉴질랜드, 호주에서 월동한다. 우리나라에는 4월 초에서 5월 말에 관찰되며, 이후 7월 말에 도래하여 10월 중순까지 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155052', '19.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('456', '큰왕눈물떼새', 'Charadrius leschenaultii', 'Lesson', '1826', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Charadriidae', '물떼새과', 'Charadrius', '물떼새속', 'leschenaultii', '큰왕눈물떼새', '도요목 물떼새과의 새로 몸길이는 약 21cm이다. 수컷 여름깃의 이마는 흰색이며, 머리꼭대기와의 경계에는 검은색 띠가 있다. 이마의 중앙에는 세로로 검은색 선이 있으며 때로는 검은색의 얼룩무늬가 있다. 머리꼭대기, 뒷머리, 뒷목, 머리 옆은 붉게 녹슨 색이고, 머리꼭대기의 중앙 부분은 잿빛 갈색이다. 뺨, 턱 밑, 멱, 목 옆 일부는 흰색이고, 아랫목에는 얇고 붉게 녹슨 색 띠가 있다. 어깨깃, 등, 허리, 위꽁지덮깃은 잿빛 갈색이며, 가슴, 배, 옆구리, 아래꽁지덮깃은 흰색이다. 꽁지는 잿빛 갈색으로 중앙 이외의 꽁지깃 끝에는 흰색의 가장자리가 있다. 부리는 검은색이고 다리는 올리브 갈색이다. 해안의 모래사장, 사구, 하구, 삼각주 등지에 도래한다. 매우 드물게 한반도를 통과하는 나그네새이다. 작은 무리 또는 큰 무리 때로는 다른 물떼새 무리에 섞여 먹이를 찾아 몸을 상하로 흔들며 걷거나 달린다. 호반의 모래땅, 사막, 사력지의 잔돌 사이에 접시 모양의 둥지를 튼다. 산란기는 6월상순경이며, 한배의 산란수는 3개이다. 곤충류와 작은 게 등 동물성 먹이를 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155055', '21.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('457', '큰물떼새', 'Charadrius veredus', 'Gould', '1848', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Charadriidae', '물떼새과', 'Charadrius', '물떼새속', 'veredus', '큰물떼새', '도요목 물떼새과의 새이다. 몸길이는 22.5cm쯤으로 중형종에 속한다. 부리가 가늘고 발이 길다. 수컷 여름깃의 머리꼭대기와 뒷머리는 엷은 갈색이다. 이마, 눈 앞, 귀깃, 뺨, 턱 밑, 멱은 흰색이며, 귀깃의 뒤끝은 엷은 갈색이다. 눈 위에 엷은 황갈색 눈썹선이 있다. 등, 어깨깃, 허리, 위꽁지덮깃은 엷은 갈색이고, 윗가슴에 녹슨 색의 띠가 있으며 아랫부분에 또다시 검은색의 띠가 있다. 가슴, 배, 옆구리, 아래꽁지덮깃은 순수한 흰색이다. 꽁지는 어두운 갈색이며, 끝에는 흰색 가장자리가 있다. 부리는 검은색이고 다리는 더러운 황색이다. 번식지에서는 고원 호반의 자갈밭이나 흐르는 냇가에 산다. 지상에서 신속히 가볍게 달리며 먹이를 찾아 빠르고 힘차게 칼새처럼 난다. 곤충류를 즐겨 먹는다. 우리나라 제주도, 강화도 등지에 서식한다. 몽골과 바이칼 등지에서 번식하고 오스트레일리아에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155056', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('458', '흑꼬리도요', 'Limosa limosa', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Limosa', '흑꼬리도요속', 'limosa', '흑꼬리도요', '도요목 도요과의 조류로 우리나라에는 봄가을에 흔하게 통과하는 나그네새이다. 몸길이 36.5-38.5cm.  다리와 부리가 길며, 부리는 직선으로 끝부분을 제외하고 분홍색이다. 날 때 꼬리 끝은 넓은 검은색, 기부는 흰색으로 보여 다른 종과 쉽게 구별된다. 수컷의 번식깃은 몸 윗면에 검은색과 적갈색의 반점이 있으며, 머리에서 앞가슴까지 적갈색이다. 옆구리에는 흰색 바탕에 검은 줄무늬가 있다. 암컷이 수컷보다 크고 머리와 가슴 부분의 붉은색이 수컷에 비해 연하며, 부리도 더 길다. 겨울깃은 붉은색 기운이 없어지고 몸 윗면이 전체적으로 회갈색으로 변하며, 옆구리와 배의 줄무늬도 없어져 흰색이 된다. 얕은 물이 고인 논이나, 강 하구, 갯벌에서 서식하며, 무리를 이루어 먹이를 찾는다. 갯지렁이, 갑각류 등 다양한 무척추동물을 먹으며, 농경지에서는 볍씨도 먹는다. 유라시아대륙 중부에서 번식하고, 아프리카, 유럽 남부, 인도, 동남아시아, 호주에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155073', '37.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('459', '큰뒷부리도요', 'Limosa lapponica', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Limosa', '흑꼬리도요속', 'lapponica', '큰뒷부리도요', '도요목 도요과의 조류로 우리나라에는 봄과 가을 흔하게 통과하는 나그네새이다. 몸길이 38.5-41cm.  부리와 다리가 길다. 부리는 위로 굽었고 어두우며, 기부 쪽은 분홍색이다. 암컷이 수컷보다 크며, 부리도 길다. 수컷의 여름깃은 머리에서 배까지 적갈색이고 몸 윗면은 흑갈색이며 적갈색 반점이 있다. 암컷은 수컷에 비해 적갈색이 연하며, 몸 윗면이 어둡게 보인다. 겨울깃은 몸 윗면이 회갈색에 흑갈색 무늬가 나타나며 몸 아랫면은 회백색으로 변한다. 해안의 모래밭, 갯벌, 강 하구, 물 고인 논, 하천가에서 생활한다. 보통 큰 무리를 이루며, 갑각류, 갯지렁이, 수서곤충 등 다양한 무척추동물을 잡아먹는다. 유라시아대륙 북부, 알래스카 서부에서 번식하고, 유럽, 아프리카, 중동, 동남아시아, 호주에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155076', '39.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('460', '흰눈썹바다오리', 'Cepphus carbo', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Alcidae', '바다오리과', 'Cepphus', '흰눈썹바다오리', 'carbo', '흰눈썹바다오리', '도요목 바다오리과의 조류로 몸길이 37cm, 날개 18~20cm가량이다. 암컷과 수컷 모두 눈 주위에 흰색의 꽁지 모양 얼룩무늬가 있다. 그 밖의 깃털은 검은색이다. 겨울깃의 몸 아랫면은 흰색이다. 부리는 검은색이며, 다리는 붉은색이다. 알은 엷은 녹청색, 회백색, 황백색 바탕에 검은 갈색의 얼룩무늬가 있다. 잠수 중에는 날개를 펴서 수중을 나는 듯이 헤엄친다. 날 때는 날개를 심하게 펄럭이며 해면을 스치듯이 낮게 비상한다. 암초에서 휴식할 때는 몸을 직립하고, 부척(跗蹠)을 굽혀 땅에 닿게 한다. 암초의 틈이나 바위틈을 잠자리로 사용한다. 둥지는 만들지 않고 해안에 있는 암초의 틈이나 바위틈, 또는 바위 위에 직접 산란한다. 4월 중순~5월, 2~3개 알을 낳는다. 먹이는 작은 어류, 갑각류 등이다. 우리나라 강원도 해안가, 경상남도 낙동강 하구, 부산 앞바다 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155159', '37.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('461', '벙어리뻐꾸기', 'Cuculus optatus', 'Gould', '1845', 'Chordata', '척삭동물문', 'Aves', '조강', 'Cuculiformes', '두견목', 'Cuculidae', '두견과', 'Cuculus', '두견속', 'optatus', '벙어리뻐꾸기', '두견목 두견과에 속하는 조류로 몸길이는 33cm가량이다. 뻐꾸기와 비슷하나 배의 줄무늬가 약간 더 굵다. 눈은 붉은색이고 노란색 눈테가 있다. 산림에 산다. 탁란하는 종은 산솔새, 쇠솔새, 숲새, 동박새, 흰눈썹황금새, 큰유리새, 삼광조 등이다. 산란기는 5월상순에 시작하나 3월부터 울음소리가 들린다. ‘보보 보보’하면서 2음절을 반복한다. 우리나라에서는 흔하지 않은 여름철새이다. 세계적으로는 유럽 동부에서 러시아, 중국 동부와 남부, 한반도, 일본에서 번식하고, 중국 남부, 동남아시아에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155179', '33.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('462', '가면올빼미', 'Tyto longimembris', 'Jerdon', '1839', 'Chordata', '척삭동물문', 'Aves', '조강', 'Strigiformes', '올빼미목', 'Tytonidae', '가면올빼미과', 'Tyto', '가면올빼미속', 'longimembris', '가면올빼미', '가면올빼미는 중형 크기의 올빼미로, 주로 작은 설치류를 먹고 산다. 몸 길이는 수컷이 32~38cm, 암컷이 35~42cm이며, 날개 길이는 100~116cm에 이른다. 이 새는 얼굴이 하트 모양의 디스크 형태를 하고 있으며, 깃털의 색은 짙은 갈색에 연한 반점이 있다. 검은색과 황갈색으로 된 날개와 아주 옅은 부리, 깃털로 덮인 다리와 눈은 짙은 갈색을 띤다. 가면올빼미는 주로 높은 초원과 습지에서 서식하며, 둥지는 주로 식물이 납작하게 눌린 곳에 짓는다. 동남아시아, 필리핀, 호주 및 서태평양 지역에 분포하며, 전 세계적으로는 ''관심 필요'' 등급으로 분류되고 있으나, 특정 지역에서는 취약한 상태로 간주된다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155189', '37.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('463', '쏙독새', 'Caprimulgus jotaka', 'Temminck & Schlegel', '1845', 'Chordata', '척삭동물문', 'Aves', '조강', 'Caprimulgiformes', '쏙독새목', 'Caprimulgidae', '쏙독새과', 'Caprimulgus', '쏙독새속', 'jotaka', '쏙독새', '쏙독새목 쏙독새과에 속하는 조류이다. 몸 전체 길이는 27~32cm이다. 머리와 눈이 비교적 크며, 날 때는 긴 날개와 긴 꼬리가 특징적이다. 암컷과 수컷 모두 몸 전체가 갈색, 검은색, 회색이 섞여서 얼룩무늬를 하고 있어서 나뭇가지나 지면에 앉아 있으면 나뭇가지의 일부 또는 낙엽처럼 보이기 때문에 눈에 띄지 않는다. 흔한 여름철새로 4-5월에 걸쳐 도래한다. 야행성으로 낙엽활엽수나 침엽수 또는 혼효림 등의 산림과관목이 산재하는 풀숲에서 생활한다. 낮에는 어두운 숲속이나 우거진 나뭇가지에 의지하여 가지와 같은 방향으로 가슴을 꼭 붙이고 있기 때문에 잘 관찰되지 않는다. 대부분은 단독 생활을 한다. 산란기는 5-8월이지만, 대부분 6월에 침엽수와 혼효림 내의 풀숲 공지에 둥우리를 틀지 않고 2개의 알을 낳고, 암수가 교대로 품는다. 주로 나방, 딱정벌레, 물매미, 벌, 메뚜기 등의 곤충류이며, 날아다니면서 큰 입을 벌려 잡아먹는다. 전국에 서식하고 세계적으로는 러시아, 극동 남부, 중국 남동부, 동남아시아, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155214', '29.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('464', '진박새', 'Periparus ater', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Paridae', '박새과', 'Periparus', '진박새속', 'ater', '진박새', '참새목 박새과의 새로 몸길이는 11cm쯤이다. 수컷 겨울깃의 눈 앞, 이마, 머리꼭대기, 뒷목의 양옆은 푸른빛이 강한 검은색이고, 뒷목의 중앙은 흰색이다. 머리꼭대기의 깃털은 작은 모관을 형성한다. 등 쪽은 그을음이 낀 것 같은 잿빛 청색으로 허리와 위꽁지덮깃은 다소 황갈색을 띤다. 가슴 이하의 아랫면은 전부 크림색으로 배와 겨드랑이는 더러운 잿빛을 띤다. 꽁지와 날개깃은 엷은 검은 갈색이다. 꽁지는 얕은 오목꽁지이다. 홍채는 어두운 갈색이고, 다리는 비교적 튼튼하고 청회색이다. 온대, 아한대 지역에서 번식한다. 아고산대의 잡목림 또는 낙엽활엽수림에서 서식한다. 우리나라 전역에서 번식하는 흔한 텃새이다. 번식기에는 암수가 함께 생활하며 기타 시기에는 군서 생활을 할 때가 많다. 천연 수동(樹洞)이나 딱따구리류가 뚫은 옛 둥지 또는 나무줄기가 갈라진 틈을 이용해서 영소한다. 5~7월에, 한배에 5~8개 산란한다. 곤충류가 주식이며 거미류 및 식물 열매도 먹는다. 유라시아 대륙에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155319', '11.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('465', '곤줄박이', 'Sittiparus varius', 'Temminck & Schlegel', '1847', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Paridae', '박새과', 'Sittiparus', '곤줄박이속', 'varius', '곤줄박이', '참새목 박새과에 속하는 조류이다. 몸길이는 14cm이다. 윗면은 푸른빛을 띤 회색이고 아랫면은 밤색이다. 머리는 검은색이고 이마와 얼굴은 유백색이다. 턱과 멱은 검은색이고 윗가슴은 유백색이다. 산림, 공원 및 삼림에 살며 흔한 텃새이다. 나무 구멍이나 건물 틈에서 번식한다. 번식기에는 암수 함께 생활하며, 그 이후에는 작은 무리로 생활하든가 다른 종과 섞여 무리를 이룬다. 4-7월에 5-8개의 알을 낳는다. 곤충을 주식으로 하고 각종 종자와 열매를 먹는다. 전국에 서식하고 세계적으로는 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155325', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('466', '종다리', 'Alauda arvensis', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Alaudidae', '종다리과', 'Alauda', '종다리속', 'arvensis', '종다리', '참새목 종다리과에 속하는 조류이다. 몸길이는 18cm이다. 몸은 엷은 황갈색이고, 머리, 등, 작은 날개덮깃과 가운데날개덮깃은 적갈색으로 무늬가 없다. 날개, 가슴에는 검은색 줄무늬가 있다. 머리깃은 짧고 둥글다. 우리나라 전역 저지대에 걸쳐 경작지 및 초지 등에서 번식하는 흔한 텃새이자 겨울철새이다. 풀밭에 마른풀이나 가는 뿌리로 밥그릇 모양의 둥지를 튼다. 알은 3-6개를 낳는다. 영국에서 일본까지 북위 30° 이북의 유럽과 아시아에 걸쳐 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155370', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('467', '개개비', 'Acrocephalus orientalis', 'Temminck & Schlegel', '1847', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Acrocephalus', '개개비속', 'orientalis', '개개비', '참새목 휘파람새과에 속하는 조류이다. 전체적으로 균일하게 옅은 갈색을 띤다. 아랫면은 더 옅은 색이고 배는 흰색에 가깝다. 흰색 눈썹선이 있다. 암컷과 수컷은 색으로 구별하기 힘들다. 습지 가장자리 갈대와 풀에 살며 여름철새이다. 둥지는 갈대나 부들 줄기를 서로 엮어 만든다. 한 장소에 여러 마리가 있기도 해서 한참 울 때는 수컷들의 소리가 많이 들린다. 갈대나 풀 속에서 주로 살며, 울 때는 갈대 끝에 올라와 큰 소리로 운다. 전국에 서식하고 세계적으로는 러시아 연해주, 중국 동남부, 일본에, 동남아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155413', '19.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('468', '쇠개개비', 'Acrocephalus bistrigiceps', 'Swinhoe', '1860', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Acrocephalus', '개개비속', 'bistrigiceps', '쇠개개비', '참새목 휘파람새과에 속하는 조류이다. 몸 크기는 14cm 정도이다. 수컷 겨울깃은 이마에서 머리꼭대기와 뒷목을 지나 등까지는 올리브색을 띤 녹슨 갈색으로 머리꼭대기의 양쪽 즉, 눈썹선 위에 연결되는 부분은 검은 갈색이다. 턱 밑, 멱의 중앙, 배의 중앙은 황갈색을 띤 흰색이다. 멱의 양쪽, 가슴, 옆구리, 아래꼬리덮깃은 황갈색으로 옆구리는 다소 진하고, 또한 갈색을 띤다. 허리와 위꽁지덮깃은 엷고 녹슨 갈색이다. 꽁지는 둥글다. 홍채는 엷은 갈색 또는 갈색이다. 다리는 잿빛과 붉은색 또는 푸른색을 띤 납색이다. 고원, 물가 또는 건조한 초지에 영소한다. 한반도 전역의 갈밭에서 드물게 번식하는 여름새이나, 봄과 가을에 우리나라를 통과하는 무리가 대부분인 나그네새이다. 단독 또는 암수 함께 생활하나 번식지에서는 서식 밀도가 보통 높다. 6~8월상순에, 한배에 보통 4~6개 산란한다. 식성은 개개비와 흡사하다. 세계적으로는 한국, 구북구 동부, 몽골, 만주, 아무르, 우수리, 사할린, 일본, 중국 등지에서 번식한다. 중국 남부, 태국, 미얀마, 인도 아샘 지방에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155417', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('469', '북방쇠개개비', 'Acrocephalus agricola', 'Jerdon', '1845', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Acrocephalus', '개개비속', 'agricola', '북방쇠개개비', '북방쇠개개비는 참새목의 조류로, 주로 습지의 긴 풀이나 갈대, 벼 같은 낮은 식생에서 서식합니다. 몸길이 약 13cm로, 유럽개개비와 비슷한 크기지만 부리와 날개 길이가 더 짧습니다. 성체는 연갈색의 등이 있으며, 따뜻한 갈색 엉덩이와 희끗한 눈썹선이 특징입니다. 암수의 모습이 동일하며, 어린 새는 더 진한 갈색을 띠고 있습니다.    이 새는 주로 곤충을 먹으며, 노래는 빠르고 다양한 모방 소리와 휘파람이 섞여 있습니다. 중부 팔레아르크틱 지역에서 번식하며, 방글라데시, 인도, 파키스탄으로 이주하여 겨울을 납니다. 드물게 서유럽에서 목격되기도 하며, 흑해 서쪽 연안에 작은 번식 집단이 존재합니다. 번식기에는 한 번에 4~5개의 알을 낳습니다. IUCN 등급에 따르면 멸종 위기에 처하지 않은 종으로 분류됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155418', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('470', '쇠흰턱딱새', 'Sylvia curruca', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Sylvia', '쇠흰턱딱새속', 'curruca', '쇠흰턱딱새', '참새목 휘파람새과의 작은 새이다. 전체 길이는 13cm이고 등은 갈색, 배는 흰색을 띤다. 멱은 순백색이고, 머리는 회색, 꽁지는 갈색이며, 바깥꽁지깃은 흰색이다. 암수가 비슷하다. 알은 회백색 바탕에 진한 점무늬가 있다. 경작지, 덤불,관목숲 등에 서식한다. 나무와 덤불에 둥지를 튼다. 5~6월에 알을 4~6개 낳는다. 러시아 유럽 지역, 시베리아 레나강, 바이칼, 중앙아시아 카자흐스탄, 이란 남부, 인도, 아프리카에서 월동한다. 우리나라에서는 부산에서 겨울에 한 마리를 채집한 기록이 있는 미조(迷鳥)이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155437', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('471', '붉은머리오목눈이', 'Suthora webbiana', 'Gould', '1852', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Timaliidae', '붉은머리오목눈이과', 'Suthora', '붉은머리오목눈이속', 'webbiana', '붉은머리오목눈이', '붉은머리오목눈이는 참새목에 속하는 작은 새로, 주로 중국, 일본, 한국, 몽골, 러시아, 타이완, 베트남 등지에 서식합니다. 자연 서식지는 아열대 또는 열대의 습윤 산악림이며, 덤불, 숲 가장자리, 대나무 숲 등에서 주로 발견됩니다. 인간이 개입한 서식지로도 쉽게 적응하여 차밭이나 묘목장에서도 볼 수 있습니다. 몸길이는 약 11~12.5cm 정도로, 암수 모두 비슷한 깃털 색을 가집니다. 윗부분은 따뜻한 갈색이며, 목과 윗가슴은 연한 핑크빛 크림색에 갈색 줄무늬가 있습니다. 붉은머리오목눈이는 사회성이 강해 무리를 지어 생활하며, 겨울철에는 최대 140마리까지 모이는 큰 무리를 형성하기도 합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155445', '11.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('472', '울새', 'Larvivora sibilans', 'Swinhoe', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Larvivora', '쇠유리새속', 'sibilans', '울새', '참새목 솔딱새과에 속하는 조류이다. 몸길이는 14cm이다. 암수의 모양은 같다. 이마의 양쪽 즉, 눈썹 앞 끝에 해당하는 부분은 붉은 흙빛 황갈색이다. 뺨, 귀깃, 목 옆은 녹슨 색을 띤 갈색이다. 아랫면은 모두 조금 붉은 흙색을 띤 흰색으로 멱과 가슴 깃털은 올리브 갈색의 좁은 가장자리가 있기 때문에 그물과 같은 모습을 나타낸다. 꼬리는 밤색이다. 날개깃은 갈색으로 바깥가장자리는 올리브 갈색이다. 배 옆과 경부는 올리브 잿빛이다. 부리는 가늘고 뾰족하며 부리의 털은 짧고, 콧구멍은 노출된다. 홍채는 어두운 갈색이다. 침엽수림에 살며 5월과 10월에 한반도를 통과하는 나그네새이다. 이른 아침에는 숲 가장자리 부근에서 행동하며, 낮에는 침엽수가 어둡게 무성한 밀림 속의 쓰러진 나무 위나 하초가 밀생한 사이를 걸어 다닌다. 6-7월에 3-4개의 알을 낳는다. 전국에서 관찰되고 세계적으로는 시베리아, 중국, 인도차이나에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000155492', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('473', '뿔제비갈매기', 'Thalasseus bernsteini', '(Schlegel)', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Thalasseus', '뿔제비갈매기속', 'bernsteini', '뿔제비갈매기', '뿔제비갈매기는 참갈매몽과에 속하는 조류로, 몸길이는 38–43cm인 중대형 새이다. 주로 흰색의 꼬리와 옅은 회색의 날개를 가지며, 번식기에는 검은색 머리 깃을 갖는다. 부리는 노란색에 검은 끝부분이 있으며, 약간 두껍다. 이 새는 멸종 위기종으로, 중국 해안 북부에서 번식하며 필리핀 남부까지 이동한다. 과거에는 널리 분포했으나, 식용을 위한 사냥과 알 채취로 인해 개체 수가 급감했다. 현재는 대만 마쭈 열도의 보호구역 내에서 몇 쌍이 발견되었으며, 대한민국 남부 지역에서도 번식이 확인되었다. 주요 위협 요인으로는 밀렵, 태풍, 어부에 의한 서식지 교란이 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000212501', '40.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('474', '쇠바위종다리', 'Prunella rubida', '(Temminck & Schlegel)', '1848', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Prunellidae', '바위종다리과', 'Prunella', '바위종다리속', 'rubida', '쇠바위종다리', '쇠바위종다리는 참새목 바위종다리과의 조류로, 주로 일본과 사할린의 온대 숲에서 서식합니다. 보존 상태는 ''관심 필요''로 분류되며, 특정한 위협 없이 안정적인 개체수를 유지하고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000213514', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('475', '비늘무늬덤불개개비', 'Sylvia nisoria', '(Bechstein)', '1795', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Sylvia', '쇠흰턱딱새속', 'nisoria', '비늘무늬덤불개개비', '비늘무늬덤불개개비는 참새목 휘파람새과에 속하는 조류로, 몸길이는 15.5~17cm이며 몸무게는 22~36g이다. 주로 유럽 중부 및 동부와 아시아 서부 및 중부의 온대 지역에서 번식하고, 겨울에는 아프리카 동부의 열대 지역으로 이동한다. 수컷 성체는 윗면이 짙은 회색이고 날개덮깃과 꼬리깃에 흰색 끝이 있으며, 아랫면에는 뚜렷한 줄무늬가 있다. 암컷은 수컷과 유사하지만 약간 더 연한 색이며 줄무늬가 덜 뚜렷하다. 어린 개체는 윗면이 회갈색이고 아랫면이 흐릿한 노란색이다. 주요 서식지는 관목이 있는 개활지로, 주로 곤충을 먹지만 늦여름과 가을에는 열매도 섭취한다. 노래는 정원휘파람새와 비슷하지만 덜 부드럽고 약간 높은 소리를 낸다. 서유럽에서 개체 수가 감소하고 있지만, 동유럽과 동남아시아에서는 안정적이다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000213517', '16.2');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('476', '붉은가슴기러기', 'Branta ruficollis', '(Pallas)', '1769', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Branta', '흑기러기속', 'ruficollis', '붉은가슴기러기', '붉은가슴기러기는 기러기목 오리과에 속하는 조류로, 화려한 외모를 자랑하는 종이다. 몸길이는 약 53~56cm로, 기러기 중에서는 비교적 작은 편이다. 이 종은 주로 검고 그을린 색을 띠는데, 이는 흰색으로 브라질리스크 기러기와 구분되는 특징이다. 번식지는 시베리아 북극지방이며, 주로 타이미르 반도, 기다 및 야말 반도에서 번식한다. 겨울에는 주로 흑해 북서부 연안(불가리아, 루마니아, 우크라이나)에서 월동하지만, 아제르바이잔에서도 발견될 수 있다.    붉은가슴기러기는 먹이를 구하거나 번식할 때 조류의 둥지 근처에 둥지를 틀며, 눈올빼미나 매 등의 맹금류 근처에 서식함으로써 포식자로부터 보호 받는다. 이와 같은 전략은 이들이 육상 포식자로부터 안전하게 지낼 수 있도록 도와준다.    보전 상태는 취약(Vulnerable)으로 평가받고 있으며, 국제자연보전연맹(IUCN)에 의해 보호가 필요하다고 여겨진다. 1997년 기준 붉은가슴기러기 개체 수는 약 88,000 마리로 추산되었으나, 주로 농지 변화와 같은 이유로 월동지에서의 서식지가 위협받고 있다. 공격적인 보전 정책과 연구를 통해 이들의 서식지와 생활방식을 보호하고자 하는 노력이 진행되고 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000213518', '54.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('477', '흰제비갈매기', 'Gygis alba', '(Sparrman)', '1786', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Gygis', '흰제비갈매기속', 'alba', '흰제비갈매기', '흰제비갈매기는 전 세계 열대 해양 지역에서 발견되는 작은 바다새입니다. 몸길이는 23~30cm에 날개 길이는 76~87cm입니다. 순백색의 깃털과 검고 푸른색을 띠는 부리가 특징입니다. 번식기에는 해안가 및 숲으로 이동하며, 주로 나무의 작은 가지나 인공 구조물에 알을 낳습니다. 흰제비갈매기의 눈에 띄는 특징은 둥지를 짓지 않고 나뭇가지 틈에 바로 알을 낳는 것으로, 이는 둥지 기생충을 줄이기 위한 것으로 보입니다. 작은 물고기를 사냥하여 먹으며 상괭이, 후첩시틀에 서식합니다. 해안에서 주로 생활하고, 구애하거나 목욕시 침입한 포식자에 대해 빠르게 반응합니다. 특히 하와이에서 공식 지역새로 지정되었습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000213523', '26.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('478', '뿔바다오리', 'Aethia cristatella', 'Pallas', '1769', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Alcidae', '바다오리과', 'Aethia', '뿔바다오리속', 'cristatella', '뿔바다오리', '뿔바다오리는 북태평양과 베링해에 분포하는 작은 바닷새이다. 이 조류는 깊은 물에서 다이빙하여 먹이를 잡으며, 주로 크릴과 여러 작은 해양 동물을 먹는다. 번식기는 5월 중순부터 8월 중순까지로, 베링해와 오호츠크해의 섬과 해안에 최대 백만 마리의 밀집된 집단에서 번식한다. 성별에 상관없이 이 새들은 이마에 검은 깃털로 이루어진 독특한 장식을 가지고 있으며, 감귤 향기를 뿜어내고 소리 내는 특징이 있다. 이러한 특징들은 주로 번식기를 통해 성 선택에 의해 진화한 것으로 보인다.    뿔바다오리는 길이 18~27cm, 날개폭 34~50cm이고 몸무게는 195~330g이다. 주로 어두운 회색의 몸을 가졌으며, 부리는 주황색을 띠며 끝에는 노란색이 감돈다. 한편, 이 새들은 혼자 또는 혼합된 종과 함께 번식하며, 알래스카의 개체들은 포식과 기름 유출로 인한 위협을 받고 있다. 그러나 전반적으로는 개체수가 600만 마리가 넘는 것으로 평가되며, ''관심 필요(Least Concern)'' 등급으로 분류된다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000232355', '22.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('479', '레이산알바트로스', 'Phoebastria immutabilis', 'Rothschild', '1893', 'Chordata', '척삭동물문', 'Aves', '조강', 'Procellariiformes', '슴새목', 'Diomedeidae', '알바트로스과', 'Phoebastria', '알바트로스속', 'immutabilis', '레이산알바트로스', '레이산알바트로스는 우리나라에서 발견되는 새로, 긴 날개를 가진 대형 바닷새입니다. 주로 북태평양에서 서식하며, 하와이 북서부 섬 지역에 대부분의 개체가 집중되어 있습니다. 평균 몸길이는 약 81cm이며, 날개 길이는 195~203cm에 달합니다. 이 종은 머리와 아래쪽이 흰색이고 윗날개와 등이 어두운 회색을 띠며, 부리는 분홍색입니다.    레이산알바트로스는 다른 알바트로스와는 달리 번식 깃털 변화가 없습니다. 하와이에서 11월부터 7월까지 번식활동을 하며, 일생 동안 한 쌍으로 유지되는 짝짓기 습성을 가집니다. 암컷끼리 쌍을 이루어 협동해서 새끼를 기르는 경우도 관찰됩니다.     주로 오징어, 물고기, 갑각류 등을 먹으며, 현재 세계자연보전연맹(IUCN)에서 ''근접 위협'' 등급으로 보호받고 있습니다. 역사적으로 깃털 채취와 낚시 도구로 인한 위험에 처했지만, 최근까지 개체수가 안정적으로 증가하고 있습니다.     위험 요소로는 주로 긴 줄 낚시와 해양 오염 등이 있으며, 이러한 상황을 개선하여 지속 가능한 보전 활동이 필요합니다. 특히, 미드웨이 환초에서 납 중독 방지를 위한 정화 작업이 진행 중입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000230685', '81.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('480', '알류샨제비갈매기', 'Onychoprion aleuticus', 'S.F.Baird', '1869', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Onychoprion', '검은등제비갈매기속', 'aleuticus', '알류샨제비갈매기', '알류샨제비갈매기는 극지방에서 주로 서식하는 철새로, 여름에는 알래스카와 러시아 동부의 해안 지역에서 번식합니다. 몸길이는 약 32~39cm이며, 날개 길이는 75~80cm로 중형 크기의 제비갈매기입니다. 검은색 부리와 다리를 가지고 있으며, 번식기에는 하얀 이마와 검은 머리색을 특징으로 합니다.    이 새는 겨울에는 서부 태평양 가까이로 이동하여 주로 인도네시아와 말레이시아 연안에서 생활합니다. 알류샨제비갈매기는 "취약"으로 분류되고 있으며, 주된 위협 요인으로 서식지 변화, 포식, 인간 방해가 있습니다.    번식 시기에는 대부분 알래스카와 시베리아에 작은 군집을 이루며, 보통 두 개의 알을 낳습니다. 번식 성공률이 낮으며, 짝은 번식지에서 형성되고 둘 다 알을 품고 새끼에게 먹이를 줍니다. 식사는 주로 작은 물고기와 갑각류, 곤충 등입니다. 이들은 호버링을 하면서 물 표면에서 먹이를 건져 올리는 방식으로 먹이를 찾습니다.    인간 활동에 매우 민감하여 방해받으면 서식지를 기피할 가능성이 높습니다. 보호 노력이 필요하며, 더 많은 연구가 진행되어야 하는 종입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000230689', '35.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('481', '검은어깨매', 'Elanus caeruleus', 'Desfontaines', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Elanus', '검은어깨매속', 'caeruleus', '검은어깨매', '검은어깨매는 참매과에 속하는 작은 맹금류로, 유라시아와 아프리카에 서식합니다. 주로 탁 트인 초원이나 반사막 지역에서 발견되며, 이 새는 특유의 호버링(공중 정지) 행태로 잘 알려져 있습니다. 날개는 길며 흰색, 회색, 검정색의 깃털을 가지고, 부엉이 같은 붉은 눈을 특징으로 합니다. 주로 설치류, 곤충 등을 먹으며, 높은 산악 지역에서도 관찰됩니다.    검은어깨매는 이례적인 번식 특성으로 정평이 나 있습니다. 해마다 여러 번 번식할 수 있으며, 설치류 개체 수가 급증할 때 이를 활용할 수 있습니다. 또한, 집단 서식지를 형성하여 높은 나무에 무리지어 쉬는 습성을 보입니다. 교미 및 번식기 동안 빈번한 소리를 내며, 남아프리카에서는 로드킬로 인한 위협을 받기도 합니다.      검은어깨매는 비교적 외부 위협에 강한 편이며, 남유럽에서는 인간 활동, 특히 농업과 목축업에 대한 적응력 덕분에 개체 수가 증가하고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000230691', '33.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('482', '풀쇠개개비', 'Acrocephalus schoenobaenus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Acrocephalus', '개개비속', 'schoenobaenus', '풀쇠개개비', '풀쇠개개비는 참새목 개개비과에 속하는 중형급 조류로, 몸길이 11.5~13cm 정도이며, 무게는 약 12g입니다. 이 새는 갈색의 줄무늬가 있는 등과 날개를 가지고 있으며, 눈 위에 흰색의 눈썹선이 뚜렷합니다. 수컷의 노래는 불규칙한 수다스러운 소리와 다른 새들의 소리를 모방하는 것이 특징입니다.    풀쇠개개비는 주로 곤충을 먹으며, 유럽과 아시아의 습지에서 번식합니다. 번식철에는 보통 갈대밭이나 수변 초목지에서 서식하며, 수컷은 노래로 암컷을 유인합니다. 8월부터 9월 사이에는 사하라 사막을 넘어 아프리카로 이주하여 겨울을 보냅니다. 아프리카에서는 세네갈에서 에티오피아, 남아프리카 공화국 동부 및 나미비아 북부까지 다양한 지역에서 발견될 수 있습니다.    이 새는 특히 습지의 감소와 사막화로 인한 위협을 받고 있지만, 국제자연보전연맹(IUCN)에 의해 멸종위기종으로는 분류되지 않았습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000230695', '12.2');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('483', '꼬마오리', 'Bucephala albeola', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Bucephala', '흰뺨오리속', 'albeola', '꼬마오리', '꼬마오리는 작은 바다오리로, 주로 북미 지역에 서식하는 겨울철새입니다. 이 오리는 몸길이 32-40cm로 작은 편이며, 수컷은 검정과 흰색 무늬의 깃털과 녹색과 보라색 광택이 있는 머리를 가지고 있습니다. 암컷은 회색 빛이 돌며 눈 뒤에 작은 흰색 반점이 있습니다. 번식지는 알래스카와 캐나다의 숲지대에 위치하며, 겨울에는 북미 남부 해안으로 이동하여 보호된 해안이나 내륙의 물가에서 겨울을 납니다.  꼬마오리는 주로 나무의 구멍에 둥지를 틉니다. 번식기에는 주로 나무의 딱따구리가 남긴 구멍을 사용하며, 매년 같은 둥지로 돌아오기도 합니다. 한 번에 6-11개의 알을 낳으며, 약 30일간의 부화 과정을 거칩니다. 유충은 둥지를 떠난 후 50-55일에 이르면 날기 시작합니다.  꼬마오리는 주로 물속에서 잠수하여 곤충, 갑각류, 패류 등을 먹습니다. 이들은 활동적이며, 그룹 내에서는 한 마리가 보초를 서고 나머지는 먹이를 찾습니다. 겨울에는 여러 무리를 이루어 생활하며, 관찰자들에게 인기가 높은 새입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000230684', '36.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('484', '검은뿔찌르레기', 'Acridotheres cristatellus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sturnidae', '찌르레기과', 'Acridotheres', '뿔찌르레기속', 'cristatellus', '검은뿔찌르레기', '검은뿔찌르레기는 동남중국과 인도차이나에 서식하는 참새목 찌르레기과의 조류입니다. 이 새의 이름은 이마에 있는 깃털이 볏 모양을 형성하기 때문에 붙여졌습니다. 주로 도시와 농촌의 개방된 공간에서 발견되며, 인기 있는 애완조로도 알려져 있습니다. 검은뿔찌르레기는 잡식성으로 곤충, 과일, 곡물, 고기 및 쓰레기를 먹습니다. 번식기에는 주로 수컷과 암컷이 함께 둥지를 만들며, 한 시즌에 두 번 산란합니다. 둥지는 주로 나무 구멍이나 도심의 틈새에 지어집니다. 검은뿔찌르레기는 넓은 서식지와 안정적인 개체수로 인해 멸종 위기에서는 벗어나 있으며, 농작물에 해를 끼치지 않고 곤충을 잡아먹어 농부들에게 유익한 새로 여겨집니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000230696', '26.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('485', '갈색지빠귀', 'Turdus feae', 'Salvadori', '1887', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'feae', '갈색지빠귀', '갈색지빠귀는 중간 크기의 온난한 갈색을 띠는 지빠귀과 조류입니다. 수컷은 적갈색 올리브빛 상체와 회색 하체를 가지며, 암컷은 목과 가슴 중앙이 흰색이고 가슴 깃털에 따뜻한 갈색 테두리가 있습니다. 중국 북동부의 산악 지대에서 번식하며, 인도와 인도차이나 지역의 아열대 또는 열대 수림으로 이동합니다. 곤충과 열매를 주로 먹고, 때때로 나무에서도 먹이를 찾습니다. 특히 눈썹지빠귀와 함께 있는 경우가 많습니다. 서식지 파괴로 위협 받고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000230697', '24.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('486', '회색머리지빠귀', 'Turdus pilaris', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'pilaris', '회색머리지빠귀', '회색머리지빠귀는 참새목 지빠귀과에 속하는 조류로, 몸길이는 약 25cm이다. 북유럽과 구북구 전역의 숲과 관목 지대에서 번식하며, 겨울에는 남쪽으로 이동해 대규모로 월동한다. 우리나라에서는 드물게 관찰된다. 잡식성으로 여름에는 연체동물, 곤충, 지렁이 등을 먹고, 겨울에는 베리, 곡식, 씨앗을 먹는다. 작은 집단으로 둥지를 틀며, 둥지는 주로 나무 위에 지어지며 한 번에 5~6개의 알을 낳는다. 번식지에서는 홀로 또는 작은 무리로 둥지를 방어하며, 겨울철에는 큰 무리를 지어 이동한다. 성별 차이는 크지 않으나 암컷이 수컷보다 약간 더 갈색을 띤다. "챠크 챠크"라는 독특한 비행 음성을 가지고 있다. 이 종은 주로 유럽과 북부 아시아에서 발견되며, 전 세계적으로는 멸종 위기 등급에서 비교적 안전한 상태로 평가된다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000230698', '25.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('487', '적갈색따오기', 'Plegadis falcinellus', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Ciconiiformes', '황새목', 'Threskiornithidae', '저어새과', 'Plegadis', '적갈색따오기속', 'falcinellus', '적갈색따오기', '적갈색따오기는 대표적인 물새로, 전 세계에 널리 분포하는 종이다. 주로 따뜻한 지역의 습지에서 번식하며, 북유럽의 경우 겨울철에는 아프리카로 이동한다. 몸길이는 48~66cm이며, 날개 길이는 80~105cm에 이른다. 번식기의 적갈색 몸과 녹색 광택이 나는 날개가 특징적이다. 서식지는 주로 강가나 호수의 습지이며, 농경지와 같은 인공 환경에서도 발견된다. 계절에 따라 먹이는 곤충과 무척추동물부터 작은 어류나 양서류까지 다양하다. 번식지는 주로 갈대숲이나 작은 나무 위에 둥지를 틀며, 3~4개의 알을 낳는다. 적갈색따오기는 국제자연보전연맹(IUCN)에서 관심대상종으로 분류되어 있으며, 서식지 파괴가 주요 위협 요인이다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000230687', '57.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('488', '회색머리노랑딱새', 'Culicicapa ceylonensis', 'Swainson', '1820', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Stenostiridae', '요정딱새과', 'Culicicapa', '회색머리노랑딱새속', 'ceylonensis', '회색머리노랑딱새', '회색머리노랑딱새는 참새목에 속하는 작은 조류로, 열대 아시아에 서식하고 있습니다. 몸길이는 약 12~13cm이며, 회색의 머리와 노란 배, 황록색의 상체를 가지고 있습니다. 성별에 따른 깃털 차이는 없습니다. 주로 숲에서 활동하며, 곤충을 잡아먹습니다. 이 종은 다양한 아종으로 나뉘며, 인도, 스리랑카, 히말라야, 미얀마, 태국 등지에서 번식합니다. 번식기는 주로 여름(4~6월)이며, 암컷이 거미줄로 만들어진 둥지에 3~4개의 알을 낳습니다. 번식지 외에도 남아시아 전역에서 관찰되며, 일부 개체는 고도에 따라 이동하기도 합니다. 활발하고 시끄럽게 생활하며, 다른 새들과 섞여 무리를 이루기도 합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000230694', '12.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('489', '검은댕기수리', 'Aviceda leuphotes', 'Dumont', '1820', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Aviceda', '댕기수리속', 'leuphotes', '검은댕기수리', '검은댕기수리는 작은 맹금류로, 몸길이 30~35cm에 날개 길이는 66~80cm이다. 주로 동남아시아 및 남아시아의 숲에 서식하며, 많은 개체들은 이동성을 띠며 인도 지역에서는 겨울에 남부 반도와 스리랑카로 이동한다. 이들은 뚜렷한 색채와 뾰족한 볏을 가지고 있어 쉽게 구분되며, 짧고 튼튼한 다리와 강한 발톱을 지녔다. 검은댕기수리는 주로 곤충을 먹으며, 날아다니거나 나뭇잎에서 곤충을 잡는다. 번식기는 4월이며, 둥지는 잔가지로 만들어져 있고, 양성이 모두 번식에 참여한다. 이 새는 가끔 혼합 무리에도 참여하며, 기름야자 열매를 섭취하기도 한다. 날카로운 소리를 내며, 주로 황혼이나 흐린 날에 활동적이다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000471321', '32.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('490', '흰목딱새', 'Phoenicurus schisticeps', 'J.E. & G.R.Gray', '1847', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Phoenicurus', '딱새속', 'schisticeps', '흰목딱새', '흰목딱새는 딱새과에 속하는 조류로, 네팔, 부탄, 중국 중부 그리고 미얀마 북부와 인도 북동부에 분포한다. 주로 온대 숲에 서식하며, 알파인 눈 초원에서도 발견된다. 보전 상태는 ''관심 필요 없음''으로 평가받고 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000471473', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('491', '북미댕기흰죽지', 'Aythya collaris', 'Donovan', '1809', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Aythya', '흰죽지속', 'collaris', '북미댕기흰죽지', '북미댕기흰죽지는 북아메리카에 서식하는 잠수성 오리입니다. 몸길이는 약 39-46cm이며, 수컷은 광택 있는 검은색 머리와 검정색 등, 두 개의 흰색 고리가 있는 회색 부리, 흰색 가슴과 노란 눈을 특징으로 합니다. 암컷은 회갈색 머리와 진한 갈색 등의 색상을 가지며, 다소 연한 부리 고리가 있습니다. 이들은 주로 북미의 호수와 연못의 나무가 우거진 지역에서 번식하며, 겨울에는 북아메리카 남부 지역으로 이동합니다. 둥지는 물 위에 만들어지며, 한 배에 8-10개의 알을 낳습니다. 이들은 주로 잠수하여 먹이를 찾는데, 어릴 때는 곤충과 같은 동물성 먹이를 주로 먹고, 성장하면서 잠긴 식물 및 민물 조개류로 식단을 바꿉니다. 유럽과 중앙아메리카에서도 가끔 관찰됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000471479', '42.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('492', '쇠검은머리흰죽지', 'Aythya affinis', 'Eyton', '1838', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Aythya', '흰죽지속', 'affinis', '쇠검은머리흰죽지', '쇠검은머리흰죽지는 북미 원산의 작은 잠수 오리로, 겨울에는 중앙아메리카까지 이동합니다. "파란 부리"라고도 불리며, 이는 독특한 푸른 부리에서 유래합니다. 수컷은 번식기에 검은색 반짝이는 머리와 흰색 배를 가지고 있으며, 암컷은 전체적으로 어두운 갈색에 부리 밑에 흰 띠가 있습니다. 식물의 씨앗과 물속 동물들을 주로 먹으며, 호수나 강에서 살고 겨울철에는 덜 얼어 있는 담수에서 지냅니다. 쇠검은머리흰죽지의 개체 수는 1980년대 중반부터 감소하고 있지만, 아직 멸종 위협 등급에는 포함되지 않습니다. 번식 성공률 감소가 주요 원인으로 추정됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000471480', '43.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('493', '덤불개개비', 'Acrocephalus dumetorum', 'Blyth', '1849', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Acrocephalus', '개개비속', 'dumetorum', '덤불개개비', '덤불개개비는 참새목 휘파람새과에 속하는 조류로, 몸길이는 약 12~14cm, 몸무게는 8~16g 정도입니다. 날개는 짧고 날씬하며 부리가 길고 두툽한 것이 특징입니다. 깃털 색상은 전반적으로 단순하며, 윗부분은 올리브 갈색이고 아랫부분은 연한 색조를 띱니다.    이 새는 유럽 동부 발트 해에서 시베리아 바이칼 호수 인근까지 번식하며, 겨울철에 인도, 스리랑카, 네팔, 방글라데시 및 미얀마로 이동합니다. 번식기는 5월 말부터 7월까지로, 주로 습한 활엽수림 가장자리나 오솔길에서 번식합니다. 이 시기에 단독으로 생활하며, 작은 둥지에 3~6개의 알을 낳고 부화시킵니다.     덤불개개비는 변주가 다양한 노래를 부르며, 거친 소리와 아름다운 휘파람 소리가 혼합되어 있습니다. 서구 유럽에서도 점차 발견이 늘어나고 있으며, 네덜란드와 스코틀랜드에서 번식이 확인되었습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000471471', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('494', '흰날개종다리', 'Melanocorypha mongolica', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Alaudidae', '종다리과', 'Melanocorypha', '흰날개종다리속', 'mongolica', '흰날개종다리', '흰날개종다리(몽골리안 종다리)는 참새목 종다리과의 새로, 주로 남부 러시아, 몽골, 중국 중부에 서식한다. 이 새는 복잡한 노래 소리로 유명하며, 수컷은 성체가 되어서도 새로운 노래를 배울 수 있다. 암컷은 노래를 하지 않지만, 독특한 노래 제어 능력을 가지고 있다. 흰날개종다리는 몽골 스텝지대의 조류 다양성을 나타내는 지표종이기도 하다. 이 새는 주로 단독으로 생활하며, 사냥꾼으로는 초원매가 있다. 특징적으로 머리는 연한 갈색에 붉은색 왕관 같은 무늬가 있으며, 등쪽은 붉은 갈색, 배 쪽은 황백색이다. 평균 3.3개의 알을 낳으며, 약 13일 동안 알을 품고, 새끼는 둥지에서 약 10일을 보낸다. 알의 절반 정도만 생존하는 경향이 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000471482', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('495', '작은새매', 'Accipiter virgatus', 'Temminck', '1822', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Accipiter', '새매속', 'virgatus', '작은새매', '작은새매는 참매과에 속하는 맹금류로, 남아시아 전역의 울창한 숲에서 번식합니다. 몸길이는 29~36cm로 중간 크기이며, 짧고 넓은 날개와 긴 꼬리를 지니고 있어 밀림 속에서 빠르게 비행하는 데 적합합니다. 번식기에는 나무에 둥지를 틀고 매년 새로운 둥지를 짓으며, 한 번에 2~5개의 알을 낳습니다. 수컷은 진한 청회색의 윗면과 흰색에 붉은 갈색 줄무늬가 있는 아랫면을 가지며, 암컷은 수컷보다 크고 윗면이 갈색입니다. 겨울철에는 더 개방된 숲이나 사바나 지역에서도 발견되며, 주로 도마뱀, 잠자리, 작은 새나 포유류를 사냥합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000471472', '32.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('496', '갈색솔딱새', 'Muscicapa muttui', 'E.L. Layard', '1854', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Muscicapa', '솔딱새속', 'muttui', '갈색솔딱새', '갈색솔딱새는 작은 참새목 딱새과의 조류로 몸길이는 13~14cm이며 몸무게는 10~14g 정도입니다. 윗부분은 올리브 갈색이며 깃털의 끝이 더 밝은 적갈색을 띄기도 합니다. 턱과 목은 흰색, 가슴과 옆구리는 연한 갈색, 배 중앙부터 아랫부분은 크림색입니다. 습성상 숲의 낮은 곳에서 곤충을 잡아먹습니다. 동남아시아, 인도 북부, 중국 중부와 남부에서 번식하며 겨울에는 인도 남부와 스리랑카로 이동합니다. 주요 울음소리는 ''치-치-치'' 하는 소리로 가까이에서만 작게 들립니다. 비슷한 종은 다리가 검은 아시아갈색솔딱새입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000505666', '13.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('497', '흰꼬리유리딱새', 'Myiomela leucura', 'Hodgson', '1845', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Myiomela', '푸른딱새속', 'leucura', '흰꼬리유리딱새', '흰꼬리유리딱새는 참새목 딱새과에 속하는 조류로, 주로 인도 아대륙 북부와 동남아시아 인근 지역에 서식합니다. 주 서식지는 아열대 또는 열대의 습윤 저지대 숲과 산악 숲입니다. 방글라데시, 부탄, 캄보디아, 인도, 라오스, 말레이시아, 미얀마, 네팔, 대만, 태국, 베트남에서 발견됩니다. 일반적으로 보호 상태는 ''관심 필요 없음''으로 분류됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000505668', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('498', '대륙점지빠귀', 'Turdus viscivorus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'viscivorus', '대륙점지빠귀', '대륙점지빠귀는 우리나라와 유럽, 아시아의 넓은 지역에 걸쳐 서식하는 참새목 지빠귀과의 조류로, 일부 지역에서는 연중 서식한다. 주로 개활지, 공원, 생울타리, 경작지 등에서 서식하며 무리 지어 겨울을 난다.    이 새는 몸길이 약 27~28cm로 목과 가슴은 회백색이며, 복부 아랫부분은 연한 노란색에 검은 반점이 있다. 수컷과 암컷의 깃털 무늬는 비슷해서 구별하기 어렵다. 노래가 크고 멀리 퍼져 나가며, 장마가 와도 노래를 한다.    대륙점지빠귀는 다양한 무척추동물과 열매를 먹고, 겨울에는 마가목, 호랑가시나무, 주목과 같은 과일 나무를 방어하여 먹이를 확보한다. 둥지는 나무 줄기나 가지 사이에 컵 모양으로 얽어 트며, 주로 한 번에 3~5개의 알을 낳고 암컷이 주로 품는다. 새끼는 약 14~16일 후에 둥지를 떠나게 된다.    국제자연보전연맹(IUCN)에 의해 멸종 위기종으로는 지정되지 않으며, 그 수와 분포가 광범위하여 ‘관심 필요’로 분류된다. 현재는 농업 방식의 변화로 인해 감소 추세에 있지만, 여전히 넓은 범위에서 흔히 볼 수 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000506934', '27.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('499', '회색머리노랑솔새', '회색머리노랑솔새', 'Anderson', '1871', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'tephrocephalus', '회색머리노랑솔새', '회색머리노랑솔새는 참새목에 속하는 작은 조류로 아시아의 여러 지역에서 발견된다. 방글라데시, 중국, 인도, 라오스, 미얀마, 태국, 베트남의 온대 및 열대 습윤 저지대나 산악림을 주요 서식지로 삼는다. 이전에는 Seicercus 속으로 분류되었으나, 2018년의 유전학적 연구 결과에 따라 Phylloscopus 속으로 통합되었다. 주로 숲에서 서식하며, 현재 멸종 위기 수준은 ''관심 필요 없음''으로 평가된다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000516074', '10.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('500', '흰이마검둥오리', 'Melanitta perspicillata', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Melanitta', '검둥오리속', 'perspicillata', '흰이마검둥오리', '흰이마검둥오리는 북아메리카에 서식하는 대형 바다오리류이다. 수컷은 이마와 목 뒤에 흰색 반점이 있는 거의 전체가 검은색이고, 암컷은 수컷보다 작고 갈색을 띤다. 이들은 주로 북부 캐나다와 알래스카에서 번식하며, 북아메리카 태평양과 대서양 해안을 따라 겨울을 보낸다. 흰이마검둥오리는 주로 바닥 무척추동물, 특히 홍합을 먹으며, 번식기에는 북부 산림 지역 호수 근처에서 둥지를 틀고 알을 낳는다. 이 종은 목욕 중 동시다발적 깃털갈이를 하며, 이 시기에 매우 취약하다. 흰이마검둥오리는 멸종 위기 등급으로는 ‘관심 필요’로 분류되어 있고, 전체 개체수는 25만~130만 마리로 추정된다. 최근 수십 년 사이 개체 수가 감소하고 있지만, 충분히 빨리 감소하는 것은 아니어서 취약한 종으로 간주되지는 않는다. 환경오염과 기름 유출이 큰 위협 요인이다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000522667', '46.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('501', '연노랑허리솔새', 'Phylloscopus yunnanensis', 'La Touche', '1922', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'yunnanensis', '연노랑허리솔새', '연노랑허리솔새는 참새목 솔새과에 속하는 조류로, 중국에만 서식합니다. 몸길이는 약 10cm이며 작은 크기입니다. 뒷모습은 연둣빛을 띠고, 노란빛의 허리를 가집니다. 수컷과 암컷은 형태가 비슷하며, 머리에는 눈에 띄는 창백한 눈썹선과 미세한 노란색의 줄무늬가 있습니다.    주로 아열대 또는 열대의 건조 숲과 고지대의 관목지에서 서식하며, 200m에서 2800m 고도의 산림 지역을 선호합니다. 번식기에는 대부분 단독으로 또는 쌍으로 생활하며, 수컷은 나무 꼭대기에서 노래합니다. 이들의 노래는 다른 종과 비교해 독특하며, 반복적이고 건조한 소리로 구성되어 있습니다.昆虫을 주로 먹이로 하며, 주로 나무의 캐노피에서 활동합니다. 현재 멸종위기종으로 분류되지는 않습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000525180', '10.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('502', '갈색할미새사촌', 'Pericrocotus cantonensis', 'Swinhoe', '1861', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Campephagidae', '할미새사촌과', 'Pericrocotus', '할미새사촌속', 'cantonensis', '갈색할미새사촌', '갈색할미새사촌은 참새목 할미새사촌과의 새입니다. 중국에서 번식하며, 동남아시아에서 겨울을 보냅니다. 주로 온대 및 열대의 습윤 산림에서 서식합니다. 보전 상태는 관심 필요종으로 분류되어 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000522668', '19.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('503', '쇠덤불개개비', 'Iduna caligata', 'M.H.K. Lichtenstein', '1823', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Iduna', '쇠덤불개개비속', 'caligata', '쇠덤불개개비', '쇠덤불개개비는 참새목 개개비과에 속하는 조류로, 작은 덤불과 높은 식생이 풍부한 개방된 지역에 서식합니다. 이 새는 보통 중앙 러시아에서 서부 중국에 걸쳐 번식하며, 겨울철에는 스리랑카를 포함한 인도 아대륙으로 이동합니다. 몸은 위쪽이 연한 갈색, 아래쪽은 흰색이며 옆구리는 엷은 갈색빛을 띱니다. 부리는 견고하고 뾰족하며, 외곽 꼬리깃은 연한 경계를 지닙니다. 이들은 주로 곤충을 먹고, 덤불에 둥지를 틀며 세 마리에서 네 마리의 알을 낳습니다. 그 범위는 최근 수십 년간 확대되어 핀란드까지 이르렀습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000525702', '12.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('504', '노랑배솔새', 'Phylloscopus ricketti', 'H.H.Slater', '1897', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'ricketti', '노랑배솔새', '노랑배솔새는 참새목 솔새과에 속하는 조류로, 중국에서 번식하며 라오스, 태국, 캄보디아에서 겨울을 나는 이동성 새이다. 주요 서식지는 온대림과 아열대 또는 열대의 습윤 저지대 숲이다. 현재 국제자연보전연맹(IUCN) 기준으로 ''관심 필요 지역종''으로 분류되어 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000525703', '11.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('505', '넓은이마홍때까치', 'Lanius cristatus confusus', 'Stegmann', '1929', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Laniidae', '때까치과', 'Lanius', '때까치속', 'cristatus', '노랑때까치', '넓은이마홍때까치 넓은이마홍때까치는 아시아에 주로 서식하는 새로, 때까치과에 속한다. 주로 개활지의 덤불 꼭대기에 앉아 먹이를 찾으며 눈에 띄는 검은 "가면" 무늬가 특징이다. 이 새는 주로 곤충을 먹고, 번식기는 늦은 5월부터 6월까지로 숲이나 반사막의 나무나 덤불에 둥지를 짓고 2~6개의 알을 낳는다. 여러 아종이 있어 시베리아, 몽골, 한국 등지에서 번식하고, 겨울철에는 동남아시아로 이동한다. 주요 먹이는 곤충이나 작은 새와 도마뱀도 먹이로 삼는다. 이동하는 동안 특정 지역에 대한 높은 충성도를 보여주며, 매년 같은 지역을 찾아오는 경향이 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528318', '18.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('506', '오목눈이', 'Aegithalos caudatus magnus', 'A.H.Clark', '1907', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Aegithalidae', '오목눈이과', 'Aegithalos', '오목눈이속', 'caudatus', '오목눈이', '오목눈이는 참새목의 조류로, 유럽과 아시아 전역에 분포하며 일반적으로 흔한 종이다. 이 새의 몸길이는 13-15cm로, 꼬리가 길어 전체 길이의 절반 이상을 차지한다. 몸은 주로 검은색과 흰색을 띠며, 약간의 회색과 분홍색이 섞여 있다. 오목눈이는 주로 곤충을 먹으며, 특히 나비와 나방의 알과 애벌레를 선호한다. 가을과 겨울에는 간혹 식물성 물질도 섭취한다. 둥지는 이끼, 깃털, 거미 알 주머니, 이끼로 만들어지며, 위장으로 연한 이끼 조각으로 장식한다. 사회적 행동으로는 가족 단위의 무리를 지으며, 겨울철에는 서로 모여 체온을 유지한다. 번식기에는 짝을 이뤄 독립적인 생활을 하며, 실패한 번식자가 다른 가족의 둥지를 돕는 협력적인 번식 행동도 관찰된다. 오목눈이는 추운 날씨에 민감하지만, 번식력이 높아 인구가 빠르게 회복된다. 현재 멸종 위기나 특별한 보전 상태는 아니며, 전 세계적으로 분포가 널리 퍼져 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528320', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('507', '한국뜸부기', 'Zapornia paykullii', 'Ljungh', '1813', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Rallidae', '뜸부기과', 'Zapornia', '쇠뜸부기사촌속', 'paykullii', '한국뜸부기', '두루미목 뜸부기과의 조류로 몸길이 20cm가량이다. 유사종 뜸부기사촌보다 몸집이 크며 부리도 강하고 크다. 멱과 가슴 중앙의 색은 엷으며 멱은 거의 흰색이다. 몸은 좌우로 몹시 측압되어 있으나, 날개는 넓고 둥글다. 꽁지는 비교적 길다. 발가락도 길다. 색은 쇠뜸부기사촌과 비슷하나 몸 아랫면의 색은 엷고 살구색이 도는 갈색을 띤다. 날개덮깃에는 가로로 된 흰색의 가는 줄이 있다. 부리는 황갈색이다. 부리 등과 끝은 검은색인데 푸른 옥빛을 띤다. 다리는 연어색이다. 비교적 드문 여름새이다. 둥지는 땅 위에 잘 숨겨둔다. 한배의 산란수는 7~9개이다. 암컷은 2년째 되는 여름에 번식하기 시작한다. 우리나라 중부 이북, 서북 해안 지대에 서식한다. 동북아시아에 분포하며, 동남아시아에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528849', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('508', '쇠뜸부기', 'Zapornia pusilla', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Rallidae', '뜸부기과', 'Zapornia', '쇠뜸부기사촌속', 'pusilla', '쇠뜸부기', '두루미목 뜸부기과의 조류이다. 몸 크기는 18cm쯤이다. 수컷의 이마 중앙, 머리꼭대기, 뒷머리, 뒷목까지는 붉게 녹슨 색을 띤 갈색으로 검은 갈색의 축반이 있다. 이마 옆에서 눈 위까지는 엷은 잿빛의 눈썹선이 있고, 눈 앞에서 눈 뒤까지는 붉게 녹슨 색을 띤 갈색의 눈선이 있으며, 턱 밑과 멱은 흰색이다. 어깨깃, 등, 허리, 위꽁지덮깃은 붉게 녹슨 색을 띤 갈색으로 각 깃털에는 검은색의 굵은 축반이 있다. 가슴과 배는 엷은 잿빛으로 갈색의 가장자리가 있다. 꽁지는 검은 갈색으로 붉게 녹슨 색을 띤 갈색 가장자리가 있다. 논, 소택지, 풀숲에서 생활한다. 봄과 가을에 드물게 통과하는 나그네새이다. 호반가 풀숲, 수초 위, 호소, 논 등지에 둥지를 튼다. 산란기는 5월하순~8월경이며, 한배의 산란수는 6~8개이다. 식성은 날도래 등의 곤충류가 주식이고, 연체동물의 작은 패류나 식물 종자도 먹는다. 우리나라 전역을 통과한다. 일본, 타이완, 중국(북동부), 우수리, 사할린, 시베리아 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528850', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('509', '쇠박새', 'Poecile palustris', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Paridae', '박새과', 'Poecile', '쇠박새속', 'palustris', '쇠박새', '몸길이 약 12.5cm이다. 정수리와 턱밑은 검은색이다. 윗면은 갈색을 띤 연한 회색으로 날개에는 흰 줄이 없다. 눈 밑, 멱의 양옆은 흰색이며 멱은 검은색이다. 가슴, 옆구리, 배는 회색을 띤 흰색이다. 꼬리는 연한 갈색을 띤 회색이다. 평지 및 산의 낙엽활엽수림 등에서 관찰되며 산림 내교목의 나무 구멍이나 딱따구리가 뚫은 구멍 및 인공 둥지 등에 풀이나 이끼류로 둥지를 만든다. 알을 낳는 시기는 4~5월이다. 유럽과 아시아의 동부, 즉 한국, 중국, 일본에 걸쳐 분포한다. 유사종인 북방쇠박새는 머리꼭대기에 광택이 없다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528889', '12.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('510', '대륙검은지빠귀', 'Turdus mandarinus', 'Bonaparte', '1850', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Turdus', '지빠귀속', 'mandarinus', '대륙검은지빠귀', '참새목 지빠귀과에 속하는 조류이다. 수컷의 몸은 전체적으로 검은색이며, 부리와 눈테는 노란색이다. 암컷은 몸 전체가 밤색을 띤 검은색이다. 몸 아랫면이 몸 윗면보다 다소 밝은 편이며 멱에서 가슴까지 흑갈색의 줄무늬가 있다. 부리와 눈테의 노란색은 수컷에 비해 희미하다. 숲과 공원에 산다. 강원도, 경기도에 서식하고 세계적으로는 서유럽, 지중해 연안, 북아프리카, 인도, 동남아시아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528904', '26.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('511', '붉은발슴새', 'Ardenna carneipes', 'Gould', '1844', 'Chordata', '척삭동물문', 'Aves', '조강', 'Procellariiformes', '슴새목', 'Procellariidae', '슴새과', 'Ardenna', '큰슴새속', 'carneipes', '붉은발슴새', '우리나라에서는 봄에 북상할 때 동해 연안을 거쳐 가는 희귀한 나그네새이다. 전체 길이 50cm 정도이며 날개를 편 길이는 1m 정도이다. 몸은 전체적으로 검은색이고, 부리는 끝이 검은색이고 기부는 담황색, 발은 살구색이다. 번식기에는 섬이나 해안의 해상에 서식하며, 비번식기에는 멀리 떨어진 해안에서 서식한다. 집단 번식하며 섬의 땅속에 구멍을 파서 알을 1개 낳는다. 오스트레일리아와 뉴질랜드에서 번식한다. 북태평양, 브리티시 컬럼비아에서 여름을 보낸다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528913', '50.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('512', '쇠부리슴새', 'Ardenna tenuirostris', 'Temminck', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Procellariiformes', '슴새목', 'Procellariidae', '슴새과', 'Ardenna', '큰슴새속', 'tenuirostris', '쇠부리슴새', '슴새목 슴새과에 속하는 조류이다. 몸길이는 35cm 정도로 소형종이다. 암컷과 수컷 모두 몸의 윗면이 검은 갈색이다. 턱 밑과 멱은 잿빛 갈색이며, 가슴, 배, 옆구리는 잿빛 갈색으로 깃 가장자리가 엷은 색이다. 꽁지는 검은 갈색이다. 부리는 검은색이며, 홍채는 어두운 갈색이다. 다리는 엷은 잿빛이다. 알은 흰색이다. 바다에서 생활한다. 5월 중순~6월 중순경에 우리나라를 통과하는 나그네새이다. 큰 무리를 이루어 생활한다. 슴새보다 몸체가 튼튼하고 날개가 짧기 때문에 잠수에 능하다. 날개를 펄럭이며 수면에서 물소리를 내면서 날아오른다. 번식은 땅속에 굴을 파서 집단으로 한다. 산란기는 11월하순이며, 알은 1개 낳아 50일 정도 포란한다. 먹이로는 어류, 두족류 등을 먹는다. 태즈메이니아섬, 바스 해협 도서 및 오스트레일리아의 동남 해안에서 번식한다. 우리나라 부산 앞바다, 낙동강 하류, 동해안 일대에서 관찰되었다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528915', '35.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('513', '흰올빼미', 'Bubo scandiacus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Strigiformes', '올빼미목', 'Strigidae', '올빼미과', 'Bubo', '수리부엉이속', 'scandiacus', '흰올빼미', '올빼미목 올빼미과에 속하는 조류이다. 날개의 길이는 수컷이 40cm 정도이고, 암컷은 45cm 정도이다. 몸 전체가 흰색이며, 등 쪽과 날개깃의 끝과 꼬리에는 어느 정도의 검은빛 갈색의 무늬가 있으며, 다리와 발가락에는 턱 모양의 깃이 빽빽이 나 있다. 암컷은 수컷보다 몸집이 더 크고, 등 쪽에 검은빛 갈색의 가로무늬가 그려져 있다. 목에는 가느다란 무늬가 있고, 부리와 발톱은 검은색이다. 알은 흰색에 무늬가 없다. 늪, 해변, 개활지에 살며 겨울철새이다. 단독 생활로 땅 위, 언덕, 바위, 말뚝 등에 가만히 앉아서 주위에 나타나는 먹이를 기다린다. 주로 쥐, 두더지, 새, 족제비, 곤충류 등을 먹는다. 4-6월에 알을 낳는다. 충청남도, 함경남도에 서식하고 세계적으로는 시베리아, 알래스카, 캐나다, 스칸디나비아반도에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528916', '61.75');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('514', '진홍때까치', 'Lanius cristatus superciliosus', 'Latham', '1801', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Laniidae', '때까치과', 'Lanius', '때까치속', 'cristatus', '노랑때까치', '진홍때까치는 주로 아시아에 서식하는 때까치과의 조류입니다. 이 종은 ''도살자새''로도 알려져 있으며 먹이를 가시 등에 꿰어 저장하는 습성이 있습니다. 주로 가시덤불 위에 앉아 먹이를 찾으며, 주로 곤충을 먹습니다. 진홍때까치는 이마를 가로지르는 검은 ''밴딧 마스크''가 특징이며 겨울철에는 검은색이 더 연해질 수 있습니다. 번식기는 주로 늦은 5월부터 6월까지이며, 북아시아의 몽골에서 시베리아까지 다양한 지역에서 번식하고 남아시아와 말레이 반도에서 겨울을 납니다. 이들은 철새로서 매년 동일한 겨울 서식지를 찾는 경향이 있으며, 특히 인도에서는 8월에서 9월 사이에 도착하고 4월에 떠납니다. 번식 시에는 나무나 덤불에 둥지를 짓고 2~6개의 알을 낳습니다. 때때로 유럽과 북미에서도 아주 드물게 관찰되기도 합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528950', '18.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('515', '재갈매기', 'Larus vegae', 'Palmén', '1887', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Larus', '갈매기속', 'vegae', '재갈매기', '10월부터 이듬해 3월까지 강, 하구, 해안, 간척지 등에서 흔히 볼 수 있는 겨울철새이다. 날개 끝이 검은색이며 흰색의 반점이 있으며, 다리가 분홍색이다. 몸은 전체적으로 흰색이며 등과 날개덮깃은 회색이다. 부리는 노란색이며, 아래쪽에 빨간색 점이 있다. 겨울에는 머리와 목에 갈색의 줄무늬가 있다. 어린 새는 몸 전체가 갈색이며, 일정한 패턴의 세로무늬가 흐릿하고, 꼬리 끝에 검은색 띠가 있다. 강 하구, 바닷가 모래밭에 내려앉아 무리 지어 휴식을 취하는 모습을 관찰할 수 있으며, 바람을 이용해 기류를 타고 이동하기도 한다. 잡식성으로 물고기, 게, 곤충, 바닷새의 알, 수생식물 등을 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528952', '61.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('516', '북방긴발톱할미새', 'Motacilla tschutschensis macronyx', 'Stresemann', '1920', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'tschutschensis', '긴발톱할미새', '북방긴발톱할미새는 참새목 할미새과의 작은 조류로, 몸길이는 15~16cm이며 긴 꼬리를 계속 흔드는 특징이 있습니다. 번식기 수컷은 상체가 올리브 녹색이고 하체가 노란색이나, 다른 깃에서는 노란색이 백색으로 희석되기도 합니다. 머리 색과 무늬는 아종에 따라 다양합니다. 다리와 부리는 검은색이며 긴발톱할미새라는 이름에 걸맞게 발톱이 길고 덜 굽어 있습니다. 동아시아와 알래스카에서 번식하고 남아시아와 호주로 이동하여 겨울을 보냅니다. 벌레를 주로 먹으며 물가의 탁 트인 지역에서 서식합니다. 둔덕에 둥지를 틀고 4~8개의 알을 낳습니다. 북방긴발톱할미새는 개체군 간의 유전적 차이가 작아 일부 조류학자는 이 종을 서부긴발톱할미새의 아종으로 보기도 합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528734', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('517', '휘파람새', 'Horornis canturians borealis', 'C.W.Campbell', '1892', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Horornis', '휘파람새속', 'canturians', '휘파람새', '휘파람새는 Cettiidae과에 속하는 조류로, 주로 중국 동북부에 분포합니다. 활동 범위가 약 1,610,000km²로 넓으며, 전 세계 개체수는 측정되지 않았으나 안정적인 추세를 보입니다. 국제 자연 보전 연맹(IUCN)에 의해 ''관심 대상''으로 평가되었습니다. 아침에 가장 활발히 활동하며, 일본아기솔새와 혼동될 수 있습니다. 그 울음소리는 "폭발적"이며 "트릴링(trilling)"으로 묘사됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528739', '16.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('518', '크낙새', 'Dryocopus javensis richardsi', 'Tristram', '1879', 'Chordata', '척삭동물문', 'Aves', '조강', 'Piciformes', '딱다구리목', 'Picidae', '딱다구리과', 'Dryocopus', '까막딱다구리속', 'javensis', '크낙새', '크낙새는 한국에서 서식하는 딱따구리의 아종으로 몸길이 46cm로 딱따구리 중에서 가장 큰 편이다. 머리 깃과 뺨은 진홍색이며, 상체는 검고 하체는 흰색이다. 초목이 울창한 1000m 이상의 산림과 도시지역에서 서식하며, 밤나무, 참나무, 포플러, 느릅나무 등이 많은 곳을 선호한다. 나무껍질을 벌려 곤충을 먹고, 번식기는 4월부터 5월까지로 3~4개의 알을 낳는다. 역사적으로 제주도와 대한해협의 대마도, 한국 반도에 서식했으나, 일본 대마도에서는 1920년 마지막 표본이 발견된 후 사라졌다. 한국에서는 대규모 벌목으로 인해 희귀해졌고 1989년 남한에서 멸종했다. 현재는 북한에서만 생존하고 있으며, 평산, 장풍, 린산 등 강원도와 황해북도의 산림에 약 50마리 미만이 남아있다. 1968년에 국가 문화재 제197호로 지정되어 보호받고 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528743', '46.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('519', '흰눈썹긴발톱할미새', 'Motacilla tschutschensis tschutschensis', 'J.F.Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'tschutschensis', '긴발톱할미새', '흰눈썹긴발톱할미새는 참새목 할미새과의 작은 새로, 몸길이 15-16cm이다. 번식기의 수컷은 등 쪽이 올리브 녹색이고 배 쪽이 노란색이다. 부리와 다리는 검은색이며, 긴발톱할미새와 구별되는 긴 뒷발톱을 가지고 있다. 주로 동팔레아르크에서 번식하며, 겨울에는 남아시아와 호주로 이동한다. 이들은 개활지에서 주로 서식하며, 물가 근처의 습지에서 주로 발견된다. 곤충을 먹고, 둔덕에 둥지를 틀어 4-8개의 알을 낳는다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528733', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('520', '흰눈썹북방긴발톱할미새', 'Motacilla tschutschensis plexa', 'Thayer & Bangs', '1914', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'tschutschensis', '긴발톱할미새', '흰눈썹북방긴발톱할미새는 작은 참새목 조류로 몸길이는 약 15~16cm입니다. 이 새는 번식기 동안 수컷이 올리브 녹색을 띠고 아래쪽은 노란색을 띱니다. 부리와 다리는 검은색이며, 뒷발톱이 길고 곡선이 크지 않은 특징이 있습니다. 주로 동아시아와 알래스카에서 번식하며, 겨울에는 남아시아와 호주로 이동합니다. 이 새는 습한 초원 등 물 근처의 개방된 지역에 서식하며, 둔덕에 둥지를 틀고 4~8개의 알을 낳습니다. 흰눈썹북방긴발톱할미새는 벌레를 주식으로 하며, 물가의 개방된 지역에서 주로 발견됩니다. 국제 자연 보호 연맹(IUCN)에 의해 ''관심대상''으로 평가된 종입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528735', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('521', '쇠동고비', 'Sitta villosa corea', 'Ogilvie-Grant', '1906', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sittidae', '동고비과', 'Sitta', '동고비속', 'villosa', '쇠동고비', '쇠동고비는 우리나라에 매우 드물게 찾아오는 새로, 주로 중국 중부부터 북동쪽까지 그리고 한국, 러시아 극동 남쪽 지역에 서식한다. 이 새는 몸길이 약 11.5cm로 작은 편에 속하며, 여름철에는 주로 곤충을 먹고 겨울철에는 씨앗과 열매를 섭취한다. 암수가 서로 다른 깃털 색을 가지며, 수컷은 검은색 왕관이 특징이다. 둥지는 주로 침엽수의 구멍에 만들어지며, 한 해에 5~6개의 새끼를 기른다. 이 종은 국제 자연 보전 연맹에 의해 ''관심 필요'' 등급으로 분류되어 있지만 서식지 파괴로 인해 개체수가 감소할 가능성이 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528736', '11.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('522', '히말라야산솔새', 'Phylloscopus claudiae', 'La Touche', '1922', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'claudiae', '히말라야산솔새', '히말라야산솔새는 중국에만 서식하는 조류로, 온대림을 주로 서식지로 삼는다. 이전에는 넓은 의미로 블라이솔새의 아종으로 분류되었으나, 현재는 독립적인 종으로 인정받고 있다. 주된 서식지는 중국의 숲이며, 국제자연보전연맹(IUCN)에서는 관심대상종으로 분류하고 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528741', '11.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('523', '동박새', 'Zosterops japonicus japonicus', 'Temminck & Schlegel', '1847', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Zosteropidae', '동박새과', 'Zosterops', '동박새속', 'japonicus', '동박새', '동박새는 작고 활발한 참새목의 새로, 동아시아 지역에 널리 분포하며 한국에서도 흔히 볼 수 있습니다. 몸길이는 약 10~11cm이며, 무게는 9.75~12.75g 정도입니다. 올리브 그린색의 윗면과 연두색의 아랫면을 가진 이 새는 하얀 눈 테두리가 특징입니다. 사회성이 강하여 번식기가 아닐 때 다른 종의 새들과 무리 지어 활동하고, 번식기에는 한 쌍씩 짝짓기를 이루어 둥지를 꾸립니다. 둥지는 주로 땅에서 1~30m 높이에 짓고, 건축에 거미줄, 이끼 등을 사용하며, 주로 한 번만 사용합니다. 잡식성으로 과일, 곤충, 꽃의 꿀 등을 먹지만, 하와이에서는 토착 조류와 먹이 경쟁을 벌이며 일부 종의 번식을 방해하는 문제를 일으키고 있습니다. 동박새는 종종 다른 종과의 경쟁과 생태적 균형 문제를 야기할 수 있어 생태 보호 측면에서 주의가 필요합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528742', '10.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('524', '넓적부리', 'Spatula clypeata', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Spatula', '넓적부리속', 'clypeata', '넓적부리', '기러기목 오리과에 속하는 조류로 흔한 겨울철새이다. 몸길이는 약 50cm. 암수 모두 넓적하고 긴 부리가 특징으로 다른 종과 쉽게 구별된다. 넓적한 부리로 물을 걸러 플랑크톤이나 수서곤충 수초 등을 먹는 수면성 오리이다. 수컷은 금속광택의 청록색 머리와 검은색의 등, 흰색의 가슴, 배 부분의 붉은색, 검은색의 넓적한 부리, 주황색 다리로 다른 오리류와 쉽게 구별된다. 변환깃은 선명한 노란색 눈과 밝은색의 날개덮깃으로 암컷과 구별되나 부리의 색은 암컷과 같아진다. 암컷의 눈은 갈색이며 부리는 검은색을 띤 갈색이고 다른 오리류의 암컷과 비슷한 평범한 갈색이나 넓적한 부리로 인해 다른 종과 쉽게 구별된다. 늪지대, 초원, 호수, 저수지에 서식하며 주로 물풀, 물속에 사는 작은 곤충, 플랑크톤 등을 주로 먹는다. 유라시아 대륙의 북부와 북아메리카 북부에서 번식하고 유럽 남부, 북아프리카, 인도, 동남아시아, 중국 남부, 한국, 북아메리카 남부 등에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528781', '50.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('525', '발구지', 'Spatula querquedula', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Spatula', '넓적부리속', 'querquedula', '발구지', '우리나라에 드물게 도래하는 겨울철새이다. 몸길이 약 38cm이다. 수컷은 머리와 뒷목, 등과 가슴이 검은 갈색이며 눈 위에서 윗목까지 흰색 눈썹선이 뚜렷하다. 아랫면은 잿빛을 띤 흰색이다. 암컷의 몸 빛깔은 얼룩진 갈색이고, 부리가 시작되는 곳에 연한 얼룩무늬가 있다. 호소나 소택지 등에서 생활한다. 온대에서 아한대에 걸쳐 번식한다. 둥지는 물가의 풀숲이나 숲속의 땅 위에 풀을 이용하여 만든다. 산란기는 4월하순~6월상순이다. 알은 8~14개를 낳는다. 먹이는 벼, 풀씨, 수서곤충, 갑각류 등이다. 아시아, 유럽, 북아메리카에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528785', '38.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('526', '흰눈썹물떼새', 'Eudromias morinellus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Charadriidae', '물떼새과', 'Eudromias', '흰눈썹물떼새속', 'morinellus', '흰눈썹물떼새', '흰눈썹물떼새는 작은 물떼새로, 흰 눈썹이 두드러져 보이며, 번식기에는 가슴에 주황색 띠가 나타납니다. 암컷이 수컷보다 화려한 색을 가지고 있습니다. 이 새는 주로 유럽 북부와 유라시아의 북극 툰드라 지역에서 번식하고, 겨울에는 북아프리카와 중동으로 이동하는 철새입니다. 흰눈썹물떼새는 땅 위에 얇게 둥지를 틀고, 수컷이 알을 품고 새끼를 돌보는 역할을 맡습니다. 반면 암컷은 다른 수컷을 찾아 추가로 산란을 합니다. 이들은 곤충과 작은 무척추 동물을 먹으며, 비행 중에는 부드러운 울음소리를 냅니다. 국제 자연 보전 연맹에 따르면, 흰눈썹물떼새는 ''관심 대상''으로 분류되며, 개체 수 감소가 우려되지만 심각한 수준은 아닙니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528794', '21.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('527', '큰솔새', 'Phylloscopus examinandus', 'Stresemann', '1913', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'examinandus', '큰솔새', '큰솔새는 참새목 솔새과에 속하는 조류입니다. 이 새는 이전에 "구세계솔새" 무리에 포함되었으며, 북극솔새 및 일본솔새와 밀접한 관계가 있습니다. 주로 캄차카, 사할린, 홋카이도 및 쿠릴 열도에서 번식하고 인도네시아와 필리핀으로 이동합니다. 보호 상태는 ''관심 필요''로 분류되어 있으며, 2021년 핀란드에서 처음 관찰되었습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528323', '12.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('528', '붉은부리갈매기', 'Chroicocephalus ridibundus', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Chroicocephalus', '붉은부리갈매기속', 'ridibundus', '붉은부리갈매기', '몸길이는 약 40cm이다. 날개 윗면은 흐린 회색이다. 날 때 날개의 바깥쪽은 흰색이고, 첫째날개깃 바깥쪽은 검은색이다. 여름깃 머리는 밤색이며, 부리와 다리는 진한 붉은색이다. 겨울깃 머리는 흰색이고, 귀깃 부분에 검은색 반점이 있다. 1년생 겨울깃으로 셋째날개깃과 날개덮깃의 일부는 짙은 갈색이다. 부리와 다리는 적갈색이다. 유사종인 검은머리갈매기는 부리가 짧고 검은색이라 이 종과 구별된다. 날 때 날개 아랫면의 검은 부분이 좁고, 날개 윗면 끝부분의 검은색이 끊어져 있다. 이 종은 드물게 도래하는 통과 철새로 여겨진다. 주로 제주도와 부산에서만 기록되었으며, 남해안 지역에 규칙적으로 도래할 것으로 판단된다. 유럽과 아시아에서 번식하고 온대에서 아열대까지 내려가 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528809', '40.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('529', '검은머리갈매기', 'Saundersilarus saundersi', 'Swinhoe', '1871', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Saundersilarus', '검은머리갈매기속', 'saundersi', '검은머리갈매기', '도요목 갈매기과에 속하는 조류이다. 몸길이는 29-32cm이고 날개는 27-30cm이다. 여름깃은 머리가 검은색이고, 겨울깃은 흰색에 어두운 무늬가 있다. 목과 배 쪽과 꼬리는 흰색이다. 뒷등은 어두운 진줏빛 회색이며, 날개깃은 대부분이 흰색이고, 부리는 검은색, 다리는 붉은색이다. 흰색 눈테가 있다. 외형으로 암컷과 수컷을 구분하기는 어렵다. 다만, 수컷의 크기가 암컷보다 약간 크다. 어린 새는 몸의 윗면이 갈색이고 깃의 가장자리는 그 색이 연하다. 겨울철새이나 일부는 서해안 지역에서 번식한다. 갯벌이 있는 해안과 강 하구에서 수 개체 또는 수백 개체까지 무리 지어 서식한다. 일반적으로 수십 개체가 한 무리를 형성하며, 대집단 행동 시에는 수백 개체 단위로도 움직인다. 갯벌 위를 저공으로 천천히 날다가 급강하해 게, 갯지렁이 등을 잡거나, 갯벌 위를 걸어 다니면서 잡아먹는다. 번식기는 4-6월이며, 알은 2-3개를 낳는다. 서남해안에 서식하고 세계적으로는 중국 동북부 해안에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528810', '30.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('530', '쇠제비갈매기', 'Sternula albifrons', 'Pallas', '1764', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Sternula', '쇠제비갈매기속', 'albifrons', '쇠제비갈매기', '몸길이가 약 24cm이다. 몸의 빛깔은 윗면은 회색이고 아랫면은 흰색이다. 여름에는 이마는 흰색, 머리 윗부분과뒷목은 검은색이며, 검은색의 눈 가장자리선이 있다. 부리는 노란색이며 끝부분은 검은색이다. 꼬리는 제비꼬리이며, 다리는 짧고 노란색을 띤 오렌지색이다. 겨울에는 이마에서 머리꼭대기까지 흰색이며, 부리는 검은색이다. 다리는 노란색을 띤 갈색이다. 꼬리는 여름깃에 비해 짧아지며 오목꼬리이다. 공중에서 물을 향해 다이빙하여 물고기를 잡아먹으며 정지비행도 한다. 전국의 하천, 해안, 하구의 모래섬에서 살며 1회에 2~3개의 알을 낳는다. 한국·일본·중국·우수리 등지에서 번식하고 필리핀·뉴기니 섬·오스트레일리아·인도차이나·인도·스리랑카 등지에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528812', '24.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('531', '검은등제비갈매기', 'Onychoprion fuscatus', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Onychoprion', '검은등제비갈매기속', 'fuscatus', '검은등제비갈매기', '적은 집단이 봄과 가을에 한반도를 통과하는 드문 나그네새이다. 전체 길이는 40cm 정도이다. 성조는 흰색 앞이마와 검은색의 등을 가지고 있다. 깊게 파인 꼬리와 얇고 검은색의 부리가 특징적이다. 해안이나 해양의 섬에 서식한다. 모래 위 움푹 팬 곳에 흰색의 적갈색 반점이 있는 알을 1개 낳는다. 우리나라를 비롯하여 말레이시아, 타이완, 일본 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528816', '40.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('532', '큰매사촌', 'Hierococcyx sparverioides', 'Vigors', '1832', 'Chordata', '척삭동물문', 'Aves', '조강', 'Cuculiformes', '두견목', 'Cuculidae', '두견과', 'Hierococcyx', '매사촌속', 'sparverioides', '큰매사촌', '큰매사촌은 참새목 뻐꾸기과에 속하는 조류로 크기가 비교적 크다. 주로 히말라야를 비롯한 온대 아시아에서 번식하며, 겨울에는 더 남쪽으로 이동한다. 이 새는 큰매 뻐꾸기와 비슷한 소리를 내지만 크레셴도 없이 일정하게 울린다. 턱 아래의 검은 반점으로 다른 유사종과 구별할 수 있다. 웃는방울새류나 히어로치 등의 새들에게 탁란을 하며, 이들의 알과 비슷한 알을 낳는다. 생태적으로 주로 온대 및 아열대 숲에 서식하며, 여름철에 크게 울며 겨울에는 조용하다. 방글라데시, 부탄, 중국, 인도 등 아시아 여러 나라에 분포한다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528832', '39.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('533', '쇠뜸부기사촌', 'Zapornia fusca', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Rallidae', '뜸부기과', 'Zapornia', '쇠뜸부기사촌속', 'fusca', '쇠뜸부기사촌', '흔하지 않은 여름새로 몸길이는 22.5cm이다. 깃털은 몸 윗면은 짙은 갈색, 턱 밑과 턱 아래 부위는 흰색이다. 가슴과 윗배는 붉은빛 도는 갈색이며, 아랫배는 회색빛을 띤 갈색으로 흰색 가로 띠가 있다. 부리는 녹갈색이며, 다리는 붉은색이다. 일반적으로 소택지, 논, 강가의 풀숲 등지에서 생활한다. 둥지는 풀숲의 땅 위에 식물 잎이나 줄기를 이용해 접시 모양으로 만든다. 알 낳는 시기는 5월하순~8월하순이다. 알은 크림색이 도는 갈색으로 붉은 갈색과 쥐색 얼룩점이 많으며, 5~9개 낳는다. 우리나라 전역과 만주 남부, 중국 동부, 일본 등지에 분포한다. 중국 동부, 미얀마, 시안 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528847', '22.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('534', '쇠찌르레기', 'Agropsar philippensis', 'Pennant', '1781', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sturnidae', '찌르레기과', 'Agropsar', '북방쇠찌르레기속', 'philippensis', '쇠찌르레기', '참새목 찌르레기과의 새이다. 몸길이는 20cm가량이다. 수컷 겨울깃의 눈 앞, 턱 밑, 멱은 엷은 황갈색이고, 귀깃과 뺨 때로는 윗가슴과 목 옆은 밤색이다. 머리의 깃털은 길다. 가슴과 배 옆은 어두운 잿빛이며 배는 흰색이다. 이마에서 뒷목까지는 크림 흰색이다. 등과 허리는 광택이 강한 검은 자색이다. 꽁지는 조금 오목꽁지인 모난꽁지며 녹색 광택이 강한 검은색이다. 윗부리는 석판 검은색이고, 아랫부리 기부는 잿빛이다. 홍채는 갈색이고, 다리는 올리브색이다. 인가 부근이나 시가지의 숲에서 번식한다. 작은 무리가 드물게 한반도를 통과하는 나그네새이다. 번식기에는 암수가 함께 생활하지만 번식이 끝나면 무리 지어 생활한다. 7월부터 떼 지어 다니기 시작한다. 대개 5~6월에 한배에 4~6개 산란한다. 거미류와 곤충류가 주식이고, 벚나무, 감, 포도 등의 나무 열매도 즐겨 먹는다. 구북구 동부에 번식하며, 한국, 중국 동부, 우수리, 타이완 등지를 통과한다. 오키나와, 필리핀, 보르네오 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528897', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('535', '점무늬가슴쥐발귀', 'Locustella davidi', 'La Touche', '1923', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Locustella', '붉은허리개개비속', 'davidi', '점무늬가슴쥐발귀', '참새목 휘파람새과의 조류이다. 전체 길이 108~113mm, 부리길이 10.7~11.5mm, 날개길이 52.0~54.5mm, 꽁지길이 42.5~45.5mm, 부척 18.6~19.4mm이다. 숲 가장자리에서 주로 발견된다. 우리나라 함경북도에서 번식한다. 2000년 5월에 전라북도 서해안의 어청도 및 상왕등도 인근 해상에서 성조(成鳥) 3개체가 확인된 바 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528899', '11.1');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('536', '홍머리오리', 'Mareca penelope', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Mareca', '홍머리오리속', 'penelope', '홍머리오리', '기러기목 오리과의 조류로 흔한 겨울철새이며, 흔한 나그네새이다. 몸길이 약 49cm. 암수 모두 부리는 청회색이며 끝이 검다. 수컷은 머리가 적갈색이며 몸은 대체로 회색을 띤다. 이마는 노란색을 띠며 가슴은 분홍색을 띤 갈색이다. 선명한 흰색 날개덮깃은 앉으면 흰 가로줄로 보이고, 아래꼬리덮깃은 검은색이다. 암컷은 전체적으로 어두운 갈색이며 다른 오리의 암컷에 비해 붉은 색채를 띤다. 수면성 오리로 낮에는 호수나 강 중앙에서 휴식하거나 수면에서 식물의 종자, 풀줄기 등을 먹는다. 해안 근처에 서식하는 개체는 해초류를 즐겨 먹는다. 유라시아 대륙 북부에서 번식하고, 유라시아 대륙 온대에서 아한대 지역, 북아프리카에서 월동한다. 국내에는 전국의 해안이나 강, 호수에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528330', '49.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('537', '흰허리큰뒷부리도요', 'Limosa lapponica menzbieri', 'Poirtendo', '1936', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Limosa', '흑꼬리도요속', 'lapponica', '큰뒷부리도요', '흰허리큰뒷부리도요는 도요목 도요과에 속하는 대형 철새로, 붉은색 번식깃, 긴 다리와 위로 휘어진 긴 부리를 특징으로 합니다. 이 새는 주로 북극의 해안과 툰드라 지역에서 번식하며, 겨울에는 호주와 뉴질랜드의 온대 및 열대 해안으로 이동합니다. 흰허리큰뒷부리도요는 무단 비행 거리 기록을 보유하고 있으며, 알래스카에서 뉴질랜드까지의 비행은 29,000km를 넘습니다. 주요 서식지는 해안 습지로, 붉은털갯지렁이와 조개류를 먹이로 삼습니다. 이 새는 현재 전 세계적으로 보호가 필요하며, 주요 위협 요인은 서식지 감소와 환경 파괴입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528650', '39.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('538', '바다매', 'Falco peregrinus pealei', 'Ridgway', '1874', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Falconidae', '매과', 'Falco', '매속', 'peregrinus', '매', '바다매는 송골매의 아종으로, 세계에서 가장 큰 송골매 아종입니다. 이 새의 몸길이는 수컷이 약 41cm, 암컷이 약 47cm이며, 날개 길이는 수컷이 약 92cm, 암컷이 약 111cm입니다. 무게는 수컷이 약 810~1,058g, 암컷이 약 1,244~1,597g입니다. 성체는 대부분의 송골매 아종과 다르게 가슴 부분에 두드러진 물방울 모양의 무늬와 더 넓고 강한 부리를 가지고 있습니다.     바다매는 거친 해안이나 섬의 바위 절벽 근처에서 주로 서식하며, 먹이는 주로 바다오리류입니다. 이 새는 해안에 가까운 새의 서식지 근처에 둥지를 틀지만, 그렇지 않은 경우에도 바다로 나가 최대 80km 떨어진 곳까지 사냥을 하기도 합니다.    이 새는 러시아 코만도르 제도에서 시작하여 알래스카와 브리티시컬럼비아, 그리고 미국의 북서부 해안까지 걸쳐 있는 해안선에서 번식합니다. 최근 몇 년간 일부 지역에서 바다 새의 감소로 바다매의 개체 수가 줄어들었다고 보고되고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528659', '44.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('539', '홍때까치', 'Lanius cristatus crisatus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Laniidae', '때까치과', 'Lanius', '때까치속', 'cristatus', '노랑때까치', '홍때까치는 주로 아시아에서 발견되는 참새목의 새로, 겨울에는 열대 아시아로 이동하는 철새입니다. 이 새는 검은색의 마스크처럼 보이는 눈 주변의 줄무늬가 특징이며, 가시 있는 관목의 꼭대기에 앉아 먹이를 찾습니다. 주요 서식지는 개방된 관목 지역입니다. 홍때까치는 주로 곤충을 먹으며, 때때로 작은 새나 도마뱀을 사냥합니다. 번식기는 5월 말부터 6월이며, 두 세 개의 알을 나무나 덤불에 지은 둥지에 낳습니다. 이 종은 다양한 아종을 가지며, 번식지와 월동지가 다를 수 있습니다. 매년 같은 월동지로 돌아오는 높은 충성도를 보입니다. 유럽과 북미에서는 드물게 관찰됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528319', '18.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('540', '흰머리오목눈이', 'Aegithalos caudatus caudatus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Aegithalidae', '오목눈이과', 'Aegithalos', '오목눈이속', 'caudatus', '오목눈이', '흰머리오목눈이는 참새목의 조류로, 유럽과 아시아에 걸쳐 널리 분포합니다. 전체 길이는 약 13-15cm이며, 긴 꼬리가 특징입니다. 이 새는 주로 해충 및 곤충의 알을 먹으며, 가을과 겨울에는 씨앗도 섭취합니다. 둥지는 이끼, 거미줄, 깃털 등으로 만들어지며, 수천 개의 작은 조각이 사용됩니다. 군집 생활을 하며, 가족 단위로 무리를 이루고 겨울에는 떼를 지어 살아남기 위해 서로서로 체온을 유지합니다. 번식기가 아니면 숲의 가장자리나 덤불, 공원 등에서 서식하고, 번식기에는 한 쌍이 되어 둥지를 만듭니다. 번식기에 둥지가 실패하면 다른 가족의 번식을 도와주는 협력 행동을 보입니다. 이 새는 국제 자연 보전 연맹(IUCN)에 의해 ''관심 필요종''으로 분류되지만, 추운 날씨에는 개체 수가 급감할 수 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528321', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('541', '청머리오리', 'Mareca falcata', 'Georgi', '1775', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Mareca', '홍머리오리속', 'falcata', '청머리오리', '기러기목 오리과의 조류로 비교적 흔한 겨울철새이다. 몸길이 약 48cm.    수컷은 머리에 특이한 형태의 긴 녹색 광택의 댕기가 있다. 흰색 멱, 검은색 목테, 이마의 흰점, 검은색 띠가 있는 노란색 엉덩이 등이 특징이다. 가슴의 깃털은 가장자리가 검은색으로 비닐 무늬처럼 보이며, 둥글고 길게 늘어진 셋째 날개깃은 꼬리를 가린다. 암컷은 전체적으로 균일한 갈색에 흑갈색 무늬가 있으며, 부리는 검은색이다. 강, 하구, 저수지, 해안에서 작은 무리를 이루어 서식한다. 다양한 형태의 씨앗(볍씨와 곡물)과 수생식물 등을 선호하며 수서곤충 등을 먹기도 한다. 해질 무렵에는 농경지로 날아들어 먹이를 찾는다. 시베리아 동부, 사할린, 캄차카반도, 북해도에서 번식하고, 한국, 일본, 중국 남부에서 월동한다. 국내에는 흔한 겨울철새로 우리나라 전역의 강과 하천 등 비교적 규모가 큰 습지 지역에서 관찰되며, 특히 남부지역(낙동강, 영산강)에서 비교적 많은 개체가 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528784', '48.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('542', '매사촌', 'Hierococcyx hyperythrus', 'Gould', '1856', 'Chordata', '척삭동물문', 'Aves', '조강', 'Cuculiformes', '두견목', 'Cuculidae', '두견과', 'Hierococcyx', '매사촌속', 'hyperythrus', '매사촌', '암수 같은 빛깔이다. 머리와 턱 밑, 목 옆은 검푸른 회색이고, 눈 앞은 흰색이며 각 깃의 가장자리는 검푸른 회색이다. 꼬리밑덮깃은 흰색이고, 꼬리는 갈색을 띤 회색이다. 깃의 끝부분에는 붉은빛을 띠는 회색 띠가 있으며, 연이어 폭이 넓은 검은색 띠가 그려져 있고, 다시 이어 좁은 검은색 띠가 3~4개 보인다. 날개길이는 191~215mm이다. 산지 숲에 서식한다. 온대 및 열대 지방에서 번식한다. 습성은 뻐꾸기와 비슷하며, 둥우리를 짓지 않고 큰유리새 등의 둥우리에 알을 낳아 놓는다. 산란기는 5월 중순~7월 중순이며, 여름새의 일종이다. 아시아 동부, 한국(평안북도 용암포)에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528831', '29.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('543', '방울새', 'Chloris sinica', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Chloris', '방울새속', 'sinica', '방울새', '참새목 되새과에 속하는 조류이다. 몸길이 14cm이다. 수컷은 머리와 등이 어두운 녹갈색, 배와 가슴은 어두운 황갈색, 부리는 밝은 분홍색, 온몸에 녹색 빛이 돌며 날개에 선명한 노란색 띠가 있다. 날개깃과 꼬리는 검고, 꼬리깃 가장자리에 노란색 무늬가 있다. 암컷은 더 연한 색이다. 평지와 구릉 또는 농촌과 주변의 야산에 살며 흔한 텃새이다. 둥지는 밥그릇 모양이다. 4-8월에 2-5개의 알을 낳는다. 보통 잡초나 곡류의 씨를 먹고 여름에는 곤충류를 많이 먹는다. 전국에 서식하고 세계적으로는 만주, 우수리 등지에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528870', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('544', '진홍가슴', 'Calliope calliope', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Calliope', '진홍가슴속', 'calliope', '진홍가슴', '참새목 솔딱새과의 새로 몸길이 약 15.5cm이다. 수컷 겨울깃의 이마에서 뒷머리까지는 어두운 다갈색이다. 긴 눈썹선과 뺨 아랫부분에서 끝나는 짧은 뺨선은 흰색이나 양쪽 깃털 끝은 다소 어두운색이기 때문에 눈썹선과 뺨선은 그리 선명하지 못하다. 여름깃은 봄철 털갈이가 없다. 암컷 겨울깃의 등 쪽, 날개, 꽁지의 색은 개체에 따라서 수컷보다 엷은 편이나 구별하기 힘든 개체가 많다. 부리가 여름에는 검은색이고 가을과 겨울에는 어두운 갈색으로 아랫부리 기부는 엷은 색이다. 홍채는 갈색이다. 다리는 갈색 또는 어두운 갈색이다. 북한의 개마고원에서 작은 무리가 번식하는 여름새이나, 우리나라 중부 이남에서는 봄과 가을에 작은 무리들이 통과한다. 주로 갯가나 골짜기의관목이나 풀이 무성한 덤불 등지에서 간혹 다른 멧새과 조류에 섞여 통과한다. 단독 또는 암수가 함께 생활한다. 알은 6~8월하순, 한배에 3~5개 낳는다. 곤충류와 각종 열매를 즐겨 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528875', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('545', '홍방울새', 'Acanthis flammea', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Acanthis', '홍방울새속', 'flammea', '홍방울새', '참새목 되새과에 속하는 조류이다. 몸길이는 13cm 정도이다. 겨울깃의 머리꼭대기는 광택이 있는 어두운 홍색이다. 등 쪽은 어두운 잿빛 황갈색이다. 허리는 엷은 색이고 부리 주위와 멱은 검은색이다. 눈썹은 다소 엷은 색이다. 부리는 뾰족하며 어두운 갈색이다. 홍채는 갈색이고, 다리는 어두운 갈색이다. 초원 지대,관목지대에 살며 불규칙적으로 도래하는 흔한 겨울새이다. 이동할 때에는 수 십가 무리를 짓고, 그 후는 작은 무리로 월동한다. 4-6월에 5-6개의 알을 낳는다. 자작나무과 종자와 석죽과 열매를 즐겨 먹고 딱정벌레도 먹는다. 전국에 서식하고 세계적으로는 유라시아, 북아메리카, 아프리카 서북부에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528859', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('546', '쇠홍방울새', 'Acanthis hornemanni', 'Holbøll', '1843', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Acanthis', '홍방울새속', 'hornemanni', '쇠홍방울새', '참새목 되새과의 작은 새이다. 몸길이는 11~14cm 정도이다. 참새과 중에서 소형종에 속한다. 부리는 작고 둔부에 줄무늬가 있다. 알은 엷은 푸른빛 바탕에 밝은 갈색 반점이 있다. 겨울에는 목초지나 노변, 여름에는 툰드라 지역에 서식한다. 나무의 새싹이나 작은 열매를 쪼아 먹는다. 바위 밑 또는 무성한 수풀 사이에 5~6개의 알을 낳는다. 생김새가 홍방울새와 흡사하지만 아랫배와 허리 부분의 깃털이 흰색이라서 구별된다. 북극 해안에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528860', '12.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('547', '푸른머리되새', 'Fringilla coelebs', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Fringilla', '되새속', 'coelebs', '푸른머리되새', '푸른머리되새는 되새과에 속하는 참새목 조류로, 유라시아에 널리 분포하는 작은 새입니다. 수컷은 푸른 회색의 머리와 붉은색의 아랫부분을 가지며, 암컷은 색이 더 연합니다. 둘 다 흰색 날개 띠와 흰색 꼬리 옆면이 특징입니다. 번식기는 주로 숲에서 이뤄지며 암컷이 나무의 두 갈래 사이에 깊은 둥지를 짓고 알을 낳습니다. 일반적으로 한 배에 4-5개의 알을 낳고, 새끼는 약 14일 후 둥지를 떠나며 부모가 몇 주간 더 돌봅니다. 번식기에는 주로 나무에서 무척추동물, 특히 애벌레를 먹으며, 번식기가 아닐 때에는 큰 무리를 지어 들판에서 씨앗을 먹습니다. 일부는 남쪽으로 이동하지만, 온화한 지역에 서식하는 개체는 이동하지 않습니다. 다양한 포식자가 알과 새끼를 위협하지만, 세계자연보전연맹에 의해 최소관심종으로 분류되었습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000525704', '14.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('548', '바위양진이', 'Bucanetes mongolicus', 'Swinhoe', '1870', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Bucanetes', '바위양진이속', 'mongolicus', '바위양진이', '바위양진이는 참새목 되새과에 속하는 소형 조류입니다. 긴 날개와 큰 머리, 짧고 두꺼운 회황색 부리가 특징입니다. 번식기 수컷은 얼굴과 아래쪽에 분홍색이 돌고, 날개에 광범위한 흰색과 분홍색 무늬가 있습니다. 암컷은 분홍색이 희미하게 보입니다. 이 새는 주로 터키에서 몽골에 이르는 중앙아시아와 서부 중국, 히말라야 지역의 산악 지대의 자갈이 많은 사막이나 반건조 관목 지대를 서식처로 선호합니다. 번식기가 아니면 조용한 편이며, 다양한 코소리와 짤막한 노랫소리를 냅니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000525706', '12.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('549', '아메리카홍머리오리', 'Mareca americana', 'J.F.Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Mareca', '홍머리오리속', 'americana', '아메리카홍머리오리', '기러기목 오리과의 새이다. 몸길이는 48cm가량, 중형종이다. 수컷의 이마와 머리꼭대기 중앙은 흰색이다. 양 눈 뒤로 어두운 녹색의 굵은 선이 지나가 뒷머리에서 합쳐진다. 얼굴은 갈색이 도는 흰색으로 검은색의 작은 얼룩무늬가 있다. 등과 어깨깃은 홍머리오리보다 어두운 색이다. 그 밖에는 홍머리오리와 비슷하다. 암컷도 홍머리오리의 암컷과 비슷하다. 알은 크림색이 도는 흰색이다. 논, 호수, 소택지 등에서 생활한다. 극히 드물게 겨울철에 도래한다. 낮에는 소택지의 풀숲에서 휴식을 취하고, 저녁부터 새벽까지 논, 호수, 소택지 등에서 채식한다. 둥지는 호수 중앙의 작은 섬이나 물가에서 떨어진 초원 등의 움푹 파인 땅 위에 마른풀과 잡초를 이용하여 만든다. 5~7월에 알을 8~13개 낳아 24~25일 동안 품는다. 수초류를 좋아하며, 수서곤충, 패류 등도 먹는다. 북아메리카 대륙 중서부에서 번식한다. 우리나라 강원도 속초 청초호·고성군, 제주도 성산포, 낙동강 하구 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528783', '48.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('550', '가창오리', 'Sibirionetta formosa', 'Georgi', '1775', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Sibirionetta', '가창오리속', 'formosa', '가창오리', '기러기목 오리과에 속하는 조류로 국지적으로 흔하게 관찰되는 겨울철새이다. 몸길이 약 40cm. 수컷의 얼굴에는 노란색, 녹색, 검은색의 독특한 모양의 무늬가 있으며, 부리와 접하는 부분부터 머리꼭대기까지는 검은색이다. 가슴 옆면에 흰색 세로줄이 있으며, 길게 늘어진 어깨깃이 뚜렷하다. 앞가슴 부분은 황갈색이며 몸의 옆면은 푸른빛을 띤 회색, 아래꼬리덮깃은 검은색이다. 암컷은 전체적으로 갈색을 띠며, 부리 기부에 둥근 흰색 점이 있고 목과 멱이 더 희다. 국내에는 대표적인 월동지에서 대규모 집단이 관찰되는 흔한 겨울철새이지만 전 세계 월동 집단의 대부분이 한국에서 겨울을 지내는 종이다. 겨울에는 주간에 저수지, 강 등의 습지에서 휴식을 취하고 야간에 주변의 농경지로 이동하여 벼 낟알과 같은 먹이를 채식한다. 월동기에 군집성이 강하여 수만에서 수십만 마리의 대집단을 이룬다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528788', '40.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('551', '솔새', 'Phylloscopus xanthodryas', 'Swinhoe', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Phylloscopus', '솔새속', 'xanthodryas', '솔새', '솔새는 참새목 솔새과에 속하는 조류로, 일본 전역(홋카이도 제외)에서 서식하며 동남아시아로 겨울을 나러 간다. 이 종은 북극솔새와 카무차카솔새와 가깝게 관련되어 있다. 일반적으로 보전 상태는 ''관심 필요 없음''으로 분류되어 있으며, 1863년에 처음 기술되었다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528322', '12.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('552', '흰이마알락할미새', 'Motacilla alba personata', 'Gould', '1861', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'alba', '알락할미새', '흰이마알락할미새는 참새목 할미새과에 속하는 작은 조류입니다. 주로 유라시아와 북아프리카에서 번식하고, 가장 온화한 지역에서는 연중 서식하지만 다른 지역에서는 아프리카로 이동합니다. 이 종은 벌레를 주식으로 하는 곤충류이며, 개활지나 물가 근처에서 자주 발견됩니다. 도시에서는 도로와 같은 포장된 지역에서도 먹이를 찾습니다. 돌담 틈새나 인공 구조물에 둥지를 짓습니다. 흰이마알락할미새는 평균 몸길이 16.5~19cm로, 꼬리를 계속 흔드는 특징이 있습니다. 여러 아종이 존재하며, 일부는 지리적 격리로 인해 발생했을 수 있습니다. 먹이는 주로 작은 곤충과 무척추동물로, 겨울에도 여전히 주식으로 삼습니다. 번식기는 4월부터 8월까지로, 주로 커다란 나뭇가지와 풀을 이용해 둥지를 만들고, 보통 3-8개의 알을 낳습니다. 이 새는 세계적으로 보존 상태가 양호하고, 인간의 환경 변화에 잘 적응했습니다. 열대나 아열대 지역에서는 큰 무리를 지어 겨울을 보냅니다. 덕분에 국제 자연 보전 연맹(IUCN)에서도 관심 대상에서 제외되었습니다. 라트비아의 국조로 알려져 있으며, 다양한 문화권에서 종종 등재됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528325', '17.8');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('553', '큰뒷부리도요', 'Limosa lapponica baueri', 'J.F.Naumann', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Limosa', '흑꼬리도요속', 'lapponica', '큰뒷부리도요', '큰뒷부리도요는 겨울철새이며, 전 세계적으로 이동성이 강한 대형 도요새입니다. 여름에는 북극 지방의 해안과 툰드라에서 번식하며, 겨울에는 호주와 뉴질랜드의 온대 및 열대 해안 지역에서 월동합니다. 이 종은 해안의 진흙펄과 강어귀에서 주로 서식하며, 긴 다리와 위로 휘어진 긴 부리를 가지고 있습니다. 번식 깃털은 독특한 붉은색을 띠며, 비번식기에는 회갈색으로 변합니다. 큰뒷부리도요는 최대 11,000km에 달하는 비행을 통해 태평양을 건너는 가장 긴 무착륙 비행을 수행하는 기록을 갖고 있습니다. 주요 먹이는 해안의 털갯지렁이와 조개류이며, 주로 무리지어 먹이를 찾습니다. 이들의 주요 번식지는 스칸디나비아에서 알래스카까지 아르틱 해안과 툰드라 지역에 걸쳐 있습니다. 기후 변화와 서식지 감소로 인해 개체 수가 감소하고 있어 IUCN에 의해 "근접 위협" 등급으로 평가되고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528647', '39.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('554', '섬휘파람새', 'Horornis diphone', 'Kittlitz', '1830', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Horornis', '휘파람새속', 'diphone', '섬휘파람새', '참새목 휘파람새과에 속하는 조류이다. 몸길이는 14-17cm이다. 암컷은 수컷보다 약간 작지만 야외에서 암수를 구별하기 어렵다. 윗면은 갈색이며, 아랫면은 노란색 또는 엷은 황갈색을 띤 흰색이다. 회백색의 눈썹선이 있으며, 눈선은 어두운 갈색이다. 이마와 머리는 다소 밝은 갈색이며, 뺨과 귀깃은 엷은 황갈색과 어두운 갈색이 혼합되어 있다. 저지대 산림의 계곡 또는 임연부, 하천 주변 또는 대나무 숲의관목림이나 덤불에 살며 한반도 전역에 도래하여 번식하는 여름철새이다. 번식은관목림, 초지, 작은 대나무 숲속에 마른풀을 이용하여 입구가 옆에 있는원형모양의 둥지를 튼다. 알은 5~7월에 4~6개를 낳는다. 알을 품기 시작하여 16일이 되면 새끼가 부화하고, 어미는 새끼에게 14일 정도 곤충이나 거미 등을 물어다 먹인다. 알을 품는 일과 새끼에게 먹이를 운반하는 일은 모두 암컷이 담당한다. 알은 붉은색을 띤 초콜릿색이며 알의 색깔이 비슷한 두견이에게 탁란을 당하는 경우도 있다. 경기도, 강원도에 서식하고 세계적으로는 중국, 극동 러시아, 타이완에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528740', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('555', '갈색머리갈매기', 'Larus brunnicephalus', 'Jerdon', '1840', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Larus', '갈매기속', 'brunnicephalus', '갈색머리갈매기', '갈색머리갈매기는 작은 갈매기 종류로 중앙아시아의 고원 지대에서 번식합니다. 여름에는 갈색 머리를 띠며, 몸은 회색이고 부리와 다리는 붉은색입니다. 겨울에는 갈색 머리가 사라지고 검은 세로 줄무늬만 남습니다. 이 새는 주로 인도 아대륙의 해안과 큰 내륙 호수에서 겨울을 납니다. 떼 지어 생활하며, 먹이를 찾아 도심에서도 쉽게 발견될 수 있습니다. 번식기는 고립된 섬이나 큰 갈대밭에 집단을 이루어 땅에 둥지를 틉니다.    한국어 이름: 갈색머리갈매기', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000529297', '42.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('556', '말똥가리', 'Buteo japonicus', 'Temminck & Schlegel', '1845', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Buteo', '말똥가리속', 'japonicus', '말똥가리', '말똥가리는 비교적 큰 맹금류로, 동아시아와 러시아 및 남아시아 일부 지역에 서식하며, 겨울에는 동남아시아에서 찾아볼 수 있다. 이 새는 육식성이며, 여러 지역에 따라 네 개의 아종이 있다. 말똥가리는 시베리아, 몽골, 북중국, 북한에서 번식하고 동남아시아로 이동하는 아종, 일본에서 번식하고 남부 일본에서 대만까지 겨울을 나는 아종 등이 포함된다. 보존 상태는 상대적으로 안정적인 것으로 평가받고 있다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528837', '50.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('557', '노랑때까치', 'Lanius cristatus lucionensis', 'Linnaeus', '1766', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Laniidae', '때까치과', 'Lanius', '때까치속', 'cristatus', '노랑때까치', '노랑때까치라는 이름의 이 새는 때까치과에 속하는 조류로, 주로 아시아에서 서식한다. 몸 윗면은 주로 갈색이며, 눈 주위에 검은 ''도둑눈가리개''가 특징적이다. 번식기에는 북부 아시아의 타이가 및 나무와 관목에서 둥지를 틀고 2~6개의 알을 낳는다. 겨울철에는 주로 열대 아시아로 이동하여 서식지를 차지하며, 인도 등지에서 겨울을 지내기 위해 8월이나 9월에 도착하고 4월에 떠난다. 주요 먹이는 나방과 같은 곤충이며, 작은 새나 도마뱀을 먹기도 한다. 먹이를 포획한 후 가시에 꽂아 두는 습성이 있다. 종종 유럽이나 북미에서도 드물게 발견된다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528317', '18.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('558', '섬촉새', 'Emberiza personata', 'Temminck', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Emberizidae', '멧새과', 'Emberiza', '멧새속', 'personata', '섬촉새', '섬촉새는 참새목 멧새과의 조류로, 주로 사할린, 쿠릴 열도 및 일본에서 발견됩니다. 이전에는 검은멧새의 아종으로 여겨졌으나, 형태학적 및 유전적 차이로 인해 별개의 종으로 구분되었습니다. 이 새는 멸종 위기에서는 ‘관심 필요’ 등급으로 분류되고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528324', '14.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('559', '작은동박새', 'Zosterops simplex', 'Swinhoe', '1861', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Zosteropidae', '동박새과', 'Zosterops', '동박새속', 'simplex', '작은동박새', '작은동박새는 동박새과에 속하는 조류로, 중국 동부, 대만, 베트남 북부, 태국-말레이 반도, 수마트라 및 보르네오에 서식합니다. 또한 남부 캘리포니아에도 도입된 개체군이 존재합니다. 이 종은 일본동박새의 아종으로 여겨졌으나 2018년 분자 계통 연구 결과에 따라 독립된 종으로 격상되었습니다. 작은동박새는 주로 산림 지역에서 생활하며, 아열대 및 열대 기후의 다양한 지역에서 발견됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528326', '10.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('560', '큰검은머리갈매기', 'Ichthyaetus ichthyaetus', 'Pallas', '1773', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Ichthyaetus', '큰검은머리갈매기속', 'ichthyaetus', '큰검은머리갈매기', '러시아, 중국에서 번식하고 미얀마 등지에서 월동하는 대형 갈매기로 우리나라에서는 미조로서 가끔 관찰된다. 몸길이 60~72cm, 몸무게 0.9~1kg이다. 눈 위아래에 흰 눈테가 있고, 부리는 전체적으로 노란색이지만 붉은색과 검은색 띠가 있다. 해안과 강, 하구역 등의 습지에 서식하며 염습지 내의 작은 섬에서 번식한다. 먹이는 죽은 물고기, 곤충 및 소형 포유류, 새 알 및 파충류 등이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528803', '66.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('561', '재때까치', 'Lanius borealis', 'Vieillot', '1808', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Laniidae', '때까치과', 'Lanius', '때까치속', 'borealis', '재때까치', '참새목 때까치과의 새로 몸길이 24.5cm가량의 대형 때까치류이다. 수컷의 이마, 머리꼭대기, 뒷머리, 뒷목은 순잿빛으로 이마 앞 끝부분은 흰색을 띤다. 눈 앞에서 귀깃까지는 검은색의 눈선이 있고, 눈선 위쪽에 흰색의 눈썹선이 지나간다. 턱 밑, 멱, 귀깃의 일부, 뺨은 흰색이다. 등과 어깨깃은 순 잿빛으로 어깨깃 바깥쪽 것은 흰색을 띤다. 허리는 잿빛이다. 날개깃은 검은색이다. 부리는 검은색이고, 홍채는 어두운 갈색이다. 다리는 검은색이고 경부의 깃털은 흰색이다. 암컷의 이마, 머리꼭대기, 뒷머리는 잿빛 갈색이고 머리의 눈선은 갈색이다. 배는 흰색으로 가느다란 가로띠가 여러 개 있다.교목이나관목의 가지 위에 영소한다. 드문 겨울새이다. 주로 단독 또는 암수로 생활한다. 산란기는 4월하순~6월상순이며, 한배의 산란수는 5~7개, 때로는 8~9개이다. 식성은 주로 동물성을 취하며 포유류의 설치목, 조류의 참새목, 양서류의 무미목과 파충류를 즐겨 먹고, 곤충류와 거미류도 먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528872', '24.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('562', '노랑배진박새', 'Pardaliparus venustulus', 'Swinhoe', '1870', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Paridae', '박새과', 'Pardaliparus', '노랑배진박새속', 'venustulus', '노랑배진박새', '노랑배진박새는 박새과에 속하는 조류로, 주로 중국에 서식하는 종입니다. 이 새는 온대 및 아열대의 습윤 저지대 숲에 주로 서식하며, 2000m 이하의 고도에서 발견됩니다. 도시의 녹지에서도 발견될 수 있습니다. 수컷은 강한 영역성을 가지며, 이웃의 영역을 존중하는 ''친한 이웃 효과''를 보입니다. 개체별로 선호하는 발이 있으며, 이는 도피 성능에 영향을 미칩니다. 노랑배진박새는 국제적으로 멸종 위협에 대한 우려가 적은 것으로 평가됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528892', '10.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('563', '섬참새', 'Passer cinnamomeus', 'Gould', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Passeridae', '참새과', 'Passer', '참새속', 'cinnamomeus', '섬참새', '참새목 참새과에 속하는 조류이다. 몸길이는 13cm이다. 수컷 윗면은 진한 적갈색에 검은 세로 줄무늬가 있으며, 아랫면은 흰색에 가까운 황갈색이고 뺨이 희다. 수컷의 겨울깃 뒷머리에 흰색에 가까운 회갈색 띠가 있다. 암컷은 윗면이 갈색이며 회백색 눈썹선이 있다. 주택지에서 멀지 않은 곳에 살며 울릉도에 흔한 텃새이다.큰키나무의 구멍에 산다. 5-7월에 5~7개의 알을 낳는다. 쌀, 조, 피 등을 즐겨 먹고 여름철에는 곤충을 먹는다. 울릉도, 제주도 및 남부연안에 서식하고 세계적으로는 한국, 일본, 중국, 시베리아에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528893', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('564', '큰부리개개비', 'Arundinax aedon', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Acrocephalidae', '개개비과', 'Arundinax', '큰부리개개비속', 'aedon', '큰부리개개비', '참새목 휘파람새과의 조류이다. 몸길이는 18cm 정도로 개개비와 비슷하다. 수컷 여름깃의 머리꼭대기, 등, 어깨는 다소 녹슨 색을 띤 올리브 황갈색이다. 눈 앞과 눈까풀의 작은 깃털은 황색을 띤 흰색이며, 귀깃은 등의 색보다 엷으며 각 깃털에는 한층 더 엷은 색의 축반이 있다. 턱 밑, 멱, 아랫가슴은 황토색을 띤 흰색이고, 윗가슴과 옆구리는 올리브색을 띤 황토색이다. 암컷은 수컷과 구별하기 어렵다. 윗부리는 어두운 뿔빛 갈색이고 아랫부리는 엷은 갈색으로 끝과 기부는 황색을 띤다. 홍채는 갈색이고, 다리는 납빛 회색 또는 어두운 뿔빛 갈색이다. 주로 물가의 풀밭이나관목숲에 영소한다. 봄과 가을에 우리나라를 지나가는 드문 나그네새로 여겨진다. 물가의 갈대밭이나 숲속에 몸을 숨기고 좀처럼 자태를 드러내지 않는다. 둥지는 0.3~1.5m 높이의 나뭇가지 위에 짓는다. 알은 6월경, 한배에 4~6개 낳는다. 주로 딱정벌레목, 메뚜기목, 벌목 등의 곤충을 먹는다. 시베리아 남부, 몽골 북부, 만주, 아무르, 우수리 등지에서 번식한다. 중국 남부, 인도 아샘~네팔, 말레이반도 등지에서 월동한다. 우리나라, 중국 동부 등지를 통과한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528902', '18.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('565', '유리딱새', 'Tarsiger cyanurus', 'Pallas', '1773', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Tarsiger', '유리딱새속', 'cyanurus', '유리딱새', '나그네새 또는 겨울철새로 그리 흔하지 않은 새이다. 길이는 14cm 정도이다. 수컷은 머리, 등, 꼬리가 짙은 청색이고 배는 흰색이며, 옆구리는 황갈색이다. 흰색의 눈썹 선은 이마까지 뻗어 있다. 암컷의 등은 옅은 황갈색이고 허리와 꼬리는 청색을 띠며 배는 흰색, 옆구리는 주황색을 띤다. 아고산대나 고산대의 숲속에서 번식한다. 바위나 언덕 벼랑의 굴속이나 큰 나무뿌리 및 쓰러진 나무 밑 등에 부드러운 식물줄기로 오목한 형태의 둥지를 만든다. 알은 흰색 바탕에 엷은 적갈색 반점이 있으며 짝짓기 후 보통 3~6개 정도 낳는다. 아시아 동부 및 중부에 분포하며 주로 대만 등지에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528886', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('566', '귤빛지빠귀', 'Geokichla citrina', 'Latham', '1790', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Geokichla', '귤빛지빠귀속', 'citrina', '귤빛지빠귀', '귤빛지빠귀는 참새목 지빠귀과에 속하는 조류로, 인도 아대륙과 동남아시아의 울창한 숲에 흔히 서식합니다. 대부분의 개체들은 텃새로, 그늘지고 습한 지역을 선호하며, 나무에 둥지를 틀지만 무리를 짓지는 않습니다. 주로 곤충, 지렁이, 과일을 먹는 잡식성입니다.    이 작은 지빠귀의 수컷은 회색의 윗부분과 귤색 머리, 아랫부분을 가지고 있으며, 암컷과 어린 새는 윗부분이 더 갈색입니다. 번식기는 주로 나무나 관목에 둥지를 지으며, 알 3~4개를 낳습니다. 둥지는 다른 부드러운 식물 재료로 안을 채운 얕은 컵 모양입니다.    귤빛지빠귀는 자몽색과 회색 깃털이 특징적이며, 종종 강가나 그늘진 계곡에서 발견됩니다. 한국에서는 보기가 드문 새지만, 숲이 성숙해지며 새로운 곳으로 서식지를 확장하고 있습니다. IUCN 적색 목록에 따르면, 귤빛지빠귀는 절멸 위협이 적은 종으로 평가됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528907', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('567', '흰눈썹지빠귀', 'Geokichla sibirica', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Turdidae', '지빠귀과', 'Geokichla', '귤빛지빠귀속', 'sibirica', '흰눈썹지빠귀', '참새목 지빠귀과의 조류로 몸길이는 23cm쯤이다. 수컷 겨울깃의 머리꼭대기는 검은색으로 각 깃털의 가장자리는 다소 석판색을 띠고 있다. 눈 앞은 검은색이며, 눈 앞쪽에서 목 옆에 이르는 폭넓은 현저한 흰색의 눈썹선이 있다. 여름깃은 봄철에 몸 깃털의 전부 또는 일부, 특히 머리, 가슴, 등을 털갈이해서 된다. 새로 생긴 깃털은 겨울깃과 비슷하다. 여름이 되면 전체가 푸른색은 감소되고 잿빛이 많아진다. 여름깃은 겨울깃과 다름이 없다. 부리는 적당한 길이이다. 수컷 부리는 아랫부리와 윗부리 모두 검은색이고, 암컷에서는 윗부리가 어두운 갈색이며 아랫부리의 끝은 어두운 갈색이다. 홍채는 갈색이고, 다리는 오렌지 황색이다. 잡목림, 낙엽활엽수림, 적송림, 아고산대의 침엽수림 등에 영소한다. 봄과 가을에 작은 무리들이 통과하는 비교적 드문 나그네새이다. 땅 위를 뛰어다니며 다리로 낙엽을 헤치면서 먹이를 찾는다. 알은 5월 중순~7월 중순, 한배에 3~4개, 보통 3개 낳는다. 곤충류와 식물의 열매를 주로 먹는다. 중국, 시베리아 등지에 분포하며, 동남아시아에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528908', '23.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('568', '바다제비', 'Hydrobates monorhis', 'Swinhoe', '1867', 'Chordata', '척삭동물문', 'Aves', '조강', 'Procellariiformes', '슴새목', 'Hydrobatidae', '바다제비과', 'Hydrobates', '바다제비속', 'monorhis', '바다제비', '슴새목 바다제비과에 속하는 조류이다. 몸 전체 길이 20cm 정도이다. 암수 모두 온몸이 암갈색이다. 이마와 덮깃은 연한 갈색이고, 배 쪽과 날개의 뒷면은 어두운 회갈색이다. 꼬리와 부리, 다리는 검은색이다. 첫째, 둘째 줄 날개깃은 검은색이고 셋째 줄 날개깃은 갈색이며 깃의 가장자리는 연한 갈색이다. 날개의 길이는 14-16cm이다. 꼬리는 검은색으로 제비꼬리 모양이다. 알은 흰색이다. 해안 앞바다 도서에 살며 일부 지역에서는 흔한 여름철새이다. 섬의 바위틈, 슴새의 낡은 땅굴, 또는 스스로 판 땅굴에 둥지를 만든다. 땅굴은 깊이 1m에 달한다. 7-8월에 알 1개를 낳고 암수가 함께 품는다. 서해안, 독도에 서식하고 세계적으로는 일본 도서, 중국, 타이완, 싱가포르, 수마트라, 자바, 인도 해양에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528911', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('569', '푸른바다직박구리', 'Monticola solitarius pandoo', 'Sykes', '1832', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Monticola', '바다직박구리속', 'solitarius', '바다직박구리', '푸른바다직박구리 푸른바다직박구리는 참새목에 속하는 조류로, 몸길이가 21~23cm 정도입니다. 수컷은 번식기에 푸른 회색 깃털을 가지며, 암컷과 어린 새는 어두운 갈색을 띱니다. 아시아, 유럽 남부, 북서 아프리카에서 번식하며, 일부 개체는 아프리카와 동남아시아에서 겨울을 납니다. 주로 산악 지역의 바위 틈에 둥지를 틀고, 한 번에 3~5개의 알을 낳습니다. 잡식성으로 곤충, 작은 파충류, 열매, 씨앗을 먹습니다. 이 새는 말타의 국조로도 유명합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528327', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('570', '바다직박구리', 'Monticola solitarius philippensis', 'P.L.S.Müller', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Monticola', '바다직박구리속', 'solitarius', '바다직박구리', '바다직박구리는 참새목 흰딱새과에 속하는 조류입니다. 몸길이는 21~23cm로, 수컷은 번식기 동안 청회색 깃털을 가지며, 날개는 더 짙은 색입니다. 암컷과 어린 개체는 어두운 갈색 상체와 옅은 갈색의 비늘 무늬를 가집니다. 주로 남유럽, 북서 아프리카, 중앙아시아에서 번식하며, 동남아시아에서도 발견됩니다. 이 새는 돌 틈이나 벽에 둥지를 틀고, 한 번에 3-5개의 알을 낳습니다. 잡식성으로 곤충, 작은 파충류, 열매와 씨앗을 먹습니다. 흔치는 않으나 서구와 북부 유럽, 북미에서도 가끔 관찰됩니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528328', '22.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('571', '에위니아제비갈매기', 'Onychoprion anaethetus', 'Scopoli', '1786', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Onychoprion', '검은등제비갈매기속', 'anaethetus', '에위니아제비갈매기', '에위니아제비갈매기는 중형의 제비갈매기로, 몸길이는 약 30~32cm이며 날개길이는 77~81cm입니다. 이 새는 열대 해양에 서식하며 겨울을 보내기 위해 더 넓은 열대 바다로 이동합니다. 몸 윗부분은 짙은 회색이며, 아랫부분은 흰색으로, 이마와 눈썹, 뒷목에 흰색 띠 무늬가 있습니다. 다리와 부리는 검은색입니다.    번식지는 주로 바위섬이며, 땅에 긁어 만든 둥지나 작은 구멍에 한 개의 알을 낳습니다. 물고기를 먹기 위해 바다에 뛰어들어 잡는 방식으로 사냥을 하며, 수면 위의 물고기를 집어 올리기도 합니다. 수컷은 구애 시, 암컷에게 물고기를 제공합니다.     전 세계 여러 지역의 열대 해양에 분포하는데, 멕시코, 카리브해, 서아프리카, 아라비아 반도, 동남아시아 및 오스트랄라시아에 서식하는 것으로 알려져 있습니다. 서유럽에서는 드물게 관찰되는 종입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528813', '31.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('572', '붉은부리큰제비갈매기', 'Hydroprogne caspia', 'Pallas', '1770', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Hydroprogne', '붉은부리큰제비갈매기속', 'caspia', '붉은부리큰제비갈매기', '제비갈매기 속의 다른 종들에 비하여 덩치가 크고, 첫째날개깃의 여덟 번째부터 첫 번째 깃까지 검은빛을 띠고, 꼬리깃이 짧다. 북미, 북유럽, 중부 유럽, 서부 시베리아, 오스트레일리아, 뉴질랜드, 남부 아프리카에서 번식하고 미국 남부, 아프리카 북서부, 남아프리카, 일본 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528815', '54.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('573', '노랑발도요', 'Tringa brevipes', 'Vieillot', '1816', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Tringa', '삑삑도요속', 'brevipes', '노랑발도요', '도요목 도요과에 속하는 조류로 국내에는 흔하게 통과하는 나그네새이다. 몸길이 25cm.  날개는 155~171mm, 꼬리는 64~76mm이다. 암수가 같은 빛깔이다. 여름깃은 머리와 윗목 및 등 쪽이 모두 어두운 회색이고 날개도 같은 색이다. 눈썹선은 흰색이다. 몸 아랫면은 흰색으로 가슴과 겨드랑이에는 회흑색의 가느다란 가로줄이 있다. 부리는 갈색 또는 초록빛 갈색이다. 간척지나 하구의 삼각주, 논, 염전 해안의 갯바위 등지에 작은 무리를 이루어 서식하며 곤충류와 갑각류 등 다양한 무척추동물을 잡아먹는다. 시베리아 동북부에서 번식하고, 동남아시아, 뉴기니, 호주에서 월동한다. 국내에는 봄과 가을에 흔하게 통과하는 나그네새이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528818', '25.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('574', '목도리도요', 'Calidris pugnax', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'pugnax', '목도리도요', '우리나라에서는 나그네새로 9~10월에 적은 무리를 볼 수 있다. 몸길이는 수컷 32cm, 암컷 25cm 정도이다. 수컷의 여름깃은 번식기에 목 부분에 흰색 또는 보라색, 붉은색 등 개체에 따라 다른 빛깔의 목도리 모양 장식깃이 생긴다. 겨울깃은 여름깃과 같은 목의 장식이 없고, 등 쪽이 회갈색이며 깃의 가장자리는 흰색이다. 몸 아랫면은 흰색 또는 회색이다. 바닷가 간석지나 습지 등에서 생활한다. 풀밭이나관목이 있는 땅에 접시 모양 둥지를 짓는다. 산란기는 5월 중순~하순이고, 알 4개 정도를 낳는다. 주로 곤충을 먹으며, 그 밖에도 조개류, 갑각류, 환형동물과 식물 열매를 먹는다. 구북구 북부에서 번식하며 아프리카와 아시아의 온대 및 열대 지역에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528821', '28.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('575', '누른도요', 'Calidris subruficollis', 'Vieillot', '1819', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'subruficollis', '누른도요', '몸길이 20cm이다. 몸의 윗면은 흑갈색이나 깃의 끝부분은 흰색이다. 얼굴과 배는 황갈색이다. 다리는 황색이다. 부리는 곧고 짧다. 알은 갈색 반점이 있다. 초지와 내륙의 물가에 서식한다. 한배에 알을 4개 낳는다. 알래스카 극지방, 북극 툰드라 지대, 캐나다 북서부에서 번식하고, 아르헨티나에서 월동한다. 우리나라에서는 미조로 기록되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528823', '20.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('576', '쇠재두루미', 'Grus virgo', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Gruidae', '두루미과', 'Grus', '두루미속', 'virgo', '쇠재두루미', '두루미목 두루미과의 새이다. 몸길이는 98cm쯤이다. 암컷과 수컷의 이마는 석판 검은색으로 잿빛이 섞여 있고 때로는 전부 잿빛 또는 검은색인 것도 있다. 머리꼭대기와 뒷머리는 엷은 잿빛 쥐색이며, 눈 앞은 검은색이고 눈 위에는 너비 10mm 정도의 검은색 눈썹선과 연결된다. 부리는 올리브색으로 끝은 붉은 오렌지색이고, 다리는 검은색이다. 초지, 습지, 강가 등지에서 영소한다. 습지에 내려와서 양쪽 다리를 교대로 움직여 조용히 걸어 다니며 먹이를 찾는다. 땅 위에 둥지를 튼다. 초지에 풀을 쌓아 올려 둥지로 하는 경우와 나지나 암석지에 작은 돌을 쌓아 둥지를 만드는 경우도 있다. 5월 중순~7월에 한배에 2개 산란한다. 메뚜기목과 딱정벌레목 곤충류, 연체동물 복족류의 유폐목, 환형동물과 곡류, 기타 식물을 먹는다. 아프리카 북부, 유럽 동남부, 몽골, 이라크, 이란, 인도, 중국 동북부, 일본 등지에 분포한다. 우리나라에서는 미조(迷鳥)로 경기도, 강화도에서 오래 전에 관찰된 기록이 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528841', '98.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('577', '시베리아흰두루미', 'Leucogeranus leucogeranus', 'Pallas', '1773', 'Chordata', '척삭동물문', 'Aves', '조강', 'Gruiformes', '두루미목', 'Gruidae', '두루미과', 'Leucogeranus', '시베리아흰두루미속', 'leucogeranus', '시베리아흰두루미', '시베리아흰두루미는 두루미목 두루미과 조류로 매우 드문 겨울철새이다. 몸길이는 약 135cm이며 암수의 형태가 유사하다. 눈 주변에 붉은색 피부가 노출되어 있다. 첫째날개깃이 검은색이지만, 흰색의 긴 셋째날개깃이 덮고 있어, 날지 않으면 몸 깃 전제가 흰색으로 보인다. 어린 새는 전체적으로 황갈색과 흰색 깃이 섞여 있다. 특히, 머리, 날개덮깃, 셋째날개깃에 황갈색이 강하다. 겨울철에 다른 두루미 무리에 섞여 1~2개체가 드물게 관찰되거나 단독으로 월동한다. 습지, 논 등에 서식하며 긴 부리를 이용해 땅에 떨어진 식물의 종자, 곤충류, 어류, 조개류를 먹는다. 러시아의 오브강과 시베리아 북동부의 콜리마천 유역에서 번식하고, 중국의 포양호, 양쯔강에서 월동한다. 국내에는 강원도 철원, 파주, 화성, 천수만, 만경강, 순천만, 흑산도, 제주도 등지에서 관찰기록이 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528844', '135.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('578', '북방쇠종다리', 'Alaudala cheleensis', 'Swinhoe', '1871', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Alaudidae', '종다리과', 'Alaudala', '북방쇠종다리속', 'cheleensis', '북방쇠종다리', '4~5월과 9~10월에 우리나라 전역을 통과하는 나그네새이다. 날개길이 8.5~10cm이다. 수컷 겨울깃의 등 쪽은 황갈색이다. 눈 앞과 눈썹선은 엷은 모래색이며, 뺨과 턱 밑 이하의 아랫면은 모래 빛 흰색으로 윗가슴에는 작고 어두운 갈색 무늬가 산재한다. 몸의 옆은 모래 빛 황갈색으로 어두운 색의 세로무늬가 있다. 위꼬리덮깃은 다소 황갈색을 띤다. 물가 모래땅 또는 자갈밭, 농경지 등에 서식한다. 번식기는 4~6월이다. 한배의 산란수는 3~4개이다. 중앙아시아, 몽골, 중국 북부에서 번식하고 남쪽으로 내려와 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528853', '14.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('579', '검은할미새사촌', 'Lalage melaschistos', 'Hodgson', '1836', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Campephagidae', '할미새사촌과', 'Lalage', '검은할미새사촌속', 'melaschistos', '검은할미새사촌', '네팔, 타이완, 미얀마, 방글라데시, 베트남, 인도, 일본, 중국 등지에 분포하며, 숲의 개방된 곳과 과수원에 서식한다. 몸길이는 19~24cm이며, 꼬리가 길다. 전체적으로 회색이고, 날개와 꼬리는 검은색이다. 수컷은 진회색이고 얼굴은 더 짙은 색이다. 암컷은 아랫부분을 제외하고는 상대적으로 색이 옅다. 비행 시에는 첫째날개깃 아래쪽에 흰색 부분이 나타난다. 무척추동물을 잡아먹는다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528855', '21.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('580', '쇠유리새', 'Larvivora cyane', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Larvivora', '쇠유리새속', 'cyane', '쇠유리새', '참새목 솔딱새과의 조류로 몸길이는 15cm쯤이다. 수컷 겨울깃의 부리 털과 눈 앞은 검은색이고, 뺨과 귀깃은 조금 푸른색을 띤 검은색이다. 목 옆과 가슴 옆은 검은색에 가까운 어두운 푸른색이다. 턱 밑에서 아래꽁지덮깃에 이르는 앞쪽 아랫면은 흰색이다. 이마, 머리꼭대기, 뒷목, 등, 어깨, 허리, 위꽁지덮깃은 어두운 푸른색이다. 꽁지는 검은색으로 중앙꽁지깃 1쌍과 그 밖의 꽁지깃 바깥 가장자리는 어두운 푸른색이다. 부리는 검은색이고, 다리는 흰색에 가까운 엷은 살구색이다. 홍채는 갈색이다. 고산에서 드물게 번식한다.관목숲, 아고산대의 침엽수림과 활엽수의 밀림 속, 평탄한 지상, 벼랑의 움푹 파인 곳, 초목의 뿌리, 쓰러진 나무 밑 등에 영소한다. 여름새이나 봄과 가을에 드물지 않게 통과하는 나그네새이다. 단독 또는 암수 함께 생활하며, 번식기에는 일정한 세력권을 점유한다. 5~7월에 한배에 3~5개, 때로는 6~7개 산란한다. 벌목, 딱정벌레목, 나비목 등의 유충과 성충을 먹는다. 시베리아 남부에서 동아시아에 이르는 지역에 분포한다. 중국 남부와 인도네시아 등지에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528882', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('581', '긴발톱할미새', 'Motacilla tschutschensis', 'J.F.Gmelin', '1789', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'tschutschensis', '긴발톱할미새', '우리나라에서는 쉽게 볼 수 없는 나그네새이다. 머리꼭대기에서 등까지는 어두운 잿빛으로 약간 노란빛을 띠며, 턱 밑에서 배까지는 연한 노란색이다. 부리는 진한 갈색으로 가늘고 길며, 다리는 검은색이다. 날개는 검고 가장자리가 약간 희다. 꼬리는 검은색이고, 바깥꼬리깃은 흰색이다. 여름깃은 이마에서 허리까지 연한 녹색이고, 턱 밑은 모두 진한 노란색이다. 낮은 지대의 초원이나 늪지대 주변에 서식한다. 주된 먹이는 곤충이다. 이동할 때에는 많은 수가 무리를 이룬다. 초원과 툰드라의 지상에 둥지를 튼다. 산란기는 5월하순~6월이며, 한배의 산란수는 4~5개이다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528728', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('582', '꼬마갈매기', 'Hydrocoloeus minutus', 'Pallas', '1776', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Hydrocoloeus', '꼬마갈매기속', 'minutus', '꼬마갈매기', '꼬마갈매기는 세상에서 가장 작은 갈매기로, 몸길이는 24~28cm, 날개 길이는 62~69cm이다. 성체의 여름깃은 검은 머리, 짙은 빨간색의 부리와 다리를 가지며 배 쪽이 장미색이 도는 경우가 있다. 겨울에는 검은 머리와 귀 부분에 점이 있고, 흰색 아랫면과 검은 부리를 가진다. 어린 새는 등과 머리에 짙은 색이 많아서 비행 시 상반부에 검은 "W" 패턴이 보인다.    이 새는 주로 시베리아 북부에서 번식하며, 겨울에는 유럽의 해안과 지중해, 흑해, 카스피해까지 남하한다. 드물게 북아메리카 대서양 해안에서 월동하기도 한다. 꼬마갈매기는 번식기에는 주로 습지나 호수 주변의 식생이 무성한 지역에 서식하며, 겨울에는 해안이나 강 하구에서 관찰된다.    번식기에는 주로 곤충을 잡아먹으며, 겨울에는 작은 물고기와 해양 무척추동물을 먹는다. 둥지는 물 위나 땅에 짓고, 다른 갈매기나 제비갈매기와 함께 군집 생활을 한다. 비번식기에는 큰 무리를 지어 다니며 먹이를 먹거나 쉬기도 한다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528807', '26.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('583', '고대갈매기', 'Ichthyaetus relictus', 'Lönnberg', '1931', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Ichthyaetus', '큰검은머리갈매기속', 'relictus', '고대갈매기', '고대갈매기는 「멸종위기 야생생물 Ⅱ급」의 갈매기과 철새로 적호갈매기라고도 불린다. 부리와 다리는 어두운 붉은색이며 각이 진 머리가 특징이다. 동아시아 전역에 서식하며 번식지는 카자흐스탄, 러시아, 몽골 일부 지역이다. 번식지 내 수량 변화로 낮은 번식성공률을 보이며, 러시아와 중국 내 인간의 방해, 기상악화로 번식지의 여건이 나빠지고 있다. 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528808', '44.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('584', '검은머리방울새', 'Spinus spinus', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Spinus', '검은머리방울새속', 'spinus', '검은머리방울새', '우리나라 전역에서 월동하는 흔한 겨울새이다. 몸길이는 12.5cm이다. 수컷 겨울깃의 이마와 머리꼭대기, 턱 밑과 멱의 중앙은 검은색이다. 배 옆은 잿빛 녹색을 띠며 검은 갈색의 세로무늬가 있으며 배는 흰색이다. 턱, 뒷목, 등, 어깨, 허리, 가슴은 황록색이다. 아래꼬리덮깃의 기부는 황색이고, 끝은 흰색으로 엷은 검은색 무늬가 있다. 부리와 다리는 갈색이다. 침엽수림이나 오리나무 숲에 서식하며 침엽수의 높은 가지에 이끼와 나뭇가지, 털 등으로 작은 컵 모양의 둥지를 만든다. 산란기는 4~6월이고 한배의 산란수는 3~5개이다. 주로 식물 열매나 씨앗을 먹는다. 구북구 아한대 지역에서 번식하고 온대와 아열대 지역에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528871', '12.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('585', '붉은가슴울새', 'Larvivora akahige', 'Temminck', '1835', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Muscicapidae', '솔딱새과', 'Larvivora', '쇠유리새속', 'akahige', '붉은가슴울새', '작은 무리가 부산 부근에 4~5월에 잠시 도래하는 미조이다. 몸길이 약 14cm이다. 등과 꼬리는 붉은 갈색이고, 머리와 멱, 윗가슴은 오렌지색이다. 가슴 아래쪽은 잿빛이다. 주로 아고산대의 침엽수림에서 번식한다. 벼랑의 파인 곳이나 나무뿌리 가까운 땅 위에 둥지를 튼다. 산란기는 6~7월이고, 한배의 산란수는 3~5개이다. 곤충류를 주식으로 한다. 일본에서 번식하며 중국 동남부에서 겨울을 난다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528873', '14.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('586', '멋쟁이새', 'Pyrrhula pyrrhula rosacea', 'Seebohm', '1882', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Pyrrhula', '멋쟁이새속', 'pyrrhula', '멋쟁이새', '멋쟁이새는 참새목 되새과에 속하는 작은 조류입니다. 이 새는 주로 유라시아 전역의 혼합 숲에서 서식하며, 노래는 부드럽고 애잔한 휘파람 소리로 묘사됩니다. 멋쟁이새는 다소 둥글고 튼튼한 몸을 가지고 있으며 성체 수컷은 붉은색 가슴과 하체를 지니고 있습니다. 주로 씨앗과 과일나무의 싹을 먹으며, 번식기에는 네에서 일곱 개의 알을 낳고 덤불이나 나무에 둥지를 틉니다. 이 새는 비교적 포획하기 쉽고, 특정 멜로디를 배우는 능력이 있어 길들여질 수 있는 것으로 알려져 있습니다. 한국에서는 보기 드문 새입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528672', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('587', '알락오리', 'Mareca strepera', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Mareca', '홍머리오리속', 'strepera', '알락오리', '기러기목 오리과의 조류로 흔한 겨울철새이다. 몸길이 약 51cm.   수컷은 전체적으로 회색이 강하며 회흑색 반점이 흩어져 있다. 부리와 아래꼬리덮깃은 검은색이다. 가슴의 깃털은 가장자리가 검은색으로 비늘무늬처럼 보인다. 변환깃은 암컷과 비슷하다. 암컷은 전체적으로 갈색이며 부리는 윗면이 검은색, 가장자리는 주황색이다. 앉아있을 때 흰점으로 보이는 둘째날개깃은 날 때 뚜렷하게 보이는 특징이다. 수면성 오리로 낮에는 강이나 하구, 저수지, 호수 등의 수면에서 자맥질해 물속 수초를 먹거나 수면 가까운 수초나 식물의 종자 등을 먹는다. 해질녘에는 물 고인 논, 습지로 이동해 식물의 종자를 먹는다. 유라시아와 북아메리카의 아한대에서 번식하고 유럽 남부, 북아프리카, 인도, 중국 동부, 한국, 일본에서 월동한다. 국내에는 흔한 겨울철새로 우리나라 전역에서 관찰된다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528789', '51.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('588', '캐나다기러기', 'Branta hutchinsii', 'Richardson', '1832', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Branta', '흑기러기속', 'hutchinsii', '캐나다기러기', '기러기목 오리과의 새로 전체 길이는 67cm가량이다. 목덜미와 목둘레의 흰색과 머리와 목의 나머지 부분이 검은색으로 대조적인 것이 특징적이다. 등과 윗날개는 흑회색의 불명확한 가로줄 무늬가 있다. 첫째날개깃은 흑갈색이고 배와 가슴은 연한 갈색이다. 위꽁지와 아래꽁지덮개 및 아래꽁지 부분은 흰색이고, 꽁지는 흑색이다. 북미에서는 습지, 논, 강, 골프장 등 숲이 아닌 평지 어디에서나 볼 수 있으며, 도심 공원에는 텃새화한 개체들도 흔히 보인다. 가족 형성은 둥지에서부터 시작된다. 둥지는 풀의 잎이나 줄기로 만들며, 그 위에 털을 올려놓는다. 알은 4-7개 낳으며 암컷 단독으로 부화한다. 수컷은 둥지 주위를 지킨다. 25-30일 만에 부화한다. 새끼는 부화된 첫날부터 물에 들어간다. 북미 대륙, 알래스카, 알류샨 열도에서 번식한다. 일본에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528791', '67.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('589', '북방쇠박새', 'Poecile montanus', 'Conrad von Baldenstein', '1827', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Paridae', '박새과', 'Poecile', '쇠박새속', 'montanus', '북방쇠박새', '몸길이 12.5cm로 쇠박새와 비슷하나 머리꼭대기는 더욱 짙은 검은색이며, 때로는 갈색을 띠기도 한다. 보통 둘째날개깃에 담색 반점이 있다. 야외에서 외형으로 쇠박새와 구분하기는 어려우나 울음소리로서 구분이 가능하다. 표본으로는 발가락 길이 및 부리의 형태로서 쇠박새와 쉽게 구분된다. 중부 이북에서 분포하는 흔하지 않은 텃새이다. 한국, 시베리아, 중국 북부, 일본에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528888', '12.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('590', '붉은배멋쟁이새', 'Pyrrhula pyrrhula cassinii', 'S.F.Baird', '1869', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Pyrrhula', '멋쟁이새속', 'pyrrhula', '멋쟁이새', '붉은배멋쟁이새는 참새목 되새과에 속하는 소형 조류입니다. 주로 유라시아 대륙의 혼합 숲에서 서식하며, 북부 지역에서는 겨울철에 남쪽으로 이동하기도 합니다. 수컷은 붉고 풍부한 색조를 띄는 가슴과 아랫면을 가지고 있으며, 암컷과 어린 새들은 회갈색의 깃털을 가집니다. 이들은 주로 나무 열매와 씨앗을 먹으며, 과수원에서는 해충으로 여겨질 수 있습니다. 둥지는 나무나 키가 큰 관목에 짓고 한 번에 4~7개의 알을 낳습니다. 번식 시기는 5월 초부터 7월 중순 사이로, 한 시즌에 2~3번의 번식을 합니다. 후에는 나무의 높고 넓은 가지나 성숙한 관목에 둥지를 틀고 살며, 보통 쌍 혹은 가족 단위로 활동합니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528676', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('591', '흰턱제비', 'Delichon lagopodum', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Hirundinidae', '제비과', 'Delichon', '털발제비속', 'lagopodum', '흰턱제비', '흰턱제비는 참새목 제비과에 속하는 조류로, 러시아 북동부, 몽골, 중국 북부의 암석 지대에서 번식한다. 이 새는 미얀마, 라오스, 베트남, 캄보디아에서 겨울을 난다. 흰턱제비는 과거 일반 제비의 아종으로 여겨졌으나, 형태와 소리의 차이로 독립된 종으로 인정받았다. 발이 깃털로 덮여 있는 것이 특징이며, 보존 상태는 관심 필요종으로 분류된다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528680', '13.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('592', '흰참매', 'Accipiter gentilis albidus', 'Menzbier', '1882', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Accipiter', '새매속', 'gentilis', '참매', '매우 넓은 지역에 분포하는 중대형 맹금류로, 수리과에 속한다. 과거에는 Accipiter 속에 포함되었으나, 분자계통학 연구 결과에 따라 현재는 Astur 속으로 분류된다. 유럽 대부분과 아시아 온대 지역의 숲에서 서식하며, 대부분 텃새지만 북부 개체군은 겨울에 남하하여 월동한다. 우리나라에서도 일부 개체가 겨울에 관찰된다.  몸길이는 수컷이 46~63cm, 암컷이 58~69cm이며, 암컷이 수컷보다 크다. 날개 길이는 수컷 89~122cm, 암컷 108~127cm로 성적 이형이 뚜렷하다. 몸빛은 지역과 아종에 따라 다양하지만 대체로 등 쪽은 푸르스름한 회갈색 또는 암회색이며, 가슴과 배 쪽은 흰 바탕에 검은 가로줄 무늬가 있다. 눈썹선(눈 위 흰 줄무늬)은 두드러지며, 북쪽 개체일수록 뚜렷하다. 어린 새는 등 쪽이 갈색이고 배 쪽은 흰색 바탕에 갈색 세로줄이 있다. 아시아의 아종은 거의 흰색부터 거의 검은색에 가까운 등빛까지 다양하며, 북쪽 아종일수록 크고 색이 옅은 경향이 있다.    전 세계적으로 9~10종의 아종이 알려져 있으며, 유럽과 아시아의 대부분에 걸쳐 번식한다. 한반도에서는 드물게 관찰되며, 주로 월동기인 겨울에 발견된다. 혼자 또는 짝을 이루어 생활하며, 번식기에는 강한 텃세를 보인다. 성숙한 숲, 특히 활엽수와 침엽수가 섞인 혼합림에 둥지를 튼다. 먹이는 비둘기, 까마귀, 멧비둘기, 들쥐, 청설모, 토끼 등 중형 조류와 포유류가 주를 이루며, 주로 나무 위에서 잠복하다가 급습해 사냥한다. 도심 인근 숲에서도 서식이 가능하며, 베를린, 함부르크 등 유럽 도심에서도 번식이 확인되었다.    둥지는 나무 높은 곳에 나뭇가지로 튼튼하게 짓고, 한 배에 2~4개의 알을 낳는다. 주로 암컷이 포란하며 수컷이 먹이를 공급한다. 포란 기간은 약 30~35일이며, 새끼는 약 5~6주 후 둥지를 떠난다. 번식 성공률은 먹이 자원과 날씨, 서식지 안정성에 크게 좌우된다.    맹금류 중에서도 높은 사냥 성공률과 뛰어난 순발력으로 유명하며, 유럽을 중심으로 오랜 시간 매사냥용으로 길러져 왔다. 한때 독일, 핀란드, 노르웨이 등에서는 밀렵과 서식지 파괴로 개체수가 감소했으나 현재는 보호종으로 지정되어 개체수가 회복 중이다. 전 세계적으로는 약 100만 마리 이상이 존재하는 것으로 추정된다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528656', '57.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('593', '뿔종다리', 'Galerida cristata coreensis', 'Taczanowski', '1888', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Alaudidae', '종다리과', 'Galerida', '뿔종다리속', 'cristata', '뿔종다리', '뿔종다리는 참새목 종다리과에 속하며, 유라시아와 북아프리카 전역에 분포합니다. 크기는 약 17cm로, 흔히 보이는 논이 없는 검은색으로, 땅에 둥지를 틉니다. 주로 곡물과 씨앗을 먹지만, 곤충도 섭취합니다. 비번식기에도 이동하지 않는 텃새로, 특히 길가나 곡물밭에서 흔히 발견됩니다. 번식 시 3~5개의 알을 낳으며, 두 차례 번식합니다. 주요 소리는 명랑한 ''휘휘휘요''나 ''트위티투'' 등입니다. IUCN에 의해 ''관심대상''으로 분류되며, 주요 서식지는 건조하고 평탄한 개활지입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528664', '17.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('594', '재색멋쟁이새', 'Pyrrhula pyrrhula griseiventris', 'Lafresnaye', '1841', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Fringillidae', '되새과', 'Pyrrhula', '멋쟁이새속', 'pyrrhula', '멋쟁이새', '재색멋쟁이새는 참새목 되새과에 속하는 작은 조류입니다. 성체 수컷은 밝은 빨간색의 가슴과 하체를 가지고 있으며, 암컷과 어린 개체는 회갈색 깃털을 가지고 있습니다. 주로 유럽과 온대 아시아의 혼합림과 공원, 정원에서 서식하며 주로 씨앗과 과일나무의 싹을 먹고 삽니다. 이들은 번식기를 제외하고는 대규모로 무리를 짓지 않고 짝이나 가족 단위로 이동합니다. 많은 북부 개체들은 겨울철에 남쪽으로 이주합니다. 목소리는 약하고 부드러운 휘파람 소리를 내며, 가까운 거리에서만 쉽게 들을 수 있습니다. 안정적인 개체수를 유지하고 있습니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528673', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('595', '툰드라매', 'Falco peregrinus calidus', 'Latham', '1790', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Falconidae', '매과', 'Falco', '매속', 'peregrinus', '매', '툰드라매는 전 세계적으로 가장 빠른 맹금류 중 하나로, 특히 빠른 스피드로 유명하다. 몸길이는 약 34~58cm이며, 날개 길이는 74~120cm에 이른다. 주로 바다나 강변의 높은 절벽에서 번식하며, 도시 지역에서는 높은 건물에서도 발견된다. 툰드라매는 여름철 북극 툰드라에서 번식하며 겨울철에는 남쪽으로 이주한다. 주요 먹이는 중간 크기의 새들로, 도시 생활에서는 비둘기를 주로 사냥한다. 이 종은 20세기 중반 살충제 DDT 때문에 급감하였으나, 이후 보호 노력 덕분에 개체수가 회복되었다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528752', '46.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('596', '휘파람새', 'Horornis canturians', 'Swinhoe', '1860', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Sylviidae', '휘파람새과', 'Horornis', '휘파람새속', 'canturians', '휘파람새', '휘파람새는 참새목 휘파람새과에 속하는 조류로, 한국과 동북아시아에서 발견됩니다. 주로 아침에 활발하며 "폭발적"이고 "트릴" 소리의 울음소리로 유명합니다. 유사한 종으로는 일본휘파람새가 있습니다. 휘파람새는 현재 ''관심 필요'' 단계의 보전 상태로, 개체수는 안정적인 것으로 평가되고 있습니다. 이 새는 넓은 분포를 가지며, 주요 서식지는 중국 동북부입니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528738', '16.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('597', '긴발톱할미새', 'Motacilla tschutschensis taivana', 'Swinhoe', '1863', 'Chordata', '척삭동물문', 'Aves', '조강', 'Passeriformes', '참새목', 'Motacillidae', '할미새과', 'Motacilla', '할미새속', 'tschutschensis', '긴발톱할미새', '긴발톱할미새는 참새목 할미새과에 속하는 작은 조류로, 몸길이는 약 15~16cm입니다. 이 새는 긴 꼬리를 계속 움직이는 것이 특징입니다. 번식기 수컷은 주로 위쪽이 올리브색을 띠고 아래쪽은 노란색입니다. 다른 깃털은 특히 첫겨울 철이나 많은 암컷의 경우 노란색이 흰색으로 희석될 수 있습니다. 부리와 다리는 검은색이며, 발톱이 길고 크게 굽지 않는 것이 특징입니다.    긴발톱할미새는 동팔레아르크와 알래스카에 번식하며, 겨울에는 남아시아와 호주로 이동합니다. 이 새는 주로 물 가까이의 열린 지역인 습초지에 서식하며, 둔덕에 둥지를 틀고 4~8개의 알을 낳습니다. 주로 곤충을 먹고 삽니다.', '영문 Wikipedia(https://en.wikipedia.org/)', 'True', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528730', '15.5');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('598', '검둥오리사촌', 'Melanitta stejnegeri', 'Ridgway', '1887', 'Chordata', '척삭동물문', 'Aves', '조강', 'Anseriformes', '기러기목', 'Anatidae', '오리과', 'Melanitta', '검둥오리속', 'stejnegeri', '검둥오리사촌', '우리나라에서 흔히 월동하는 겨울철새이다. 몸길이 56cm이다. 날 때 둘째날개깃에 크고 뚜렷한 흰무늬가 보인다. 머리와 날개에 흰 점이 있다. 수컷은 온몸이 검은색이고 부리는 황색이나 부리 위의 혹은 검은색이다. 암컷은 부리 위에 혹이 없다. 유라시아, 북아메리카대륙의 아한대부터 한대 지역에서 번식하며 침엽수림의 호소, 암석으로 덮인 작은 도서의 낮은 수목과 풀숲이 우거진 곳에서 서식한다. 월동지에서는 작은 무리를 이루어 해상 생활을 한다. 잠수 능력이 뛰어나 적이 접근할 때, 비상하기보다는 잠수하여 몸을 숨긴다. 둥지는 호반의 풀숲이나 툰드라의 땅 위에 마른풀과 나뭇가지를 이용하여 만든다. 먹이는 주로 동물성이지만, 수초를 먹기도 한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528792', '56.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('599', '긴목갈매기', 'Chroicocephalus genei', 'Brème', '1839', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Laridae', '갈매기과', 'Chroicocephalus', '붉은부리갈매기속', 'genei', '긴목갈매기', '몸길이 42~44cm, 날개 폭 102~110cm이다. 이 새는 붉은부리갈매기와 유사하지만, 부리가 붉은색으로 가늘고 목이 길며, 가슴과 배가 엷은 핑크색을 띤다. 가는 부리보다 긴 목이 두드러져 긴목갈매기로 불린다. 염수호, 연안 석호, 내륙 염수호 등지에서 알을 낳으며 드물게 연안가에서도 번식한다. 터키, 스페인, 그리스, 프랑스에 분포한다. 대부분 철새로 인도나 북아프리카에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528795', '43.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('600', '넓적부리도요', 'Calidris pygmaea', 'Linnaeus', '1758', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'pygmaea', '넓적부리도요', '도요목 도요과에 속하는 조류로 국내에는 봄과 가을에 극히 적은 수가 통과하는 나그네새이다[멸종위기 야생생물 I급]. 몸길이 15cm. 부리는 색이 검고, 끝이 주걱 모양으로 생긴 것이 특징이다. 여름에는 얼굴, 가슴, 등이 붉은 갈색이며, 가슴 옆에는 어두운 얼룩무늬가 있다. 겨울에는 머리가 엷은 회색으로 변하며, 눈썹선과 가슴은 모두 흰색으로 변한다. 다리는 검은색이다. 해안의 간척지, 염전, 소택지, 하구, 해안 등 주로 모래가 섞인 갯벌을 선호한다. 갯지렁이, 작은 새우류와 같은 해양무척추동물을 주로 잡아먹는다. 산란기는 6월하순~7월 중순이며 한배 산란 수는 4개이다. 서해안과 남동 해안에 도래하고 세계적으로는 동북 시베리아 북극권과 알래스카에 분포한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528817', '15.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('601', '송곳부리도요', 'Calidris falcinellus', 'Pontoppidan', '1763', 'Chordata', '척삭동물문', 'Aves', '조강', 'Charadriiformes', '도요목', 'Scolopacidae', '도요과', 'Calidris', '붉은가슴도요속', 'falcinellus', '송곳부리도요', '도요목 도요과에 속하는 조류로 국내에는 봄과 가을에 드물게 통과하는 나그네새이다. 몸길이 17cm. 암수 같은 빛깔이다. 여름깃은 머리 위와 등 쪽이 갈색이다. 허리와 꽁지위덮깃 및 가운데 꽁지깃도 검은색에 가깝다. 배 쪽은 흰색이며, 목과 앞목과 겨드랑이에는 갈색의 줄무늬가 있다. 겨울깃은 깃의 가장자리가 회갈색이고 각 깃축은 검게 보인다. 갯벌, 강 하구, 물 고인 논에서 단독 또는 2~5마리씩 작은 떼를 지어 민물도요, 좀도요 등과 섞여서 함께 서식한다. 번식기에는 접시 모양의 둥지를 짓고 알자리에는 마른풀이나 마른 잎을 깐다. 한배에 4개 산란한다. 6월상순~7월하순에 번식한다. 갑각류, 조개류, 환형동물 등과 식물의 열매도 먹는다. 스칸디나비아반도 북부와 러시아 서북부, 시베리아 동북부에서 번식하고, 중동, 인도, 동남아시아, 호주에서 월동한다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528819', '17.0');
INSERT INTO bird (id, korean_name, scientific_name, scientific_author, scientific_year, phylum_eng, phylum_kor, class_eng, class_kor, order_eng, order_kor, family_eng, family_kor, genus_eng, genus_kor, species_eng, species_kor, description, description_source, description_is_ai_generated, nibr_url, body_length_cm) VALUES ('602', '항라머리검독수리', 'Clanga clanga', 'Pallas', '1811', 'Chordata', '척삭동물문', 'Aves', '조강', 'Falconiformes', '매목', 'Accipitridae', '수리과', 'Clanga', '항라머리검독수리속', 'clanga', '항라머리검독수리', '항라머리검독수리는 「멸종위기 야생생물 Ⅱ급」의 수리과 조류로 드문 겨울철새이다. 흑갈색의 대형 맹금류로 근거리에서는 머리와 목에 다소 흰색의 반점을 볼 수 있다. 산림과 농경지 감소와 밀렵 등으로 점차 개체 수가 줄고 있으며, 현재 한국적색목록에 멸종위기범주로 평가되어 있다.', '국립생물자원관 한반도의 생물다양성(https://species.nibr.go.kr/)', 'False', 'https://species.nibr.go.kr/home/mainHome.do?cont_link=009&subMenu=009002&contCd=009002&pageMode=view&ktsn=120000528834', '66.5');
